contracts/protocol/LendingMarket.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.19;

// interfaces
import {ILendingMarket} from "./interfaces/ILendingMarket.sol";
// libraries
import {Contracts} from "./libraries/Contracts.sol";
import {Constants} from "./libraries/Constants.sol";
import {OrderActionLogic} from "./libraries/logics/OrderActionLogic.sol";
import {OrderBookLogic} from "./libraries/logics/OrderBookLogic.sol";
import {OrderReaderLogic} from "./libraries/logics/OrderReaderLogic.sol";
import {RoundingUint256} from "./libraries/math/RoundingUint256.sol";
import {FilledOrder, PartiallyFilledOrder} from "./libraries/OrderBookLib.sol";
// mixins
import {MixinAddressResolver} from "./mixins/MixinAddressResolver.sol";
// types
import {ProtocolTypes} from "./types/ProtocolTypes.sol";
// utils
import {Pausable} from "./utils/Pausable.sol";
import {Proxyable} from "./utils/Proxyable.sol";
// storages
import {LendingMarketStorage as Storage, ItayoseLog} from "./storages/LendingMarketStorage.sol";

import "hardhat/console.sol";

/**
 * @notice Implements the module that allows order book participants to execute/cancel/unwind orders.
 *
 * For updates, this contract is basically called from `LendingMarketController.sol`instead of being called
 * directly by the user.
 *
 * @dev Open orders is stored in structured red-black trees and doubly linked lists in each node.
 */
contract LendingMarket is ILendingMarket, MixinAddressResolver, Pausable, Proxyable {
    using RoundingUint256 for uint256;

    /// @dev Used for minimum reliable amount in base currency for block unit price
    uint256 immutable MINIMUM_RELIABLE_AMOUNT_IN_BASE_CURRENCY;

    /**
     * @notice Modifier to make a function callable only by order maker.
     * @param _orderBookId The order book id
     * @param _user User's address
     * @param _orderId Market order id
     */
    modifier onlyMaker(
        uint8 _orderBookId,
        address _user,
        uint48 _orderId
    ) {
        (, , , address maker, , , ) = getOrder(_orderBookId, _orderId);
        if (maker == address(0)) revert NoOrderExists();
        if (_user != maker) revert CallerNotMaker();
        _;
    }

    /**
     * @notice Modifier to check if the market is opened.
     * @param _orderBookId The order book id
     */
    modifier ifOpened(uint8 _orderBookId) {
        if (!isOpened(_orderBookId)) revert MarketNotOpened();
        _;
    }

    /**
     * @notice Modifier to check if the market is under the Itayose period.
     * @param _orderBookId The order book id
     */
    modifier ifItayosePeriod(uint8 _orderBookId) {
        if (!isItayosePeriod(_orderBookId)) revert NotItayosePeriod();
        _;
    }

    /**
     * @notice Modifier to check if the market is not under the Itayose period.
     * @param _orderBookId The order book id
     */
    modifier ifNotItayosePeriod(uint8 _orderBookId) {
        if (isItayosePeriod(_orderBookId)) revert AlreadyItayosePeriod();
        _;
    }

    /**
     * @notice Modifier to check if the market is under the pre-order period.
     * @param _orderBookId The order book id
     */
    modifier ifPreOrderPeriod(uint8 _orderBookId) {
        if (!isPreOrderPeriod(_orderBookId)) revert NotPreOrderPeriod();
        _;
    }

    /**
     * @notice Contract constructor function.
     * @param _minimumReliableAmount The minimum reliable amount the base currency for calculating block unit price
     */
    constructor(uint256 _minimumReliableAmount) {
        MINIMUM_RELIABLE_AMOUNT_IN_BASE_CURRENCY = _minimumReliableAmount;
    }

    /**
     * @notice Initializes the contract.
     * @dev Function is invoked by the proxy contract when the contract is added to the ProxyController.
     * @param _resolver The address of the Address Resolver contract
     * @param _ccy The main currency for the order book
     */
    function initialize(
        address _resolver,
        bytes32 _ccy,
        uint256 _orderFeeRate,
        uint256 _cbLimitRange
    ) public initializer onlyBeacon {
        registerAddressResolver(_resolver);
        Storage.slot().ccy = _ccy;

        OrderBookLogic.updateOrderFeeRate(_orderFeeRate);
        OrderBookLogic.updateCircuitBreakerLimitRange(_cbLimitRange);

        buildCache();
    }

    // @inheritdoc MixinAddressResolver
    function requiredContracts() public pure override returns (bytes32[] memory contracts) {
        contracts = new bytes32[](2);
        contracts[0] = Contracts.CURRENCY_CONTROLLER;
        contracts[1] = Contracts.LENDING_MARKET_CONTROLLER;
    }

    /**
     * @notice Gets if the market is ready.
     * @param _orderBookId The order book id
     * @return The boolean if the market is ready or not
     */
    function isReady(uint8 _orderBookId) public view override returns (bool) {
        return OrderBookLogic.isReady(_orderBookId);
    }

    /**
     * @notice Gets if the market is matured.
     * @param _orderBookId The order book id
     * @return The boolean if the market is matured or not
     */
    function isMatured(uint8 _orderBookId) public view override returns (bool) {
        return OrderBookLogic.isMatured(_orderBookId);
    }

    /**
     * @notice Gets if the market is opened.
     * @param _orderBookId The order book id
     * @return The boolean if the market is opened or not
     */
    function isOpened(uint8 _orderBookId) public view override returns (bool) {
        return OrderBookLogic.isOpened(_orderBookId);
    }

    /**
     * @notice Gets if the market is under the Itayose period.
     * @param _orderBookId The order book id
     * @return The boolean if the market is under the Itayose period.
     */
    function isItayosePeriod(uint8 _orderBookId) public view returns (bool) {
        return OrderBookLogic.isItayosePeriod(_orderBookId);
    }

    /**
     * @notice Gets if the market is under the pre-order period.
     * @param _orderBookId The order book id
     * @return The boolean if the market is under the pre-order period.
     */
    function isPreOrderPeriod(uint8 _orderBookId) public view override returns (bool) {
        return OrderBookLogic.isPreOrderPeriod(_orderBookId);
    }

    // @inheritdoc Proxyable
    function getRevision() external pure override returns (uint256) {
        return 0x2;
    }

    /**
     * @notice Gets the minimum reliable amount in base currency for calculating block unit price.
     * @return The minimum reliable amount in base currency
     */
    function minimumReliableAmountInBaseCurrency() external view override returns (uint256) {
        return MINIMUM_RELIABLE_AMOUNT_IN_BASE_CURRENCY;
    }

    /**
     * @notice Gets the order book detail.
     * @param _orderBookId The order book id
     * @return ccy The currency of the order book
     * @return maturity The maturity of the order book
     * @return openingDate The opening date of the order book
     * @return preOpeningDate The pre-opening date of the order book
     */
    function getOrderBookDetail(
        uint8 _orderBookId
    )
        public
        view
        override
        returns (bytes32 ccy, uint256 maturity, uint256 openingDate, uint256 preOpeningDate)
    {
        return OrderBookLogic.getOrderBookDetail(_orderBookId);
    }

    /**
     * @notice Gets unit price Thresholds by CircuitBreaker.
     * @param _orderBookId The order book id
     * @return maxLendUnitPrice The maximum unit price for lending
     * @return minBorrowUnitPrice The minimum unit price for borrowing
     */
    function getCircuitBreakerThresholds(
        uint8 _orderBookId
    ) external view override returns (uint256 maxLendUnitPrice, uint256 minBorrowUnitPrice) {
        return OrderBookLogic.getCircuitBreakerThresholds(_orderBookId);
    }

    /**
     * @notice Gets the best price for lending.
     * @param _orderBookId The order book id
     * @return The best price for lending
     */
    function getBestLendUnitPrice(uint8 _orderBookId) public view override returns (uint256) {
        return OrderBookLogic.getBestLendUnitPrice(_orderBookId);
    }

    /**
     * @notice Gets the best prices for lending.
     * @return The array of the best price for lending
     */
    function getBestLendUnitPrices(
        uint8[] calldata _orderBookIds
    ) external view override returns (uint256[] memory) {
        return OrderBookLogic.getBestLendUnitPrices(_orderBookIds);
    }

    /**
     * @notice Gets the best price for borrowing.
     * @param _orderBookId The order book id
     * @return The best price for borrowing
     */
    function getBestBorrowUnitPrice(uint8 _orderBookId) public view override returns (uint256) {
        return OrderBookLogic.getBestBorrowUnitPrice(_orderBookId);
    }

    /**
     * @notice Gets the best prices for borrowing.
     * @return The array of the best price for borrowing
     */
    function getBestBorrowUnitPrices(
        uint8[] calldata _orderBookIds
    ) external view override returns (uint256[] memory) {
        return OrderBookLogic.getBestBorrowUnitPrices(_orderBookIds);
    }

    /**
     * @notice Gets the market unit price
     * @param _orderBookId The order book id
     * @return The market unit price
     */
    function getMarketUnitPrice(uint8 _orderBookId) external view override returns (uint256) {
        return OrderBookLogic.getMarketUnitPrice(_orderBookId);
    }

    /**
     * @notice Gets the block timestamp of the last filled order.
     * @param _orderBookId The order book id
     * @return The block number
     */
    function getLastOrderTimestamp(uint8 _orderBookId) external view override returns (uint48) {
        return OrderBookLogic.getLastOrderTimestamp(_orderBookId);
    }

    /**
     * @notice Gets the block unit price history
     * @param _orderBookId The order book id
     * @return unitPrices The array of the block unit price
     * @return timestamp Timestamp of the last block unit price
     */
    function getBlockUnitPriceHistory(
        uint8 _orderBookId
    ) external view override returns (uint256[] memory unitPrices, uint48 timestamp) {
        return OrderBookLogic.getBlockUnitPriceHistory(_orderBookId);
    }

    /**
     * @notice Gets the block unit price average.
     * @param _orderBookId The order book id
     * @param _count Count of data used for averaging
     * @return The block unit price average
     */
    function getBlockUnitPriceAverage(
        uint8 _orderBookId,
        uint256 _count
    ) external view override returns (uint256) {
        return OrderBookLogic.getBlockUnitPriceAverage(_orderBookId, _count);
    }

    /**
     * @notice Gets the order book of borrow orders.
     * This function supports pagination. If a unit price is specified in `_start`,
     * the orders are returned for the _limit from there. The unit price that can be
     * set to `_start` of the next data fetching is set to the return value, `next`.
     * If `_start` is 0, this function returns from the first order.
     * @param _orderBookId The order book id
     * @param _start The starting unit price to get order book
     * @param _limit The max limit for getting unit prices
     * @return unitPrices The array of order unit prices
     * @return amounts The array of order amounts
     * @return quantities The array of order quantities
     * @return next The next starting unit price to get order book
     */
    function getBorrowOrderBook(
        uint8 _orderBookId,
        uint256 _start,
        uint256 _limit
    )
        external
        view
        override
        returns (
            uint256[] memory unitPrices,
            uint256[] memory amounts,
            uint256[] memory quantities,
            uint256 next
        )
    {
        return OrderBookLogic.getBorrowOrderBook(_orderBookId, _start, _limit);
    }

    /**
     * @notice Gets the order book of lend orders.
     * This function supports pagination. If a unit price is specified in `_start`,
     * the orders are returned for the _limit from there. The unit price that can be
     * set to `_start` of the next data fetching is set to the return value, `next`.
     * If `_start` is 0, this function returns from the first order.
     * @param _orderBookId The order book id
     * @param _start The starting unit price to get order book
     * @param _limit The max limit for getting unit prices
     * @return unitPrices The array of order unit prices
     * @return amounts The array of order amounts
     * @return quantities The array of order quantities
     * @return next The next starting unit price to get order book
     */
    function getLendOrderBook(
        uint8 _orderBookId,
        uint256 _start,
        uint256 _limit
    )
        external
        view
        override
        returns (
            uint256[] memory unitPrices,
            uint256[] memory amounts,
            uint256[] memory quantities,
            uint256 next
        )
    {
        return OrderBookLogic.getLendOrderBook(_orderBookId, _start, _limit);
    }

    /**
     * @notice Gets the estimation of the Itayose process.
     * @param _orderBookId The order book id
     * @return openingUnitPrice The opening price when Itayose is executed
     * @return lastLendUnitPrice The price of the last lend order filled by Itayose.
     * @return lastBorrowUnitPrice The price of the last borrow order filled by Itayose.
     * @return totalOffsetAmount The total amount of the orders filled by Itayose.
     */
    function getItayoseEstimation(
        uint8 _orderBookId
    )
        external
        view
        returns (
            uint256 openingUnitPrice,
            uint256 lastLendUnitPrice,
            uint256 lastBorrowUnitPrice,
            uint256 totalOffsetAmount
        )
    {
        return OrderBookLogic.getItayoseEstimation(_orderBookId);
    }

    /**
     * @notice Gets the current market maturity.
     * @param _orderBookId The order book id
     * @return maturity The market maturity
     */
    function getMaturity(uint8 _orderBookId) public view override returns (uint256 maturity) {
        return Storage.slot().orderBooks[_orderBookId].maturity;
    }

    /**
     * @notice Gets the order book maturities.
     * @return maturities The array of maturity
     */
    function getMaturities(
        uint8[] calldata _orderBookIds
    ) external view override returns (uint256[] memory maturities) {
        return OrderBookLogic.getMaturities(_orderBookIds);
    }

    /**
     * @notice Gets the market currency.
     * @return currency The market currency
     */
    function getCurrency() external view override returns (bytes32 currency) {
        return Storage.slot().ccy;
    }

    /**
     * @notice Gets the order fee rate
     * @return The order fee rate received by protocol
     */
    function getOrderFeeRate() external view override returns (uint256) {
        return Storage.slot().orderFeeRate;
    }

    /**
     * @notice Gets the limit range in unit price for the circuit breaker
     * @return The limit range in unit price for the circuit breaker
     */
    function getCircuitBreakerLimitRange() external view override returns (uint256) {
        return Storage.slot().circuitBreakerLimitRange;
    }

    /**
     * @notice Gets the market opening date.
     * @param _orderBookId The order book id
     * @return openingDate The market opening date
     */
    function getOpeningDate(uint8 _orderBookId) public view override returns (uint256 openingDate) {
        return Storage.slot().orderBooks[_orderBookId].openingDate;
    }

    /**
     * @notice Gets the market itayose logs.
     * @param _maturity The market maturity
     * @return ItayoseLog of the market
     */
    function getItayoseLog(uint256 _maturity) external view override returns (ItayoseLog memory) {
        return Storage.slot().itayoseLogs[_maturity];
    }

    /**
     * @notice Gets the market order from the order book.
     * @param _orderBookId The order book id
     * @param _orderId The market order id
     * @return side Order position type, Borrow or Lend
     * @return unitPrice Amount of interest unit price
     * @return maturity The maturity of the selected order
     * @return maker The order maker
     * @return amount Order amount
     * @return timestamp Timestamp when the order was created
     * @return isPreOrder The boolean if the order is a pre-order.
     */
    function getOrder(
        uint8 _orderBookId,
        uint48 _orderId
    )
        public
        view
        override
        returns (
            ProtocolTypes.Side side,
            uint256 unitPrice,
            uint256 maturity,
            address maker,
            uint256 amount,
            uint256 timestamp,
            bool isPreOrder
        )
    {
        return OrderReaderLogic.getOrder(_orderBookId, _orderId);
    }

    /**
     * @notice Calculates and gets the active and inactive amounts from the user orders of lending deals.
     * @param _orderBookId The order book id
     * @param _user User's address
     * @return activeAmount The total amount of active order on the order book
     * @return inactiveAmount The total amount of inactive orders filled on the order book
     * @return inactiveFutureValue The total future value amount of inactive orders filled on the order book
     * @return maturity The maturity of market that orders were placed.
     */
    function getTotalAmountFromLendOrders(
        uint8 _orderBookId,
        address _user
    )
        external
        view
        override
        returns (
            uint256 activeAmount,
            uint256 inactiveAmount,
            uint256 inactiveFutureValue,
            uint256 maturity
        )
    {
        return OrderReaderLogic.getTotalAmountFromLendOrders(_orderBookId, _user);
    }

    /**
     * @notice Calculates and gets the active and inactive amounts from the user orders of borrowing deals.
     * @param _orderBookId The order book id
     * @param _user User's address
     * @return activeAmount The total amount of active order on the order book
     * @return inactiveAmount The total amount of inactive orders filled on the order book
     * @return inactiveFutureValue The total future value amount of inactive orders filled on the order book
     * @return maturity The maturity of market that orders were placed.
     */
    function getTotalAmountFromBorrowOrders(
        uint8 _orderBookId,
        address _user,
        uint256 _minUnitPrice
    )
        external
        view
        override
        returns (
            uint256 activeAmount,
            uint256 inactiveAmount,
            uint256 inactiveFutureValue,
            uint256 maturity
        )
    {
        return OrderReaderLogic.getTotalAmountFromBorrowOrders(_orderBookId, _user, _minUnitPrice);
    }

    /**
     * @notice Gets active and inactive order IDs in the lending order book.
     * @param _orderBookId The order book id
     * @param _user User's address
     */
    function getLendOrderIds(
        uint8 _orderBookId,
        address _user
    )
        external
        view
        override
        returns (uint48[] memory activeOrderIds, uint48[] memory inActiveOrderIds)
    {
        return OrderReaderLogic.getLendOrderIds(_orderBookId, _user);
    }

    /**
     * @notice Gets active and inactive order IDs in the borrowing order book.
     * @param _orderBookId The order book id
     * @param _user User's address
     */
    function getBorrowOrderIds(
        uint8 _orderBookId,
        address _user
    )
        external
        view
        override
        returns (uint48[] memory activeOrderIds, uint48[] memory inActiveOrderIds)
    {
        return OrderReaderLogic.getBorrowOrderIds(_orderBookId, _user);
    }

    /**
     * @notice Calculates the amount to be filled when executing an order in the order book.
     * @param _orderBookId The order book id
     * @param _side Order position type, Borrow or Lend
     * @param _amount Amount of funds the user wants to borrow/lend
     * @param _unitPrice Unit price user want to borrow/lend
     * @return lastUnitPrice The last unit price that is filled on the order book
     * @return filledAmount The amount that is filled on the order book
     * @return filledAmountInFV The amount in the future value that is filled on the order book
     * @return orderFeeInFV The order fee amount in the future value
     * @return placedAmount The amount that is placed to the order book
     */
    function calculateFilledAmount(
        uint8 _orderBookId,
        ProtocolTypes.Side _side,
        uint256 _amount,
        uint256 _unitPrice
    )
        external
        view
        override
        returns (
            uint256 lastUnitPrice,
            uint256 filledAmount,
            uint256 filledAmountInFV,
            uint256 orderFeeInFV,
            uint256 placedAmount
        )
    {
        console.log("LendingMarket.calculateFilledAmount");
        return OrderReaderLogic.calculateFilledAmount(_orderBookId, _side, _amount, _unitPrice);
    }

    /**
     * @notice Calculates the amount to be filled when executing an order in the order book from the future value.
     * @param _orderBookId The order book id
     * @param _side Order position type, Borrow or Lend
     * @param _amountInFV Amount of funds the user wants to borrow/lend in future value
     * @return lastUnitPrice The last unit price that is filled on the order book
     * @return filledAmount The amount that is filled on the order book
     * @return filledAmountInFV The amount in the future value that is filled on the order book
     * @return orderFeeInFV The order fee amount in the future value
     */
    function calculateFilledAmountFromFV(
        uint8 _orderBookId,
        ProtocolTypes.Side _side,
        uint256 _amountInFV
    )
        external
        view
        override
        returns (
            uint256 lastUnitPrice,
            uint256 filledAmount,
            uint256 filledAmountInFV,
            uint256 orderFeeInFV
        )
    {
        return OrderReaderLogic.calculateFilledAmountFromFV(_orderBookId, _side, _amountInFV);
    }

    /**
     * @notice Creates a new order book.
     * @param _maturity The initial maturity of the order book
     * @param _openingDate The timestamp when the order book opens
     * @param _preOpeningDate The timestamp when the order book pre-opens
     */
    function createOrderBook(
        uint256 _maturity,
        uint256 _openingDate,
        uint256 _preOpeningDate
    ) external override onlyLendingMarketController returns (uint8 orderBookId) {
        return OrderBookLogic.createOrderBook(_maturity, _openingDate, _preOpeningDate);
    }

    function executeAutoRoll(
        uint8 _maturedOrderBookId,
        uint8 _newNearestOrderBookId,
        uint256 _autoRollUnitPrice
    ) external override onlyLendingMarketController {
        OrderBookLogic.executeAutoRoll(
            _maturedOrderBookId,
            _newNearestOrderBookId,
            _autoRollUnitPrice
        );
    }

    /**
     * @notice Cancels the order.
     * @param _orderBookId The order book id
     * @param _user User address
     * @param _orderId Market order id
     */
    function cancelOrder(
        uint8 _orderBookId,
        address _user,
        uint48 _orderId
    )
        external
        override
        whenNotPaused
        onlyLendingMarketController
        onlyMaker(_orderBookId, _user, _orderId)
        ifNotItayosePeriod(_orderBookId)
    {
        OrderActionLogic.cancelOrder(_orderBookId, _user, _orderId);
    }

    /**
     * @notice Cleans up own orders to remove order ids that are already filled on the order book.
     * @dev The order list per user is not updated in real-time when an order is filled.
     * This function removes the filled order from that order list per user to reduce gas costs
     * for lazy evaluation if the collateral is enough or not.
     *
     * @param _user User address
     * @return activeLendOrderCount The total amount of active lend order on the order book
     * @return activeBorrowOrderCount The total amount of active borrow order on the order book
     * @return removedLendOrderFutureValue The total FV amount of the removed lend order amount from the order book
     * @return removedBorrowOrderFutureValue The total FV amount of the removed borrow order amount from the order book
     * @return removedLendOrderAmount The total PV amount of the removed lend order amount from the order book
     * @return removedBorrowOrderAmount The total PV amount of the removed borrow order amount from the order book
     * @return maturity The maturity of the removed orders
     */
    function cleanUpOrders(
        uint8 _orderBookId,
        address _user
    )
        external
        override
        onlyLendingMarketController
        returns (
            uint256 activeLendOrderCount,
            uint256 activeBorrowOrderCount,
            uint256 removedLendOrderFutureValue,
            uint256 removedBorrowOrderFutureValue,
            uint256 removedLendOrderAmount,
            uint256 removedBorrowOrderAmount,
            uint256 maturity
        )
    {
        return OrderActionLogic.cleanUpOrders(_orderBookId, _user);
    }

    /**
     * @notice Executes an order. Takes orders if the order is matched,
     * and places new order if not match it.
     * @param _orderBookId The order book id
     * @param _side Order position type, Borrow or Lend
     * @param _user User's address
     * @param _amount Amount of funds the user wants to borrow/lend
     * @param _unitPrice Unit price user wish to borrow/lend
     * @return filledOrder User's Filled order of the user
     * @return partiallyFilledOrder Partially filled order on the order book
     */
    function executeOrder(
        uint8 _orderBookId,
        ProtocolTypes.Side _side,
        address _user,
        uint256 _amount,
        uint256 _unitPrice
    )
        external
        override
        whenNotPaused
        onlyLendingMarketController
        ifOpened(_orderBookId)
        returns (
            FilledOrder memory filledOrder,
            PartiallyFilledOrder memory partiallyFilledOrder,
            uint256 feeInFV
        )
    {
        return
            OrderActionLogic.executeOrder(
                _orderBookId,
                _side,
                _user,
                _amount,
                _unitPrice,
                currencyController().convertFromBaseCurrency(
                    Storage.slot().ccy,
                    MINIMUM_RELIABLE_AMOUNT_IN_BASE_CURRENCY
                )
            );
    }

    /**
     * @notice Executes a pre-order. A pre-order will only be accepted from 168 hours (7 days) to 1 hour
     * before the market opens (Pre-order period). At the end of this period, Itayose will be executed.
     *
     * @param _orderBookId The order book id
     * @param _side Order position type, Borrow or Lend
     * @param _amount Amount of funds the maker wants to borrow/lend
     * @param _unitPrice Unit price taker wish to borrow/lend
     */
    function executePreOrder(
        uint8 _orderBookId,
        ProtocolTypes.Side _side,
        address _user,
        uint256 _amount,
        uint256 _unitPrice
    ) external override whenNotPaused onlyLendingMarketController ifPreOrderPeriod(_orderBookId) {
        OrderActionLogic.executePreOrder(_orderBookId, _side, _user, _amount, _unitPrice);
    }

    /**
     * @notice Unwinds lending or borrowing positions by a specified future value amount.
     * @param _orderBookId The order book id
     * @param _side Order position type, Borrow or Lend
     * @param _user User's address
     * @param _futureValue Amount of future value unwound
     * @return filledOrder User's Filled order of the user
     * @return partiallyFilledOrder Partially filled order
     */
    function unwindPosition(
        uint8 _orderBookId,
        ProtocolTypes.Side _side,
        address _user,
        uint256 _futureValue
    )
        external
        override
        whenNotPaused
        onlyLendingMarketController
        ifOpened(_orderBookId)
        returns (
            FilledOrder memory filledOrder,
            PartiallyFilledOrder memory partiallyFilledOrder,
            uint256 feeInFV
        )
    {
        return
            OrderActionLogic.unwindPosition(
                _orderBookId,
                _side,
                _user,
                _futureValue,
                currencyController().convertFromBaseCurrency(
                    Storage.slot().ccy,
                    MINIMUM_RELIABLE_AMOUNT_IN_BASE_CURRENCY
                )
            );
    }

    /**
     * @notice Executes Itayose to aggregate pre-orders and determine the opening unit price.
     * After this action, the market opens.
     * @dev If the opening date had already passed when this contract was created, this Itayose need not be executed.
     * @param _orderBookId The order book id
     * @return openingUnitPrice The opening price when Itayose is executed
     * @return totalOffsetAmount The total filled amount when Itayose is executed
     * @return openingDate The timestamp when the market opens
     * @return partiallyFilledLendingOrder Partially filled lending order on the order book
     * @return partiallyFilledBorrowingOrder Partially filled borrowing order on the order book
     */
    function executeItayoseCall(
        uint8 _orderBookId
    )
        external
        override
        whenNotPaused
        onlyLendingMarketController
        ifItayosePeriod(_orderBookId)
        returns (
            uint256 openingUnitPrice,
            uint256 totalOffsetAmount,
            uint256 openingDate,
            PartiallyFilledOrder memory partiallyFilledLendingOrder,
            PartiallyFilledOrder memory partiallyFilledBorrowingOrder
        )
    {
        return OrderBookLogic.executeItayoseCall(_orderBookId);
    }

    /**
     * @notice Updates the order fee rate
     * @param _orderFeeRate The order fee rate received by protocol
     */
    function updateOrderFeeRate(
        uint256 _orderFeeRate
    ) external override onlyLendingMarketController {
        OrderBookLogic.updateOrderFeeRate(_orderFeeRate);
    }

    /**
     * @notice Updates the auto-roll fee rate
     * @param _cbLimitRange The circuit breaker limit range
     */
    function updateCircuitBreakerLimitRange(
        uint256 _cbLimitRange
    ) external override onlyLendingMarketController {
        OrderBookLogic.updateCircuitBreakerLimitRange(_cbLimitRange);
    }

    /**
     * @notice Pauses the lending market.
     */
    function pause() external override onlyLendingMarketController {
        _pause();
    }

    /**
     * @notice Unpauses the lending market.
     */
    function unpause() external override onlyLendingMarketController {
        _unpause();
    }
}

contracts/protocol/LendingMarketControll.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.19;

// dependencies
import {ReentrancyGuard} from "../dependencies/openzeppelin/security/ReentrancyGuard.sol";
import {EnumerableSet} from "../dependencies/openzeppelin/utils/structs/EnumerableSet.sol";
import {Multicall} from "../dependencies/openzeppelin/utils/Multicall.sol";
// interfaces
import {ILendingMarketController} from "./interfaces/ILendingMarketController.sol";
import {ILendingMarket} from "./interfaces/ILendingMarket.sol";
// libraries
import {Contracts} from "./libraries/Contracts.sol";
import {FilledOrder, PartiallyFilledOrder} from "./libraries/OrderBookLib.sol";
import {FundManagementLogic} from "./libraries/logics/FundManagementLogic.sol";
import {LendingMarketOperationLogic} from "./libraries/logics/LendingMarketOperationLogic.sol";
import {LendingMarketUserLogic} from "./libraries/logics/LendingMarketUserLogic.sol";
import {LiquidationLogic} from "./libraries/logics/LiquidationLogic.sol";
// mixins
import {MixinAccessControl} from "./mixins/MixinAccessControl.sol";
import {MixinAddressResolver} from "./mixins/MixinAddressResolver.sol";
import {MixinLendingMarketConfiguration} from "./mixins/MixinLendingMarketConfiguration.sol";
// types
import {ProtocolTypes} from "./types/ProtocolTypes.sol";
// utils
import {Proxyable} from "./utils/Proxyable.sol";
import {LockAndMsgSender} from "./utils/LockAndMsgSender.sol";
// storages
import {LendingMarketControllerStorage as Storage, ZCTokenInfo, TerminationCurrencyCache} from "./storages/LendingMarketControllerStorage.sol";

/**
 * @notice Implements the module to manage separated lending market contracts per currency.
 *
 * This contract also works as a factory contract that can deploy (start) a new lending market & order book
 * for selected currency and maturity and has the calculation logic for the following user's funds in addition.
 * - Present Value(PV)
 * - Future Value(FV)
 * - Genesis Value(GV)
 *
 * Once the order book is created, it will be rotated and reused once it reaches its maturity date. At the time of rotation,
 * a new maturity date is set and the compound factor is updated.
 *
 * The users mainly call this contract to execute orders to lend or borrow funds.
 */
contract LendingMarketController is
    ILendingMarketController,
    MixinLendingMarketConfiguration,
    MixinAccessControl,
    MixinAddressResolver,
    ReentrancyGuard,
    Proxyable,
    LockAndMsgSender,
    Multicall
{
    using EnumerableSet for EnumerableSet.Bytes32Set;

    /**
     * @notice Modifier to check if there is an order book in the maturity.
     * @param _ccy Currency name in bytes32
     * @param _maturity The maturity of the order book
     */
    modifier ifValidMaturity(bytes32 _ccy, uint256 _maturity) {
        if (!isValidMaturity(_ccy, _maturity)) revert InvalidMaturity();
        _;
    }

    /**
     * @notice Modifier to check if the protocol is active.
     */
    modifier ifActive() {
        if (isTerminated()) revert MarketTerminated();
        _;
    }

    /**
     * @notice Modifier to check if the protocol is inactive.
     */
    modifier ifInactive() {
        if (!isTerminated()) revert NotTerminated();
        _;
    }

    /**
     * @notice Initializes the contract.
     * @dev Function is invoked by the proxy contract when the contract is added to the ProxyController.
     * @param _owner The address of the contract owner
     * @param _resolver The address of the Address Resolver contract
     * @param _marketBasePeriod The base period for market maturity
     */
    function initialize(
        address _owner,
        address _resolver,
        uint256 _marketBasePeriod
    ) public initializer onlyProxy {
        Storage.slot().marketBasePeriod = _marketBasePeriod;
        MixinLendingMarketConfiguration._initialize(_owner);
        MixinAccessControl._setupInitialRoles(_owner);
        registerAddressResolver(_resolver);
    }

    // @inheritdoc MixinAddressResolver
    function requiredContracts() public pure override returns (bytes32[] memory contracts) {
        contracts = new bytes32[](5);
        contracts[0] = Contracts.BEACON_PROXY_CONTROLLER;
        contracts[1] = Contracts.CURRENCY_CONTROLLER;
        contracts[2] = Contracts.GENESIS_VALUE_VAULT;
        contracts[3] = Contracts.RESERVE_FUND;
        contracts[4] = Contracts.TOKEN_VAULT;
    }

    /**
     * @notice Gets if the maturity is valid.
     * @param _ccy Currency name in bytes32
     * @param _maturity The maturity of the order book
     * @return The boolean if the maturity is valid
     */
    function isValidMaturity(bytes32 _ccy, uint256 _maturity) public view override returns (bool) {
        return Storage.slot().maturityOrderBookIds[_ccy][_maturity] != 0;
    }

    /**
     * @notice Gets if the protocol has not been terminated.
     * @return The boolean if the protocol has not been terminated
     */
    function isTerminated() public view override returns (bool) {
        return Storage.slot().terminationDate > 0;
    }

    /**
     * @notice Gets if the user needs to redeem the funds.
     * @return The boolean if the user needs to redeem the funds
     */
    function isRedemptionRequired(address _user) external view override returns (bool) {
        return isTerminated() && !Storage.slot().isRedeemed[_user];
    }

    // @inheritdoc Proxyable
    function getRevision() external pure override returns (uint256) {
        return 0x2;
    }

    /**
     * @notice Gets the base period for market maturity
     * @return The base period
     */
    function getMarketBasePeriod() external view override returns (uint256) {
        return Storage.slot().marketBasePeriod;
    }

    /**
     * @notice Gets the date at the emergency termination.
     * @return The termination date
     */
    function getTerminationDate() external view override returns (uint256) {
        return Storage.slot().terminationDate;
    }

    /**
     * @notice Gets the currency information cached at the emergency termination.
     * @param _ccy Currency name in bytes32
     * @return The price cached
     */
    function getTerminationCurrencyCache(
        bytes32 _ccy
    ) external view override returns (TerminationCurrencyCache memory) {
        return Storage.slot().terminationCurrencyCaches[_ccy];
    }

    /**
     * @notice Gets the collateral ratio of each token in TokenVault at the emergency termination.
     * @param _ccy Currency name in bytes32
     * @return The ratio
     */
    function getTerminationCollateralRatio(bytes32 _ccy) external view override returns (uint256) {
        return Storage.slot().terminationCollateralRatios[_ccy];
    }

    /**
     * @notice Gets the min debt unit price for the selected currency.
     * This price is based on a one-year maturity.
     * @param _ccy Currency name in bytes32
     * @return The genesis date
     */
    function getMinDebtUnitPrice(bytes32 _ccy) external view override returns (uint256) {
        return Storage.slot().minDebtUnitPrices[_ccy];
    }

    /**
     * @notice Gets the current min debt unit price.
     * This price fluctuates depending on the current maturity.
     * @param _ccy Currency name in bytes32
     * @param _maturity The maturity of the order book
     * @return The current min debt unit price
     */
    function getCurrentMinDebtUnitPrice(
        bytes32 _ccy,
        uint256 _maturity
    ) external view override returns (uint256) {
        return
            FundManagementLogic.getCurrentMinDebtUnitPrice(
                _maturity,
                Storage.slot().minDebtUnitPrices[_ccy]
            );
    }

    /**
     * @notice Gets the genesis date when the first market opens for the selected currency.
     * @param _ccy Currency name in bytes32
     * @return The genesis date
     */
    function getGenesisDate(bytes32 _ccy) external view override returns (uint256) {
        return Storage.slot().genesisDates[_ccy];
    }

    /**
     * @notice Gets the lending market contract address for the selected currency.
     * @param _ccy Currency name in bytes32
     * @return Array with the lending market address
     */
    function getLendingMarket(bytes32 _ccy) external view override returns (address) {
        return Storage.slot().lendingMarkets[_ccy];
    }

    /**
     * @notice Gets the order book id for the selected currency and maturity.
     * @param _ccy Currency name in bytes32
     * @param _maturity The maturity of the order book
     * @return The order book id
     */
    function getOrderBookId(
        bytes32 _ccy,
        uint256 _maturity
    ) external view override returns (uint8) {
        return Storage.slot().maturityOrderBookIds[_ccy][_maturity];
    }

    /**
     * @notice Gets the future value contract address for the selected currency and maturity.
     * @param _ccy Currency name in bytes32
     * @return The future value vault address
     */
    function getFutureValueVault(bytes32 _ccy) public view override returns (address) {
        return Storage.slot().futureValueVaults[_ccy];
    }

    /**
     * @notice Gets the total amount of pending orders that is not cleaned up yet.
     * @param _ccy Currency name in bytes32
     * @param _maturity The maturity of the order book
     * @return The total amount
     */
    function getPendingOrderAmount(
        bytes32 _ccy,
        uint256 _maturity
    ) external view override returns (uint256) {
        return Storage.slot().pendingOrderAmounts[_ccy][_maturity];
    }

    /**
     * @notice Gets the estimated order result by the calculation of the amount to be filled when executing an order in the order books.
     * @param _params The parameters to calculate the order estimation <br>
     * - ccy: Currency name in bytes32 of the selected market <br>
     * - maturity: The maturity of the market <br>
     * - side: Order position type, Borrow or Lend <br>
     * - amount: Amount of funds the maker wants to borrow/lend <br>
     * - unitPrice: Amount of unit price taker wish to borrow/lend <br>
     * - additionalDepositAmount: Additional amount to be deposited with the lending order <br>
     * - ignoreBorrowedAmount: The boolean if the borrowed amount is ignored and not used as collateral or not
     * @return lastUnitPrice The last unit price that is filled on the order book
     * @return filledAmount The amount that is filled on the order book
     * @return filledAmountInFV The amount in the future value that is filled on the order book
     * @return orderFeeInFV The order fee amount in the future value
     * @return placedAmount The amount that is placed to the order book
     * @return coverage The rate of collateral used
     * @return isInsufficientDepositAmount The boolean if the order amount for lending in the selected currency is insufficient
     * for the deposit amount or not
     */
    function getOrderEstimation(
        GetOrderEstimationParams calldata _params
    )
        external
        view
        override
        returns (
            uint256 lastUnitPrice,
            uint256 filledAmount,
            uint256 filledAmountInFV,
            uint256 orderFeeInFV,
            uint256 placedAmount,
            uint256 coverage,
            bool isInsufficientDepositAmount
        )
    {
        return LendingMarketUserLogic.getOrderEstimation(_params);
    }

    /**
     * @notice Gets the estimated order result by the calculation of the amount to be filled when executing an order in the order books.
     * This function is used to estimate the order from future value.
     * @param _params The parameters to calculate the order estimation <br>
     * - ccy: Currency name in bytes32 of the selected market <br>
     * - maturity: The maturity of the market <br>
     * - user: User's address <br>
     * - side: Order position type, Borrow or Lend <br>
     * - amountInFV: Amount of funds in future value the maker wants to borrow/lend <br>
     * - additionalDepositAmount: Additional amount to be deposited with the lending order <br>
     * - ignoreBorrowedAmount: The boolean if the borrowed amount is ignored and not used as collateral or not
     * @return lastUnitPrice The last unit price that is filled on the order book
     * @return filledAmount The amount that is filled on the order book
     * @return filledAmountInFV The amount in the future value that is filled on the order book
     * @return orderFeeInFV The order fee amount in the future value
     * @return coverage The rate of collateral used
     * @return isInsufficientDepositAmount The boolean if the order amount for lending in the selected currency is insufficient
     * for the deposit amount or not
     */
    function getOrderEstimationFromFV(
        GetOrderEstimationFromFVParams calldata _params
    )
        external
        view
        override
        returns (
            uint256 lastUnitPrice,
            uint256 filledAmount,
            uint256 filledAmountInFV,
            uint256 orderFeeInFV,
            uint256 coverage,
            bool isInsufficientDepositAmount
        )
    {
        return LendingMarketUserLogic.getOrderEstimationFromFV(_params);
    }

    /**
     * @notice Gets maturities for the selected currency.
     * @param _ccy Currency name in bytes32
     * @return Array with the lending market maturity
     */
    function getMaturities(bytes32 _ccy) public view override returns (uint256[] memory) {
        return
            ILendingMarket(Storage.slot().lendingMarkets[_ccy]).getMaturities(
                Storage.slot().orderBookIdLists[_ccy]
            );
    }

    /**
     * @notice Gets the order book ids.
     * @param _ccy Currency name in bytes32
     * @return The array of order book id
     */
    function getOrderBookIds(bytes32 _ccy) external view override returns (uint8[] memory) {
        return Storage.slot().orderBookIdLists[_ccy];
    }

    /**
     * @notice Get all the currencies in which the user has lending positions or orders.
     * @param _user User's address
     * @return The array of the currency
     */
    function getUsedCurrencies(address _user) external view override returns (bytes32[] memory) {
        return Storage.slot().usedCurrencies[_user].values();
    }

    /**
     * @notice Gets the total present value of the account for selected currency.
     * @param _ccy Currency name in bytes32 for Lending Market
     * @param _user User's address
     * @return totalPresentValue The total present value
     */
    function getTotalPresentValue(
        bytes32 _ccy,
        address _user
    ) external view override returns (int256 totalPresentValue) {
        totalPresentValue = FundManagementLogic.getActualFunds(_ccy, 0, _user, 0).presentValue;
    }

    /**
     * @notice Gets the total present value of the account converted to base currency.
     * @param _user User's address
     * @return totalPresentValue The total present value in base currency
     */
    function getTotalPresentValueInBaseCurrency(
        address _user
    ) external view override returns (int256 totalPresentValue) {
        EnumerableSet.Bytes32Set storage currencySet = Storage.slot().usedCurrencies[_user];

        for (uint256 i; i < currencySet.length(); i++) {
            bytes32 ccy = currencySet.at(i);
            int256 amount = FundManagementLogic.getActualFunds(ccy, 0, _user, 0).presentValue;
            totalPresentValue += currencyController().convertToBaseCurrency(ccy, amount);
        }
    }

    /**
     * @notice Gets the genesis value of the account.
     * @param _ccy Currency name in bytes32 for Lending Market
     * @param _user User's address
     * @return amount The genesis value amount
     * @return amountInPV The genesis value amount in present value
     * @return amountInFV The genesis value amount in future value
     */
    function getGenesisValue(
        bytes32 _ccy,
        address _user
    ) external view override returns (int256 amount, int256 amountInPV, int256 amountInFV) {
        return FundManagementLogic.getGenesisValue(_ccy, _user);
    }

    /**
     * @notice Gets user's active position from the future value vault
     * @param _ccy Currency name in bytes32
     * @param _maturity The maturity of the selected order book
     * @param _user User's address
     * @return presentValue The present value of the position
     * @return futureValue The future value of the position
     */
    function getPosition(
        bytes32 _ccy,
        uint256 _maturity,
        address _user
    ) external view override returns (int256 presentValue, int256 futureValue) {
        (presentValue, futureValue) = FundManagementLogic.getPosition(_ccy, _maturity, _user);
    }

    /**
     * @notice Gets ZC Token address for the selected currency and maturity.
     * @param _ccy Currency name in bytes32
     * @param _maturity The maturity of the selected order book
     * @return The zcToken address
     */
    function getZCToken(bytes32 _ccy, uint256 _maturity) external view override returns (address) {
        return Storage.slot().zcTokens[_ccy][_maturity];
    }

    /**
     * @notice Gets ZC Token info for the selected ZC Token address.
     * @param _zcToken The zcToken address
     * @return The zcToken data
     */
    function getZCTokenInfo(address _zcToken) external view override returns (ZCTokenInfo memory) {
        return Storage.slot().zcTokenInfo[_zcToken];
    }

    /**
     * @notice Gets the withdrawable ZCToken amount for the selected currency and maturity.
     * ZC perpetual token amount is returned only when the maturity is 0.
     * @param _ccy Currency name in bytes32
     * @param _maturity The maturity of the order book
     * @param _user User's address
     * @return amount The total amount of ZCToken that can be minted
     */
    function getWithdrawableZCTokenAmount(
        bytes32 _ccy,
        uint256 _maturity,
        address _user
    ) external view override returns (uint256 amount) {
        return LendingMarketUserLogic.getWithdrawableZCTokenAmount(_ccy, _maturity, _user);
    }

    /**
     * @notice Gets the funds that are calculated from the user's lending and borrowing order list
     * for the selected currency.
     * @param _ccy Currency name in bytes32
     * @param _user User's address
     * @param _liquidationThresholdRate The liquidation threshold rate
     * @return funds The funds calculated from the user's lending and borrowing order list
     */
    function calculateFunds(
        bytes32 _ccy,
        address _user,
        uint256 _liquidationThresholdRate
    ) external view override returns (CalculatedFunds memory funds) {
        if (Storage.slot().usedCurrencies[_user].contains(_ccy)) {
            AdditionalFunds memory emptyAdditionalFunds;

            return
                FundManagementLogic.calculateFunds(
                    _ccy,
                    _user,
                    emptyAdditionalFunds,
                    _liquidationThresholdRate
                );
        }
    }

    /**
     * @notice Gets the funds that are calculated from the user's lending and borrowing order list
     * for all currencies in base currency.
     * @param _user User's address
     * @param _additionalFunds The funds to be added for calculating the total funds
     * @param _liquidationThresholdRate The liquidation threshold rate
     * @return funds The total funds calculated from the user's lending and borrowing order list
     */
    function calculateTotalFundsInBaseCurrency(
        address _user,
        AdditionalFunds calldata _additionalFunds,
        uint256 _liquidationThresholdRate
    ) external view override returns (CalculatedTotalFunds memory funds) {
        return
            FundManagementLogic.calculateTotalFundsInBaseCurrency(
                _user,
                _additionalFunds,
                _liquidationThresholdRate
            );
    }

    /**
     * @notice Gets if the lending market is initialized.
     * @param _ccy Currency name in bytes32
     * @return The boolean if the lending market is initialized or not
     */
    function isInitializedLendingMarket(bytes32 _ccy) public view override returns (bool) {
        return Storage.slot().genesisDates[_ccy] != 0;
    }

    /**
     * @notice Initialize the lending market to set a genesis date and compound factor
     * @param _ccy Currency name in bytes32
     * @param _genesisDate The genesis date when the initial market is opened
     * @param _compoundFactor The initial compound factor when the initial market is opened
     * @param _orderFeeRate The order fee rate received by protocol
     * @param _circuitBreakerLimitRange The circuit breaker limit range
     */
    function initializeLendingMarket(
        bytes32 _ccy,
        uint256 _genesisDate,
        uint256 _compoundFactor,
        uint256 _orderFeeRate,
        uint256 _circuitBreakerLimitRange,
        uint256 _minDebtUnitPrice
    ) external override onlyOwner {
        if (isInitializedLendingMarket(_ccy)) revert AlreadyInitialized();

        LendingMarketOperationLogic.initializeLendingMarket(
            _ccy,
            _genesisDate,
            _compoundFactor,
            _orderFeeRate,
            _circuitBreakerLimitRange,
            _minDebtUnitPrice
        );
    }

    /**
     * @notice Creates new order book.
     * @param _ccy Main currency for new order book
     * @param _openingDate The timestamp when the order book opens
     * @param _preOpeningDate The timestamp when the order book pre-opens
     */
    function createOrderBook(
        bytes32 _ccy,
        uint256 _openingDate,
        uint256 _preOpeningDate
    ) external override ifActive onlyOwner {
        LendingMarketOperationLogic.createOrderBook(_ccy, _openingDate, _preOpeningDate);
    }

    /**
     * @notice Executes an order. Takes orders if the order is matched,
     * and places new order if not match it.
     *
     * In addition, converts the future value to the genesis value if there is future value in past maturity
     * before the execution of order creation.
     *
     * @param _ccy Currency name in bytes32 of the selected order book
     * @param _maturity The maturity of the selected order book
     * @param _side Order position type, Borrow or Lend
     * @param _amount Amount of funds the maker wants to borrow/lend
     * @param _unitPrice Amount of unit price taker wish to borrow/lend
     * @return True if the execution of the operation succeeds
     */
    function executeOrder(
        bytes32 _ccy,
        uint256 _maturity,
        ProtocolTypes.Side _side,
        uint256 _amount,
        uint256 _unitPrice
    ) external override nonReentrant ifValidMaturity(_ccy, _maturity) ifActive returns (bool) {
        LendingMarketUserLogic.executeOrder(
            _ccy,
            _maturity,
            msg.sender,
            _side,
            _amount,
            _unitPrice
        );
        return true;
    }

    /**
     * @notice Deposits funds and executes an order at the same time.
     *
     * @param _ccy Currency name in bytes32 of the selected order book
     * @param _maturity The maturity of the selected order book
     * @param _side Order position type, Borrow or Lend
     * @param _amount Amount of funds the maker wants to borrow/lend
     * @param _unitPrice Amount of unit price taker wish to borrow/lend
     * @return True if the execution of the operation succeeds
     */
    function depositAndExecuteOrder(
        bytes32 _ccy,
        uint256 _maturity,
        ProtocolTypes.Side _side,
        uint256 _amount,
        uint256 _unitPrice
    )
        external
        payable
        override
        nonReentrant
        ifValidMaturity(_ccy, _maturity)
        ifActive
        returns (bool)
    {
        tokenVault().depositFrom{value: msg.value}(msg.sender, _ccy, _amount);
        LendingMarketUserLogic.executeOrder(
            _ccy,
            _maturity,
            msg.sender,
            _side,
            _amount,
            _unitPrice
        );
        return true;
    }

    /**
     * @notice Deposits funds with transfer approval of asset via permit function
     * and executes an order at the same time.
     *
     * @param _ccy Currency name in bytes32 of the selected order book
     * @param _maturity The maturity of the selected order book
     * @param _side Order position type, Borrow or Lend
     * @param _amount Amount of funds the maker wants to borrow/lend
     * @param _unitPrice Amount of unit price taker wish to borrow/lend
     * @param _deadline The deadline timestamp that the permit is valid
     * @param _permitV The V parameter of ERC712 permit sig
     * @param _permitR The R parameter of ERC712 permit sig
     * @param _permitS The S parameter of ERC712 permit sig
     * @return True if the execution of the operation succeeds
     */
    function depositWithPermitAndExecuteOrder(
        bytes32 _ccy,
        uint256 _maturity,
        ProtocolTypes.Side _side,
        uint256 _amount,
        uint256 _unitPrice,
        uint256 _deadline,
        uint8 _permitV,
        bytes32 _permitR,
        bytes32 _permitS
    ) external override nonReentrant ifValidMaturity(_ccy, _maturity) ifActive returns (bool) {
        tokenVault().depositWithPermitFrom(
            msg.sender,
            _ccy,
            _amount,
            _deadline,
            _permitV,
            _permitR,
            _permitS
        );

        LendingMarketUserLogic.executeOrder(
            _ccy,
            _maturity,
            msg.sender,
            _side,
            _amount,
            _unitPrice
        );
        return true;
    }

    /**
     * @notice Executes a pre-order. A pre-order will only be accepted from 168 hours (7 days) to 1 hour
     * before the order book opens (Pre-order period). At the end of this period, Itayose will be executed.
     *
     * @param _ccy Currency name in bytes32 of the selected order book
     * @param _maturity The maturity of the selected order book
     * @param _side Order position type, Borrow or Lend
     * @param _amount Amount of funds the maker wants to borrow/lend
     * @param _unitPrice Amount of unit price taker wish to borrow/lend
     * @return True if the execution of the operation succeeds
     */
    function executePreOrder(
        bytes32 _ccy,
        uint256 _maturity,
        ProtocolTypes.Side _side,
        uint256 _amount,
        uint256 _unitPrice
    ) public override nonReentrant ifValidMaturity(_ccy, _maturity) ifActive returns (bool) {
        LendingMarketUserLogic.executePreOrder(
            _ccy,
            _maturity,
            msg.sender,
            _side,
            _amount,
            _unitPrice
        );

        return true;
    }

    /**
     * @notice Deposits funds and executes a pre-order at the same time.
     *
     * @param _ccy Currency name in bytes32 of the selected order book
     * @param _maturity The maturity of the selected order book
     * @param _side Order position type, Borrow or Lend
     * @param _amount Amount of funds the maker wants to borrow/lend
     * @param _unitPrice Amount of unit price taker wish to borrow/lend
     * @return True if the execution of the operation succeeds
     */
    function depositAndExecutesPreOrder(
        bytes32 _ccy,
        uint256 _maturity,
        ProtocolTypes.Side _side,
        uint256 _amount,
        uint256 _unitPrice
    )
        external
        payable
        override
        nonReentrant
        ifValidMaturity(_ccy, _maturity)
        ifActive
        returns (bool)
    {
        tokenVault().depositFrom{value: msg.value}(msg.sender, _ccy, _amount);
        LendingMarketUserLogic.executePreOrder(
            _ccy,
            _maturity,
            msg.sender,
            _side,
            _amount,
            _unitPrice
        );

        return true;
    }

    /**
     * @notice Deposits funds with transfer approval of asset via permit function
     * and executes a pre-order at the same time.
     *
     * @param _ccy Currency name in bytes32 of the selected order book
     * @param _maturity The maturity of the selected order book
     * @param _side Order position type, Borrow or Lend
     * @param _amount Amount of funds the maker wants to borrow/lend
     * @param _unitPrice Amount of unit price taker wish to borrow/lend
     * @param _deadline The deadline timestamp that the permit is valid
     * @param _permitV The V parameter of ERC712 permit sig
     * @param _permitR The R parameter of ERC712 permit sig
     * @param _permitS The S parameter of ERC712 permit sig
     * @return True if the execution of the operation succeeds
     */
    function depositWithPermitAndExecutePreOrder(
        bytes32 _ccy,
        uint256 _maturity,
        ProtocolTypes.Side _side,
        uint256 _amount,
        uint256 _unitPrice,
        uint256 _deadline,
        uint8 _permitV,
        bytes32 _permitR,
        bytes32 _permitS
    ) external override nonReentrant ifValidMaturity(_ccy, _maturity) ifActive returns (bool) {
        tokenVault().depositWithPermitFrom(
            msg.sender,
            _ccy,
            _amount,
            _deadline,
            _permitV,
            _permitR,
            _permitS
        );

        LendingMarketUserLogic.executePreOrder(
            _ccy,
            _maturity,
            msg.sender,
            _side,
            _amount,
            _unitPrice
        );

        return true;
    }

    /**
     * @notice Unwinds user's lending or borrowing positions by creating an opposite position order.
     * @param _ccy Currency name in bytes32 of the selected order book
     * @param _maturity The maturity of the selected order book
     */
    function unwindPosition(
        bytes32 _ccy,
        uint256 _maturity
    ) external override nonReentrant ifValidMaturity(_ccy, _maturity) ifActive returns (bool) {
        LendingMarketUserLogic.unwindPosition(_ccy, _maturity, msg.sender, 0);
        return true;
    }

    /**
     * @notice Unwinds user's lending or borrowing positions by creating an opposite position order with a cap.
     * @param _ccy Currency name in bytes32 of the selected order book
     * @param _maturity The maturity of the selected order book
     * @param _maxAmountInFV Maximum amount in future value to unwind. If 0, no cap is applied.
     * @return filledAmount The amount that is filled on the order book
     * @return filledAmountInFV The amount in the future value that is filled on the order book
     * @return feeInFV The order fee amount in the future value
     */
    function unwindPositionWithCap(
        bytes32 _ccy,
        uint256 _maturity,
        uint256 _maxAmountInFV
    )
        external
        override
        nonReentrant
        ifValidMaturity(_ccy, _maturity)
        ifActive
        returns (uint256 filledAmount, uint256 filledAmountInFV, uint256 feeInFV)
    {
        return LendingMarketUserLogic.unwindPosition(_ccy, _maturity, msg.sender, _maxAmountInFV);
    }

    /**
     * @notice Redeem user's lending positions.
     * Redemption can only be executed once the order book has matured after the currency has been delisted.
     * @param _ccy Currency name in bytes32 of the selected order book
     * @param _maturity The maturity of the selected order book
     */
    function executeRedemption(
        bytes32 _ccy,
        uint256 _maturity
    ) external override nonReentrant ifValidMaturity(_ccy, _maturity) ifActive returns (bool) {
        FundManagementLogic.executeRedemption(_ccy, _maturity, msg.sender);
        return true;
    }

    /**
     * @notice Repay user's borrowing positions.
     * Repayment can only be executed once the order book has matured after the currency has been delisted.
     * @param _ccy Currency name in bytes32 of the selected order book
     * @param _maturity The maturity of the selected order book
     */
    function executeRepayment(
        bytes32 _ccy,
        uint256 _maturity
    ) external override nonReentrant ifValidMaturity(_ccy, _maturity) ifActive returns (bool) {
        FundManagementLogic.executeRepayment(_ccy, _maturity, msg.sender, 0);
        return true;
    }

    /**
     * @notice Force settlement of all lending and borrowing positions.
     * This function is executed under the present value as of the termination date.
     *
     * @return True if the execution of the operation succeeds
     */
    function executeEmergencySettlement() external override nonReentrant ifInactive returns (bool) {
        FundManagementLogic.executeEmergencySettlement(msg.sender);
        return true;
    }

    /**
     * @notice Executes the Itayose call per selected currency.
     * @param _ccy Currency name in bytes32
     * @param _maturity The maturity of the selected order book
     */
    function executeItayoseCall(
        bytes32 _ccy,
        uint256 _maturity
    ) external override nonReentrant ifActive returns (bool) {
        (
            PartiallyFilledOrder memory partiallyFilledLendingOrder,
            PartiallyFilledOrder memory partiallyFilledBorrowingOrder
        ) = LendingMarketOperationLogic.executeItayoseCall(_ccy, _maturity);

        LendingMarketUserLogic.updateFundsForMaker(
            _ccy,
            _maturity,
            ProtocolTypes.Side.LEND,
            partiallyFilledLendingOrder
        );
        LendingMarketUserLogic.updateFundsForMaker(
            _ccy,
            _maturity,
            ProtocolTypes.Side.BORROW,
            partiallyFilledBorrowingOrder
        );

        return true;
    }

    /**
     * @notice Cancels the own order.
     * @param _ccy Currency name in bytes32 of the selected order book
     * @param _maturity The maturity of the selected order book
     * @param _orderId Market order id
     */
    function cancelOrder(
        bytes32 _ccy,
        uint256 _maturity,
        uint48 _orderId
    ) external override nonReentrant ifValidMaturity(_ccy, _maturity) ifActive returns (bool) {
        ILendingMarket(Storage.slot().lendingMarkets[_ccy]).cancelOrder(
            Storage.slot().maturityOrderBookIds[_ccy][_maturity],
            msg.sender,
            _orderId
        );

        return true;
    }

    /**
     * @notice Liquidates a lending or borrowing position if the user's coverage is hight.
     * @param _collateralCcy Currency name to be used as collateral
     * @param _debtCcy Currency name to be used as debt
     * @param _debtMaturity The order book maturity of the debt
     * @param _user User's address
     * @return True if the execution of the operation succeeds
     */
    function executeLiquidationCall(
        bytes32 _collateralCcy,
        bytes32 _debtCcy,
        uint256 _debtMaturity,
        address _user
    )
        external
        override
        isNotLocked
        ifValidMaturity(_debtCcy, _debtMaturity)
        ifActive
        returns (bool)
    {
        LiquidationLogic.executeLiquidation(
            msg.sender,
            _user,
            _collateralCcy,
            _debtCcy,
            _debtMaturity
        );

        return true;
    }

    /**
     * @notice Execute forced repayment for a borrowing position if repayment date is over.
     * @param _collateralCcy Currency name to be used as collateral
     * @param _debtCcy Currency name to be used as debt
     * @param _debtMaturity The order book maturity of the debt
     * @param _user User's address
     * @return True if the execution of the operation succeeds
     */
    function executeForcedRepayment(
        bytes32 _collateralCcy,
        bytes32 _debtCcy,
        uint256 _debtMaturity,
        address _user
    )
        external
        override
        isNotLocked
        ifValidMaturity(_debtCcy, _debtMaturity)
        ifActive
        returns (bool)
    {
        LiquidationLogic.executeForcedRepayment(
            msg.sender,
            _user,
            _collateralCcy,
            _debtCcy,
            _debtMaturity
        );

        return true;
    }

    /**
     * @notice Rotates the order books. In this rotation, the following actions are happened.
     * - Updates the maturity at the beginning of the order book array.
     * - Moves the beginning of the order book array to the end of it (Market rotation).
     * - Update the compound factor in this contract using the next order book unit price. (Auto-rolls)
     * - Clean up the reserve fund contract
     *
     * @param _ccy Currency name in bytes32 of the selected order book
     */
    function rotateOrderBooks(bytes32 _ccy) external override nonReentrant ifActive {
        LendingMarketOperationLogic.rotateOrderBooks(_ccy);
        FundManagementLogic.cleanUpFunds(_ccy, address(reserveFund()));
    }

    /**
     * @notice Executes an emergency termination to stop the protocol. Once this function is executed,
     * the protocol cannot be run again. Also, users will only be able to redeem and withdraw.
     */
    function executeEmergencyTermination() external override ifActive onlyOwner {
        LendingMarketOperationLogic.executeEmergencyTermination();
    }

    /**
     * @notice Pauses the lending market by currency
     * @param _ccy Currency for pausing all lending markets
     * @return True if the execution of the operation succeeds
     */
    function pauseLendingMarket(
        bytes32 _ccy
    ) external override ifActive onlyOperator returns (bool) {
        LendingMarketOperationLogic.pauseLendingMarket(_ccy);
        return true;
    }

    /**
     * @notice Unpauses the lending market by currency
     * @param _ccy Currency name in bytes32
     * @return True if the execution of the operation succeeds
     */
    function unpauseLendingMarket(
        bytes32 _ccy
    ) external override ifActive onlyOperator returns (bool) {
        LendingMarketOperationLogic.unpauseLendingMarket(_ccy);
        return true;
    }

    /**
     * @notice Clean up all funds of the user
     * @param _user User's address
     */
    function cleanUpAllFunds(address _user) external override nonReentrant returns (bool) {
        FundManagementLogic.cleanUpAllFunds(_user);
        return true;
    }

    /**
     * @notice Clean up user funds used for lazy evaluation by the following actions:
     * - Removes order IDs that is already filled on the order book.
     * - Convert Future values that have already been auto-rolled to Genesis values.
     * @param _ccy Currency name in bytes32
     * @param _user User's address
     */
    function cleanUpFunds(
        bytes32 _ccy,
        address _user
    ) external override nonReentrant returns (uint256 totalActiveOrderCount) {
        return FundManagementLogic.cleanUpFunds(_ccy, _user);
    }

    /**
     * @notice Updates the min debt unit price for the selected currency.
     * @param _ccy Currency name in bytes32
     * @param _minDebtUnitPrice The min debt unit price
     */
    function updateMinDebtUnitPrice(
        bytes32 _ccy,
        uint256 _minDebtUnitPrice
    ) external override ifActive onlyOwner {
        LendingMarketOperationLogic.updateMinDebtUnitPrice(_ccy, _minDebtUnitPrice);
    }

    /**
     * @notice Withdraws ZCToken for the selected currency and maturity.
     * ZC perpetual token can be withdrawn only when the maturity is 0.
     * @param _ccy Currency name in bytes32
     * @param _maturity The maturity of the order book
     * @param _amount The amount of ZCToken to mint
     */
    function withdrawZCToken(
        bytes32 _ccy,
        uint256 _maturity,
        uint256 _amount
    ) external override nonReentrant ifActive {
        if (_maturity != 0 && !isValidMaturity(_ccy, _maturity)) revert InvalidMaturity();

        LendingMarketUserLogic.withdrawZCToken(_ccy, _maturity, msg.sender, _amount);
    }

    /**
     * @notice Deposits ZCToken for the selected currency and maturity.
     * ZC perpetual token can be deposited only when the maturity is 0.
     * This function is not inactivated after the emergency termination to allow users to redeem ZCToken.
     * @param _ccy Currency name in bytes32
     * @param _maturity The maturity of the order book
     * @param _amount The amount of ZCToken to burn
     */
    function depositZCToken(
        bytes32 _ccy,
        uint256 _maturity,
        uint256 _amount
    ) external override nonReentrant {
        if (_maturity != 0 && !isValidMaturity(_ccy, _maturity)) revert InvalidMaturity();

        LendingMarketUserLogic.depositZCToken(_ccy, _maturity, msg.sender, _amount);
    }

    /**
     * @notice Migrate the lending market to the new version.
     * @dev ZCTokens do not exist in markets that were deployed before the contract upgrade,
     * so they must be configured individually.
     * This function can be deleted after executing for all currencies and maturities.
     * @param _ccy Currency name in bytes32
     * @param _maturity The maturity of the ZCToken
     */
    function migrateLendingMarket(bytes32 _ccy, uint256 _maturity) external onlyOwner {
        LendingMarketOperationLogic.migrateLendingMarket(_ccy, _maturity);
    }
}

contracts/protocol/libraries/AddressReso.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.19;

import {Contracts} from "../libraries/Contracts.sol";
import {IBeaconProxyController} from "../interfaces/IBeaconProxyController.sol";
import {ICurrencyController} from "../interfaces/ICurrencyController.sol";
import {IGenesisValueVault} from "../interfaces/IGenesisValueVault.sol";
import {ILendingMarketController} from "../interfaces/ILendingMarketController.sol";
import {IReserveFund} from "../interfaces/IReserveFund.sol";
import {ITokenVault} from "../interfaces/ITokenVault.sol";
import {MixinAddressResolverStorage as Storage} from "../storages/mixins/MixinAddressResolverStorage.sol";

library AddressResolverLib {
    error MissingAddress(string name);

    function getAddress(bytes32 name) internal view returns (address) {
        address _foundAddress = Storage.slot().addressCache[name];
        if (_foundAddress == address(0)) revert MissingAddress(string(abi.encodePacked(name)));
        return _foundAddress;
    }

    function beaconProxyController() internal view returns (IBeaconProxyController) {
        return IBeaconProxyController(getAddress(Contracts.BEACON_PROXY_CONTROLLER));
    }

    function currencyController() internal view returns (ICurrencyController) {
        return ICurrencyController(getAddress(Contracts.CURRENCY_CONTROLLER));
    }

    function genesisValueVault() internal view returns (IGenesisValueVault) {
        return IGenesisValueVault(getAddress(Contracts.GENESIS_VALUE_VAULT));
    }

    function lendingMarketController() internal view returns (ILendingMarketController) {
        return ILendingMarketController(getAddress(Contracts.LENDING_MARKET_CONTROLLER));
    }

    function reserveFund() internal view returns (IReserveFund) {
        return IReserveFund(getAddress(Contracts.RESERVE_FUND));
    }

    function tokenVault() internal view returns (ITokenVault) {
        return ITokenVault(getAddress(Contracts.TOKEN_VAULT));
    }
}

