pragma solidity 0.8.19;

import {Constants} from "./Constants.sol";
import {ProtocolTypes} from "../types/ProtocolTypes.sol";
import {OrderStatisticsTreeLib, PartiallyRemovedOrder} from "./OrderStatisticsTreeLib.sol";
import {RoundingUint256} from "./math/RoundingUint256.sol";

struct PlacedOrder {
    ProtocolTypes.Side side;
    uint256 unitPrice;
    uint256 maturity;
    uint256 timestamp;
}

struct FilledOrder {
    uint256 amount;
    uint256 unitPrice;
    uint256 futureValue;
    uint256 ignoredAmount;
}

struct PartiallyFilledOrder {
    uint48 orderId;
    address maker;
    uint256 amount;
    uint256 futureValue;
}

library OrderBookLib {
    using OrderStatisticsTreeLib for OrderStatisticsTreeLib.Tree;
    using RoundingUint256 for uint256;

    uint256 public constant PRE_ORDER_BASE_PERIOD = 7 days;
    uint256 public constant ITAYOSE_PERIOD = 1 hours;
    uint256 public constant CIRCUIT_BREAKER_MINIMUM_LEND_RANGE = 700;
    uint256 public constant CIRCUIT_BREAKER_MINIMUM_BORROW_RANGE = 200;

    error EmptyOrderBook();
    error PastMaturityOrderExists();

    struct OrderBook {
        uint256 maturity;
        uint256 openingDate;
        uint256 preOpeningDate;
        uint48 lastOrderId;
        uint48 lastOrderTimestamp;
        bool isReliableBlock;
        // Micro slots for block unit price history
        uint80 blockUnitPriceHistory;
        uint48 lastBlockUnitPriceTimestamp;
        uint256 blockTotalAmount;
        uint256 blockTotalFutureValue;
        mapping(address user => uint48[] orderIds) activeLendOrderIds;
        mapping(address user => uint48[] orderIds) activeBorrowOrderIds;
        // Maturity when user last executes order
        mapping(address user => uint256 maturity) userCurrentMaturities;
        // Micro slots for order
        mapping(uint48 orderId => uint256 slots) orders;
        mapping(uint48 orderId => bool isPreOrder) isPreOrder;
        mapping(uint256 maturity => OrderStatisticsTreeLib.Tree orders) lendOrders;
        mapping(uint256 maturity => OrderStatisticsTreeLib.Tree orders) borrowOrders;
    }

    function initialize(
        OrderBook storage self,
        uint256 _maturity,
        uint256 _openingDate,
        uint256 _preOpeningDate
    ) internal returns (bool isReady) {
        self.maturity = _maturity;
        self.openingDate = _openingDate;
        self.preOpeningDate = _preOpeningDate;

        self.blockTotalAmount = 0;
        self.blockTotalFutureValue = 0;
        self.blockUnitPriceHistory = 0;
        self.lastOrderTimestamp = 0;
        self.lastBlockUnitPriceTimestamp = 0;
        self.isReliableBlock = false;

        if (block.timestamp >= (_openingDate - ITAYOSE_PERIOD)) {
            isReady = true;
        }
    }

    function isMatured(OrderBook storage self) internal view returns (bool) {
        return block.timestamp >= self.maturity;
    }

    function getBestBorrowUnitPrice(OrderBook storage self) internal view returns (uint256) {
        return self.lendOrders[self.maturity].last();
    }

    function getBestLendUnitPrice(OrderBook storage self) internal view returns (uint256) {
        uint256 unitPrice = self.borrowOrders[self.maturity].first();
        return unitPrice == 0 ? Constants.PRICE_DIGIT : unitPrice;
    }

    function hasBorrowOrder(OrderBook storage self, address _user) internal view returns (bool) {
        return self.activeBorrowOrderIds[_user].length != 0;
    }

    function hasLendOrder(OrderBook storage self, address _user) internal view returns (bool) {
        return self.activeLendOrderIds[_user].length != 0;
    }

    function getOrder(
        OrderBook storage self,
        uint48 _orderId
    ) internal view returns (PlacedOrder memory order) {
        (
            ProtocolTypes.Side side,
            uint256 unitPrice,
            uint256 maturity,
            uint256 timestamp
        ) = _unpackOrder(self.orders[_orderId]);
        order = PlacedOrder(side, unitPrice, maturity, timestamp);
    }

    function getBlockUnitPriceHistory(
        OrderBook storage self,
        bool _isReadOnly
    ) internal view returns (uint256[] memory prices, uint48 timestamp) {
        prices = _unpackBlockUnitPriceHistory(self.blockUnitPriceHistory);

        // NOTE: If an order is in the first block of the order book, the block unit price history is empty.
        // In this case, the first history record is calculated from the current block total amount and total future value
        // along with the `getMarketUnitPrice` function logic.
        if (
            (self.lastOrderTimestamp != block.timestamp || prices[0] == 0 || _isReadOnly) &&
            self.isReliableBlock
        ) {
            for (uint256 i = prices.length - 1; i > 0; i--) {
                prices[i] = prices[i - 1];
            }

            prices[0] = (self.blockTotalAmount * Constants.PRICE_DIGIT).div(
                self.blockTotalFutureValue
            );
            timestamp = self.lastOrderTimestamp;
        } else {
            timestamp = self.lastBlockUnitPriceTimestamp;
        }
    }

    function getMarketUnitPrice(
        OrderBook storage self,
        bool _isReadOnly
    ) internal view returns (uint256 unitPrice) {
        unitPrice = _unpackBlockUnitPriceHistory(self.blockUnitPriceHistory)[0];

        // NOTE: If an order is in the first block of the order book, the block unit price history is empty.
        // In this case, the market unit price is calculated from the current block total amount and total future value
        // to avoid unwinding or liquidation the order in the same block using 0 as the market unit price.
        if (
            (self.lastOrderTimestamp != block.timestamp || unitPrice == 0 || _isReadOnly) &&
            self.isReliableBlock
        ) {
            unitPrice = (self.blockTotalAmount * Constants.PRICE_DIGIT).div(
                self.blockTotalFutureValue
            );
        }
    }

    function getBlockUnitPriceAverage(
        OrderBook storage self,
        uint256 maxCount,
        bool _isReadOnly
    ) internal view returns (uint256 unitPrice) {
        uint256[] memory unitPrices = _unpackBlockUnitPriceHistory(self.blockUnitPriceHistory);
        uint256 length = unitPrices.length;
        uint256 sum;
        uint256 count;

        if ((self.lastOrderTimestamp != block.timestamp || _isReadOnly) && self.isReliableBlock) {
            sum = (self.blockTotalAmount * Constants.PRICE_DIGIT).div(self.blockTotalFutureValue);
            count = 1;
            maxCount--;
        }

        for (uint256 i; i < maxCount; i++) {
            if (i >= length || unitPrices[i] == 0) {
                break;
            }

            sum += unitPrices[i];
            count++;
        }

        unitPrice = count > 0 ? sum.div(count) : 0;
    }

    function getLendOrderBook(
        OrderBook storage self,
        uint256 _start,
        uint256 _limit
    )
        internal
        view
        returns (
            uint256[] memory unitPrices,
            uint256[] memory amounts,
            uint256[] memory quantities,
            uint256 next
        )
    {
        unitPrices = new uint256[](_limit);
        amounts = new uint256[](_limit);
        quantities = new uint256[](_limit);

        uint256 unitPrice;

        if (_start == 0) {
            unitPrice = self.lendOrders[self.maturity].last();
        } else {
            (bool exists, uint256 parent) = self.lendOrders[self.maturity].search(_start);

            if (exists) {
                unitPrice = _start;
            } else if (parent < _start) {
                unitPrice = parent;
            } else {
                unitPrice = self.lendOrders[self.maturity].prev(parent);
            }
        }

        unitPrices[0] = unitPrice;
        amounts[0] = self.lendOrders[self.maturity].getNodeTotalAmount(unitPrice);
        quantities[0] = self.lendOrders[self.maturity].getNodeCount(unitPrice);

        for (uint256 i = 1; i < unitPrices.length; i++) {
            if (unitPrice == 0) {
                break;
            }

            unitPrice = self.lendOrders[self.maturity].prev(unitPrice);
            unitPrices[i] = unitPrice;
            amounts[i] = self.lendOrders[self.maturity].getNodeTotalAmount(unitPrice);
            quantities[i] = self.lendOrders[self.maturity].getNodeCount(unitPrice);
        }

        if (unitPrice != 0) {
            next = self.lendOrders[self.maturity].prev(unitPrice);
        }
    }

    function getBorrowOrderBook(
        OrderBook storage self,
        uint256 _start,
        uint256 _limit
    )
        internal
        view
        returns (
            uint256[] memory unitPrices,
            uint256[] memory amounts,
            uint256[] memory quantities,
            uint256 next
        )
    {
        unitPrices = new uint256[](_limit);
        amounts = new uint256[](_limit);
        quantities = new uint256[](_limit);

        uint256 unitPrice;

        if (_start == 0) {
            unitPrice = self.borrowOrders[self.maturity].first();
        } else {
            (bool exists, uint256 parent) = self.borrowOrders[self.maturity].search(_start);

            if (exists) {
                unitPrice = _start;
            } else if (parent > _start) {
                unitPrice = parent;
            } else {
                unitPrice = self.borrowOrders[self.maturity].next(parent);
            }
        }

        unitPrices[0] = unitPrice;
        amounts[0] = self.borrowOrders[self.maturity].getNodeTotalAmount(unitPrice);
        quantities[0] = self.borrowOrders[self.maturity].getNodeCount(unitPrice);

        for (uint256 i = 1; i < unitPrices.length; i++) {
            if (unitPrice == 0) {
                break;
            }

            unitPrice = self.borrowOrders[self.maturity].next(unitPrice);
            unitPrices[i] = unitPrice;
            amounts[i] = self.borrowOrders[self.maturity].getNodeTotalAmount(unitPrice);
            quantities[i] = self.borrowOrders[self.maturity].getNodeCount(unitPrice);
        }

        if (unitPrice != 0) {
            next = self.borrowOrders[self.maturity].next(unitPrice);
        }
    }

    function getLendOrderIds(
        OrderBook storage self,
        address _user
    ) internal view returns (uint48[] memory activeOrderIds, uint48[] memory inActiveOrderIds) {
        uint256 activeOrderCount = 0;
        uint256 inActiveOrderCount = 0;
        uint256 userMaturity = self.userCurrentMaturities[_user];
        bool isPastMaturity = block.timestamp >= userMaturity;

        uint48[] memory orderIds = self.activeLendOrderIds[_user];
        uint256 orderIdLength = orderIds.length;
        activeOrderIds = new uint48[](isPastMaturity ? 0 : orderIdLength);
        inActiveOrderIds = new uint48[](orderIdLength);

        for (uint256 i; i < orderIdLength; i++) {
            uint48 orderId = orderIds[i];
            (, uint256 unitPrice, , ) = _unpackOrder(self.orders[orderId]);

            if (!self.lendOrders[userMaturity].isActiveOrderId(unitPrice, orderId)) {
                unchecked {
                    inActiveOrderCount += 1;
                }
                inActiveOrderIds[i - activeOrderCount] = orderId;
                if (!isPastMaturity) {
                    assembly {
                        mstore(activeOrderIds, sub(mload(activeOrderIds), 1))
                    }
                }
            } else {
                unchecked {
                    activeOrderCount += 1;
                }
                if (!isPastMaturity) {
                    activeOrderIds[i - inActiveOrderCount] = orderId;
                }
                assembly {
                    mstore(inActiveOrderIds, sub(mload(inActiveOrderIds), 1))
                }
            }
        }
    }

    function getBorrowOrderIds(
        OrderBook storage self,
        address _user
    ) internal view returns (uint48[] memory activeOrderIds, uint48[] memory inActiveOrderIds) {
        uint256 activeOrderCount = 0;
        uint256 inActiveOrderCount = 0;
        uint256 userMaturity = self.userCurrentMaturities[_user];
        bool isPastMaturity = block.timestamp >= userMaturity;

        uint48[] memory orderIds = self.activeBorrowOrderIds[_user];
        uint256 orderIdLength = orderIds.length;
        activeOrderIds = new uint48[](isPastMaturity ? 0 : orderIdLength);
        inActiveOrderIds = new uint48[](orderIdLength);

        for (uint256 i; i < orderIdLength; i++) {
            uint48 orderId = orderIds[i];
            (, uint256 unitPrice, , ) = _unpackOrder(self.orders[orderId]);

            if (!self.borrowOrders[userMaturity].isActiveOrderId(unitPrice, orderId)) {
                unchecked {
                    inActiveOrderCount += 1;
                }
                inActiveOrderIds[i - activeOrderCount] = orderId;
                if (!isPastMaturity) {
                    assembly {
                        mstore(activeOrderIds, sub(mload(activeOrderIds), 1))
                    }
                }
            } else {
                unchecked {
                    activeOrderCount += 1;
                }
                if (!isPastMaturity) {
                    activeOrderIds[i - inActiveOrderCount] = orderId;
                }
                assembly {
                    mstore(inActiveOrderIds, sub(mload(inActiveOrderIds), 1))
                }
            }
        }
    }

    function calculateFilledAmount(
        OrderBook storage self,
        ProtocolTypes.Side _side,
        uint256 _amount,
        uint256 _unitPrice
    )
        internal
        view
        returns (uint256 lastUnitPrice, uint256 filledAmount, uint256 filledAmountInFV)
    {
        if (_amount == 0) return (0, 0, 0);

        if (_side == ProtocolTypes.Side.LEND) {
            return
                self.borrowOrders[self.maturity].calculateDroppedAmountFromLeft(
                    _amount,
                    0,
                    _unitPrice
                );
        } else {
            return
                self.lendOrders[self.maturity].calculateDroppedAmountFromRight(
                    _amount,
                    0,
                    _unitPrice
                );
        }
    }

    function updateUserMaturity(OrderBook storage self, address _user) internal {
        uint256 userMaturity = self.userCurrentMaturities[_user];
        uint256 orderBookMaturity = self.maturity;

        if (userMaturity != orderBookMaturity) {
            if (
                self.activeLendOrderIds[_user].length > 0 ||
                self.activeBorrowOrderIds[_user].length > 0
            ) {
                revert PastMaturityOrderExists();
            }

            self.userCurrentMaturities[_user] = orderBookMaturity;
        }
    }

    function placeOrder(
        OrderBook storage self,
        ProtocolTypes.Side _side,
        address _user,
        uint256 _amount,
        uint256 _unitPrice
    ) internal returns (uint48 orderId) {
        orderId = _nextOrderId(self);
        self.orders[orderId] = _packOrder(_side, _unitPrice, self.maturity, block.timestamp);

        if (_side == ProtocolTypes.Side.LEND) {
            self.lendOrders[self.maturity].insertOrder(_unitPrice, orderId, _user, _amount);
            self.activeLendOrderIds[_user].push(orderId);
        } else if (_side == ProtocolTypes.Side.BORROW) {
            self.borrowOrders[self.maturity].insertOrder(_unitPrice, orderId, _user, _amount);
            self.activeBorrowOrderIds[_user].push(orderId);
        }
    }

    function fillOrders(
        OrderBook storage self,
        ProtocolTypes.Side _side,
        uint256 _amount,
        uint256 _amountInFV,
        uint256 _unitPrice
    )
        internal
        returns (
            FilledOrder memory filledOrder,
            PartiallyFilledOrder memory partiallyFilledOrder,
            uint256 remainingAmount,
            bool orderExists
        )
    {
        PartiallyRemovedOrder memory partiallyRemovedOrder;

        if (_side == ProtocolTypes.Side.BORROW) {
            OrderStatisticsTreeLib.Tree storage orders = self.lendOrders[self.maturity];
            (
                filledOrder.unitPrice,
                filledOrder.amount,
                filledOrder.futureValue,
                remainingAmount,
                partiallyRemovedOrder
            ) = orders.dropRight(_amount, _amountInFV, _unitPrice);
            orderExists = orders.hasOrders();
        } else if (_side == ProtocolTypes.Side.LEND) {
            OrderStatisticsTreeLib.Tree storage orders = self.borrowOrders[self.maturity];
            (
                filledOrder.unitPrice,
                filledOrder.amount,
                filledOrder.futureValue,
                remainingAmount,
                partiallyRemovedOrder
            ) = orders.dropLeft(_amount, _amountInFV, _unitPrice);
            orderExists = orders.hasOrders();
        }

        partiallyFilledOrder = PartiallyFilledOrder(
            partiallyRemovedOrder.orderId,
            partiallyRemovedOrder.maker,
            partiallyRemovedOrder.amount,
            partiallyRemovedOrder.futureValue
        );
    }

    function setInitialBlockUnitPrice(OrderBook storage self, uint256 _unitPrice) internal {
        self.blockUnitPriceHistory = uint16(_unitPrice);
        self.lastBlockUnitPriceTimestamp = uint48(block.timestamp);
        self.lastOrderTimestamp = uint48(block.timestamp);
    }

    function updateBlockUnitPriceHistory(
        OrderBook storage self,
        uint256 _filledAmount,
        uint256 _filledFutureValue,
        uint256 _minimumReliableAmount
    ) internal returns (uint256 latestBlockUnitPrice, bool isUpdated) {
        latestBlockUnitPrice = _unpackBlockUnitPriceHistory(self.blockUnitPriceHistory)[0];

        if (self.lastOrderTimestamp != block.timestamp) {
            if (self.isReliableBlock) {
                latestBlockUnitPrice = (self.blockTotalAmount * Constants.PRICE_DIGIT).div(
                    self.blockTotalFutureValue
                );

                // Remove the oldest block unit price and add the latest block unit price
                self.blockUnitPriceHistory =
                    uint16(latestBlockUnitPrice) |
                    (self.blockUnitPriceHistory << 16);
                self.lastBlockUnitPriceTimestamp = uint48(block.timestamp);
                isUpdated = true;
            }

            self.lastOrderTimestamp = uint48(block.timestamp);
            self.blockTotalAmount = _filledAmount;
            self.blockTotalFutureValue = _filledFutureValue;
            self.isReliableBlock = false;
        } else {
            self.blockTotalAmount += _filledAmount;
            self.blockTotalFutureValue += _filledFutureValue;
        }

        if (
            self.blockTotalAmount >= _minimumReliableAmount ||
            (self.blockTotalAmount > 0 && latestBlockUnitPrice == 0)
        ) {
            self.isReliableBlock = true;
        }
    }

    function removeOrder(
        OrderBook storage self,
        address _user,
        uint48 _orderId
    ) internal returns (ProtocolTypes.Side, uint256, uint256) {
        (ProtocolTypes.Side side, uint256 unitPrice, , ) = _unpackOrder(self.orders[_orderId]);
        uint256 removedAmount;

        if (side == ProtocolTypes.Side.LEND) {
            removedAmount = self.lendOrders[self.maturity].removeOrder(unitPrice, _orderId);
            _removeOrderIdFromOrders(self.activeLendOrderIds[_user], _orderId);
        } else if (side == ProtocolTypes.Side.BORROW) {
            removedAmount = self.borrowOrders[self.maturity].removeOrder(unitPrice, _orderId);
            _removeOrderIdFromOrders(self.activeBorrowOrderIds[_user], _orderId);
        }

        delete self.orders[_orderId];

        return (side, removedAmount, unitPrice);
    }

    function calculateItayoseResult(
        OrderBook storage self
    )
        internal
        view
        returns (
            uint256 openingUnitPrice,
            uint256 lastLendUnitPrice,
            uint256 lastBorrowUnitPrice,
            uint256 totalOffsetAmount
        )
    {
        uint256 lendUnitPrice = self.lendOrders[self.maturity].last();
        uint256 borrowUnitPrice = self.borrowOrders[self.maturity].first();
        uint256 lendAmount = self.lendOrders[self.maturity].getNodeTotalAmount(lendUnitPrice);
        uint256 borrowAmount = self.borrowOrders[self.maturity].getNodeTotalAmount(borrowUnitPrice);

        OrderStatisticsTreeLib.Tree storage borrowOrders = self.borrowOrders[self.maturity];
        OrderStatisticsTreeLib.Tree storage lendOrders = self.lendOrders[self.maturity];

        // Return 0 if no orders is filled
        if (borrowUnitPrice > lendUnitPrice || borrowUnitPrice == 0 || lendUnitPrice == 0) {
            return (0, 0, 0, 0);
        }

        while (borrowUnitPrice <= lendUnitPrice && borrowUnitPrice > 0 && lendUnitPrice > 0) {
            lastLendUnitPrice = lendUnitPrice;
            lastBorrowUnitPrice = borrowUnitPrice;

            if (lendAmount > borrowAmount) {
                openingUnitPrice = lendUnitPrice;
                totalOffsetAmount += borrowAmount;
                lendAmount -= borrowAmount;
                borrowUnitPrice = borrowOrders.next(borrowUnitPrice);
                borrowAmount = borrowOrders.getNodeTotalAmount(borrowUnitPrice);
            } else if (lendAmount < borrowAmount) {
                openingUnitPrice = borrowUnitPrice;
                totalOffsetAmount += lendAmount;
                borrowAmount -= lendAmount;
                lendUnitPrice = lendOrders.prev(lendUnitPrice);
                lendAmount = lendOrders.getNodeTotalAmount(lendUnitPrice);
            } else {
                openingUnitPrice = (lendUnitPrice + borrowUnitPrice).div(2);
                totalOffsetAmount += lendAmount;
                lendUnitPrice = lendOrders.prev(lendUnitPrice);
                borrowUnitPrice = borrowOrders.next(borrowUnitPrice);
                lendAmount = lendOrders.getNodeTotalAmount(lendUnitPrice);
                borrowAmount = borrowOrders.getNodeTotalAmount(borrowUnitPrice);
            }
        }
    }

    function getOrderExecutionConditions(
        OrderBook storage self,
        ProtocolTypes.Side _side,
        uint256 _unitPrice,
        uint256 _circuitBreakerLimitRange,
        bool _isReadOnly
    )
        internal
        view
        returns (
            bool isFilled,
            uint256 executedUnitPrice,
            bool ignoreRemainingAmount,
            bool orderExists
        )
    {
        bool isLend = _side == ProtocolTypes.Side.LEND;
        uint256 cbThresholdUnitPrice;
        uint256 bestUnitPrice;

        if (isLend) {
            bestUnitPrice = self.borrowOrders[self.maturity].first();
            cbThresholdUnitPrice = getLendCircuitBreakerThreshold(
                self,
                _circuitBreakerLimitRange,
                _isReadOnly
            );
        } else {
            bestUnitPrice = self.lendOrders[self.maturity].last();
            cbThresholdUnitPrice = getBorrowCircuitBreakerThreshold(
                self,
                _circuitBreakerLimitRange,
                _isReadOnly
            );
        }

        orderExists = bestUnitPrice != 0;

        if (
            _unitPrice == 0 ||
            (orderExists &&
                cbThresholdUnitPrice != 0 &&
                ((isLend && _unitPrice > cbThresholdUnitPrice) ||
                    (!isLend && _unitPrice < cbThresholdUnitPrice)))
        ) {
            executedUnitPrice = cbThresholdUnitPrice;
            ignoreRemainingAmount = true;
        } else {
            executedUnitPrice = _unitPrice;
            ignoreRemainingAmount = false;
        }

        if (orderExists) {
            isFilled = isLend
                ? bestUnitPrice <= executedUnitPrice
                : bestUnitPrice >= executedUnitPrice;
        }
    }

    function getLendCircuitBreakerThreshold(
        OrderBook storage self,
        uint256 _circuitBreakerLimitRange,
        bool _isReadOnly
    ) internal view returns (uint256 cbThresholdUnitPrice) {
        uint256 blockUnitPriceAverage = getBlockUnitPriceAverage(self, 3, _isReadOnly);
        cbThresholdUnitPrice = (blockUnitPriceAverage *
            (Constants.PCT_DIGIT + _circuitBreakerLimitRange * 2)).div(Constants.PCT_DIGIT);

        if (cbThresholdUnitPrice > Constants.PRICE_DIGIT || blockUnitPriceAverage == 0) {
            cbThresholdUnitPrice = Constants.PRICE_DIGIT;
        } else if (
            cbThresholdUnitPrice < blockUnitPriceAverage + CIRCUIT_BREAKER_MINIMUM_LEND_RANGE
        ) {
            cbThresholdUnitPrice = blockUnitPriceAverage + CIRCUIT_BREAKER_MINIMUM_LEND_RANGE;
        }
    }

    function getBorrowCircuitBreakerThreshold(
        OrderBook storage self,
        uint256 _circuitBreakerLimitRange,
        bool _isReadOnly
    ) internal view returns (uint256 cbThresholdUnitPrice) {
        uint256 blockUnitPriceAverage = getBlockUnitPriceAverage(self, 5, _isReadOnly);
        cbThresholdUnitPrice = (blockUnitPriceAverage *
            (Constants.PCT_DIGIT - _circuitBreakerLimitRange)).div(Constants.PCT_DIGIT);

        if (
            cbThresholdUnitPrice == 0 ||
            blockUnitPriceAverage == 0 ||
            blockUnitPriceAverage <= CIRCUIT_BREAKER_MINIMUM_BORROW_RANGE
        ) {
            cbThresholdUnitPrice = 1;
        } else if (
            blockUnitPriceAverage < cbThresholdUnitPrice + CIRCUIT_BREAKER_MINIMUM_BORROW_RANGE
        ) {
            cbThresholdUnitPrice = blockUnitPriceAverage - CIRCUIT_BREAKER_MINIMUM_BORROW_RANGE;
        }
    }

    /**
     * @notice Increases and returns id of last order in order book.
     * @return The new order id
     */
    function _nextOrderId(OrderBook storage self) private returns (uint48) {
        self.lastOrderId++;
        return self.lastOrderId;
    }

    function _removeOrderIdFromOrders(uint48[] storage orders, uint256 orderId) private {
        uint256 lastOrderIndex = orders.length - 1;
        for (uint256 i; i <= lastOrderIndex; i++) {
            if (orders[i] == orderId) {
                if (i != lastOrderIndex) {
                    uint48 lastOrderId = orders[lastOrderIndex];
                    orders[i] = lastOrderId;
                }

                orders.pop();
                break;
            }
        }
    }

    /**
     * @notice Packs order parameters into uint256
     */
    function _packOrder(
        ProtocolTypes.Side _side,
        uint256 _unitPrice,
        uint256 _maturity,
        uint256 _timestamp
    ) private pure returns (uint256) {
        return uint256(_side) | (_unitPrice << 8) | (_maturity << 24) | (_timestamp << 88);
    }

    /**
     * @notice Unpacks order parameters from uint256
     */
    function _unpackOrder(
        uint256 _order
    )
        private
        pure
        returns (ProtocolTypes.Side side, uint256 unitPrice, uint256 maturity, uint256 timestamp)
    {
        side = ProtocolTypes.Side(uint8(_order));
        unitPrice = uint16(_order >> 8);
        maturity = uint64(_order >> 24);
        timestamp = uint64(_order >> 88);
    }

    function _unpackBlockUnitPriceHistory(
        uint80 _blockUnitPriceHistory
    ) private pure returns (uint256[] memory prices) {
        prices = new uint256[](5);

        prices[0] = uint16(_blockUnitPriceHistory);
        prices[1] = uint16(_blockUnitPriceHistory >> 16);
        prices[2] = uint16(_blockUnitPriceHistory >> 32);
        prices[3] = uint16(_blockUnitPriceHistory >> 48);
        prices[4] = uint16(_blockUnitPriceHistory >> 64);
    }
}
","keccak256":"0x7154c684dd5c19c57505b0123296ed06d885214856c266ff75371689eb542320","license":"BUSL-1.1"},"contracts/protocol/libraries/OrderStatisticsTreeLib.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.19;

// libraries
import {Constants} from "../libraries/Constants.sol";
// types
import {RoundingUint256} from "./math/RoundingUint256.sol";

struct RemainingOrder {
    uint48 orderId;
    address maker;
    uint256 amount;
    uint256 unitPrice;
}

struct PartiallyRemovedOrder {
    uint48 orderId;
    address maker;
    uint256 amount;
    uint256 futureValue;
}

/**
 * @notice OrderStatisticsTreeLib is a Red-Black Tree binary search library
 * based on the following library that is extended to manage order data.
 *
 * https://github.com/rob-Hitchens/OrderStatisticsTree
 *
 */
library OrderStatisticsTreeLib {
    using RoundingUint256 for uint256;
    uint256 private constant EMPTY = 0;

    struct Node {
        uint256 parent;
        uint256 left;
        uint256 right;
        bool red;
        uint48 head;
        uint48 tail;
        uint256 orderCounter;
        uint256 orderTotalAmount;
        mapping(uint48 orderId => OrderItem) orders;
    }

    struct Tree {
        uint256 root;
        mapping(uint256 value => Node) nodes;
    }

    struct OrderItem {
        uint48 orderId;
        uint48 next;
        uint48 prev;
        address maker;
        uint256 amount;
    }

    function first(Tree storage self) internal view returns (uint256 value) {
        value = self.root;
        if (value == EMPTY) return 0;
        while (self.nodes[value].left != EMPTY) {
            value = self.nodes[value].left;
        }
    }

    function last(Tree storage self) internal view returns (uint256 value) {
        value = self.root;
        if (value == EMPTY) return 0;
        while (self.nodes[value].right != EMPTY) {
            value = self.nodes[value].right;
        }
    }

    function hasOrders(Tree storage self) internal view returns (bool) {
        return self.root != EMPTY;
    }

    function next(Tree storage self, uint256 value) internal view returns (uint256 cursor) {
        require(value != EMPTY, "OrderStatisticsTreeLib: Starting value cannot be zero");
        if (self.nodes[value].right != EMPTY) {
            cursor = treeMinimum(self, self.nodes[value].right);
        } else {
            cursor = self.nodes[value].parent;
            while (cursor != EMPTY && value == self.nodes[cursor].right) {
                value = cursor;
                cursor = self.nodes[cursor].parent;
            }
        }
    }

    function prev(Tree storage self, uint256 value) internal view returns (uint256 cursor) {
        require(value != EMPTY, "OrderStatisticsTreeLib: Starting value cannot be zero");
        if (self.nodes[value].left != EMPTY) {
            cursor = treeMaximum(self, self.nodes[value].left);
        } else {
            cursor = self.nodes[value].parent;
            while (cursor != EMPTY && value == self.nodes[cursor].left) {
                value = cursor;
                cursor = self.nodes[cursor].parent;
            }
        }
    }

    function search(
        Tree storage self,
        uint256 value
    ) internal view returns (bool valueExists, uint256 parent) {
        uint256 cursor = self.root;

        while (cursor != EMPTY) {
            if (value < cursor) {
                parent = cursor;
                cursor = self.nodes[cursor].left;
            } else if (value > cursor) {
                parent = cursor;
                cursor = self.nodes[cursor].right;
            }

            if (value == cursor) {
                break;
            }
        }

        valueExists = cursor != EMPTY;
    }

    function exists(Tree storage self, uint256 value) internal view returns (bool) {
        if (value == self.root) return true;

        uint256 cursor = value;
        while (self.nodes[cursor].parent != EMPTY) {
            uint256 parent = self.nodes[cursor].parent;
            Node storage gn = self.nodes[parent];
            if (gn.left != cursor && gn.right != cursor) {
                return false;
            }
            if (parent == self.root) {
                return true;
            }
            cursor = parent;
        }
        return false;
    }

    function isActiveOrderId(
        Tree storage self,
        uint256 value,
        uint48 orderId
    ) internal view returns (bool) {
        return orderIdExists(self, value, orderId) && exists(self, value);
    }

    function getNode(
        Tree storage self,
        uint256 value
    ) internal view returns (uint256, uint256, uint256, bool, uint256, uint256, uint256, uint256) {
        require(exists(self, value), "OrderStatisticsTreeLib: Value does not exist");
        Node storage gn = self.nodes[value];
        return (
            gn.parent,
            gn.left,
            gn.right,
            gn.red,
            gn.head,
            gn.tail,
            gn.orderCounter,
            gn.orderTotalAmount
        );
    }

    function getNodeCount(Tree storage self, uint256 value) internal view returns (uint256) {
        Node storage gn = self.nodes[value];
        return gn.orderCounter;
    }

    function getNodeTotalAmount(
        Tree storage self,
        uint256 value
    ) internal view returns (uint256 totalAmount) {
        return self.nodes[value].orderTotalAmount;
    }

    function getNodeOrderIds(
        Tree storage self,
        uint256 value
    ) internal view returns (uint48[] memory orderIds) {
        Node storage gn = self.nodes[value];
        OrderItem memory order = gn.orders[gn.head];
        orderIds = new uint48[](gn.orderCounter);

        for (uint256 i; i < gn.orderCounter; i++) {
            orderIds[i] = order.orderId;
            order = gn.orders[order.next];
        }
    }

    function count(Tree storage self) internal view returns (uint256) {
        return getNodeCount(self, self.root);
    }

    function insert(Tree storage self, uint256 value) internal {
        require(value != EMPTY, "OrderStatisticsTreeLib: Value to insert cannot be zero");
        uint256 cursor;
        uint256 probe = self.root;
        while (probe != EMPTY) {
            cursor = probe;
            if (value < probe) {
                probe = self.nodes[probe].left;
            } else if (value > probe) {
                probe = self.nodes[probe].right;
            } else if (value == probe) {
                return;
            }
        }

        Node storage nValue = self.nodes[value];
        // Update order info as a new one if there is already an old node
        if (self.root == EMPTY || nValue.orderCounter != 0) {
            nValue.orderCounter = 0;
            nValue.orderTotalAmount = 0;
            _setHead(self, value, 0);
            _setTail(self, value, 0);
        }
        nValue.parent = cursor;
        nValue.left = EMPTY;
        nValue.right = EMPTY;
        nValue.red = true;

        if (cursor == EMPTY) {
            self.root = value;
        } else if (value < cursor) {
            self.nodes[cursor].left = value;
        } else {
            self.nodes[cursor].right = value;
        }
        insertFixup(self, value);
    }

    function remove(Tree storage self, uint256 value) internal {
        require(value != EMPTY, "OrderStatisticsTreeLib: Value to remove cannot be zero");
        Node storage nValue = self.nodes[value];
        uint256 probe;
        uint256 cursor;
        if (nValue.orderCounter == 0) {
            if (self.nodes[value].left == EMPTY || self.nodes[value].right == EMPTY) {
                cursor = value;
            } else {
                cursor = self.nodes[value].right;
                while (self.nodes[cursor].left != EMPTY) {
                    cursor = self.nodes[cursor].left;
                }
            }
            if (self.nodes[cursor].left != EMPTY) {
                probe = self.nodes[cursor].left;
            } else {
                probe = self.nodes[cursor].right;
            }
            uint256 cursorParent = self.nodes[cursor].parent;
            self.nodes[probe].parent = cursorParent;
            if (cursorParent != EMPTY) {
                if (cursor == self.nodes[cursorParent].left) {
                    self.nodes[cursorParent].left = probe;
                } else {
                    self.nodes[cursorParent].right = probe;
                }
            } else {
                self.root = probe;
            }
            bool doFixup = !self.nodes[cursor].red;
            if (cursor != value) {
                replaceParent(self, cursor, value);
                self.nodes[cursor].left = self.nodes[value].left;
                self.nodes[self.nodes[cursor].left].parent = cursor;
                self.nodes[cursor].right = self.nodes[value].right;
                self.nodes[self.nodes[cursor].right].parent = cursor;
                self.nodes[cursor].red = self.nodes[value].red;
                (cursor, value) = (value, cursor);
            }
            if (doFixup) {
                removeFixup(self, probe);
            }
            delete self.nodes[cursor];
        }
    }

    function treeMinimum(Tree storage self, uint256 value) private view returns (uint256) {
        while (self.nodes[value].left != EMPTY) {
            value = self.nodes[value].left;
        }
        return value;
    }

    function treeMaximum(Tree storage self, uint256 value) private view returns (uint256) {
        while (self.nodes[value].right != EMPTY) {
            value = self.nodes[value].right;
        }
        return value;
    }

    function rotateLeft(Tree storage self, uint256 value) private {
        uint256 cursor = self.nodes[value].right;
        uint256 parent = self.nodes[value].parent;
        uint256 cursorLeft = self.nodes[cursor].left;
        self.nodes[value].right = cursorLeft;
        if (cursorLeft != EMPTY) {
            self.nodes[cursorLeft].parent = value;
        }
        self.nodes[cursor].parent = parent;
        if (parent == EMPTY) {
            self.root = cursor;
        } else if (value == self.nodes[parent].left) {
            self.nodes[parent].left = cursor;
        } else {
            self.nodes[parent].right = cursor;
        }
        self.nodes[cursor].left = value;
        self.nodes[value].parent = cursor;
    }

    function rotateRight(Tree storage self, uint256 value) private {
        uint256 cursor = self.nodes[value].left;
        uint256 parent = self.nodes[value].parent;
        uint256 cursorRight = self.nodes[cursor].right;
        self.nodes[value].left = cursorRight;
        if (cursorRight != EMPTY) {
            self.nodes[cursorRight].parent = value;
        }
        self.nodes[cursor].parent = parent;
        if (parent == EMPTY) {
            self.root = cursor;
        } else if (value == self.nodes[parent].right) {
            self.nodes[parent].right = cursor;
        } else {
            self.nodes[parent].left = cursor;
        }
        self.nodes[cursor].right = value;
        self.nodes[value].parent = cursor;
    }

    function insertFixup(Tree storage self, uint256 value) private {
        uint256 cursor;
        while (value != self.root && self.nodes[self.nodes[value].parent].red) {
            uint256 valueParent = self.nodes[value].parent;
            if (valueParent == self.nodes[self.nodes[valueParent].parent].left) {
                cursor = self.nodes[self.nodes[valueParent].parent].right;
                if (self.nodes[cursor].red) {
                    self.nodes[valueParent].red = false;
                    self.nodes[cursor].red = false;
                    self.nodes[self.nodes[valueParent].parent].red = true;
                    value = self.nodes[valueParent].parent;
                } else {
                    if (value == self.nodes[valueParent].right) {
                        value = valueParent;
                        rotateLeft(self, value);
                    }
                    valueParent = self.nodes[value].parent;
                    self.nodes[valueParent].red = false;
                    self.nodes[self.nodes[valueParent].parent].red = true;
                    rotateRight(self, self.nodes[valueParent].parent);
                }
            } else {
                cursor = self.nodes[self.nodes[valueParent].parent].left;
                if (self.nodes[cursor].red) {
                    self.nodes[valueParent].red = false;
                    self.nodes[cursor].red = false;
                    self.nodes[self.nodes[valueParent].parent].red = true;
                    value = self.nodes[valueParent].parent;
                } else {
                    if (value == self.nodes[valueParent].left) {
                        value = valueParent;
                        rotateRight(self, value);
                    }
                    valueParent = self.nodes[value].parent;
                    self.nodes[valueParent].red = false;
                    self.nodes[self.nodes[valueParent].parent].red = true;
                    rotateLeft(self, self.nodes[valueParent].parent);
                }
            }
        }
        self.nodes[self.root].red = false;
    }

    function replaceParent(Tree storage self, uint256 a, uint256 b) private {
        uint256 bParent = self.nodes[b].parent;
        self.nodes[a].parent = bParent;
        if (bParent == EMPTY) {
            self.root = a;
        } else {
            if (b == self.nodes[bParent].left) {
                self.nodes[bParent].left = a;
            } else {
                self.nodes[bParent].right = a;
            }
        }
    }

    function removeFixup(Tree storage self, uint256 value) private {
        uint256 cursor;
        while (value != self.root && !self.nodes[value].red) {
            uint256 valueParent = self.nodes[value].parent;
            if (value == self.nodes[valueParent].left) {
                cursor = self.nodes[valueParent].right;
                if (self.nodes[cursor].red) {
                    self.nodes[cursor].red = false;
                    self.nodes[valueParent].red = true;
                    rotateLeft(self, valueParent);
                    cursor = self.nodes[valueParent].right;
                }
                if (
                    !self.nodes[self.nodes[cursor].left].red &&
                    !self.nodes[self.nodes[cursor].right].red
                ) {
                    self.nodes[cursor].red = true;
                    value = valueParent;
                } else {
                    if (!self.nodes[self.nodes[cursor].right].red) {
                        self.nodes[self.nodes[cursor].left].red = false;
                        self.nodes[cursor].red = true;
                        rotateRight(self, cursor);
                        cursor = self.nodes[valueParent].right;
                    }
                    self.nodes[cursor].red = self.nodes[valueParent].red;
                    self.nodes[valueParent].red = false;
                    self.nodes[self.nodes[cursor].right].red = false;
                    rotateLeft(self, valueParent);
                    value = self.root;
                }
            } else {
                cursor = self.nodes[valueParent].left;
                if (self.nodes[cursor].red) {
                    self.nodes[cursor].red = false;
                    self.nodes[valueParent].red = true;
                    rotateRight(self, valueParent);
                    cursor = self.nodes[valueParent].left;
                }
                if (
                    !self.nodes[self.nodes[cursor].right].red &&
                    !self.nodes[self.nodes[cursor].left].red
                ) {
                    self.nodes[cursor].red = true;
                    value = valueParent;
                } else {
                    if (!self.nodes[self.nodes[cursor].left].red) {
                        self.nodes[self.nodes[cursor].right].red = false;
                        self.nodes[cursor].red = true;
                        rotateLeft(self, cursor);
                        cursor = self.nodes[valueParent].left;
                    }
                    self.nodes[cursor].red = self.nodes[valueParent].red;
                    self.nodes[valueParent].red = false;
                    self.nodes[self.nodes[cursor].left].red = false;
                    rotateRight(self, valueParent);
                    value = self.root;
                }
            }
        }
        self.nodes[value].red = false;
    }

    function calculateDroppedAmountFromLeft(
        Tree storage self,
        uint256 amount,
        uint256 amountInFV,
        uint256 limitValue
    )
        internal
        view
        returns (uint256 droppedValue, uint256 droppedAmount, uint256 droppedAmountInFV)
    {
        (droppedValue, , , droppedAmount, droppedAmountInFV, , ) = _calculateDroppedAmountFromLeft(
            self,
            amount,
            amountInFV,
            limitValue,
            first(self)
        );
    }

    function calculateDroppedAmountFromRight(
        Tree storage self,
        uint256 amount,
        uint256 amountInFV,
        uint256 limitValue
    )
        internal
        view
        returns (uint256 droppedValue, uint256 droppedAmount, uint256 droppedAmountInFV)
    {
        (droppedValue, , , droppedAmount, droppedAmountInFV, , ) = _calculateDroppedAmountFromRight(
            self,
            amount,
            amountInFV,
            limitValue,
            last(self)
        );
    }

    function dropLeft(
        Tree storage self,
        uint256 amount,
        uint256 amountInFV,
        uint256 limitValue
    )
        internal
        returns (
            uint256 droppedValue,
            uint256 droppedAmount,
            uint256 droppedAmountInFV,
            uint256 remainingAmount,
            PartiallyRemovedOrder memory partiallyRemovedOrder
        )
    {
        uint256 cursor = first(self);
        uint256 cursorNodeAmount;
        uint256 exceededAmount;
        uint256 exceededAmountInFV;

        require(cursor <= limitValue || limitValue == 0, "Insufficient limit value");

        (
            droppedValue,
            cursor,
            cursorNodeAmount,
            droppedAmount,
            droppedAmountInFV,
            exceededAmount,
            exceededAmountInFV
        ) = _calculateDroppedAmountFromLeft(self, amount, amountInFV, limitValue, cursor);

        uint256 totalNodeAmount = droppedAmount + exceededAmount;

        if (totalNodeAmount > 0) {
            if (exceededAmount > 0) {
                cursor = droppedValue;
                // Update order ids in the node.
                partiallyRemovedOrder = removeOrders(
                    self,
                    cursor,
                    cursorNodeAmount - exceededAmount
                );
            } else if (exceededAmountInFV > 0) {
                cursor = droppedValue;
                // Update order ids in the node.
                partiallyRemovedOrder = removeOrders(
                    self,
                    cursor,
                    cursorNodeAmount - _calculatePresentValue(cursor, exceededAmountInFV)
                );
            }

            self.nodes[cursor].left = 0;

            uint256 parent = self.nodes[cursor].parent;

            if (cursor != EMPTY) {
                while (parent != EMPTY) {
                    if (parent > cursor) {
                        // Relink the nodes
                        if (self.nodes[cursor].parent != parent) {
                            self.nodes[cursor].parent = parent;
                            self.nodes[parent].left = cursor;
                        }

                        cursor = parent;
                    }

                    parent = self.nodes[parent].parent;
                }
            }
        }

        if (amount > totalNodeAmount) {
            remainingAmount = amount - totalNodeAmount;
        }

        uint256 lastNode = last(self);

        if (lastNode == droppedValue && self.nodes[lastNode].orderTotalAmount == 0) {
            // The case that all node is dropped.
            self.root = EMPTY;
        } else if (
            droppedValue > self.root ||
            (droppedValue == self.root && droppedAmount == totalNodeAmount)
        ) {
            // The case that the root node is dropped
            self.root = cursor;
            self.nodes[cursor].parent = 0;
        }

        rotateTreeToLeft(self);
    }

    function dropRight(
        Tree storage self,
        uint256 amount,
        uint256 amountInFV,
        uint256 limitValue
    )
        internal
        returns (
            uint256 droppedValue,
            uint256 droppedAmount,
            uint256 droppedAmountInFV,
            uint256 remainingAmount,
            PartiallyRemovedOrder memory partiallyRemovedOrder
        )
    {
        uint256 cursor = last(self);
        uint256 cursorNodeAmount;
        uint256 exceededAmount;
        uint256 exceededAmountInFV;

        require(cursor >= limitValue || limitValue == 0, "Insufficient limit value");

        (
            droppedValue,
            cursor,
            cursorNodeAmount,
            droppedAmount,
            droppedAmountInFV,
            exceededAmount,
            exceededAmountInFV
        ) = _calculateDroppedAmountFromRight(self, amount, amountInFV, limitValue, cursor);

        uint256 totalNodeAmount = droppedAmount + exceededAmount;

        if (totalNodeAmount > 0) {
            if (exceededAmount > 0) {
                cursor = droppedValue;
                // Update order ids in the node.
                partiallyRemovedOrder = removeOrders(
                    self,
                    cursor,
                    cursorNodeAmount - exceededAmount
                );
            } else if (exceededAmountInFV > 0) {
                cursor = droppedValue;
                // Update order ids in the node.
                partiallyRemovedOrder = removeOrders(
                    self,
                    cursor,
                    cursorNodeAmount - _calculatePresentValue(cursor, exceededAmountInFV)
                );
            }

            self.nodes[cursor].right = 0;

            uint256 parent = self.nodes[cursor].parent;

            if (cursor != EMPTY) {
                while (parent != EMPTY) {
                    if (parent < cursor) {
                        // Relink the nodes
                        if (self.nodes[cursor].parent != parent) {
                            self.nodes[cursor].parent = parent;
                            self.nodes[parent].right = cursor;
                        }

                        cursor = parent;
                    }

                    parent = self.nodes[parent].parent;
                }
            }
        }

        if (amount > totalNodeAmount) {
            remainingAmount = amount - totalNodeAmount;
        }

        uint256 firstNode = first(self);

        if (firstNode == droppedValue && self.nodes[firstNode].orderTotalAmount == 0) {
            // The case that all node is dropped.
            self.root = EMPTY;
        } else if (
            droppedValue < self.root ||
            (droppedValue == self.root && droppedAmount == totalNodeAmount)
        ) {
            // The case that the root node is dropped
            self.root = cursor;
            self.nodes[cursor].parent = 0;
        }

        rotateTreeToRight(self);
    }

    function rotateTreeToLeft(Tree storage self) internal {
        if (self.nodes[self.root].left == 0 && self.nodes[self.root].right != 0) {
            if (self.nodes[self.nodes[self.root].right].left != 0) {
                rotateRight(self, self.nodes[self.root].right);
            }
            rotateLeft(self, self.root);
        }

        if (self.nodes[self.root].red) {
            self.nodes[self.root].red = false;
        }
    }

    function rotateTreeToRight(Tree storage self) internal {
        if (self.nodes[self.root].right == 0 && self.nodes[self.root].left != 0) {
            if (self.nodes[self.nodes[self.root].left].right != 0) {
                rotateLeft(self, self.nodes[self.root].left);
            }
            rotateRight(self, self.root);
        }

        if (self.nodes[self.root].red) {
            self.nodes[self.root].red = false;
        }
    }

    function getFutureValue(
        Tree storage self,
        uint256 value,
        uint48 orderId
    ) internal view returns (uint256) {
        return _calculateFutureValue(value, self.nodes[value].orders[orderId].amount);
    }

    // Double linked list functions
    /**
     * @dev Retrieves the Object denoted by `_id`.
     */
    function getOrderById(
        Tree storage self,
        uint256 value,
        uint48 orderId
    ) internal view returns (address maker, uint256 amount) {
        Node storage gn = self.nodes[value];
        OrderItem memory order = gn.orders[orderId];

        maker = order.maker;
        amount = order.amount;
    }

    /**
     * @dev Return boolean if value, amount and orderId exist in doubly linked list
     */
    function orderIdExists(
        Tree storage self,
        uint256 value,
        uint48 orderId
    ) internal view returns (bool) {
        uint48 cursor = orderId;
        Node storage gn = self.nodes[value];
        OrderItem memory order = gn.orders[cursor];

        if (order.orderId != cursor) {
            return false;
        }

        while (order.prev != EMPTY) {
            cursor = order.prev;
            order = gn.orders[cursor];
        }

        return cursor == gn.head;
    }

    function insertOrder(
        Tree storage self,
        uint256 value,
        uint48 orderId,
        address user,
        uint256 amount
    ) internal {
        require(amount > 0, "Insufficient amount");
        require(value <= Constants.PRICE_DIGIT, "Value too high");
        insert(self, value);

        addTail(self, value, orderId, user, amount);
    }

    function removeOrder(
        Tree storage self,
        uint256 value,
        uint48 orderId
    ) internal returns (uint256 amount) {
        amount = _removeOrder(self, value, orderId);
        remove(self, value);
    }

    function removeOrders(
        Tree storage self,
        uint256 value,
        uint256 _amount
    ) internal returns (PartiallyRemovedOrder memory partiallyRemovedOrder) {
        Node storage gn = self.nodes[value];

        require(
            gn.orderTotalAmount >= _amount,
            "OrderStatisticsTreeLib: Amount to remove is insufficient"
        );

        uint256 remainingAmount = _amount;
        OrderItem memory currentOrder = gn.orders[gn.head];
        uint48 orderId = gn.head;

        while (orderId != 0 && remainingAmount != 0) {
            currentOrder = gn.orders[orderId];

            if (currentOrder.amount <= remainingAmount) {
                remainingAmount -= currentOrder.amount;
                orderId = currentOrder.next;
            } else {
                partiallyRemovedOrder = PartiallyRemovedOrder(
                    currentOrder.orderId,
                    currentOrder.maker,
                    remainingAmount,
                    _calculateFutureValue(value, remainingAmount)
                );
                currentOrder = gn.orders[currentOrder.prev];
                break;
            }
        }

        if (currentOrder.orderId != 0) {
            _removeOrders(self, value, currentOrder.orderId);
        }

        if (partiallyRemovedOrder.amount > 0) {
            self.nodes[value].orders[partiallyRemovedOrder.orderId].amount -= partiallyRemovedOrder
                .amount;
            self.nodes[value].orderTotalAmount -= partiallyRemovedOrder.amount;
        }
    }

    /**
     * @dev Insert a new OrderItem as the new Head with `_amount` in the amount field, and orderId.
     */
    function addHead(
        Tree storage self,
        uint256 _value,
        uint48 _orderId,
        address _user,
        uint256 _amount
    ) internal {
        Node storage gn = self.nodes[_value];
        uint48 orderId = _createOrder(self, _value, _orderId, _user, _amount);
        _link(self, _value, orderId, gn.head);
        _setHead(self, _value, orderId);
        if (gn.tail == 0) _setTail(self, _value, orderId);
    }

    /**
     * @dev Insert a new OrderItem as the new Tail with `_amount` in the amount field, and orderId.
     */
    function addTail(
        Tree storage self,
        uint256 _value,
        uint48 _orderId,
        address _user,
        uint256 _amount
    ) internal {
        Node storage gn = self.nodes[_value];

        if (gn.head == 0) {
            addHead(self, _value, _orderId, _user, _amount);
        } else {
            uint48 orderId = _createOrder(self, _value, _orderId, _user, _amount);
            _link(self, _value, gn.tail, orderId);
            _setTail(self, _value, orderId);
        }
    }

    function _calculateDroppedAmountFromLeft(
        Tree storage self,
        uint256 amount,
        uint256 amountInFV,
        uint256 limitValue,
        uint256 firstValue
    )
        private
        view
        returns (
            uint256 droppedValue,
            uint256 cursor,
            uint256 cursorNodeAmount,
            uint256 droppedAmount,
            uint256 droppedAmountInFV,
            uint256 exceededAmount,
            uint256 exceededAmountInFV
        )
    {
        cursor = firstValue;

        while (
            (droppedAmount < amount || amount == EMPTY) &&
            (droppedAmountInFV < amountInFV || amountInFV == EMPTY) &&
            cursor != EMPTY &&
            (cursor <= limitValue || limitValue == EMPTY)
        ) {
            cursorNodeAmount = self.nodes[cursor].orderTotalAmount;
            droppedValue = cursor;

            droppedAmountInFV += _calculateFutureValue(cursor, cursorNodeAmount);
            droppedAmount += cursorNodeAmount;

            if (droppedAmount > amount && amount != EMPTY) {
                exceededAmount = droppedAmount - amount;
                exceededAmountInFV = _calculateFutureValue(cursor, exceededAmount);
            } else if (droppedAmountInFV > amountInFV && amountInFV != EMPTY) {
                exceededAmountInFV = droppedAmountInFV - amountInFV;
                exceededAmount = _calculatePresentValue(cursor, exceededAmountInFV);
            }

            cursor = next(self, cursor);
        }

        if (exceededAmount > 0) {
            droppedAmount -= exceededAmount;
        }
        if (exceededAmountInFV > 0) {
            droppedAmountInFV -= exceededAmountInFV;
        }
    }

    function _calculateDroppedAmountFromRight(
        Tree storage self,
        uint256 amount,
        uint256 amountInFV,
        uint256 limitValue,
        uint256 lastValue
    )
        private
        view
        returns (
            uint256 droppedValue,
            uint256 cursor,
            uint256 cursorNodeAmount,
            uint256 droppedAmount,
            uint256 droppedAmountInFV,
            uint256 exceededAmount,
            uint256 exceededAmountInFV
        )
    {
        cursor = lastValue;

        while (
            (droppedAmount < amount || amount == EMPTY) &&
            (droppedAmountInFV < amountInFV || amountInFV == EMPTY) &&
            cursor != EMPTY &&
            (cursor >= limitValue || limitValue == EMPTY)
        ) {
            cursorNodeAmount = self.nodes[cursor].orderTotalAmount;
            droppedValue = cursor;

            droppedAmountInFV += _calculateFutureValue(cursor, cursorNodeAmount);
            droppedAmount += cursorNodeAmount;

            if (droppedAmount > amount && amount != EMPTY) {
                exceededAmount = droppedAmount - amount;
                exceededAmountInFV = _calculateFutureValue(cursor, exceededAmount);
            } else if (droppedAmountInFV > amountInFV && amountInFV != EMPTY) {
                exceededAmountInFV = droppedAmountInFV - amountInFV;
                exceededAmount = _calculatePresentValue(cursor, exceededAmountInFV);
            }

            cursor = prev(self, cursor);
        }

        if (exceededAmount > 0) {
            droppedAmount -= exceededAmount;
        }
        if (exceededAmountInFV > 0) {
            droppedAmountInFV -= exceededAmountInFV;
        }
    }

    /**
     * @dev Internal function to create an unlinked Order.
     */
    function _createOrder(
        Tree storage self,
        uint256 value,
        uint48 orderId,
        address user,
        uint256 amount
    ) internal returns (uint48) {
        Node storage gn = self.nodes[value];
        require(
            gn.orders[orderId].maker == address(0),
            "OrderStatisticsTreeLib: Order id already exists"
        );

        gn.orderCounter += 1;
        gn.orderTotalAmount += amount;
        OrderItem memory order = OrderItem(orderId, 0, 0, user, amount);
        gn.orders[orderId] = order;
        return order.orderId;
    }

    /**
     * @dev Remove the OrderItem denoted by `_id` from the list.
     */
    function _removeOrder(
        Tree storage self,
        uint256 value,
        uint48 orderId
    ) internal returns (uint256 amount) {
        require(
            isActiveOrderId(self, value, orderId),
            "OrderStatisticsTreeLib: Order does not exist"
        );
        Node storage gn = self.nodes[value];

        OrderItem memory order = gn.orders[orderId];
        amount = order.amount;

        if (gn.head == orderId && gn.tail == orderId) {
            _setHead(self, value, 0);
            _setTail(self, value, 0);
        } else if (gn.head == orderId) {
            _setHead(self, value, order.next);
            gn.orders[order.next].prev = 0;
        } else if (gn.tail == orderId) {
            _setTail(self, value, order.prev);
            gn.orders[order.prev].next = 0;
        } else {
            _link(self, value, order.prev, order.next);
        }
        delete gn.orders[order.orderId];
        gn.orderCounter -= 1;
        gn.orderTotalAmount -= order.amount;
    }

    /**
     * @dev Remove the OrderItems older than or equal `orderId` from the list
     */
    function _removeOrders(Tree storage self, uint256 value, uint48 orderId) internal {
        require(
            isActiveOrderId(self, value, orderId),
            "OrderStatisticsTreeLib: Order does not exist"
        );
        Node storage gn = self.nodes[value];

        OrderItem memory order = gn.orders[orderId];
        uint48 cursor = gn.head;
        uint256 removedCount = 1;
        uint256 removedAmount = gn.orders[cursor].amount;

        while (cursor != orderId) {
            cursor = gn.orders[cursor].next;
            removedCount++;
            removedAmount += gn.orders[cursor].amount;
        }

        if (gn.tail == orderId) {
            _setHead(self, value, 0);
            _setTail(self, value, 0);
        } else {
            _setHead(self, value, order.next);
            gn.orders[order.next].prev = 0;
        }

        gn.orderCounter -= removedCount;
        gn.orderTotalAmount -= removedAmount;
    }

    /**
     * @dev Internal function to update the Head pointer.
     */
    function _setHead(Tree storage self, uint256 value, uint48 orderId) internal {
        Node storage gn = self.nodes[value];

        gn.head = orderId;
    }

    /**
     * @dev Internal function to update the Tail pointer.
     */
    function _setTail(Tree storage self, uint256 value, uint48 orderId) internal {
        Node storage gn = self.nodes[value];

        gn.tail = orderId;
    }

    /**
     * @dev Internal function to link an Object to another.
     */
    function _link(Tree storage self, uint256 value, uint48 prevId, uint48 nextId) internal {
        Node storage gn = self.nodes[value];

        gn.orders[prevId].next = nextId;
        gn.orders[nextId].prev = prevId;
    }

    function _calculateFutureValue(
        uint256 unitPrice,
        uint256 amount
    ) internal pure returns (uint256) {
        return (amount * Constants.PRICE_DIGIT).div(unitPrice);
    }

    function _calculatePresentValue(
        uint256 unitPrice,
        uint256 amount
    ) internal pure returns (uint256) {
        return (amount * unitPrice).div(Constants.PRICE_DIGIT);
    }
}
","keccak256":"0x7e726e6f70fd1ef9d56b3104a1a2963fc121a578313227b9dd087d81e9222a0b","license":"BUSL-1.1"},"contracts/protocol/libraries/QuickSort.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.19;

library QuickSort {
    function sort(uint256[] memory data) public pure returns (uint256[] memory) {
        quickSort(data, int256(0), int256(data.length - 1));
        return data;
    }

    function quickSort(uint256[] memory arr, int256 left, int256 right) internal pure {
        int256 i = left;
        int256 j = right;
        if (i == j) return;
        uint256 pivot = arr[uint256(left + (right - left) / 2)];
        while (i <= j) {
            while (arr[uint256(i)] < pivot) i++;
            while (pivot < arr[uint256(j)]) j--;
            if (i <= j) {
                (arr[uint256(i)], arr[uint256(j)]) = (arr[uint256(j)], arr[uint256(i)]);
                i++;
                j--;
            }
        }
        if (left < j) quickSort(arr, left, j);
        if (i < right) quickSort(arr, i, right);
    }
}
","keccak256":"0x1e5ceda4895c1ac2523f8fb4bc42996b10a12a683c6943c31614699a6717586a","license":"BUSL-1.1"},"contracts/protocol/libraries/logics/FundManagementLogic.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.19;

// dependencies
import {EnumerableSet} from "../../../dependencies/openzeppelin/utils/structs/EnumerableSet.sol";
import {SafeCast} from "../../../dependencies/openzeppelin/utils/math/SafeCast.sol";
// interfaces
import {ILendingMarket} from "../../interfaces/ILendingMarket.sol";
import {ILendingMarketController} from "../../interfaces/ILendingMarketController.sol";
import {IFutureValueVault} from "../../interfaces/IFutureValueVault.sol";
import {ILiquidationReceiver} from "../../interfaces/ILiquidationReceiver.sol";

// libraries
import {AddressResolverLib} from "../AddressResolverLib.sol";
import {QuickSort} from "../QuickSort.sol";
import {Constants} from "../Constants.sol";
import {RoundingUint256} from "../math/RoundingUint256.sol";
import {RoundingInt256} from "../math/RoundingInt256.sol";
// types
import {ProtocolTypes} from "../../types/ProtocolTypes.sol";
// storages
import {LendingMarketControllerStorage as Storage, TerminationCurrencyCache} from "../../storages/LendingMarketControllerStorage.sol";

library FundManagementLogic {
    using EnumerableSet for EnumerableSet.Bytes32Set;
    using EnumerableSet for EnumerableSet.UintSet;
    using SafeCast for uint256;
    using SafeCast for int256;
    using RoundingUint256 for uint256;
    using RoundingInt256 for int256;

    uint256 public constant BASE_MIN_DEBT_UNIT_PRICE = 9600;

    error NotRedemptionPeriod();
    error NotRepaymentPeriod();
    error NoRedemptionAmount();
    error NoRepaymentAmount();
    error AlreadyRedeemed();
    error InsufficientCollateral();

    struct CalculatedTotalFundInBaseCurrencyVars {
        address user;
        ILendingMarketController.AdditionalFunds additionalFunds;
        uint256 liquidationThresholdRate;
        bool[] isCollateral;
        bytes32[] ccys;
    }

    struct ActualFunds {
        int256 presentValue;
        uint256 claimableAmount;
        uint256 debtAmount;
        int256 futureValue;
        uint256 workingLendOrdersAmount;
        uint256 lentAmount;
        uint256 workingBorrowOrdersAmount;
        uint256 borrowedAmount;
        int256 genesisValue;
        int256 genesisValueInPV;
        int256 genesisValueInFV;
    }

    struct CalculateActualFundsVars {
        bool isTotal;
        bool isDefaultMarket;
        uint8 orderBookId;
        uint8 defaultOrderBookId;
        uint256 defaultOrderBookMarketUnitPrice;
        uint256[] maturities;
        int256 presentValueOfDefaultMarket;
        ILendingMarket market;
        IFutureValueVault futureValueVault;
        uint256 minDebtUnitPrice;
        uint256 defaultOrderBookMinDebtUnitPrice;
    }

    struct FutureValueVaultFunds {
        int256 genesisValue;
        int256 presentValue;
        int256 futureValue;
    }

    struct InactiveBorrowOrdersFunds {
        int256 genesisValue;
        int256 presentValue;
        int256 futureValue;
        uint256 workingOrdersAmount;
        uint256 borrowedAmount;
    }

    struct InactiveLendOrdersFunds {
        int256 genesisValue;
        int256 presentValue;
        int256 futureValue;
        uint256 workingOrdersAmount;
        uint256 lentAmount;
    }

    event OrderFilled(
        address indexed taker,
        bytes32 indexed ccy,
        ProtocolTypes.Side side,
        uint256 indexed maturity,
        uint256 amount,
        uint256 amountInFV,
        uint256 feeInFV
    );

    event OrdersFilledInAsync(
        address indexed taker,
        bytes32 indexed ccy,
        ProtocolTypes.Side side,
        uint256 indexed maturity,
        uint256 amount,
        uint256 amountInFV
    );

    event OrderPartiallyFilled(
        uint48 orderId,
        address indexed maker,
        bytes32 indexed ccy,
        ProtocolTypes.Side side,
        uint256 maturity,
        uint256 amount,
        uint256 amountInFV
    );

    event RedemptionExecuted(
        address indexed user,
        bytes32 indexed ccy,
        uint256 indexed maturity,
        uint256 amount
    );

    event RepaymentExecuted(
        address indexed user,
        bytes32 indexed ccy,
        uint256 indexed maturity,
        uint256 amount
    );

    event EmergencySettlementExecuted(address indexed user, uint256 amount);

    /**
     * @notice Converts the future value to the genesis value if there is balance in the past maturity.
     * @param _ccy Currency for pausing all lending markets
     * @param _user User's address
     * @return Current future value amount after update
     */
    function convertFutureValueToGenesisValue(
        bytes32 _ccy,
        uint8 _orderBookId,
        uint256 _maturity,
        address _user
    ) public returns (int256) {
        address futureValueVault = Storage.slot().futureValueVaults[_ccy];
        bool isAutoRolled = AddressResolverLib.genesisValueVault().isAutoRolled(_ccy, _maturity);

        if (!isAutoRolled) {
            (int256 amount, ) = IFutureValueVault(futureValueVault).getBalance(_orderBookId, _user);
            return amount;
        }

        (
            int256 removedAmount,
            int256 currentAmount,
            uint256 basisMaturity,
            bool isAllRemoved
        ) = IFutureValueVault(futureValueVault).reset(_orderBookId, _user);

        if (removedAmount != 0) {
            // Overwrite the `removedAmount` with the unsettled amount left of the Genesis Value
            // to handle the fractional amount generated by the lazy evaluation when the user is
            // the last user who is converting their own future value in the selected maturity.
            if (isAllRemoved && Storage.slot().pendingOrderAmounts[_ccy][basisMaturity] == 0) {
                AddressResolverLib.genesisValueVault().updateGenesisValueWithResidualAmount(
                    _ccy,
                    _user,
                    basisMaturity
                );
            } else {
                AddressResolverLib.genesisValueVault().updateGenesisValueWithFutureValue(
                    _ccy,
                    _user,
                    basisMaturity,
                    removedAmount
                );
            }
        }

        return currentAmount;
    }

    function updateFunds(
        bytes32 _ccy,
        uint256 _maturity,
        address _user,
        ProtocolTypes.Side _side,
        uint256 _filledAmount,
        uint256 _filledAmountInFV,
        uint256 _feeInFV
    ) external {
        address futureValueVault = Storage.slot().futureValueVaults[_ccy];
        uint8 orderBookId = Storage.slot().maturityOrderBookIds[_ccy][_maturity];

        if (_side == ProtocolTypes.Side.BORROW) {
            AddressResolverLib.tokenVault().addDepositAmount(_user, _ccy, _filledAmount);
            IFutureValueVault(futureValueVault).decrease(
                orderBookId,
                _user,
                _filledAmountInFV + _feeInFV,
                _maturity
            );
        } else {
            AddressResolverLib.tokenVault().removeDepositAmount(_user, _ccy, _filledAmount);
            IFutureValueVault(futureValueVault).increase(
                orderBookId,
                _user,
                _filledAmountInFV - _feeInFV,
                _maturity
            );
        }

        if (_feeInFV > 0) {
            address reserveFundAddr = address(AddressResolverLib.reserveFund());
            IFutureValueVault(futureValueVault).increase(
                orderBookId,
                reserveFundAddr,
                _feeInFV,
                _maturity
            );

            registerCurrencyAndMaturity(_ccy, _maturity, reserveFundAddr);
        }
    }

    function registerCurrencyAndMaturity(bytes32 _ccy, uint256 _maturity, address _user) public {
        if (!Storage.slot().usedMaturities[_ccy][_user].contains(_maturity)) {
            Storage.slot().usedMaturities[_ccy][_user].add(_maturity);

            registerCurrency(_ccy, _user);
        }
    }

    function registerCurrency(bytes32 _ccy, address _user) public {
        if (!Storage.slot().usedCurrencies[_user].contains(_ccy)) {
            Storage.slot().usedCurrencies[_user].add(_ccy);
        }
    }

    function executeRedemption(bytes32 _ccy, uint256 _maturity, address _user) external {
        if (
            AddressResolverLib.currencyController().currencyExists(_ccy) ||
            block.timestamp < _maturity + 1 weeks
        ) revert NotRedemptionPeriod();

        cleanUpFunds(_ccy, _user);

        int256 amount = getActualFunds(_ccy, _maturity, _user, 0).futureValue;
        if (amount <= 0) revert NoRedemptionAmount();

        uint256 redemptionAmount = _resetFundsPerMaturity(_ccy, _maturity, _user, amount)
            .toUint256();
        AddressResolverLib.tokenVault().addDepositAmount(_user, _ccy, redemptionAmount);

        emit RedemptionExecuted(_user, _ccy, _maturity, redemptionAmount);
    }

    function executeRepayment(
        bytes32 _ccy,
        uint256 _maturity,
        address _user,
        uint256 _amount
    ) public returns (uint256 repaymentAmount) {
        if (
            AddressResolverLib.currencyController().currencyExists(_ccy) ||
            block.timestamp < _maturity
        ) revert NotRepaymentPeriod();

        cleanUpFunds(_ccy, _user);

        int256 resetAmount = _amount == 0
            ? getActualFunds(_ccy, _maturity, _user, 0).futureValue
            : -_amount.toInt256();

        if (resetAmount >= 0) revert NoRepaymentAmount();

        repaymentAmount = (-_resetFundsPerMaturity(_ccy, _maturity, _user, resetAmount))
            .toUint256();
        AddressResolverLib.tokenVault().removeDepositAmount(_user, _ccy, repaymentAmount);

        emit RepaymentExecuted(_user, _ccy, _maturity, repaymentAmount);
    }

    function executeEmergencySettlement(address _user) external {
        if (Storage.slot().isRedeemed[_user]) revert AlreadyRedeemed();

        int256 redemptionAmountInBaseCurrency;

        bytes32[] memory currencies = Storage.slot().usedCurrencies[_user].values();

        for (uint256 i; i < currencies.length; i++) {
            bytes32 ccy = currencies[i];
            // First, clean up future values and genesis values to redeem those amounts.
            cleanUpFunds(ccy, _user);

            int256 amountInCcy = _resetFundsPerCurrency(ccy, _user);
            redemptionAmountInBaseCurrency += _convertToBaseCurrencyAtMarketTerminationPrice(
                ccy,
                amountInCcy
            );
        }

        bytes32[] memory collateralCurrencies = AddressResolverLib
            .tokenVault()
            .getCollateralCurrencies();

        for (uint256 i; i < collateralCurrencies.length; i++) {
            int256 amountInCcy = AddressResolverLib
                .tokenVault()
                .executeForcedReset(_user, collateralCurrencies[i])
                .toInt256();

            redemptionAmountInBaseCurrency += _convertToBaseCurrencyAtMarketTerminationPrice(
                collateralCurrencies[i],
                amountInCcy
            );
        }

        if (redemptionAmountInBaseCurrency > 0) {
            uint256[] memory terminationCollateralRatios = new uint256[](
                collateralCurrencies.length
            );
            uint256 terminationRatioTotal;

            for (uint256 i; i < collateralCurrencies.length; i++) {
                bytes32 ccy = collateralCurrencies[i];
                terminationCollateralRatios[i] = Storage.slot().terminationCollateralRatios[ccy];
                terminationRatioTotal += terminationCollateralRatios[i];
            }

            for (uint256 i; i < collateralCurrencies.length; i++) {
                bytes32 ccy = collateralCurrencies[i];
                uint256 addedAmount = _convertFromBaseCurrencyAtMarketTerminationPrice(
                    ccy,
                    (redemptionAmountInBaseCurrency.toUint256() * terminationCollateralRatios[i])
                        .div(terminationRatioTotal)
                );

                AddressResolverLib.tokenVault().addDepositAmount(_user, ccy, addedAmount);
            }
        } else if (redemptionAmountInBaseCurrency < 0) {
            revert InsufficientCollateral();
        }

        Storage.slot().isRedeemed[_user] = true;
        emit EmergencySettlementExecuted(_user, redemptionAmountInBaseCurrency.toUint256());
    }

    function getActualFunds(
        bytes32 _ccy,
        uint256 _maturity,
        address _user,
        uint256 _minDebtUnitPrice
    ) public view returns (ActualFunds memory actualFunds) {
        CalculateActualFundsVars memory vars;
        vars.market = ILendingMarket(Storage.slot().lendingMarkets[_ccy]);
        vars.futureValueVault = IFutureValueVault(Storage.slot().futureValueVaults[_ccy]);
        vars.defaultOrderBookId = Storage.slot().orderBookIdLists[_ccy][0];
        vars.minDebtUnitPrice = _minDebtUnitPrice;

        if (_maturity == 0) {
            vars.isTotal = true;
            vars.orderBookId = vars.defaultOrderBookId;
            vars.isDefaultMarket = true;
        } else {
            vars.isTotal = false;
            vars.orderBookId = Storage.slot().maturityOrderBookIds[_ccy][_maturity];
            vars.isDefaultMarket = vars.orderBookId == vars.defaultOrderBookId;
        }
        actualFunds.genesisValue = AddressResolverLib.genesisValueVault().getBalance(_ccy, _user);

        vars.maturities = getUsedMaturities(_ccy, _user);

        for (uint256 i; i < vars.maturities.length; i++) {
            uint256 currentMaturity = vars.maturities[i];
            uint8 currentOrderBookId = Storage.slot().maturityOrderBookIds[_ccy][currentMaturity];
            bool isAutoRolled = AddressResolverLib.genesisValueVault().isAutoRolled(
                _ccy,
                currentMaturity
            );

            if (vars.isDefaultMarket || currentOrderBookId == vars.orderBookId) {
                {
                    bool isDefaultMarket = currentOrderBookId == vars.defaultOrderBookId;

                    // Get current funds from Future Value Vault by lazy evaluations.
                    FutureValueVaultFunds
                        memory futureValueVaultFunds = _getFundsFromFutureValueVault(
                            _ccy,
                            _user,
                            vars,
                            currentOrderBookId,
                            isDefaultMarket,
                            isAutoRolled
                        );
                    // Get current funds from borrowing orders by lazy evaluations.
                    InactiveBorrowOrdersFunds
                        memory borrowOrdersFunds = _getFundsFromInactiveBorrowOrders(
                            _ccy,
                            _user,
                            vars,
                            currentOrderBookId,
                            currentMaturity,
                            isDefaultMarket,
                            isAutoRolled
                        );
                    // Get current funds from lending orders by lazy evaluations.
                    InactiveLendOrdersFunds
                        memory lendOrdersFunds = _getFundsFromInactiveLendOrders(
                            _ccy,
                            _user,
                            vars,
                            currentOrderBookId,
                            isDefaultMarket,
                            isAutoRolled
                        );

                    // Set genesis value.
                    actualFunds.genesisValue +=
                        futureValueVaultFunds.genesisValue -
                        borrowOrdersFunds.genesisValue +
                        lendOrdersFunds.genesisValue;

                    // Set present value.
                    int256 presentValue = futureValueVaultFunds.presentValue -
                        borrowOrdersFunds.presentValue +
                        lendOrdersFunds.presentValue;
                    int256 futureValue = futureValueVaultFunds.futureValue -
                        borrowOrdersFunds.futureValue +
                        lendOrdersFunds.futureValue;

                    actualFunds.presentValue += presentValue;

                    if (isDefaultMarket) {
                        vars.presentValueOfDefaultMarket = presentValue;
                    }

                    if (presentValue > 0) {
                        actualFunds.claimableAmount += presentValue.toUint256();
                    } else if (presentValue < 0) {
                        actualFunds.debtAmount += (-presentValue).toUint256();
                    }

                    // Set future value.
                    // Note: When calculating total funds, total future value will be 0 because different maturities can not be added.
                    if (!vars.isTotal) {
                        actualFunds.futureValue += futureValue;
                    }

                    actualFunds.workingBorrowOrdersAmount += borrowOrdersFunds.workingOrdersAmount;
                    actualFunds.workingLendOrdersAmount += lendOrdersFunds.workingOrdersAmount;
                    actualFunds.borrowedAmount += borrowOrdersFunds.borrowedAmount;
                    actualFunds.lentAmount += lendOrdersFunds.lentAmount;
                }

                // Get balance fluctuation amount by auto-rolls
                if (actualFunds.genesisValue < 0) {
                    actualFunds.genesisValue += AddressResolverLib
                        .genesisValueVault()
                        .calculateBalanceFluctuationByAutoRolls(
                            _ccy,
                            actualFunds.genesisValue,
                            vars.maturities[i],
                            i == vars.maturities.length - 1 ? 0 : vars.maturities[i + 1]
                        );
                }
            }
        }

        // Add GV to PV & FV if the market is that the lending position is rolled to.
        if (vars.isDefaultMarket && actualFunds.genesisValue != 0) {
            int256 futureValue = AddressResolverLib.genesisValueVault().calculateFVFromGV(
                _ccy,
                0,
                actualFunds.genesisValue
            );

            uint256 unitPrice = _getDefaultOrderBookMarketUnitPrice(vars);
            uint256 defaultOrderBookMinDebtUnitPrice = _getDefaultOrderBookMinDebtUnitPrice(vars);

            int256 presentValue = _calculatePVFromFV(
                futureValue,
                unitPrice >= defaultOrderBookMinDebtUnitPrice
                    ? unitPrice
                    : defaultOrderBookMinDebtUnitPrice
            );

            actualFunds.presentValue += presentValue;
            actualFunds.genesisValueInPV = presentValue;

            // Add GV to the claimable amount or debt amount.
            // Before that, offset the present value of the default market and the genesis value in addition.
            if (presentValue > 0) {
                if (vars.presentValueOfDefaultMarket < 0) {
                    int256 offsetAmount = presentValue > -vars.presentValueOfDefaultMarket
                        ? -vars.presentValueOfDefaultMarket
                        : presentValue;
                    actualFunds.debtAmount -= (offsetAmount).toUint256();
                    presentValue -= offsetAmount;
                }

                actualFunds.claimableAmount += presentValue.toUint256();
            } else if (presentValue < 0) {
                if (vars.presentValueOfDefaultMarket > 0) {
                    int256 offsetAmount = -presentValue > vars.presentValueOfDefaultMarket
                        ? vars.presentValueOfDefaultMarket
                        : -presentValue;

                    actualFunds.claimableAmount -= (offsetAmount).toUint256();
                    presentValue += offsetAmount;
                }

                actualFunds.debtAmount += (-presentValue).toUint256();
            }

            actualFunds.genesisValueInFV = futureValue;
            if (!vars.isTotal) {
                actualFunds.futureValue += futureValue;
            }
        }
    }

    function getCurrentMinDebtUnitPrice(
        uint256 _maturity,
        uint256 _minDebtUnitPrice
    ) public view returns (uint256) {
        if (_minDebtUnitPrice == 0) return 0;

        return
            _maturity > block.timestamp
                ? BASE_MIN_DEBT_UNIT_PRICE -
                    ((BASE_MIN_DEBT_UNIT_PRICE - _minDebtUnitPrice) *
                        (_maturity - block.timestamp)) /
                    Constants.SECONDS_IN_YEAR
                : BASE_MIN_DEBT_UNIT_PRICE;
    }

    function calculateFunds(
        bytes32 _ccy,
        address _user,
        ILendingMarketController.AdditionalFunds memory _additionalFunds,
        uint256 _liquidationThresholdRate
    ) public view returns (ILendingMarketController.CalculatedFunds memory funds) {
        ActualFunds memory actualFunds = getActualFunds(
            _ccy,
            0,
            _user,
            Storage.slot().minDebtUnitPrices[_ccy]
        );

        funds.workingLendOrdersAmount =
            actualFunds.workingLendOrdersAmount +
            _additionalFunds.workingLendOrdersAmount;
        funds.claimableAmount = actualFunds.claimableAmount + _additionalFunds.claimableAmount;
        funds.lentAmount = actualFunds.lentAmount + _additionalFunds.lentAmount;
        funds.workingBorrowOrdersAmount =
            actualFunds.workingBorrowOrdersAmount +
            _additionalFunds.workingBorrowOrdersAmount;
        funds.debtAmount = actualFunds.debtAmount + _additionalFunds.debtAmount;
        funds.borrowedAmount = actualFunds.borrowedAmount + _additionalFunds.borrowedAmount;

        if (funds.claimableAmount > 0) {
            // If the debt and claimable amount are in the same currency, the claimable amount can be allocated
            // as collateral up to the amount that the liquidation threshold is reached.
            // For calculation purposes, the working amount for borrowing orders is treated as potential debt in addition.
            uint256 maxAllocableCollateralAmountInSameCcy = ((funds.debtAmount +
                funds.workingBorrowOrdersAmount) * _liquidationThresholdRate).div(
                    Constants.PCT_DIGIT
                );

            // If the claimable amount is over the allocable amount as collateral, the over amount is used as collateral
            // for the other currency after being multiplied by a haircut.
            if (funds.claimableAmount > maxAllocableCollateralAmountInSameCcy) {
                uint256 haircut = AddressResolverLib.currencyController().getHaircut(_ccy);
                funds.unallocatedCollateralAmount =
                    funds.claimableAmount -
                    maxAllocableCollateralAmountInSameCcy;
                funds.collateralAmount =
                    maxAllocableCollateralAmountInSameCcy +
                    (haircut * (funds.unallocatedCollateralAmount)).div(Constants.PCT_DIGIT);
            } else {
                funds.collateralAmount = funds.claimableAmount;
                funds.unallocatedCollateralAmount = 0;
            }
        }
    }

    function calculateTotalFundsInBaseCurrency(
        address _user,
        ILendingMarketController.AdditionalFunds calldata _additionalFunds,
        uint256 _liquidationThresholdRate
    ) external view returns (ILendingMarketController.CalculatedTotalFunds memory totalFunds) {
        EnumerableSet.Bytes32Set storage currencySet = Storage.slot().usedCurrencies[_user];
        CalculatedTotalFundInBaseCurrencyVars memory vars;

        if (
            !currencySet.contains(_additionalFunds.ccy) &&
            AddressResolverLib.currencyController().currencyExists(_additionalFunds.ccy)
        ) {
            uint256 length = currencySet.length();
            vars.ccys = new bytes32[](length + 1);
            for (uint256 i; i < length; i++) {
                vars.ccys[i] = currencySet.at(i);
            }
            vars.ccys[length] = _additionalFunds.ccy;
        } else {
            vars.ccys = currencySet.values();
        }

        vars.user = _user;
        vars.additionalFunds = _additionalFunds;
        vars.liquidationThresholdRate = _liquidationThresholdRate;
        vars.isCollateral = AddressResolverLib.tokenVault().isCollateral(vars.ccys);

        // Calculate total funds from the user's order list
        for (uint256 i; i < vars.ccys.length; i++) {
            // bytes32 ccy = vars.ccys[i];
            ILendingMarketController.AdditionalFunds memory additionalFunds;

            if (vars.ccys[i] == vars.additionalFunds.ccy) {
                additionalFunds = vars.additionalFunds;
            }

            uint256[] memory amounts = new uint256[](7);

            // 0: workingLendOrdersAmount
            // 1: claimableAmount
            // 2: collateralAmount
            // 3: lentAmount
            // 4: workingBorrowOrdersAmount
            // 5: debtAmount
            // 6: borrowedAmount
            ILendingMarketController.CalculatedFunds memory funds = calculateFunds(
                vars.ccys[i],
                vars.user,
                additionalFunds,
                vars.liquidationThresholdRate
            );

            amounts[0] = funds.workingLendOrdersAmount;
            amounts[1] = funds.claimableAmount;
            amounts[2] = funds.collateralAmount;
            amounts[3] = funds.lentAmount;
            amounts[4] = funds.workingBorrowOrdersAmount;
            amounts[5] = funds.debtAmount;
            amounts[6] = funds.borrowedAmount;

            if (vars.ccys[i] == vars.additionalFunds.ccy) {
                // plusDepositAmount: borrowedAmount
                // minusDepositAmount: workingLendOrdersAmount + lentAmount
                totalFunds.plusDepositAmountInAdditionalFundsCcy += amounts[6];
                totalFunds.minusDepositAmountInAdditionalFundsCcy += amounts[0] + amounts[3];
            }

            uint256[] memory amountsInBaseCurrency = AddressResolverLib
                .currencyController()
                .convertToBaseCurrency(vars.ccys[i], amounts);

            totalFunds.claimableAmount += amountsInBaseCurrency[1];
            totalFunds.collateralAmount += amountsInBaseCurrency[2];
            totalFunds.workingBorrowOrdersAmount += amountsInBaseCurrency[4];
            totalFunds.debtAmount += amountsInBaseCurrency[5];

            // NOTE: Lent amount and working lend orders amount are excluded here as they are not used
            // for the collateral calculation.
            // Those amounts need only to check whether there is enough deposit amount in the selected currency.
            if (vars.isCollateral[i]) {
                totalFunds.workingLendOrdersAmount += amountsInBaseCurrency[0];
                totalFunds.lentAmount += amountsInBaseCurrency[3];
                totalFunds.borrowedAmount += amountsInBaseCurrency[6];
            }
        }
    }

    function getUsedMaturities(
        bytes32 _ccy,
        address _user
    ) public view returns (uint256[] memory maturities) {
        maturities = Storage.slot().usedMaturities[_ccy][_user].values();
        if (maturities.length > 0) {
            maturities = QuickSort.sort(maturities);
        }
    }

    function getGenesisValue(
        bytes32 _ccy,
        address _user
    ) public view returns (int256 amount, int256 amountInPV, int256 amountInFV) {
        FundManagementLogic.ActualFunds memory funds = getActualFunds(_ccy, 0, _user, 0);
        amount = funds.genesisValue;
        amountInPV = funds.genesisValueInPV;
        amountInFV = funds.genesisValueInFV;
    }

    function getPosition(
        bytes32 _ccy,
        uint256 _maturity,
        address _user
    ) public view returns (int256 presentValue, int256 futureValue) {
        FundManagementLogic.ActualFunds memory funds = getActualFunds(_ccy, _maturity, _user, 0);
        presentValue = funds.presentValue;
        futureValue = funds.futureValue;
    }

    function cleanUpAllFunds(address _user) external {
        bytes32[] memory currencies = Storage.slot().usedCurrencies[_user].values();
        uint256 length = currencies.length;
        for (uint256 i; i < length; i++) {
            cleanUpFunds(currencies[i], _user);
        }
    }

    function cleanUpFunds(
        bytes32 _ccy,
        address _user
    ) public returns (uint256 totalActiveOrderCount) {
        bool futureValueExists = false;
        uint256[] memory maturities = getUsedMaturities(_ccy, _user);

        for (uint256 i; i < maturities.length; i++) {
            uint256 maturity = maturities[i];
            uint8 orderBookId = Storage.slot().maturityOrderBookIds[_ccy][maturity];
            uint256 activeOrderCount = _cleanUpOrders(_ccy, orderBookId, _user);
            totalActiveOrderCount += activeOrderCount;

            int256 currentFutureValue = convertFutureValueToGenesisValue(
                _ccy,
                orderBookId,
                maturity,
                _user
            );

            if (currentFutureValue != 0) {
                futureValueExists = true;
            }

            if (currentFutureValue == 0 && activeOrderCount == 0) {
                Storage.slot().usedMaturities[_ccy][_user].remove(maturity);
            }

            AddressResolverLib.genesisValueVault().cleanUpBalance(
                _ccy,
                _user,
                i == maturities.length - 1 ? 0 : maturities[i + 1]
            );
        }

        if (totalActiveOrderCount == 0) {
            AddressResolverLib.tokenVault().cleanUpUsedCurrencies(_user, _ccy);

            if (
                !futureValueExists &&
                AddressResolverLib.genesisValueVault().getBalance(_ccy, _user) == 0
            ) {
                Storage.slot().usedCurrencies[_user].remove(_ccy);
            }
        }
    }

    function _cleanUpOrders(
        bytes32 _ccy,
        uint8 _orderBookId,
        address _user
    ) internal returns (uint256 activeOrderCount) {
        (
            uint256 activeLendOrderCount,
            uint256 activeBorrowOrderCount,
            uint256 removedLendOrderFutureValue,
            uint256 removedBorrowOrderFutureValue,
            uint256 removedLendOrderAmount,
            uint256 removedBorrowOrderAmount,
            uint256 userCurrentMaturity
        ) = ILendingMarket(Storage.slot().lendingMarkets[_ccy]).cleanUpOrders(_orderBookId, _user);

        if (removedLendOrderAmount > removedBorrowOrderAmount) {
            AddressResolverLib.tokenVault().removeDepositAmount(
                _user,
                _ccy,
                removedLendOrderAmount - removedBorrowOrderAmount
            );
        } else if (removedLendOrderAmount < removedBorrowOrderAmount) {
            AddressResolverLib.tokenVault().addDepositAmount(
                _user,
                _ccy,
                removedBorrowOrderAmount - removedLendOrderAmount
            );
        }

        if (removedLendOrderFutureValue > 0) {
            IFutureValueVault(Storage.slot().futureValueVaults[_ccy]).increase(
                _orderBookId,
                _user,
                removedLendOrderFutureValue,
                userCurrentMaturity
            );
            emit OrdersFilledInAsync(
                _user,
                _ccy,
                ProtocolTypes.Side.LEND,
                userCurrentMaturity,
                removedLendOrderAmount,
                removedLendOrderFutureValue
            );
        }

        if (removedBorrowOrderFutureValue > 0) {
            IFutureValueVault(Storage.slot().futureValueVaults[_ccy]).decrease(
                _orderBookId,
                _user,
                removedBorrowOrderFutureValue,
                userCurrentMaturity
            );
            emit OrdersFilledInAsync(
                _user,
                _ccy,
                ProtocolTypes.Side.BORROW,
                userCurrentMaturity,
                removedBorrowOrderAmount,
                removedBorrowOrderFutureValue
            );
        }

        Storage.slot().pendingOrderAmounts[_ccy][userCurrentMaturity] -=
            removedLendOrderAmount +
            removedBorrowOrderAmount;

        activeOrderCount = activeLendOrderCount + activeBorrowOrderCount;
    }

    function _getFundsFromFutureValueVault(
        bytes32 _ccy,
        address _user,
        CalculateActualFundsVars memory vars,
        uint8 currentOrderBookId,
        bool isDefaultMarket,
        bool isAutoRolled
    ) internal view returns (FutureValueVaultFunds memory funds) {
        (int256 futureValueInMaturity, uint256 fvMaturity) = vars.futureValueVault.getBalance(
            currentOrderBookId,
            _user
        );

        if (futureValueInMaturity != 0) {
            if (isAutoRolled) {
                if (vars.isDefaultMarket) {
                    funds.genesisValue = AddressResolverLib.genesisValueVault().calculateGVFromFV(
                        _ccy,
                        fvMaturity,
                        futureValueInMaturity
                    );
                }
            } else {
                if (vars.isTotal && !isDefaultMarket) {
                    uint256 unitPrice = _getDefaultOrderBookMarketUnitPrice(vars);

                    (funds.presentValue, funds.futureValue) = _convertFVtoOtherMaturity(
                        _ccy,
                        vars.market,
                        fvMaturity,
                        futureValueInMaturity,
                        unitPrice
                    );

                    if (funds.futureValue < 0) {
                        uint256 defaultOrderBookMinDebtUnitPrice = _getDefaultOrderBookMinDebtUnitPrice(
                                vars
                            );

                        if (unitPrice < defaultOrderBookMinDebtUnitPrice) {
                            funds.presentValue = _calculatePVFromFV(
                                funds.futureValue,
                                defaultOrderBookMinDebtUnitPrice
                            );
                        }
                    }
                } else if (vars.isTotal || !vars.isDefaultMarket || isDefaultMarket) {
                    uint256 unitPrice = vars.market.getMarketUnitPrice(vars.orderBookId);
                    funds.futureValue = futureValueInMaturity;

                    // Apply min debt unit price if the future value is negative (debt).
                    if (funds.futureValue < 0) {
                        uint256 currentMinDebtUnitPrice = getCurrentMinDebtUnitPrice(
                            fvMaturity,
                            vars.minDebtUnitPrice
                        );

                        funds.presentValue = _calculatePVFromFV(
                            futureValueInMaturity,
                            unitPrice < currentMinDebtUnitPrice
                                ? currentMinDebtUnitPrice
                                : unitPrice
                        );
                    } else {
                        funds.presentValue = _calculatePVFromFV(futureValueInMaturity, unitPrice);
                    }
                }
            }
        }
    }

    function _getFundsFromInactiveBorrowOrders(
        bytes32 _ccy,
        address _user,
        CalculateActualFundsVars memory vars,
        uint8 currentOrderBookId,
        uint256 currentMaturity,
        bool isDefaultMarket,
        bool isAutoRolled
    ) internal view returns (InactiveBorrowOrdersFunds memory funds) {
        uint256 filledFutureValue;
        uint256 orderMaturity;
        uint256 currentMinDebtUnitPrice = getCurrentMinDebtUnitPrice(
            currentMaturity,
            vars.minDebtUnitPrice
        );

        (funds.workingOrdersAmount, funds.borrowedAmount, filledFutureValue, orderMaturity) = vars
            .market
            .getTotalAmountFromBorrowOrders(currentOrderBookId, _user, currentMinDebtUnitPrice);

        if (isAutoRolled) {
            funds.workingOrdersAmount = 0;
        }

        if (filledFutureValue != 0) {
            if (isAutoRolled) {
                if (vars.isDefaultMarket) {
                    funds.genesisValue = AddressResolverLib.genesisValueVault().calculateGVFromFV(
                        _ccy,
                        orderMaturity,
                        filledFutureValue.toInt256()
                    );
                }
            } else {
                if (vars.isTotal && !isDefaultMarket) {
                    uint256 unitPrice = _getDefaultOrderBookMarketUnitPrice(vars);

                    (funds.presentValue, funds.futureValue) = _convertFVtoOtherMaturity(
                        _ccy,
                        vars.market,
                        orderMaturity,
                        filledFutureValue.toInt256(),
                        unitPrice
                    );

                    uint256 defaultOrderBookMinDebtUnitPrice = _getDefaultOrderBookMinDebtUnitPrice(
                        vars
                    );

                    if (unitPrice < defaultOrderBookMinDebtUnitPrice) {
                        funds.presentValue = _calculatePVFromFV(
                            funds.futureValue,
                            defaultOrderBookMinDebtUnitPrice
                        );
                    }
                } else if (vars.isTotal || !vars.isDefaultMarket || isDefaultMarket) {
                    uint256 unitPrice = vars.market.getMarketUnitPrice(vars.orderBookId);

                    funds.futureValue = filledFutureValue.toInt256();
                    funds.presentValue = _calculatePVFromFV(
                        funds.futureValue,
                        unitPrice < currentMinDebtUnitPrice ? currentMinDebtUnitPrice : unitPrice
                    );
                }
            }
        }
    }

    function _getFundsFromInactiveLendOrders(
        bytes32 _ccy,
        address _user,
        CalculateActualFundsVars memory vars,
        uint8 currentOrderBookId,
        bool isDefaultMarket,
        bool isAutoRolled
    ) internal view returns (InactiveLendOrdersFunds memory funds) {
        uint256 filledFutureValue;
        uint256 orderMaturity;
        (funds.workingOrdersAmount, funds.lentAmount, filledFutureValue, orderMaturity) = vars
            .market
            .getTotalAmountFromLendOrders(currentOrderBookId, _user);

        if (isAutoRolled) {
            funds.workingOrdersAmount = 0;
        }

        if (filledFutureValue != 0) {
            if (isAutoRolled) {
                if (vars.isDefaultMarket) {
                    funds.genesisValue = AddressResolverLib.genesisValueVault().calculateGVFromFV(
                        _ccy,
                        orderMaturity,
                        filledFutureValue.toInt256()
                    );
                }
            } else {
                if (vars.isTotal && !isDefaultMarket) {
                    (funds.presentValue, funds.futureValue) = _convertFVtoOtherMaturity(
                        _ccy,
                        vars.market,
                        orderMaturity,
                        filledFutureValue.toInt256(),
                        vars.market.getMarketUnitPrice(vars.defaultOrderBookId)
                    );
                } else if (vars.isTotal || !vars.isDefaultMarket || isDefaultMarket) {
                    funds.futureValue = filledFutureValue.toInt256();
                    funds.presentValue = _calculatePVFromFV(
                        vars.market,
                        vars.orderBookId,
                        funds.futureValue
                    );
                }
            }
        }
    }

    function _convertFVtoOtherMaturity(
        bytes32 _ccy,
        ILendingMarket _market,
        uint256 _fromMaturity,
        int256 _fromFutureValue,
        uint256 _toUnitPrice
    ) internal view returns (int256 presentValue, int256 futureValue) {
        if (
            AddressResolverLib.genesisValueVault().getAutoRollLog(_ccy, _fromMaturity).unitPrice ==
            0
        ) {
            presentValue = _calculatePVFromFV(
                _market,
                Storage.slot().maturityOrderBookIds[_ccy][_fromMaturity],
                _fromFutureValue
            );
            futureValue = _calculateFVFromPV(presentValue, _toUnitPrice);
        } else {
            futureValue = AddressResolverLib.genesisValueVault().calculateFVFromFV(
                _ccy,
                _fromMaturity,
                0,
                _fromFutureValue
            );
            presentValue = _calculatePVFromFV(futureValue, _toUnitPrice);
        }
    }

    function calculatePVFromFV(
        bytes32 _ccy,
        uint256 _maturity,
        int256 _futureValue
    ) public view returns (int256 presentValue) {
        presentValue = _calculatePVFromFV(
            ILendingMarket(Storage.slot().lendingMarkets[_ccy]),
            Storage.slot().maturityOrderBookIds[_ccy][_maturity],
            _futureValue
        );
    }

    function calculateFVFromPV(
        bytes32 _ccy,
        uint256 _maturity,
        int256 _presentValue
    ) public view returns (int256 futureValue) {
        uint256 unitPrice = ILendingMarket(Storage.slot().lendingMarkets[_ccy]).getMarketUnitPrice(
            Storage.slot().maturityOrderBookIds[_ccy][_maturity]
        );
        futureValue = _calculateFVFromPV(_presentValue, unitPrice);
    }

    function calculateFVFromPV(
        bytes32 _ccy,
        uint256 _maturity,
        uint256 _presentValue
    ) public view returns (uint256 futureValue) {
        uint256 unitPrice = ILendingMarket(Storage.slot().lendingMarkets[_ccy]).getMarketUnitPrice(
            Storage.slot().maturityOrderBookIds[_ccy][_maturity]
        );
        futureValue = _calculateFVFromPV(_presentValue, unitPrice);
    }

    function _convertToBaseCurrencyAtMarketTerminationPrice(
        bytes32 _ccy,
        int256 _amount
    ) internal view returns (int256) {
        TerminationCurrencyCache memory currency = Storage.slot().terminationCurrencyCaches[_ccy];
        return (_amount * currency.price).div((10 ** currency.decimals).toInt256());
    }

    function _convertFromBaseCurrencyAtMarketTerminationPrice(
        bytes32 _ccy,
        uint256 _amount
    ) internal view returns (uint256) {
        TerminationCurrencyCache memory currency = Storage.slot().terminationCurrencyCaches[_ccy];
        return (_amount * 10 ** currency.decimals).div(currency.price.toUint256());
    }

    function _resetFundsPerCurrency(bytes32 _ccy, address _user) internal returns (int256 amount) {
        amount = getActualFunds(_ccy, 0, _user, 0).presentValue;

        uint256[] memory maturities = Storage.slot().usedMaturities[_ccy][_user].values();
        for (uint256 j; j < maturities.length; j++) {
            IFutureValueVault(Storage.slot().futureValueVaults[_ccy]).executeForcedReset(
                Storage.slot().maturityOrderBookIds[_ccy][maturities[j]],
                _user
            );
        }

        AddressResolverLib.genesisValueVault().executeForcedReset(_ccy, _user);

        Storage.slot().usedCurrencies[_user].remove(_ccy);
    }

    function _resetFundsPerMaturity(
        bytes32 _ccy,
        uint256 _maturity,
        address _user,
        int256 _amount
    ) internal returns (int256 totalRemovedAmount) {
        int256 currentFVAmount;
        int256 currentGVAmount;

        (totalRemovedAmount, currentFVAmount) = IFutureValueVault(
            Storage.slot().futureValueVaults[_ccy]
        ).executeForcedReset(Storage.slot().maturityOrderBookIds[_ccy][_maturity], _user, _amount);

        int256 remainingAmount = _amount - totalRemovedAmount;

        bool isDefaultMarket = Storage.slot().maturityOrderBookIds[_ccy][_maturity] ==
            Storage.slot().orderBookIdLists[_ccy][0];

        if (isDefaultMarket && remainingAmount != 0) {
            int256 removedAmount;
            (removedAmount, currentGVAmount) = AddressResolverLib
                .genesisValueVault()
                .executeForcedReset(_ccy, _maturity, _user, remainingAmount);
            totalRemovedAmount += removedAmount;
        }

        if (currentFVAmount == 0 && currentGVAmount == 0) {
            Storage.slot().usedMaturities[_ccy][_user].remove(_maturity);

            if (Storage.slot().usedMaturities[_ccy][_user].length() == 0) {
                Storage.slot().usedCurrencies[_user].remove(_ccy);
            }
        }
    }

    function _getDefaultOrderBookMinDebtUnitPrice(
        CalculateActualFundsVars memory vars
    ) private view returns (uint256) {
        if (vars.defaultOrderBookMinDebtUnitPrice == 0 && vars.minDebtUnitPrice != 0) {
            vars.defaultOrderBookMinDebtUnitPrice = getCurrentMinDebtUnitPrice(
                vars.market.getMaturity(vars.defaultOrderBookId),
                vars.minDebtUnitPrice
            );
        }

        return vars.defaultOrderBookMinDebtUnitPrice;
    }

    function _getDefaultOrderBookMarketUnitPrice(
        CalculateActualFundsVars memory vars
    ) private view returns (uint256) {
        if (vars.defaultOrderBookMarketUnitPrice == 0) {
            vars.defaultOrderBookMarketUnitPrice = vars.market.getMarketUnitPrice(
                vars.defaultOrderBookId
            );
        }

        return vars.defaultOrderBookMarketUnitPrice;
    }

    function _calculatePVFromFV(
        ILendingMarket _market,
        uint8 _orderBookId,
        int256 _futureValue
    ) internal view returns (int256 presentValue) {
        uint256 unitPrice = _market.getMarketUnitPrice(_orderBookId);
        presentValue = _calculatePVFromFV(_futureValue, unitPrice);
    }

    function _calculatePVFromFV(
        int256 _futureValue,
        uint256 _unitPrice
    ) internal pure returns (int256) {
        uint256 unitPrice = _unitPrice == 0 ? Constants.PRICE_DIGIT : _unitPrice;
        // NOTE: The formula is: presentValue = futureValue * unitPrice.
        return (_futureValue * unitPrice.toInt256()).div(Constants.PRICE_DIGIT.toInt256());
    }

    function _calculatePVFromFV(
        uint256 _futureValue,
        uint256 _unitPrice
    ) internal pure returns (uint256) {
        uint256 unitPrice = _unitPrice == 0 ? Constants.PRICE_DIGIT : _unitPrice;
        // NOTE: The formula is: presentValue = futureValue * unitPrice.
        return (_futureValue * unitPrice).div(Constants.PRICE_DIGIT);
    }

    function _calculateFVFromPV(
        int256 _presentValue,
        uint256 _unitPrice
    ) internal pure returns (int256) {
        uint256 unitPrice = _unitPrice == 0 ? Constants.PRICE_DIGIT : _unitPrice;
        // NOTE: The formula is: futureValue = presentValue / unitPrice.
        return (_presentValue * Constants.PRICE_DIGIT.toInt256()).div(unitPrice.toInt256());
    }

    function _calculateFVFromPV(
        uint256 _presentValue,
        uint256 _unitPrice
    ) internal pure returns (uint256) {
        uint256 unitPrice = _unitPrice == 0 ? Constants.PRICE_DIGIT : _unitPrice;
        // NOTE: The formula is: futureValue = presentValue / unitPrice.
        return (_presentValue * Constants.PRICE_DIGIT).div(unitPrice);
    }
}
