        uint16 chainId,
        uint256 amount,
        uint256 timestamp,
        string txHash,
        bytes32 settlementId
    );
    event ExternalAdapterAdded(address indexed adapter, bytes32 ccy);

    function addExternalAdapter(address _adapter, bytes32 _ccy) external;

    function getExternalAdapters(bytes32) external view returns (address);

    function fulfillSettlementRequest(
        bytes32 _requestId,
        IExternalAdapterTxResponse.FulfillData calldata _txData,
        bytes32 _ccy
    ) external;

    function getVersion() external view returns (uint16);

    function getSettlementRequests(bytes32)
        external
        view
        returns (ProtocolTypes.SettlementRequest memory);

    function verifyPayment(
        address _counterparty,
        bytes32 _ccy,
        uint256 _payment,
        uint256 _timestamp,
        string calldata _txHash
    ) external payable returns (bytes32);
}
