","keccak256":"0x85fea92a32d4ac0f08532d339ceeef07a468748ab7628bd2fd60980090e53a5d","license":"MIT"},"contracts/interfaces/ICollateralAggregator.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

interface ICollateralAggregator {
    event Register(address indexed addr);
    event ReleaseUnsettled(address indexed party, bytes32 ccy, uint256 amount);
    event UseUnsettledCollateral(address indexed party, bytes32 ccy, uint256 amount);

    function isCovered(address _user) external view returns (bool);

    function isRegisteredUser(address addr) external view returns (bool);

    function getWithdrawableCollateral(address _user) external view returns (uint256 maxWithdraw);

    function getCoverage(address _user) external view returns (uint256 coverage);

    function getUnsettledCollateral(address user, bytes32 ccy) external view returns (uint256);

    function getUnusedCollateral(address _user) external view returns (uint256);

    function getTotalUnsettledExposure(address _user) external view returns (uint256);

    function getCollateralParameters()
        external
        view
        returns (
            uint256,
            uint256,
            uint256,
            uint256
        );

    function register() external;

    function useUnsettledCollateral(
        address user,
        bytes32 ccy,
        uint256 amount
    ) external;

    function releaseUnsettledCollateral(
        address user,
        bytes32 ccy,
        uint256 amount
    ) external;

    function setCollateralParameters(
        uint256 marginCallThresholdRate,
        uint256 autoLiquidationThresholdRate,
        uint256 liquidationPriceRate,
        uint256 minCollateralRate
    ) external;
}
","keccak256":"0x044f9ce00b347c34307571cb24b3d8c3435db583f9d92b5554dbf847f7fcd477","license":"MIT"},"contracts/interfaces/ICollateralVault.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

interface ICollateralVault {
    event Deposit(address user, bytes32 ccy, uint256 amount);
    event Withdraw(address from, bytes32 ccy, uint256 amount);
    event CurrencyRegistered(bytes32 ccy, address tokenAddress);

    function deposit(bytes32 _ccy, uint256 _amount) external payable;

    function getIndependentCollateral(address _user, bytes32 _ccy) external view returns (uint256);

    function getIndependentCollateralInETH(address _user, bytes32 _ccy)
        external
        view
        returns (uint256);

    function withdraw(bytes32 _ccy, uint256 _amount) external;

    function getUsedCurrencies(address user) external view returns (bytes32[] memory);

    function getTotalIndependentCollateralInETH(address _party) external view returns (uint256);
}
","keccak256":"0xb67032b6e0eaf42b0eab142605bd2310d6432cee771ed1fe8a89546a7a1c2ef4","license":"MIT"},"contracts/interfaces/ICurrencyController.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "../types/ProtocolTypes.sol";

/**
 * @dev Currency Controller contract is responsible for managing supported
 * currencies in Secured Finance Protocol
 *
 * Contract links new currencies to ETH Chainlink price feeds, without existing price feed
 * contract owner is not able to add a new currency into the protocol
 */
interface ICurrencyController {
    event CcyAdded(bytes32 indexed ccy, string name, uint256 haircut);
    event CcyCollateralUpdate(bytes32 indexed ccy, bool isCollateral);
    event CcySupportUpdate(bytes32 indexed ccy, bool isSupported);
    event HaircutUpdated(bytes32 indexed ccy, uint256 haircut);
    event MinMarginUpdated(bytes32 indexed ccy, uint256 minMargin);
    event PriceFeedAdded(bytes32 ccy, string secondCcy, address indexed priceFeed);
    event PriceFeedRemoved(bytes32 ccy, string secondCcy, address indexed priceFeed);

    function convertFromETH(bytes32 _ccy, uint256 _amountETH) external view returns (uint256);

    function convertToETH(bytes32 _ccy, uint256 _amount) external view returns (uint256);

    function convertToETH(bytes32 _ccy, int256 _amount) external view returns (int256);

    function getCurrencies(bytes32) external view returns (ProtocolTypes.Currency memory);

    function getEthDecimals(bytes32) external view returns (uint8);

    function getUsdDecimals(bytes32) external view returns (uint8);

    function getHaircut(bytes32 _ccy) external view returns (uint256);

    function getHistoricalETHPrice(bytes32 _ccy, uint80 _roundId) external view returns (int256);

    function getHistoricalUSDPrice(bytes32 _ccy, uint80 _roundId) external view returns (int256);

    function getLastETHPrice(bytes32 _ccy) external view returns (int256);

    function getLastUSDPrice(bytes32 _ccy) external view returns (int256);

    function isCollateral(bytes32) external view returns (bool);

    function isSupportedCcy(bytes32 _ccy) external view returns (bool);

    function linkPriceFeed(
        bytes32 _ccy,
        address _priceFeedAddr,
        bool _isEthPriceFeed
    ) external returns (bool);

    function removePriceFeed(bytes32 _ccy, bool _isEthPriceFeed) external;

    function supportCurrency(
        bytes32 _ccy,
        string memory _name,
        address _ethPriceFeed,
        uint256 _haircut,
        address _tokenAddress
    ) external;

    function updateCcyHaircut(bytes32 _ccy, uint256 _haircut) external;

    function updateCollateralSupport(bytes32 _ccy, bool _isSupported) external;

    function updateCurrencySupport(bytes32 _ccy, bool _isSupported) external;

    function getTokenAddresses(bytes32) external view returns (address);
}
