    function updateCompoundFactor(
        bytes32 ccy,
        uint256 maturity,
        uint256 nextMaturity,
        uint256 unitPrice
    ) external;

    function addGenesisValue(
        bytes32 ccy,
        address user,
        uint256 basisMaturity,
        int256 futureValue
