pragma solidity ^0.8.9;

interface IFutureValueVault {
    event Transfer(address indexed from, address indexed to, int256 value);

    function getTotalLendingSupply(uint256 _maturity) external view returns (uint256);

    function getTotalBorrowingSupply(uint256 _maturity) external view returns (uint256);

    function getFutureValue(address _user)
        external
        view
        returns (int256 futureValue, uint256 maturity);

    function hasFutureValueInPastMaturity(address _user, uint256 _maturity)
        external
        view
        returns (bool);

    function addLendFutureValue(
        address _user,
        uint256 _amount,
        uint256 _maturity
    ) external returns (bool);

    function addBorrowFutureValue(
        address _user,
        uint256 _amount,
        uint256 _maturity
    ) external returns (bool);

    function removeFutureValue(address _user, uint256 _maturity)
        external
        returns (
            int256 removedAmount,
            int256 currentAmount,
            uint256 maturity
        );
}
","keccak256":"0xb1b5c4dc86b463b0952f2c902a5bbe0dbeb83df579a02a3f34d51d74e28f36bd","license":"MIT"},"contracts/interfaces/IGenesisValueVault.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import {MaturityUnitPrice} from "../storages/GenesisValueVaultStorage.sol";

interface IGenesisValueVault {
    event Transfer(bytes32 indexed ccy, address indexed from, address indexed to, int256 value);
    event CompoundFactorUpdated(
        bytes32 indexed ccy,
        uint256 compoundFactor,
        uint256 unitPrice,
        uint256 currentMaturity,
        uint256 previousMaturity
    );

    function isInitialized(bytes32 ccy) external view returns (bool);

    function decimals(bytes32 ccy) external view returns (uint8);

    function getTotalLendingSupply(bytes32 ccy) external view returns (uint256);

    function getTotalBorrowingSupply(bytes32 ccy) external view returns (uint256);

    function getGenesisValue(bytes32 ccy, address user) external view returns (int256);

    function getCurrentMaturity(bytes32 ccy) external view returns (uint256);

    function getCompoundFactor(bytes32 ccy) external view returns (uint256);

    function getMaturityUnitPrice(bytes32 ccy, uint256 maturity)
        external
        view
        returns (MaturityUnitPrice memory);

    function getGenesisValueInFutureValue(bytes32 ccy, address user) external view returns (int256);

    function calculateCurrentFVFromFVInMaturity(
        bytes32 _ccy,
        uint256 _basisMaturity,
        int256 _futureValue
    ) external view returns (int256);

    function calculateGVFromFV(
        bytes32 ccy,
        uint256 basisMaturity,
        int256 futureValue
    ) external view returns (int256);

    function calculateFVFromGV(
        bytes32 ccy,
        uint256 basisMaturity,
        int256 genesisValue
    ) external view returns (int256);

    function initialize(
        bytes32 ccy,
        uint8 decimals,
        uint256 compoundFactor
    ) external;

    function updateCompoundFactor(
        bytes32 ccy,
        uint256 maturity,
        uint256 nextMaturity,
        uint256 unitPrice
    ) external;

    function addGenesisValue(
        bytes32 ccy,
        address user,
        uint256 basisMaturity,
        int256 futureValue
    ) external returns (bool);
}
