        external
        view
        returns (bool);

    function addLendFutureValue(
        address user,
        uint256 amount,
        uint256 maturity,
        bool isTaker
    ) external;

    function addBorrowFutureValue(
        address user,
        uint256 amount,
        uint256 maturity,
