// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import {IAddressResolver} from "./interfaces/IAddressResolver.sol";
import {Ownable} from "./utils/Ownable.sol";
import {Proxyable} from "./utils/Proxyable.sol";
import {AddressResolverStorage as Storage} from "./storages/AddressResolverStorage.sol";

/**
 * @notice Implements the logic to manage the contract addresses.
 *
 * This contract store the contract name and contract address. When the contract calls other contracts,
 * the caller contract gets the contract address from this contract.
 * However, the contract addresses are cashed into the caller contract through the `MixinAddressResolver.sol` at the deployment,
 * so the caller doesn't need to call this contract each time it calls other contracts.
 *
 * @dev This contract is used through the `./mixins/MixinAddressResolver.sol`. The names of the contracts that
 * need to be imported into this contract are managed in `./libraries/Contracts.sol`.
 */
contract AddressResolver is IAddressResolver, Ownable, Proxyable {
    /**
     * @notice Initializes the contract.
     * @dev Function is invoked by the proxy contract when the contract is added to the ProxyController.
     * @param _owner The address of the contract owner
     */
    function initialize(address _owner) public initializer onlyProxy {
        _transferOwnership(_owner);
    }

    /**
     * @notice Imports contract addresses.
     * @dev All addresses in the contract are overridden by `_addresses` in the argument.
     */
    function importAddresses(
        bytes32[] memory _names,
        address[] memory _addresses
    ) public onlyOwner {
        if (_names.length != _addresses.length) revert UnmatchedInputs();

        Storage.slot().addressCaches = _addresses;

        for (uint256 i; i < _names.length; i++) {
            bytes32 name = _names[i];
            address destination = _addresses[i];
            Storage.slot().addresses[name] = destination;
            emit AddressImported(name, destination);
        }
    }

    /**
     * @notice Gets if the addresses are imported.
     * @return The boolean if the addresses are imported or not
     */
    function areAddressesImported(
        bytes32[] calldata _names,
        address[] calldata _addresses
    ) external view returns (bool) {
        for (uint256 i; i < _names.length; i++) {
            if (Storage.slot().addresses[_names[i]] != _addresses[i]) {
                return false;
            }
        }
        return true;
    }

    /**
     * @notice Gets the imported contract addresses for the name with error.
     * @dev This method is used when the caller need to get an error if the address in the name
     * is not imported.
     * @return The contract address
     */
    function getAddress(
        bytes32 _name,
        string calldata _reason
    ) external view override returns (address) {
        address _foundAddress = Storage.slot().addresses[_name];
        if (_foundAddress == address(0)) revert(_reason);
        return _foundAddress;
    }

    /**
     * @notice Gets the imported contract addresses for the name.
     * @dev This method is used when the caller doesn't need to get an error if the address in the name
     * is not imported.
     * @return The contract address
     */
    function getAddress(bytes32 _name) external view override returns (address) {
        return Storage.slot().addresses[_name];
    }

    /**
     * @notice Gets the all imported contract addresses.
     * @return Array with the contract address
     */
    function getAddresses() external view override returns (address[] memory) {
        return Storage.slot().addressCaches;
    }
}

contracts/protocol/BeaconProxyController.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

// dependencies
import {Multicall} from "../dependencies/openzeppelin/utils/Multicall.sol";
// interfaces
import {IBeaconProxyController} from "./interfaces/IBeaconProxyController.sol";
import {IProxyController} from "./interfaces/IProxyController.sol";
// libraries
import {AddressResolverLib} from "./libraries/AddressResolverLib.sol";
import {BeaconContracts, Contracts} from "./libraries/Contracts.sol";
// mixins
import {MixinAddressResolver} from "./mixins/MixinAddressResolver.sol";
// utils
import {UpgradeableBeacon} from "./utils/UpgradeableBeacon.sol";
import {Ownable} from "./utils/Ownable.sol";
import {Proxyable} from "./utils/Proxyable.sol";
import {UpgradeabilityBeaconProxy} from "./utils/UpgradeabilityBeaconProxy.sol";
// storages
import {BeaconProxyControllerStorage as Storage} from "./storages/BeaconProxyControllerStorage.sol";

/**
 * @notice Implements the management of beacon proxy contracts.
 *
 * All beacon proxy contracts are deployed from this contract.
 * This contract is also used to update the beacon proxy implementation.
 */
contract BeaconProxyController is
    IBeaconProxyController,
    MixinAddressResolver,
    Ownable,
    Proxyable,
    Multicall
{
    /**
     * @notice Initializes the contract.
     * @dev Function is invoked by the proxy contract when the contract is added to the ProxyController.
     * @param _owner The address of the contract owner
     * @param _resolver The address of the Address Resolver contract
     */
    function initialize(address _owner, address _resolver) public initializer onlyProxy {
        _transferOwnership(_owner);
        registerAddressResolver(_resolver);
    }

    // @inheritdoc MixinAddressResolver
    function requiredContracts() public pure override returns (bytes32[] memory contracts) {
        contracts = new bytes32[](1);
        contracts[0] = Contracts.LENDING_MARKET_CONTROLLER;
    }

    // @inheritdoc MixinAddressResolver
    function acceptedContracts() public pure override returns (bytes32[] memory contracts) {
        contracts = new bytes32[](1);
        contracts[0] = Contracts.LENDING_MARKET_CONTROLLER;
    }

    /**
     * @notice Gets the beacon proxy address to the selected name.
     * @param beaconName The cache name of the beacon proxy
     * @return beaconProxyAddress The beacon proxy address
     */
    function getBeaconProxyAddress(
        bytes32 beaconName
    ) external view override returns (address beaconProxyAddress) {
        beaconProxyAddress = Storage.slot().registeredBeaconProxies[beaconName];
        if (beaconProxyAddress == address(0)) revert NoBeaconProxyContract();

        UpgradeabilityBeaconProxy beaconProxy = UpgradeabilityBeaconProxy(
            payable(beaconProxyAddress)
        );

        if (beaconProxy.implementation() == address(0)) revert InvalidProxyContract();
    }

    /**
     * @notice Sets the implementation contract of FutureValueVault
     * @param newImpl The address of implementation contract
     */
    function setFutureValueVaultImpl(address newImpl) external override onlyOwner {
        _updateBeaconImpl(BeaconContracts.FUTURE_VALUE_VAULT, newImpl);
    }

    /**
     * @notice Sets the implementation contract of LendingMarket
     * @param newImpl The address of implementation contract
     */
    function setLendingMarketImpl(address newImpl) external override onlyOwner {
        _updateBeaconImpl(BeaconContracts.LENDING_MARKET, newImpl);
    }

    /**
     * @notice Deploys new FutureValueVault
     * @notice Reverts on deployment market with existing currency and term
     */
    function deployFutureValueVault()
        external
        override
        onlyAcceptedContracts
        returns (address futureValue)
    {
        bytes memory data = abi.encodeWithSignature("initialize(address)", address(resolver()));
        futureValue = _createProxy(BeaconContracts.FUTURE_VALUE_VAULT, data);
    }

    /**
     * @notice Deploys new LendingMarket
     * @param _ccy Main currency for new lending market
     * @param _orderFeeRate The order fee rate received by protocol
     * @param _cbLimitRange The circuit breaker limit range
     * @return market The proxy contract address of created lending market
     */
    function deployLendingMarket(
        bytes32 _ccy,
        uint256 _orderFeeRate,
        uint256 _cbLimitRange
    ) external override onlyAcceptedContracts returns (address market) {
        bytes memory data = abi.encodeWithSignature(
            "initialize(address,bytes32,uint256,uint256)",
            address(resolver()),
            _ccy,
            _orderFeeRate,
            _cbLimitRange
        );
        market = _createProxy(BeaconContracts.LENDING_MARKET, data);
    }

    function _createProxy(bytes32 beaconName, bytes memory data) internal returns (address) {
        address beaconProxyAddress = Storage.slot().registeredBeaconProxies[beaconName];
        if (beaconProxyAddress == address(0)) revert NoBeaconProxyContract();

        return address(new UpgradeabilityBeaconProxy(beaconProxyAddress, data));
    }

    function _updateBeaconImpl(
        bytes32 name,
        address newAddress
    ) internal returns (address beaconProxyAddress) {
        beaconProxyAddress = Storage.slot().registeredBeaconProxies[name];
        UpgradeableBeacon beacon;

        if (beaconProxyAddress == address(0)) {
            beacon = new UpgradeableBeacon(newAddress);

            Storage.slot().registeredBeaconProxies[name] = beaconProxyAddress = address(beacon);

            emit BeaconProxyCreated(name, beaconProxyAddress, newAddress);
        } else {
            beacon = UpgradeableBeacon(beaconProxyAddress);
            address oldAddress = beacon.implementation();
            beacon.upgradeTo(newAddress);
            emit BeaconProxyUpdated(name, beaconProxyAddress, newAddress, oldAddress);
        }
    }
}

contracts/protocol/CurrencyController.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

// dependencies
import {AggregatorV3Interface} from "../dependencies/chainlink/AggregatorV3Interface.sol";
import {EnumerableSet} from "../dependencies/openzeppelin/utils/structs/EnumerableSet.sol";
import {SafeCast} from "../dependencies/openzeppelin/utils/math/SafeCast.sol";
import {Multicall} from "../dependencies/openzeppelin/utils/Multicall.sol";
// interfaces
import {ICurrencyController} from "./interfaces/ICurrencyController.sol";
// libraries
import {RoundingUint256} from "./libraries/math/RoundingUint256.sol";
import {RoundingInt256} from "./libraries/math/RoundingInt256.sol";
// utils
import {Ownable} from "./utils/Ownable.sol";
import {Proxyable} from "./utils/Proxyable.sol";
// storages
import {CurrencyControllerStorage as Storage, PriceFeed} from "./storages/CurrencyControllerStorage.sol";

/**
 * @notice Implements managing of the supported currencies in the protocol.
 *
 * This contract links new currencies to Chainlink price feeds. To add a new currency to the protocol except for the base currency,
 * the owner needs to also add an existing price feed contract.
 */
contract CurrencyController is ICurrencyController, Ownable, Proxyable, Multicall {
    using EnumerableSet for EnumerableSet.Bytes32Set;
    using SafeCast for uint256;
    using SafeCast for int256;
    using RoundingUint256 for uint256;
    using RoundingInt256 for int256;

    /**
     * @notice Modifier to check if the currency is supported.
     * @param _ccy Currency name in bytes32
     */
    modifier onlySupportedCurrency(bytes32 _ccy) {
        if (!currencyExists(_ccy)) revert InvalidCurrency();
        _;
    }

    /**
     * @notice Initializes the contract.
     * @dev Function is invoked by the proxy contract when the contract is added to the ProxyController.
     * @param _owner The address of the contract owner
     */
    function initialize(address _owner) public initializer onlyProxy {
        _transferOwnership(_owner);
    }

    /**
     * @notice Gets aggregated and cached decimals of the price feeds for the selected currency.
     * @param _ccy Currency name in bytes32
     */
    function getDecimals(bytes32 _ccy) external view override returns (uint8) {
        return Storage.slot().decimalsCaches[_ccy];
    }

    /**
     * @notice Gets all currencies.
     * @return The array of the currency
     */
    function getCurrencies() external view override returns (bytes32[] memory) {
        return Storage.slot().currencies.values();
    }

    /**
     * @notice Gets haircut ratio for the selected currency.
     * Haircut is used in bilateral netting cross-calculation.
     * @param _ccy Currency name in bytes32
     */
    function getHaircut(bytes32 _ccy) external view override returns (uint256) {
        return Storage.slot().haircuts[_ccy];
    }

    /**
     * @notice Gets price feed for the selected currency.
     * @param _ccy Currency name in bytes32
     */
    function getPriceFeed(bytes32 _ccy) external view override returns (PriceFeed memory) {
        return Storage.slot().priceFeeds[_ccy];
    }

    /**
     * @notice Gets if the selected currency is supported.
     * @param _ccy Currency name in bytes32
     * @return The boolean if the selected currency is supported or not
     */
    function currencyExists(bytes32 _ccy) public view override returns (bool) {
        return Storage.slot().currencies.contains(_ccy);
    }

    /**
     * @notice Adds new currency into the protocol and links with existing price feed.
     * @param _ccy Currency name in bytes32k
     * @param _decimals Currency decimals
     * @param _haircut Remaining ratio after haircut
     * @param _priceFeeds Array with the contract address of price feed
     */
    function addCurrency(
        bytes32 _ccy,
        uint8 _decimals,
        uint256 _haircut,
        address[] calldata _priceFeeds,
        uint256 _heartbeat
    ) public override onlyOwner {
        Storage.slot().currencies.add(_ccy);

        _updateHaircut(_ccy, _haircut);
        _updatePriceFeed(_ccy, _decimals, _priceFeeds, _heartbeat);

        emit CurrencyAdded(_ccy, _haircut);
    }

    /**
     * @notice Updates the flag indicating if the currency is supported in the protocol.
     * @param _ccy Currency name in bytes32
     */
    function removeCurrency(bytes32 _ccy) public override onlyOwner {
        Storage.slot().currencies.remove(_ccy);
        emit CurrencyRemoved(_ccy);
    }

    /**
     * @notice Updates the haircut ratio for supported currency
     * @param _ccy Currency name in bytes32
     * @param _haircut Remaining ratio after haircut
     */
    function updateHaircut(
        bytes32 _ccy,
        uint256 _haircut
    ) public override onlyOwner onlySupportedCurrency(_ccy) {
        _updateHaircut(_ccy, _haircut);
    }

    /**
     * @notice Update the price feed contract addresses.
     * @param _ccy Currency name in bytes32
     * @param _decimals Currency decimals
     * @param _priceFeeds Array with the contract address of price feed
     */
    function updatePriceFeed(
        bytes32 _ccy,
        uint8 _decimals,
        address[] calldata _priceFeeds,
        uint256 _heartbeat
    ) public override onlyOwner onlySupportedCurrency(_ccy) {
        _updatePriceFeed(_ccy, _decimals, _priceFeeds, _heartbeat);
    }

    /**
     * @notice Removes existing Chainlink price feed.
     * @param _ccy Currency name in bytes32
     */
    function removePriceFeed(bytes32 _ccy) external override onlyOwner onlySupportedCurrency(_ccy) {
        PriceFeed memory priceFeed = Storage.slot().priceFeeds[_ccy];

        if (priceFeed.instances.length == 0) revert NoPriceFeedExists();
        delete Storage.slot().priceFeeds[_ccy];
        delete Storage.slot().decimalsCaches[_ccy];

        emit PriceFeedRemoved(_ccy);
    }

    /**
     * @notice Gets the last price of the selected currency in the base currency.
     * @param _ccy Currency name in bytes32
     * @return price The last price
     */
    function getLastPrice(bytes32 _ccy) public view override returns (int256 price) {
        price = _getAggregatedLastPrice(_ccy);
        PriceFeed memory priceFeeds = Storage.slot().priceFeeds[_ccy];

        for (uint256 i; i < priceFeeds.instances.length - 1; i++) {
            uint8 decimals = priceFeeds.instances[i].decimals();
            price = price.div((10 ** decimals).toInt256());
        }
    }

    /**
     * @notice Gets the last price of the selected currency in the base currency.
     * This price is returned in the aggregated decimals of all price feeds.
     * @param _ccy Currency name in bytes32
     * @return price The last price
     */
    function getAggregatedLastPrice(bytes32 _ccy) public view override returns (int256 price) {
        price = _getAggregatedLastPrice(_ccy);
    }

    /**
     * @notice Gets the converted amount of currency.
     * @param _fromCcy Currency to convert from
     * @param _toCcy Currency to convert to
     * @param _amount Amount to be converted
     * @return amount The converted amount
     */
    function convert(
        bytes32 _fromCcy,
        bytes32 _toCcy,
        uint256 _amount
    ) external view override returns (uint256 amount) {
        if (_fromCcy == _toCcy) return _amount;
        if (_amount == 0) return 0;

        int256 fromPrice = _getAggregatedLastPrice(_fromCcy);
        int256 toPrice = _getAggregatedLastPrice(_toCcy);

        amount = (_amount * uint256(fromPrice) * 10 ** Storage.slot().decimalsCaches[_toCcy]).div(
            10 ** Storage.slot().decimalsCaches[_fromCcy] * uint256(toPrice)
        );
    }

    /**
     * @notice Gets the converted amounts of currency.
     * @param _fromCcy Currency to convert from
     * @param _toCcy Currency to convert to
     * @param _amounts Amounts to be converted
     * @return amounts The converted amounts
     */
    function convert(
        bytes32 _fromCcy,
        bytes32 _toCcy,
        uint256[] calldata _amounts
    ) external view override returns (uint256[] memory amounts) {
        if (_fromCcy == _toCcy) return _amounts;

        int256 fromPrice = _getAggregatedLastPrice(_fromCcy);
        int256 toPrice = _getAggregatedLastPrice(_toCcy);
        uint256 toDecimals = Storage.slot().decimalsCaches[_toCcy];
        uint256 fromDecimals = Storage.slot().decimalsCaches[_fromCcy];
        amounts = new uint256[](_amounts.length);

        for (uint256 i; i < _amounts.length; i++) {
            if (_amounts[i] == 0) continue;

            amounts[i] = (_amounts[i] * uint256(fromPrice) * 10 ** toDecimals).div(
                10 ** fromDecimals * uint256(toPrice)
            );
        }
    }

    /**
     * @notice Gets the converted amount in the base currency.
     * @param _ccy Currency that has to be converted to the base currency
     * @param _amount Amount to be converted
     * @return amount The converted amount
     */
    function convertToBaseCurrency(
        bytes32 _ccy,
        uint256 _amount
    ) public view override returns (uint256 amount) {
        if (_amount == 0) return 0;

        amount = (_amount * _getAggregatedLastPrice(_ccy).toUint256()).div(
            10 ** Storage.slot().decimalsCaches[_ccy]
        );
    }

    /**
     * @notice Gets the converted amount in the base currency.
     * @param _ccy Currency that has to be converted to the base currency.
     * @param _amount Amount to be converted
     * @return amount The converted amount
     */
    function convertToBaseCurrency(
        bytes32 _ccy,
        int256 _amount
    ) external view override returns (int256 amount) {
        if (_amount == 0) return 0;

        amount = (_amount * _getAggregatedLastPrice(_ccy)).div(
            (10 ** Storage.slot().decimalsCaches[_ccy]).toInt256()
        );
    }

    /**
     * @notice Gets the converted amounts in the base currency.
     * @param _ccy Currency that has to be converted to the base currency.
     * @param _amounts Amounts to be converted
     * @return amounts The converted amounts
     */
    function convertToBaseCurrency(
        bytes32 _ccy,
        uint256[] calldata _amounts
    ) public view override returns (uint256[] memory amounts) {
        amounts = new uint256[](_amounts.length);
        uint256 price = _getAggregatedLastPrice(_ccy).toUint256();
        uint256 decimals = Storage.slot().decimalsCaches[_ccy];

        for (uint256 i; i < _amounts.length; i++) {
            if (_amounts[i] == 0) continue;
            amounts[i] = (_amounts[i] * price).div(10 ** decimals);
        }
    }

    /**
     * @notice Gets the converted amount to the selected currency from the base currency.
     * @param _ccy Currency that has to be converted from the base currency.
     * @param _amount Amount in the base currency to be converted
     * @return amount The converted amount
     */
    function convertFromBaseCurrency(
        bytes32 _ccy,
        uint256 _amount
    ) public view override returns (uint256 amount) {
        amount = (_amount * 10 ** Storage.slot().decimalsCaches[_ccy]).div(
            _getAggregatedLastPrice(_ccy).toUint256()
        );
    }

    /**
     * @notice Gets the converted amounts to the selected currency from the base currency.
     * @param _ccy Currency that has to be converted to the base currency.
     * @param _amounts Amounts in the base currency to be converted
     * @return amounts The converted amounts
     */
    function convertFromBaseCurrency(
        bytes32 _ccy,
        uint256[] calldata _amounts
    ) public view override returns (uint256[] memory amounts) {
        amounts = new uint256[](_amounts.length);
        uint256 price = _getAggregatedLastPrice(_ccy).toUint256();
        uint256 decimals = Storage.slot().decimalsCaches[_ccy];

        for (uint256 i; i < _amounts.length; i++) {
            if (_amounts[i] == 0) continue;

            amounts[i] = (_amounts[i] * 10 ** decimals).div(price);
        }
    }

    function _getAggregatedLastPrice(bytes32 _ccy) internal view returns (int256 totalPrice) {
        PriceFeed memory priceFeeds = Storage.slot().priceFeeds[_ccy];
        totalPrice = 1;

        for (uint256 i; i < priceFeeds.instances.length; i++) {
            (, int256 price, , uint256 updatedAt, ) = priceFeeds.instances[i].latestRoundData();

            if (updatedAt < block.timestamp - priceFeeds.heartbeat - 5 minutes) {
                revert StalePriceFeed(
                    address(priceFeeds.instances[i]),
                    priceFeeds.heartbeat,
                    updatedAt,
                    block.timestamp
                );
            }
            totalPrice = totalPrice * price;
        }
    }

    function _updateHaircut(bytes32 _ccy, uint256 _haircut) internal {
        if (_haircut > 10000) revert InvalidHaircut();

        Storage.slot().haircuts[_ccy] = _haircut;

        emit HaircutUpdated(_ccy, _haircut);
    }

    function _updatePriceFeed(
        bytes32 _ccy,
        uint8 _decimals,
        address[] calldata _priceFeeds,
        uint256 _heartbeat
    ) internal {
        AggregatorV3Interface[] memory priceFeeds = new AggregatorV3Interface[](_priceFeeds.length);

        if (_priceFeeds.length == 0) revert InvalidPriceFeed();

        for (uint256 i; i < _priceFeeds.length; i++) {
            priceFeeds[i] = AggregatorV3Interface(_priceFeeds[i]);
            (, int256 price, , , ) = priceFeeds[i].latestRoundData();
            if (price < 0) revert InvalidPrice();

            uint8 decimals = priceFeeds[i].decimals();
            if (decimals > 18) revert InvalidDecimals();
        }

        Storage.slot().priceFeeds[_ccy] = PriceFeed(priceFeeds, _heartbeat);
        Storage.slot().decimalsCaches[_ccy] = _decimals;

        emit PriceFeedUpdated(_ccy, _decimals, _priceFeeds);
    }
}

contracts/protocol/FutureValueVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

// dependencies
import {SafeCast} from "../dependencies/openzeppelin/utils/math/SafeCast.sol";
// interfaces
import {IFutureValueVault} from "./interfaces/IFutureValueVault.sol";
// libraries
import {Contracts} from "./libraries/Contracts.sol";
// mixins
import {MixinAddressResolver} from "./mixins/MixinAddressResolver.sol";
// types
import {ProtocolTypes} from "./types/ProtocolTypes.sol";
// utils
import {Proxyable} from "./utils/Proxyable.sol";
// storages
import {FutureValueVaultStorage as Storage} from "./storages/FutureValueVaultStorage.sol";

/**
 * @notice Implements the management of the future value as an amount for Lending positions in each currency.
 */
contract FutureValueVault is IFutureValueVault, MixinAddressResolver, Proxyable {
    using SafeCast for uint256;
    using SafeCast for int256;

    /**
     * @notice Initializes the contract.
     * @dev Function is invoked by the proxy contract when the contract is added to the ProxyController.
     * @param _resolver The address of the Address Resolver contract
     */
    function initialize(address _resolver) external initializer onlyBeacon {
        registerAddressResolver(_resolver);
        buildCache();
    }

    // @inheritdoc MixinAddressResolver
    function requiredContracts() public pure override returns (bytes32[] memory contracts) {
        contracts = new bytes32[](1);
        contracts[0] = Contracts.LENDING_MARKET_CONTROLLER;
    }

    // @inheritdoc MixinAddressResolver
    function acceptedContracts() public pure override returns (bytes32[] memory contracts) {
        contracts = new bytes32[](1);
        contracts[0] = Contracts.LENDING_MARKET_CONTROLLER;
    }

    /**
     * @notice Gets the total supply.
     * @param _maturity The maturity of the market
     */
    function getTotalSupply(uint256 _maturity) external view override returns (uint256) {
        return Storage.slot().totalSupply[_maturity];
    }

    /**
     * @notice Gets the user balance.
     * @param _user User's address
     * @return balance The user balance
     * @return maturity The maturity of the market that the future value was added
     */
    function getBalance(
        uint8 _orderBookId,
        address _user
    ) public view override returns (int256 balance, uint256 maturity) {
        return (
            Storage.slot().balances[_orderBookId][_user],
            Storage.slot().balanceMaturities[_orderBookId][_user]
        );
    }

    /**
     * @notice Gets if the account has past maturity balance at the selected maturity.
     * @param _user User's address
     * @param _maturity The maturity of the market
     * @return The boolean if the lending market is initialized or not
     */
    function hasBalanceAtPastMaturity(
        uint8 _orderBookId,
        address _user,
        uint256 _maturity
    ) public view override returns (bool) {
        if (Storage.slot().balanceMaturities[_orderBookId][_user] == _maturity) {
            return false;
        } else {
            return Storage.slot().balances[_orderBookId][_user] != 0;
        }
    }

    /**
     * @notice Increases amount for lending deals.
     * @dev Since the total supply can be determined by totaling only the amounts on one side of the order
     * when the order is fulfilled, the total supply is incremented only when the executor of the original order
     * is the taker.
     * @param _user User's address
     * @param _amount The amount to add
     * @param _maturity The maturity of the market
     * @param _isTaker The boolean if the original order is created by a taker
     */
    function increase(
        uint8 _orderBookId,
        address _user,
        uint256 _amount,
        uint256 _maturity,
        bool _isTaker
    ) public override onlyAcceptedContracts {
        if (_user == address(0)) revert UserIsZero();
        if (hasBalanceAtPastMaturity(_orderBookId, _user, _maturity))
            revert PastMaturityBalanceExists({user: _user});

        int256 previousBalance = Storage.slot().balances[_orderBookId][_user];
        Storage.slot().balanceMaturities[_orderBookId][_user] = _maturity;
        Storage.slot().balances[_orderBookId][_user] += _amount.toInt256();
        emit Transfer(address(0), _user, _orderBookId, _maturity, _amount.toInt256());

        int256 currentBalance = Storage.slot().balances[_orderBookId][_user];
        _updateTotalSupply(_maturity, previousBalance, currentBalance, _isTaker);
    }

    /**
     * @notice Decreases amount for borrowing deals.
     * @dev Since the total supply can be determined by totaling only the amounts on one side of the order
     * when the order is fulfilled, the total supply is incremented only when the executor of the original order
     * is the taker.
     * @param _user User's address
     * @param _amount The amount to add
     * @param _maturity The maturity of the market
     * @param _isTaker The boolean if the original order is created by a taker
     */
    function decrease(
        uint8 _orderBookId,
        address _user,
        uint256 _amount,
        uint256 _maturity,
        bool _isTaker
    ) public override onlyAcceptedContracts {
        if (_user == address(0)) revert UserIsZero();
        if (hasBalanceAtPastMaturity(_orderBookId, _user, _maturity))
            revert PastMaturityBalanceExists({user: _user});

        int256 previousBalance = Storage.slot().balances[_orderBookId][_user];
        Storage.slot().balanceMaturities[_orderBookId][_user] = _maturity;
        Storage.slot().balances[_orderBookId][_user] -= _amount.toInt256();
        emit Transfer(address(0), _user, _orderBookId, _maturity, -(_amount.toInt256()));

        int256 currentBalance = Storage.slot().balances[_orderBookId][_user];
        _updateTotalSupply(_maturity, previousBalance, currentBalance, _isTaker);
    }

    /**
     * @notice Transfers the future value from sender to receiver.
     * @param _sender Sender's address
     * @param _receiver Receiver's address
     * @param _amount Amount of funds to sent
     * @param _maturity The maturity of the market
     */
    function transferFrom(
        uint8 _orderBookId,
        address _sender,
        address _receiver,
        int256 _amount,
        uint256 _maturity
    ) external override onlyAcceptedContracts {
        if (hasBalanceAtPastMaturity(_orderBookId, _sender, _maturity))
            revert PastMaturityBalanceExists({user: _sender});
        if (hasBalanceAtPastMaturity(_orderBookId, _receiver, _maturity))
            revert PastMaturityBalanceExists({user: _receiver});

        Storage.slot().balanceMaturities[_orderBookId][_receiver] = _maturity;
        Storage.slot().balances[_orderBookId][_sender] -= _amount;
        Storage.slot().balances[_orderBookId][_receiver] += _amount;

        emit Transfer(_sender, _receiver, _orderBookId, _maturity, _amount);
    }

    /**
     * @notice Reset all amount if there is an amount in the past maturity.
     * @param _user User's address
     * @return removedAmount Removed future value amount
     * @return currentAmount Current future value amount after update
     * @return maturity Maturity of future value
     * @return isAllRemoved The boolean if the all future value amount in the selected maturity is removed
     */
    function reset(
        uint8 _orderBookId,
        address _user,
        uint256 _activeMaturity
    )
        external
        override
        onlyAcceptedContracts
        returns (int256 removedAmount, int256 currentAmount, uint256 maturity, bool isAllRemoved)
    {
        currentAmount = Storage.slot().balances[_orderBookId][_user];

        if (
            Storage.slot().balanceMaturities[_orderBookId][_user] != _activeMaturity &&
            currentAmount != 0
        ) {
            removedAmount = currentAmount;
            maturity = Storage.slot().balanceMaturities[_orderBookId][_user];

            isAllRemoved = false;
            if (removedAmount >= 0) {
                Storage.slot().removedLendingSupply[maturity] += removedAmount.toUint256();
            } else {
                Storage.slot().removedBorrowingSupply[maturity] += (-removedAmount).toUint256();
            }

            Storage.slot().balances[_orderBookId][_user] = 0;
            currentAmount = 0;

            emit Transfer(_user, address(0), _orderBookId, maturity, removedAmount);
        }

        isAllRemoved =
            Storage.slot().removedLendingSupply[maturity] == Storage.slot().totalSupply[maturity] &&
            Storage.slot().removedBorrowingSupply[maturity] == Storage.slot().totalSupply[maturity];
    }

    /**
     * @notice Sets initial total supply at market opening
     * @param _maturity The maturity of the market
     * @param _amount The amount to add
     */
    function setInitialTotalSupply(
        uint256 _maturity,
        int256 _amount
    ) external override onlyAcceptedContracts {
        if (Storage.slot().totalSupply[_maturity] != 0) revert TotalSupplyNotZero();
        _updateTotalSupply(_maturity, 0, _amount, true);
    }

    /**
     * @notice Forces a reset of the user's future value.
     * @param _user User's address
     */
    function executeForcedReset(
        uint8 _orderBookId,
        address _user
    ) external override onlyAcceptedContracts {
        int256 removedAmount = Storage.slot().balances[_orderBookId][_user];

        if (removedAmount != 0) {
            Storage.slot().balances[_orderBookId][_user] -= removedAmount;
            uint256 maturity = Storage.slot().balanceMaturities[_orderBookId][_user];

            emit Transfer(_user, address(0), _orderBookId, maturity, removedAmount);
        }
    }

    /**
     * @notice Forces a reset of the user's future value.
     * @param _user User's address
     * @param _amount The amount to reset
     */
    function executeForcedReset(
        uint8 _orderBookId,
        address _user,
        int256 _amount
    ) external override onlyAcceptedContracts returns (int256 removedAmount, int256 balance) {
        removedAmount = Storage.slot().balances[_orderBookId][_user];

        if ((_amount > 0 && removedAmount < 0) || (_amount < 0 && removedAmount > 0)) {
            revert InvalidResetAmount();
        }

        if ((_amount > 0 && _amount < removedAmount) || (_amount < 0 && _amount > removedAmount)) {
            removedAmount = _amount;
        }

        if (removedAmount != 0) {
            Storage.slot().balances[_orderBookId][_user] -= removedAmount;
            uint256 maturity = Storage.slot().balanceMaturities[_orderBookId][_user];

            emit Transfer(_user, address(0), _orderBookId, maturity, removedAmount);
        }

        balance = Storage.slot().balances[_orderBookId][_user];
    }

    function _updateTotalSupply(
        uint256 _maturity,
        int256 _previous,
        int256 _current,
        bool _isTaker
    ) private {
        uint256 absPrevious = _previous >= 0 ? _previous.toUint256() : (-_previous).toUint256();
        uint256 absCurrent = _current >= 0 ? _current.toUint256() : (-_current).toUint256();

        // Since total supply can be calculated only by taker amount, total supply will not be increased by maker amount.
        // However, if a maker has an offset volume when cleaning up its own orders, the total supply must be reduced.
        if (absPrevious > absCurrent) {
            Storage.slot().totalSupply[_maturity] -= absPrevious - absCurrent;
        } else if (_isTaker) {
            Storage.slot().totalSupply[_maturity] += absCurrent - absPrevious;
        }
    }
}

contracts/protocol/GenesisValueVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

// dependencies
import {SafeCast} from "../dependencies/openzeppelin/utils/math/SafeCast.sol";
// interfaces
import {IGenesisValueVault} from "./interfaces/IGenesisValueVault.sol";
// libraries
import {Contracts} from "./libraries/Contracts.sol";
import {Constants} from "./libraries/Constants.sol";
import {RoundingUint256} from "./libraries/math/RoundingUint256.sol";
import {RoundingInt256} from "./libraries/math/RoundingInt256.sol";
import {FullMath} from "./libraries/math/FullMath.sol";
// mixins
import {MixinAddressResolver} from "./mixins/MixinAddressResolver.sol";
// utils
import {Proxyable} from "./utils/Proxyable.sol";
// storages
import {GenesisValueVaultStorage as Storage, AutoRollLog} from "./storages/GenesisValueVaultStorage.sol";

/**
 * @notice Implements the management of the genesis value as an amount for Lending positions.
 */
contract GenesisValueVault is IGenesisValueVault, MixinAddressResolver, Proxyable {
    using SafeCast for uint256;
    using SafeCast for int256;
    using RoundingUint256 for uint256;
    using RoundingInt256 for int256;

    /**
     * @notice Initializes the contract.
     * @dev Function is invoked by the proxy contract when the contract is added to the ProxyController.
     * @param _resolver The address of the Address Resolver contract
     */
    function initialize(address _resolver) public initializer onlyProxy {
        registerAddressResolver(_resolver);
    }

    // @inheritdoc MixinAddressResolver
    function requiredContracts() public pure override returns (bytes32[] memory contracts) {
        contracts = new bytes32[](2);
        contracts[0] = Contracts.LENDING_MARKET_CONTROLLER;
        contracts[1] = Contracts.RESERVE_FUND;
    }

    // @inheritdoc MixinAddressResolver
    function acceptedContracts() public pure override returns (bytes32[] memory contracts) {
        contracts = new bytes32[](1);
        contracts[0] = Contracts.LENDING_MARKET_CONTROLLER;
    }

    /**
     * @notice Gets if the currency is initialized.
     * @param _ccy Currency name in bytes32
     * @return The boolean if the currency is initialized or not
     */
    function isInitialized(bytes32 _ccy) public view override returns (bool) {
        return Storage.slot().isInitialized[_ccy];
    }

    /**
     * @notice Gets if the decimals of the genesis value.
     * @param _ccy Currency name in bytes32
     * @return The decimals of the genesis value.
     */
    function decimals(bytes32 _ccy) public view override returns (uint8) {
        return Storage.slot().decimals[_ccy];
    }

    /**
     * @notice Gets the total supply of lending
     * @param _ccy Currency name in bytes32
     * @return The total supply of lending
     */
    function getTotalLendingSupply(bytes32 _ccy) external view override returns (uint256) {
        return Storage.slot().totalLendingSupplies[_ccy];
    }

    /**
     * @notice Gets the total supply of borrowing
     * @param _ccy Currency name in bytes32
     * @return The total supply of borrowing
     */
    function getTotalBorrowingSupply(bytes32 _ccy) external view override returns (uint256) {
        return Storage.slot().totalBorrowingSupplies[_ccy];
    }

    /**
     * @notice Gets the user balance.
     * @param _ccy Currency name in bytes32
     * @param _user User's address
     * @return The user balance
     */
    function getBalance(bytes32 _ccy, address _user) public view override returns (int256) {
        (int256 balance, int256 fluctuation) = _getActualBalance(
            _ccy,
            _user,
            getCurrentMaturity(_ccy)
        );
        return balance + fluctuation;
    }

    /**
     * @notice Gets the future value of the user balance.
     * @param _ccy Currency name in bytes32
     * @param _user User's address
     * @return The future value of the user balance
     */
    function getBalanceInFutureValue(
        bytes32 _ccy,
        address _user
    ) external view override returns (int256) {
        // NOTE: The formula is:
        // futureValue = genesisValue * currentCompoundFactor.
        return
            (getBalance(_ccy, _user) * getLendingCompoundFactor(_ccy).toInt256()).div(
                (10 ** decimals(_ccy)).toInt256()
            );
    }

    /**
     * @notice Gets the current total supply per maturity
     * @param _ccy Currency name in bytes32
     * @param _maturity The maturity
     * @return The current total supply
     */
    function getMaturityGenesisValue(
        bytes32 _ccy,
        uint256 _maturity
    ) external view override returns (int256) {
        return Storage.slot().maturityBalances[_ccy][_maturity];
    }

    /**
     * @notice Gets the current maturity
     * @param _ccy Currency name in bytes32
     * @return The current maturity
     */
    function getCurrentMaturity(bytes32 _ccy) public view override returns (uint256) {
        return Storage.slot().currentMaturity[_ccy];
    }

    /**
     * @notice Gets the lending compound factor
     * @param _ccy Currency name in bytes32
     * @return The lending compound factor
     */
    function getLendingCompoundFactor(bytes32 _ccy) public view override returns (uint256) {
        return Storage.slot().lendingCompoundFactors[_ccy];
    }

    /**
     * @notice Gets the borrowing compound factor
     * @param _ccy Currency name in bytes32
     * @return The lending compound factor
     */
    function getBorrowingCompoundFactor(bytes32 _ccy) public view override returns (uint256) {
        return Storage.slot().borrowingCompoundFactors[_ccy];
    }

    /**
     * @notice Gets the auto-roll log
     * @param _ccy Currency name in bytes32
     * @param _maturity The maturity
     * @return The auto-roll log
     */
    function getAutoRollLog(
        bytes32 _ccy,
        uint256 _maturity
    ) external view override returns (AutoRollLog memory) {
        return Storage.slot().autoRollLogs[_ccy][_maturity];
    }

    /**
     * @notice Gets the latest auto-roll log
     * @param _ccy Currency name in bytes32
     * @return The auto-roll log
     */
    function getLatestAutoRollLog(
        bytes32 _ccy
    ) external view override returns (AutoRollLog memory) {
        return Storage.slot().autoRollLogs[_ccy][Storage.slot().currentMaturity[_ccy]];
    }

    /**
     * @notice Calculates the future value from the basis maturity to the destination maturity using the compound factor.
     * @param _ccy Currency name in bytes32
     * @return The future value at the destination maturity
     */
    function calculateFVFromFV(
        bytes32 _ccy,
        uint256 _basisMaturity,
        uint256 _destinationMaturity,
        int256 _futureValue
    ) external view override returns (int256) {
        if (_futureValue == 0) {
            return 0;
        } else if (_basisMaturity == _destinationMaturity) {
            return _futureValue;
        } else {
            // NOTE: These calculation steps "FV -> GV -> FV" are needed to match the actual conversion step.
            // Otherwise, Solidity's truncation specification creates a difference in the calculated values.
            // The formula is:
            // genesisValue = featureValueInMaturity / compoundFactorInMaturity.
            // currentFeatureValue = genesisValue * currentCompoundFactor
            int256 genesisValue = calculateGVFromFV(_ccy, _basisMaturity, _futureValue);
            return calculateFVFromGV(_ccy, _destinationMaturity, genesisValue);
        }
    }

    /**
     * @notice Calculates the genesis value from the future value at the basis maturity using the compound factor.
     * @param _ccy Currency name in bytes32
     * @return The genesis value
     */
    function calculateGVFromFV(
        bytes32 _ccy,
        uint256 _basisMaturity,
        int256 _futureValue
    ) public view override returns (int256) {
        uint256 compoundFactor = _basisMaturity == 0 ||
            _basisMaturity == Storage.slot().currentMaturity[_ccy]
            ? getLendingCompoundFactor(_ccy)
            : Storage.slot().autoRollLogs[_ccy][_basisMaturity].lendingCompoundFactor;

        if (compoundFactor == 0) revert NoCompoundFactorExists({maturity: _basisMaturity});

        // NOTE: The formula is: genesisValue = featureValue / compoundFactor.
        bool isPlus = _futureValue > 0;
        uint256 absFv = (isPlus ? _futureValue : -_futureValue).toUint256();
        uint256 absGv = (absFv * 10 ** decimals(_ccy)).div(compoundFactor);
        return isPlus ? absGv.toInt256() : -(absGv.toInt256());
    }

    /**
     * @notice Calculates the future value at the basis maturity from the genesis value using the compound factor.
     * @param _ccy Currency name in bytes32
     * @return The future value
     */
    function calculateFVFromGV(
        bytes32 _ccy,
        uint256 _basisMaturity,
        int256 _genesisValue
    ) public view override returns (int256) {
        uint256 compoundFactor = _basisMaturity == 0 ||
            _basisMaturity == Storage.slot().currentMaturity[_ccy]
            ? getLendingCompoundFactor(_ccy)
            : Storage.slot().autoRollLogs[_ccy][_basisMaturity].lendingCompoundFactor;

        if (compoundFactor == 0) revert NoCompoundFactorExists({maturity: _basisMaturity});

        bool isPlus = _genesisValue > 0;
        uint256 absGv = (isPlus ? _genesisValue : -_genesisValue).toUint256();
        uint256 absFv = (absGv * compoundFactor).div(10 ** decimals(_ccy));

        return isPlus ? absFv.toInt256() : -(absFv.toInt256());
    }

    /**
     * @notice Initializes the currency setting.
     * @param _ccy Currency name in bytes32
     * @param _decimals Compound factor decimals
     * @param _compoundFactor Initial compound factor
     * @param _maturity Initial maturity
     */
    function initializeCurrencySetting(
        bytes32 _ccy,
        uint8 _decimals,
        uint256 _compoundFactor,
        uint256 _maturity
    ) external override onlyAcceptedContracts {
        if (_compoundFactor == 0) revert CompoundFactorIsZero();
        if (isInitialized(_ccy)) revert CurrencyAlreadyInitialized();

        Storage.slot().isInitialized[_ccy] = true;
        Storage.slot().decimals[_ccy] = _decimals;
        Storage.slot().initialCompoundFactors[_ccy] = _compoundFactor;
        Storage.slot().lendingCompoundFactors[_ccy] = _compoundFactor;
        Storage.slot().borrowingCompoundFactors[_ccy] = _compoundFactor;
        Storage.slot().currentMaturity[_ccy] = _maturity;

        // Update autoRollLogs by initial compound factor.
        // These values are updated by the first Itayose call of the nearest maturity market
        // if it is executed.
        Storage.slot().autoRollLogs[_ccy][_maturity] = AutoRollLog({
            unitPrice: Constants.PRICE_DIGIT,
            lendingCompoundFactor: _compoundFactor,
            borrowingCompoundFactor: _compoundFactor,
            prev: 0,
            next: 0
        });
    }

    /**
     * @notice Update the currency setting.
     * @dev This function is allowed to be called only before the initial compound factor is finalized.
     * @param _ccy Currency name in bytes32
     * @param _unitPrice The unit price used to calculate the compound factor
     */
    function updateInitialCompoundFactor(
        bytes32 _ccy,
        uint256 _unitPrice
    ) external override onlyAcceptedContracts {
        uint256 maturity = Storage.slot().currentMaturity[_ccy];

        if (Storage.slot().autoRollLogs[_ccy][maturity].prev != 0) {
            revert InitialCompoundFactorAlreadyFinalized();
        }

        _updateCompoundFactor(_ccy, _unitPrice, 0, 0);
        Storage.slot().autoRollLogs[_ccy][maturity] = AutoRollLog({
            unitPrice: _unitPrice,
            lendingCompoundFactor: Storage.slot().lendingCompoundFactors[_ccy],
            borrowingCompoundFactor: Storage.slot().borrowingCompoundFactors[_ccy],
            prev: 0,
            next: 0
        });
    }

    /**
     * @notice Executes the auto-roll.
     * @param _ccy Currency name in bytes32
     * @param _maturity Current maturity
     * @param _nextMaturity Next maturity to be rolled
     * @param _unitPrice Unit price of auto-roll
     * @param _orderFeeRate Order fee rate used to calculate the auto-roll fee
     */
    function executeAutoRoll(
        bytes32 _ccy,
        uint256 _maturity,
        uint256 _nextMaturity,
        uint256 _unitPrice,
        uint256 _orderFeeRate
    ) external override onlyAcceptedContracts {
        _updateCompoundFactor(_ccy, _unitPrice, _orderFeeRate, _nextMaturity - _maturity);
        _updateAutoRollLogs(_ccy, _maturity, _nextMaturity, _unitPrice);

        emit AutoRollExecuted(
            _ccy,
            Storage.slot().lendingCompoundFactors[_ccy],
            Storage.slot().borrowingCompoundFactors[_ccy],
            _unitPrice,
            _nextMaturity,
            _maturity
        );
    }

    function _updateAutoRollLogs(
        bytes32 _ccy,
        uint256 _maturity,
        uint256 _nextMaturity,
        uint256 _unitPrice
    ) private {
        if (_unitPrice == 0) revert UnitPriceIsZero();
        if (_nextMaturity <= _maturity) revert InvalidMaturity();

        AutoRollLog memory currentLog = Storage.slot().autoRollLogs[_ccy][_maturity];
        AutoRollLog memory nextLog = Storage.slot().autoRollLogs[_ccy][_nextMaturity];

        if (
            currentLog.next != 0 ||
            currentLog.lendingCompoundFactor == 0 ||
            currentLog.borrowingCompoundFactor == 0 ||
            nextLog.lendingCompoundFactor != 0
        ) {
            revert AutoRollLogAlreadyUpdated({
                currentMaturity: _maturity,
                nextMaturity: _nextMaturity
            });
        }

        Storage.slot().currentMaturity[_ccy] = _nextMaturity;
        Storage.slot().autoRollLogs[_ccy][_maturity].next = _nextMaturity;
        Storage.slot().autoRollLogs[_ccy][_nextMaturity] = AutoRollLog({
            unitPrice: _unitPrice,
            lendingCompoundFactor: Storage.slot().lendingCompoundFactors[_ccy],
            borrowingCompoundFactor: Storage.slot().borrowingCompoundFactors[_ccy],
            prev: _maturity,
            next: 0
        });
    }

    /**
     * @notice Updates the user's balance of the genesis value with the input future value.
     * @param _ccy Currency name in bytes32
     * @param _user User's address
     * @param _basisMaturity The basis maturity
     * @param _fvAmount The amount in the future value
     */
    function updateGenesisValueWithFutureValue(
        bytes32 _ccy,
        address _user,
        uint256 _basisMaturity,
        int256 _fvAmount
    ) external override onlyAcceptedContracts {
        int256 amount = calculateGVFromFV(_ccy, _basisMaturity, _fvAmount);

        _updateBalance(_ccy, _user, _basisMaturity, amount);
    }

    /**
     * @notice Updates the user's balance of the genesis value without the input future value.
     * @dev This function is used only in the case that the user is the last person who updates the genesis value at maturity,
     * and called only one time per maturity.
     * @param _ccy Currency name in bytes32
     * @param _user User's address
     * @param _basisMaturity The basis maturity
     */
    function updateGenesisValueWithResidualAmount(
        bytes32 _ccy,
        address _user,
        uint256 _basisMaturity
    ) external override onlyAcceptedContracts {
        int256 residualGVAmount = Storage.slot().maturityBalances[_ccy][_basisMaturity];

        _updateBalance(_ccy, _user, _basisMaturity, -residualGVAmount);

        if (Storage.slot().maturityBalances[_ccy][_basisMaturity] != 0) {
            revert ResidualAmountIsNotZero();
        }
    }

    /**
     * @notice Transfers the genesis value from sender to receiver.
     * @param _ccy Currency name in bytes32
     * @param _sender Sender's address
     * @param _receiver Receiver's address
     * @param _amount Amount of funds to sent
     */
    function transferFrom(
        bytes32 _ccy,
        address _sender,
        address _receiver,
        int256 _amount
    ) external override onlyAcceptedContracts {
        Storage.slot().balances[_ccy][_sender] -= _amount;
        Storage.slot().balances[_ccy][_receiver] += _amount;

        emit Transfer(_ccy, _sender, _receiver, _amount);
    }

    /**
     * @notice Clean up balance of the user per maturity.
     * @dev The genesis value of borrowing fluctuates when it is auto-rolled, but it is not updated in real-time.
     * This function removes the fluctuation amount calculated by lazy evaluation to reduce gas costs.
     * @param _ccy Currency name in bytes32
     * @param _user User's address
     * @param _maturity The maturity
     */
    function cleanUpBalance(
        bytes32 _ccy,
        address _user,
        uint256 _maturity
    ) external override onlyAcceptedContracts {
        uint256 maturity = _maturity == 0 ? getCurrentMaturity(_ccy) : _maturity;
        int256 fluctuation = _getBalanceFluctuationByAutoRolls(_ccy, _user, maturity);

        if (fluctuation < 0) {
            address reserveFundAddr = address(reserveFund());

            _updateTotalSupplies(_ccy, fluctuation, Storage.slot().balances[_ccy][_user]);
            _updateTotalSupplies(
                _ccy,
                -fluctuation,
                Storage.slot().balances[_ccy][reserveFundAddr]
            );

            Storage.slot().userMaturities[_ccy][_user] = maturity;
            Storage.slot().balances[_ccy][_user] += fluctuation;
            Storage.slot().balances[_ccy][reserveFundAddr] += -fluctuation;

            emit Transfer(_ccy, _user, reserveFundAddr, -fluctuation);
        }
    }

    /**
     * @notice Forces a reset of the user's genesis value.
     * @param _ccy Currency name in bytes32
     * @param _user User's address
     */
    function executeForcedReset(
        bytes32 _ccy,
        address _user
    ) external override onlyAcceptedContracts {
        int256 removedAmount = Storage.slot().balances[_ccy][_user];

        if (removedAmount != 0) {
            Storage.slot().balances[_ccy][_user] = 0;
            emit Transfer(_ccy, _user, address(0), removedAmount);
        }
    }

    /**
     * @notice Forces a reset of the user's genesis value.
     * @param _ccy Currency name in bytes32
     * @param _user User's address
     * @param _amountInFV The amount in the future value to reset
     */
    function executeForcedReset(
        bytes32 _ccy,
        uint256 _maturity,
        address _user,
        int256 _amountInFV
    ) external override onlyAcceptedContracts returns (int256 removedAmountInFV, int256 balance) {
        int256 _amount = calculateGVFromFV(_ccy, _maturity, _amountInFV);
        int256 removedAmount = Storage.slot().balances[_ccy][_user];

        if ((_amount > 0 && removedAmount < 0) || (_amount < 0 && removedAmount > 0)) {
            revert InvalidAmount();
        }

        if ((_amount > 0 && _amount < removedAmount) || (_amount < 0 && _amount > removedAmount)) {
            removedAmount = _amount;
        }

        if (removedAmount != 0) {
            Storage.slot().balances[_ccy][_user] -= removedAmount;
            emit Transfer(_ccy, _user, address(0), removedAmount);
        }

        removedAmountInFV = calculateFVFromGV(_ccy, _maturity, removedAmount);
        balance = Storage.slot().balances[_ccy][_user];
    }

    /**
     * @notice Gets the fluctuation amount of genesis value caused by auto-rolls.
     * @param _ccy Currency name in bytes32
     * @param _user User's address
     * @param _maturity The maturity
     */
    function getBalanceFluctuationByAutoRolls(
        bytes32 _ccy,
        address _user,
        uint256 _maturity
    ) external view override returns (int256 fluctuation) {
        uint256 maturity = _maturity == 0 ? getCurrentMaturity(_ccy) : _maturity;
        fluctuation = _getBalanceFluctuationByAutoRolls(_ccy, _user, maturity);
    }

    /**
     * @notice Calculates the fluctuation amount of genesis value caused by auto-rolls at a certain maturity
     * @param _ccy Currency name in bytes32
     * @param _balance User's balance
     * @param _fromMaturity The maturity at start
     * @param _toMaturity The maturity at end
     */
    function calculateBalanceFluctuationByAutoRolls(
        bytes32 _ccy,
        int256 _balance,
        uint256 _fromMaturity,
        uint256 _toMaturity
    ) external view override returns (int256 fluctuation) {
        uint256 toMaturity = _toMaturity == 0 ? getCurrentMaturity(_ccy) : _toMaturity;
        fluctuation = _calculateBalanceFluctuationByAutoRolls(
            _ccy,
            _balance,
            _fromMaturity,
            toMaturity
        );
    }

    function _updateBalance(
        bytes32 _ccy,
        address _user,
        uint256 _maturity,
        int256 _amount
    ) private {
        (int256 balance, int256 fluctuation) = _getActualBalance(_ccy, _user, _maturity);
        int256 totalAmount = _amount;

        // Note: `fluctuation` is always 0 or less because the genesis value fluctuates
        // only when it is negative.
        // Here, only the opposite amount of the fluctuation is added to the reserve fund as a fee.
        if (fluctuation < 0) {
            totalAmount += fluctuation;
            address reserveFundAddr = address(reserveFund());
            Storage.slot().balances[_ccy][reserveFundAddr] += -fluctuation;

            _updateTotalSupplies(
                _ccy,
                -fluctuation,
                Storage.slot().balances[_ccy][reserveFundAddr]
            );

            emit Transfer(_ccy, _user, reserveFundAddr, -fluctuation);
        }

        _updateTotalSupplies(_ccy, totalAmount, balance);

        Storage.slot().userMaturities[_ccy][_user] = _maturity;
        Storage.slot().balances[_ccy][_user] += totalAmount;
        Storage.slot().maturityBalances[_ccy][_maturity] += _amount;

        emit Transfer(_ccy, address(0), _user, _amount);
    }

    function _updateTotalSupplies(bytes32 _ccy, int256 _amount, int256 _balance) private {
        if (_amount >= 0) {
            uint256 absAmount = _amount.toUint256();
            if (_balance >= 0) {
                Storage.slot().totalLendingSupplies[_ccy] += absAmount;
            } else {
                int256 diff = _amount + _balance;
                if (diff >= 0) {
                    Storage.slot().totalLendingSupplies[_ccy] += diff.toUint256();
                    Storage.slot().totalBorrowingSupplies[_ccy] -= (-_balance).toUint256();
                } else {
                    Storage.slot().totalBorrowingSupplies[_ccy] -= absAmount;
                }
            }
        } else {
            uint256 absAmount = (-_amount).toUint256();
            if (_balance <= 0) {
                Storage.slot().totalBorrowingSupplies[_ccy] += absAmount;
            } else {
                int256 diff = _amount + _balance;
                if (diff <= 0) {
                    Storage.slot().totalBorrowingSupplies[_ccy] += (-diff).toUint256();
                    Storage.slot().totalLendingSupplies[_ccy] -= _balance.toUint256();
                } else {
                    Storage.slot().totalLendingSupplies[_ccy] -= absAmount;
                }
            }
        }
    }

    function _getActualBalance(
        bytes32 _ccy,
        address _user,
        uint256 _maturity
    ) private view returns (int256 balance, int256 fluctuation) {
        fluctuation = _getBalanceFluctuationByAutoRolls(_ccy, _user, _maturity);
        balance = Storage.slot().balances[_ccy][_user];
    }

    /**
     * @notice Calculates the fluctuation amount of genesis value caused by auto-rolls.
     * @dev The genesis value means the present value of the lending position at the time
     * when the initial market is opened, so the genesis value amount will fluctuate
     * by the fee rate due to auto-rolls if it is negative (equals to the borrowing position).
     * @param _ccy Currency for pausing all lending markets
     * @param _user User's address
     * @return fluctuation The fluctuated genesis value amount
     */
    function _getBalanceFluctuationByAutoRolls(
        bytes32 _ccy,
        address _user,
        uint256 _maturity
    ) private view returns (int256 fluctuation) {
        int256 balance = Storage.slot().balances[_ccy][_user];
        uint256 userMaturity = Storage.slot().userMaturities[_ccy][_user];

        fluctuation = _calculateBalanceFluctuationByAutoRolls(
            _ccy,
            balance,
            userMaturity,
            _maturity
        );
    }

    function _calculateBalanceFluctuationByAutoRolls(
        bytes32 _ccy,
        int256 _balance,
        uint256 _fromMaturity,
        uint256 _toMaturity
    ) private view returns (int256 fluctuation) {
        if (_balance >= 0 || _toMaturity <= _fromMaturity || _fromMaturity == 0) {
            return 0;
        }

        AutoRollLog memory autoRollLog = Storage.slot().autoRollLogs[_ccy][_fromMaturity];

        uint256 destinationBorrowingCF;
        uint256 destinationLendingCF;
        uint256 currentMaturity = getCurrentMaturity(_ccy);

        if (_toMaturity > currentMaturity) {
            return 0;
        } else if (_toMaturity == currentMaturity) {
            destinationBorrowingCF = Storage.slot().borrowingCompoundFactors[_ccy];
            destinationLendingCF = Storage.slot().lendingCompoundFactors[_ccy];
        } else {
            AutoRollLog memory destinationAutoRollLog = Storage.slot().autoRollLogs[_ccy][
                _toMaturity
            ];
            destinationBorrowingCF = destinationAutoRollLog.borrowingCompoundFactor;
            destinationLendingCF = destinationAutoRollLog.lendingCompoundFactor;
        }

        // Note: The formula is:
        // fluctuation = currentBalance * ((currentBCF / userBCF) * (userLCF / currentLCF) - 1)
        fluctuation =
            -FullMath
                .mulDiv(
                    FullMath.mulDiv(
                        (-_balance).toUint256(),
                        destinationBorrowingCF,
                        autoRollLog.borrowingCompoundFactor
                    ),
                    autoRollLog.lendingCompoundFactor,
                    destinationLendingCF
                )
                .toInt256() -
            _balance;
    }

    function _updateCompoundFactor(
        bytes32 _ccy,
        uint256 _unitPrice,
        uint256 _orderFeeRate,
        uint256 _currentMaturity
    ) private {
        if (_orderFeeRate > Constants.PCT_DIGIT) revert InvalidOrderFeeRate();

        // Save actual compound factor here due to calculating the genesis value from future value.
        // NOTE: The formula is:
        // autoRollRate = 1 / unitPrice
        // rollFeeRate = orderFeeRate * (currentMaturity / SECONDS_IN_YEAR)
        // newLendingCompoundFactor = currentLendingCompoundFactor * (autoRollRate - rollFeeRate)
        // newBorrowingCompoundFactor = currentBorrowingCompoundFactor * (autoRollRate + rollFeeRate)
        uint256 denominator = (Constants.PCT_DIGIT * Constants.SECONDS_IN_YEAR * _unitPrice);

        Storage.slot().lendingCompoundFactors[_ccy] = (Storage.slot().lendingCompoundFactors[_ccy] *
            ((Constants.PRICE_DIGIT * Constants.PCT_DIGIT * Constants.SECONDS_IN_YEAR) -
                (_orderFeeRate * _currentMaturity * _unitPrice))).div(denominator);

        Storage.slot().borrowingCompoundFactors[_ccy] = (Storage.slot().borrowingCompoundFactors[
            _ccy
        ] *
            ((Constants.PRICE_DIGIT * Constants.PCT_DIGIT * Constants.SECONDS_IN_YEAR) +
                (_orderFeeRate * _currentMaturity * _unitPrice))).div(denominator);
    }
}

