contracts/protocol/libraries/logics/Depo.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

// dependencies
import {EnumerableSet} from "../../../dependencies/openzeppelin/utils/structs/EnumerableSet.sol";
// interfaces
import {ILendingMarketController} from "../../interfaces/ILendingMarketController.sol";
// libraries
import {AddressResolverLib} from "../AddressResolverLib.sol";
import {TransferHelper} from "../TransferHelper.sol";
import {Constants} from "../Constants.sol";
import {RoundingUint256} from "../math/RoundingUint256.sol";
// storages
import {TokenVaultStorage as Storage} from "../../storages/TokenVaultStorage.sol";

library DepositManagementLogic {
    using EnumerableSet for EnumerableSet.Bytes32Set;
    using RoundingUint256 for uint256;

    error NotEnoughDeposit(bytes32 ccy);
    error CollateralIsZero(bytes32 ccy);
    error ProtocolIsInsolvent(bytes32 ccy);

    struct CalculatedFundVars {
        uint256 plusDepositAmountInAdditionalFundsCcy;
        uint256 minusDepositAmountInAdditionalFundsCcy;
        uint256 workingLendOrdersAmount;
        uint256 collateralAmount;
        uint256 lentAmount;
        uint256 workingBorrowOrdersAmount;
        uint256 debtAmount;
        uint256 borrowedAmount;
    }

    function isCovered(address _user) public view returns (bool) {
        (uint256 totalCollateral, uint256 totalUsedCollateral, ) = getTotalCollateralAmount(_user);

        return
            totalUsedCollateral == 0 ||
            (totalCollateral * Constants.PCT_DIGIT >=
                totalUsedCollateral * Storage.slot().liquidationThresholdRate);
    }

    function getUsedCurrencies(address _user) public view returns (bytes32[] memory) {
        EnumerableSet.Bytes32Set storage currencySet = Storage.slot().usedCurrencies[_user];

        uint256 length = currencySet.length();
        bytes32[] memory currencies = new bytes32[](length);

        for (uint256 i; i < length; i++) {
            bytes32 currency = currencySet.at(i);
            currencies[i] = currency;
        }

        return currencies;
    }

    function getDepositAmount(address _user, bytes32 _ccy) public view returns (uint256) {
        ILendingMarketController.CalculatedFunds memory funds = AddressResolverLib
            .lendingMarketController()
            .calculateFunds(_ccy, _user, Storage.slot().liquidationThresholdRate);

        return
            Storage.slot().depositAmounts[_user][_ccy] +
            funds.borrowedAmount -
            funds.lentAmount -
            funds.workingLendOrdersAmount;
    }

    function getTotalCollateralAmount(
        address _user
    )
        public
        view
        returns (uint256 totalCollateral, uint256 totalUsedCollateral, uint256 totalDeposit)
    {
        ILendingMarketController.AdditionalFunds memory _funds;
        (totalCollateral, totalUsedCollateral, totalDeposit, ) = _calculateCollateral(
            _user,
            _funds
        );
    }

    function getCollateralAmount(
        address _user,
        bytes32 _ccy
    )
        public
        view
        returns (uint256 totalCollateral, uint256 totalUsedCollateral, uint256 totalDeposit)
    {
        ILendingMarketController.CalculatedFunds memory funds = AddressResolverLib
            .lendingMarketController()
            .calculateFunds(_ccy, _user, Storage.slot().liquidationThresholdRate);

        uint256 plusDeposit = Storage.slot().depositAmounts[_user][_ccy] + funds.borrowedAmount;
        uint256 minusDeposit = funds.workingLendOrdersAmount + funds.lentAmount;
        uint256 plusCollateral = plusDeposit + funds.collateralAmount;

        totalCollateral = plusCollateral >= minusDeposit ? plusCollateral - minusDeposit : 0;
        totalUsedCollateral = funds.workingBorrowOrdersAmount + funds.debtAmount;
        totalDeposit = plusDeposit >= minusDeposit ? plusDeposit - minusDeposit : 0;
    }

    function getCoverage(address _user) external view returns (uint256 coverage) {
        ILendingMarketController.AdditionalFunds memory _emptyAdditionalFunds;
        (coverage, ) = calculateCoverage(_user, _emptyAdditionalFunds);
    }

    function getTotalUnusedCollateralAmount(address _user) public view returns (uint256) {
        (uint256 totalCollateral, uint256 totalUsedCollateral, ) = getTotalCollateralAmount(_user);

        return totalCollateral > totalUsedCollateral ? totalCollateral - totalUsedCollateral : 0;
    }

    function getBorrowableAmount(address _user, bytes32 _ccy) external view returns (uint256) {
        uint256[] memory amounts = new uint256[](2);
        (amounts[0], amounts[1], ) = getTotalCollateralAmount(_user);
        amounts = AddressResolverLib.currencyController().convertFromBaseCurrency(_ccy, amounts);

        uint256 totalCollateralAmount = amounts[0];
        uint256 totalUsedCollateral = amounts[1];
        uint256 liquidationThresholdRate = Storage.slot().liquidationThresholdRate;

        ILendingMarketController.CalculatedFunds memory funds = AddressResolverLib
            .lendingMarketController()
            .calculateFunds(_ccy, _user, liquidationThresholdRate);

        uint256 unallocatedCollateralAmount = funds.claimableAmount > funds.collateralAmount
            ? funds.claimableAmount - funds.collateralAmount
            : 0;

        uint256 borrowableAmount = ((totalCollateralAmount + unallocatedCollateralAmount) *
            Constants.PCT_DIGIT).div(liquidationThresholdRate);

        return borrowableAmount > totalUsedCollateral ? borrowableAmount - totalUsedCollateral : 0;
    }

    function calculateCoverage(
        address _user,
        ILendingMarketController.AdditionalFunds memory _additionalFunds
    ) public view returns (uint256 coverage, bool isInsufficientDepositAmount) {
        uint256 totalCollateral;
        uint256 totalUsedCollateral;

        (
            totalCollateral,
            totalUsedCollateral,
            ,
            isInsufficientDepositAmount
        ) = _calculateCollateral(_user, _additionalFunds);

        if (totalCollateral == 0) {
            coverage = totalUsedCollateral == 0 ? 0 : type(uint256).max;
        } else {
            coverage = (totalUsedCollateral * Constants.PCT_DIGIT) / totalCollateral;
        }
    }

    function _calculateCollateral(
        address _user,
        ILendingMarketController.AdditionalFunds memory _funds
    )
        internal
        view
        returns (
            uint256 totalCollateral,
            uint256 totalUsedCollateral,
            uint256 totalDeposit,
            bool isInsufficientDepositAmount
        )
    {
        CalculatedFundVars memory vars;

        ILendingMarketController.CalculatedTotalFunds memory funds = AddressResolverLib
            .lendingMarketController()
            .calculateTotalFundsInBaseCurrency(
                _user,
                _funds,
                Storage.slot().liquidationThresholdRate
            );

        vars.plusDepositAmountInAdditionalFundsCcy = funds.plusDepositAmountInAdditionalFundsCcy;
        vars.minusDepositAmountInAdditionalFundsCcy = funds.minusDepositAmountInAdditionalFundsCcy;
        vars.workingLendOrdersAmount = funds.workingLendOrdersAmount;
        vars.collateralAmount = funds.collateralAmount;
        vars.lentAmount = funds.lentAmount;
        vars.workingBorrowOrdersAmount = funds.workingBorrowOrdersAmount;
        vars.debtAmount = funds.debtAmount;
        vars.borrowedAmount = funds.borrowedAmount;

        // Check if the user has enough deposit amount for lending in the selected currency.
        if (
            _funds.lentAmount != 0 &&
            (vars.plusDepositAmountInAdditionalFundsCcy +
                Storage.slot().depositAmounts[_user][_funds.ccy] <
                vars.minusDepositAmountInAdditionalFundsCcy)
        ) {
            isInsufficientDepositAmount = true;
        }

        uint256 totalInternalDepositAmount = _getTotalInternalDepositAmountInBaseCurrency(_user);

        uint256 plusDeposit = totalInternalDepositAmount + vars.borrowedAmount;
        uint256 minusDeposit = vars.workingLendOrdersAmount + vars.lentAmount;
        uint256 plusCollateral = plusDeposit + vars.collateralAmount;

        totalCollateral = plusCollateral >= minusDeposit ? plusCollateral - minusDeposit : 0;
        totalUsedCollateral = vars.workingBorrowOrdersAmount + vars.debtAmount;
        totalDeposit = plusDeposit >= minusDeposit ? plusDeposit - minusDeposit : 0;
    }

    function getWithdrawableCollateral(address _user) public view returns (uint256) {
        (
            uint256 totalCollateral,
            uint256 totalUsedCollateral,
            uint256 totalDeposit
        ) = getTotalCollateralAmount(_user);

        if (totalUsedCollateral == 0) {
            return totalDeposit;
        } else if (
            totalCollateral * Constants.PRICE_DIGIT >
            totalUsedCollateral * Storage.slot().liquidationThresholdRate
        ) {
            // NOTE: The formula is:
            // maxWithdraw = (totalCollateral / liquidationThresholdRate) - totalUsedCollateral.
            uint256 maxWithdraw = (totalCollateral *
                Constants.PRICE_DIGIT -
                (totalUsedCollateral) *
                Storage.slot().liquidationThresholdRate).div(Constants.PRICE_DIGIT);

            return maxWithdraw >= totalDeposit ? totalDeposit : maxWithdraw;
        } else {
            return 0;
        }
    }

    function getWithdrawableCollateral(
        bytes32 _ccy,
        address _user
    ) public view returns (uint256 withdrawableAmount) {
        uint256 depositAmount = getDepositAmount(_user, _ccy);
        if (Storage.slot().collateralCurrencies.contains(_ccy)) {
            uint256 maxWithdrawInBaseCurrency = getWithdrawableCollateral(_user);
            uint256 maxWithdraw = AddressResolverLib.currencyController().convertFromBaseCurrency(
                _ccy,
                maxWithdrawInBaseCurrency
            );

            withdrawableAmount = depositAmount >= maxWithdraw ? maxWithdraw : depositAmount;
        } else {
            withdrawableAmount = depositAmount;
        }
    }

    function addDepositAmount(address _user, bytes32 _ccy, uint256 _amount) public {
        Storage.slot().depositAmounts[_user][_ccy] += _amount;
        _updateUsedCurrencies(_user, _ccy);
    }

    function removeDepositAmount(address _user, bytes32 _ccy, uint256 _amount) public {
        if (Storage.slot().depositAmounts[_user][_ccy] < _amount) {
            revert NotEnoughDeposit({ccy: _ccy});
        }

        Storage.slot().depositAmounts[_user][_ccy] -= _amount;
        _updateUsedCurrencies(_user, _ccy);
    }

    function executeForcedReset(
        address _user,
        bytes32 _ccy
    ) external returns (uint256 removedAmount) {
        removedAmount = Storage.slot().depositAmounts[_user][_ccy];
        Storage.slot().depositAmounts[_user][_ccy] = 0;

        Storage.slot().usedCurrencies[_user].remove(_ccy);
    }

    function deposit(address _user, bytes32 _ccy, uint256 _amount) public {
        TransferHelper.depositAssets(
            Storage.slot().tokenAddresses[_ccy],
            _user,
            address(this),
            _amount
        );

        addDepositAmount(_user, _ccy, _amount);
        Storage.slot().totalDepositAmount[_ccy] += _amount;
    }

    function withdraw(
        address _user,
        bytes32 _ccy,
        uint256 _amount
    ) public returns (uint256 withdrawableAmount) {
        withdrawableAmount = getWithdrawableCollateral(_ccy, _user);
        withdrawableAmount = _amount > withdrawableAmount ? withdrawableAmount : _amount;

        if (Storage.slot().totalDepositAmount[_ccy] < withdrawableAmount) {
            revert ProtocolIsInsolvent({ccy: _ccy});
        }

        Storage.slot().totalDepositAmount[_ccy] -= withdrawableAmount;
        removeDepositAmount(_user, _ccy, withdrawableAmount);

        TransferHelper.withdrawAssets(
            Storage.slot().tokenAddresses[_ccy],
            _user,
            withdrawableAmount
        );

        return withdrawableAmount;
    }

    function getLiquidationAmount(
        address _user,
        bytes32 _liquidationCcy,
        uint256 _liquidationAmountMaximum
    ) public view returns (uint256 liquidationAmount, uint256 protocolFee, uint256 liquidatorFee) {
        (
            uint256 totalCollateralInBaseCcy,
            uint256 totalUsedCollateralInBaseCcy,

        ) = getTotalCollateralAmount(_user);

        (uint256 collateralAmount, , ) = getCollateralAmount(_user, _liquidationCcy);

        if (collateralAmount == 0) revert CollateralIsZero({ccy: _liquidationCcy});

        uint256 liquidationAmountInBaseCcy = totalCollateralInBaseCcy * Constants.PCT_DIGIT >=
            totalUsedCollateralInBaseCcy * Storage.slot().liquidationThresholdRate
            ? 0
            : totalUsedCollateralInBaseCcy.div(2);

        uint256[] memory amountsInBaseCcy = new uint256[](2);
        amountsInBaseCcy[0] = liquidationAmountInBaseCcy;
        amountsInBaseCcy[1] = totalCollateralInBaseCcy;

        uint256[] memory amounts = AddressResolverLib.currencyController().convertFromBaseCurrency(
            _liquidationCcy,
            amountsInBaseCcy
        );

        liquidationAmount = amounts[0];
        uint256 totalCollateralAmount = amounts[1];

        if (liquidationAmount > _liquidationAmountMaximum) {
            liquidationAmount = _liquidationAmountMaximum;
        }

        (protocolFee, liquidatorFee) = calculateLiquidationFees(liquidationAmount);

        uint256 liquidationTotalAmount = liquidationAmount + protocolFee + liquidatorFee;

        // NOTE: If `totalCollateralAmount > collateralAmount` is true, it means that a user has collateral in other currencies
        // In this case, this liquidation is not covered by the reserve fund.
        // Therefore, we need to keep the total liquidation amount within the maximum amount.
        if (liquidationTotalAmount > collateralAmount && totalCollateralAmount > collateralAmount) {
            liquidationTotalAmount = collateralAmount;
            uint256 liquidatorFeeRate = Storage.slot().liquidatorFeeRate;
            uint256 liquidationProtocolFeeRate = Storage.slot().liquidationProtocolFeeRate;

            protocolFee = (liquidationTotalAmount * liquidationProtocolFeeRate).div(
                Constants.PCT_DIGIT + liquidatorFeeRate + liquidationProtocolFeeRate
            );
            liquidatorFee = (liquidationTotalAmount * liquidatorFeeRate).div(
                Constants.PCT_DIGIT + liquidatorFeeRate + liquidationProtocolFeeRate
            );
            liquidationAmount = liquidationTotalAmount - protocolFee - liquidatorFee;
        }
    }

    function calculateLiquidationFees(
        uint256 _amount
    ) public view returns (uint256 protocolFee, uint256 liquidatorFee) {
        protocolFee = (_amount * Storage.slot().liquidationProtocolFeeRate).div(
            Constants.PCT_DIGIT
        );
        liquidatorFee = (_amount * Storage.slot().liquidatorFeeRate).div(Constants.PCT_DIGIT);
    }

    function transferFrom(
        bytes32 _ccy,
        address _from,
        address _to,
        uint256 _amount
    ) external returns (uint256 untransferredAmount) {
        uint256 depositAmount = Storage.slot().depositAmounts[_from][_ccy];
        uint256 amount = depositAmount >= _amount ? _amount : depositAmount;
        untransferredAmount = _amount - amount;

        removeDepositAmount(_from, _ccy, amount);
        addDepositAmount(_to, _ccy, amount);
    }

    /**
     * @notice Gets the total of amount deposited in the user's collateral of all currencies
     *  in this contract by converting it to ETH.
     * @param _user Address of collateral user
     * @return totalDepositAmount The total deposited amount in the base currency
     */
    function _getTotalInternalDepositAmountInBaseCurrency(
        address _user
    ) internal view returns (uint256 totalDepositAmount) {
        EnumerableSet.Bytes32Set storage currencies = Storage.slot().usedCurrencies[_user];
        uint256 length = currencies.length();

        for (uint256 i; i < length; i++) {
            bytes32 ccy = currencies.at(i);
            if (Storage.slot().collateralCurrencies.contains(ccy)) {
                uint256 depositAmount = Storage.slot().depositAmounts[_user][ccy];
                totalDepositAmount += AddressResolverLib.currencyController().convertToBaseCurrency(
                    ccy,
                    depositAmount
                );
            }
        }

        return totalDepositAmount;
    }

    function _updateUsedCurrencies(address _user, bytes32 _ccy) internal {
        if (Storage.slot().depositAmounts[_user][_ccy] > 0) {
            Storage.slot().usedCurrencies[_user].add(_ccy);
        } else {
            Storage.slot().usedCurrencies[_user].remove(_ccy);
        }
    }
}

contracts/protocol/libraries/logics/Fund.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

// dependencies
import {EnumerableSet} from "../../../dependencies/openzeppelin/utils/structs/EnumerableSet.sol";
import {SafeCast} from "../../../dependencies/openzeppelin/utils/math/SafeCast.sol";
// interfaces
import {ILendingMarket} from "../../interfaces/ILendingMarket.sol";
import {ILendingMarketController} from "../../interfaces/ILendingMarketController.sol";
import {IFutureValueVault} from "../../interfaces/IFutureValueVault.sol";
import {ILiquidationReceiver} from "../../interfaces/ILiquidationReceiver.sol";

// libraries
import {AddressResolverLib} from "../AddressResolverLib.sol";
import {QuickSort} from "../QuickSort.sol";
import {Constants} from "../Constants.sol";
import {RoundingUint256} from "../math/RoundingUint256.sol";
import {RoundingInt256} from "../math/RoundingInt256.sol";
// types
import {ProtocolTypes} from "../../types/ProtocolTypes.sol";
// storages
import {LendingMarketControllerStorage as Storage} from "../../storages/LendingMarketControllerStorage.sol";

library FundManagementLogic {
    using EnumerableSet for EnumerableSet.Bytes32Set;
    using EnumerableSet for EnumerableSet.UintSet;
    using SafeCast for uint256;
    using SafeCast for int256;
    using RoundingUint256 for uint256;
    using RoundingInt256 for int256;

    uint256 public constant BASE_MIN_DEBT_UNIT_PRICE = 9600;

    error NotRedemptionPeriod();
    error NotRepaymentPeriod();
    error NoRedemptionAmount();
    error NoRepaymentAmount();
    error AlreadyRedeemed();
    error InsufficientCollateral();

    struct CalculatedTotalFundInBaseCurrencyVars {
        address user;
        ILendingMarketController.AdditionalFunds additionalFunds;
        uint256 liquidationThresholdRate;
        bool[] isCollateral;
        bytes32[] ccys;
    }

    struct ActualFunds {
        int256 presentValue;
        uint256 claimableAmount;
        uint256 debtAmount;
        int256 futureValue;
        uint256 workingLendOrdersAmount;
        uint256 lentAmount;
        uint256 workingBorrowOrdersAmount;
        uint256 borrowedAmount;
        int256 genesisValue;
    }

    struct CalculateActualFundsVars {
        bool isTotal;
        bool isDefaultMarket;
        uint8 orderBookId;
        uint8 defaultOrderBookId;
        uint256 defaultOrderBookMarketUnitPrice;
        uint256[] maturities;
        int256 presentValueOfDefaultMarket;
        ILendingMarket market;
        IFutureValueVault futureValueVault;
        uint256 minDebtUnitPrice;
        uint256 defaultOrderBookMinDebtUnitPrice;
    }

    struct FutureValueVaultFunds {
        int256 genesisValue;
        int256 presentValue;
        int256 futureValue;
    }

    struct InactiveBorrowOrdersFunds {
        int256 genesisValue;
        int256 presentValue;
        int256 futureValue;
        uint256 workingOrdersAmount;
        uint256 borrowedAmount;
    }

    struct InactiveLendOrdersFunds {
        int256 genesisValue;
        int256 presentValue;
        int256 futureValue;
        uint256 workingOrdersAmount;
        uint256 lentAmount;
    }

    event OrderFilled(
        address indexed taker,
        bytes32 indexed ccy,
        ProtocolTypes.Side side,
        uint256 indexed maturity,
        uint256 amount,
        uint256 amountInFV,
        uint256 feeInFV
    );

    event OrdersFilledInAsync(
        address indexed taker,
        bytes32 indexed ccy,
        ProtocolTypes.Side side,
        uint256 indexed maturity,
        uint256 amount,
        uint256 amountInFV
    );

    event OrderPartiallyFilled(
        uint48 orderId,
        address indexed maker,
        bytes32 indexed ccy,
        ProtocolTypes.Side side,
        uint256 maturity,
        uint256 amount,
        uint256 amountInFV
    );

    event RedemptionExecuted(
        address indexed user,
        bytes32 indexed ccy,
        uint256 indexed maturity,
        uint256 amount
    );

    event RepaymentExecuted(
        address indexed user,
        bytes32 indexed ccy,
        uint256 indexed maturity,
        uint256 amount
    );

    event EmergencySettlementExecuted(address indexed user, uint256 amount);

    /**
     * @notice Converts the future value to the genesis value if there is balance in the past maturity.
     * @param _ccy Currency for pausing all lending markets
     * @param _user User's address
     * @return Current future value amount after update
     */
    function convertFutureValueToGenesisValue(
        bytes32 _ccy,
        uint8 _orderBookId,
        uint256 _maturity,
        address _user
    ) public returns (int256) {
        address futureValueVault = Storage.slot().futureValueVaults[_ccy];
        (
            int256 removedAmount,
            int256 currentAmount,
            uint256 basisMaturity,
            bool isAllRemoved
        ) = IFutureValueVault(futureValueVault).reset(_orderBookId, _user, _maturity);

        if (removedAmount != 0) {
            // Overwrite the `removedAmount` with the unsettled amount left of the Genesis Value
            // to handle the fractional amount generated by the lazy evaluation.
            if (isAllRemoved) {
                AddressResolverLib.genesisValueVault().updateGenesisValueWithResidualAmount(
                    _ccy,
                    _user,
                    basisMaturity
                );
            } else {
                AddressResolverLib.genesisValueVault().updateGenesisValueWithFutureValue(
                    _ccy,
                    _user,
                    basisMaturity,
                    removedAmount
                );
            }
        }

        return currentAmount;
    }

    function updateFunds(
        bytes32 _ccy,
        uint256 _maturity,
        address _user,
        ProtocolTypes.Side _side,
        uint256 _filledAmount,
        uint256 _filledAmountInFV,
        uint256 _feeInFV,
        bool _isTaker
    ) external {
        address futureValueVault = Storage.slot().futureValueVaults[_ccy];
        uint8 orderBookId = Storage.slot().maturityOrderBookIds[_ccy][_maturity];

        if (_side == ProtocolTypes.Side.BORROW) {
            AddressResolverLib.tokenVault().addDepositAmount(_user, _ccy, _filledAmount);
            IFutureValueVault(futureValueVault).decrease(
                orderBookId,
                _user,
                _filledAmountInFV + _feeInFV,
                _maturity,
                _isTaker
            );
        } else {
            AddressResolverLib.tokenVault().removeDepositAmount(_user, _ccy, _filledAmount);
            IFutureValueVault(futureValueVault).increase(
                orderBookId,
                _user,
                _filledAmountInFV - _feeInFV,
                _maturity,
                _isTaker
            );
        }

        if (_feeInFV > 0) {
            address reserveFundAddr = address(AddressResolverLib.reserveFund());
            IFutureValueVault(futureValueVault).increase(
                orderBookId,
                reserveFundAddr,
                _feeInFV,
                _maturity,
                _side == ProtocolTypes.Side.LEND
            );

            registerCurrencyAndMaturity(_ccy, _maturity, reserveFundAddr);
        }
    }

    function registerCurrencyAndMaturity(bytes32 _ccy, uint256 _maturity, address _user) public {
        if (!Storage.slot().usedMaturities[_ccy][_user].contains(_maturity)) {
            Storage.slot().usedMaturities[_ccy][_user].add(_maturity);

            registerCurrency(_ccy, _user);
        }
    }

    function registerCurrency(bytes32 _ccy, address _user) public {
        if (!Storage.slot().usedCurrencies[_user].contains(_ccy)) {
            Storage.slot().usedCurrencies[_user].add(_ccy);
        }
    }

    function executeRedemption(bytes32 _ccy, uint256 _maturity, address _user) external {
        if (
            AddressResolverLib.currencyController().currencyExists(_ccy) ||
            block.timestamp < _maturity + 1 weeks
        ) revert NotRedemptionPeriod();

        cleanUpFunds(_ccy, _user);

        int256 amount = getActualFunds(_ccy, _maturity, _user, 0).futureValue;
        if (amount <= 0) revert NoRedemptionAmount();

        uint256 redemptionAmount = _resetFundsPerMaturity(_ccy, _maturity, _user, amount)
            .toUint256();
        AddressResolverLib.tokenVault().addDepositAmount(_user, _ccy, redemptionAmount);

        emit RedemptionExecuted(_user, _ccy, _maturity, redemptionAmount);
    }

    function executeRepayment(
        bytes32 _ccy,
        uint256 _maturity,
        address _user,
        uint256 _amount
    ) public returns (uint256 repaymentAmount) {
        if (
            AddressResolverLib.currencyController().currencyExists(_ccy) ||
            block.timestamp < _maturity
        ) revert NotRepaymentPeriod();

        cleanUpFunds(_ccy, _user);

        int256 resetAmount = _amount == 0
            ? getActualFunds(_ccy, _maturity, _user, 0).futureValue
            : -_amount.toInt256();

        if (resetAmount >= 0) revert NoRepaymentAmount();

        repaymentAmount = (-_resetFundsPerMaturity(_ccy, _maturity, _user, resetAmount))
            .toUint256();
        AddressResolverLib.tokenVault().removeDepositAmount(_user, _ccy, repaymentAmount);

        emit RepaymentExecuted(_user, _ccy, _maturity, repaymentAmount);
    }

    function executeEmergencySettlement(address _user) external {
        if (Storage.slot().isRedeemed[_user]) revert AlreadyRedeemed();

        int256 redemptionAmountInBaseCurrency;

        bytes32[] memory currencies = Storage.slot().usedCurrencies[_user].values();

        for (uint256 i; i < currencies.length; i++) {
            bytes32 ccy = currencies[i];
            // First, clean up future values and genesis values to redeem those amounts.
            cleanUpFunds(ccy, _user);

            int256 amountInCcy = _resetFundsPerCurrency(ccy, _user);
            redemptionAmountInBaseCurrency += _convertToBaseCurrencyAtMarketTerminationPrice(
                ccy,
                amountInCcy
            );
        }

        bytes32[] memory collateralCurrencies = AddressResolverLib
            .tokenVault()
            .getCollateralCurrencies();

        for (uint256 i; i < collateralCurrencies.length; i++) {
            int256 amountInCcy = AddressResolverLib
                .tokenVault()
                .executeForcedReset(_user, collateralCurrencies[i])
                .toInt256();

            redemptionAmountInBaseCurrency += _convertToBaseCurrencyAtMarketTerminationPrice(
                collateralCurrencies[i],
                amountInCcy
            );
        }

        if (redemptionAmountInBaseCurrency > 0) {
            uint256[] memory marketTerminationRatios = new uint256[](collateralCurrencies.length);
            uint256 marketTerminationRatioTotal;

            for (uint256 i; i < collateralCurrencies.length; i++) {
                bytes32 ccy = collateralCurrencies[i];
                marketTerminationRatios[i] = Storage.slot().marketTerminationRatios[ccy];
                marketTerminationRatioTotal += marketTerminationRatios[i];
            }

            for (uint256 i; i < collateralCurrencies.length; i++) {
                bytes32 ccy = collateralCurrencies[i];
                uint256 addedAmount = _convertFromBaseCurrencyAtMarketTerminationPrice(
                    ccy,
                    (redemptionAmountInBaseCurrency.toUint256() * marketTerminationRatios[i]).div(
                        marketTerminationRatioTotal
                    )
                );

                AddressResolverLib.tokenVault().addDepositAmount(_user, ccy, addedAmount);
            }
        } else if (redemptionAmountInBaseCurrency < 0) {
            revert InsufficientCollateral();
        }

        Storage.slot().isRedeemed[_user] = true;
        emit EmergencySettlementExecuted(_user, redemptionAmountInBaseCurrency.toUint256());
    }

    function getActualFunds(
        bytes32 _ccy,
        uint256 _maturity,
        address _user,
        uint256 _minDebtUnitPrice
    ) public view returns (ActualFunds memory actualFunds) {
        CalculateActualFundsVars memory vars;
        vars.market = ILendingMarket(Storage.slot().lendingMarkets[_ccy]);
        vars.futureValueVault = IFutureValueVault(Storage.slot().futureValueVaults[_ccy]);
        vars.defaultOrderBookId = Storage.slot().orderBookIdLists[_ccy][0];
        vars.minDebtUnitPrice = _minDebtUnitPrice;

        if (_maturity == 0) {
            vars.isTotal = true;
            vars.orderBookId = vars.defaultOrderBookId;
            vars.isDefaultMarket = true;
        } else {
            vars.isTotal = false;
            vars.orderBookId = Storage.slot().maturityOrderBookIds[_ccy][_maturity];
            vars.isDefaultMarket = vars.orderBookId == vars.defaultOrderBookId;
        }
        actualFunds.genesisValue = AddressResolverLib.genesisValueVault().getBalance(_ccy, _user);

        vars.maturities = getUsedMaturities(_ccy, _user);

        for (uint256 i; i < vars.maturities.length; i++) {
            uint8 currentOrderBookId = Storage.slot().maturityOrderBookIds[_ccy][
                vars.maturities[i]
            ];

            if (vars.isDefaultMarket || currentOrderBookId == vars.orderBookId) {
                {
                    uint256 currentMaturity = vars.market.getMaturity(currentOrderBookId);
                    bool isDefaultMarket = currentOrderBookId == vars.defaultOrderBookId;

                    // Get current funds from Future Value Vault by lazy evaluations.
                    FutureValueVaultFunds
                        memory futureValueVaultFunds = _getFundsFromFutureValueVault(
                            _ccy,
                            _user,
                            vars,
                            currentOrderBookId,
                            currentMaturity,
                            isDefaultMarket
                        );
                    // Get current funds from borrowing orders by lazy evaluations.
                    InactiveBorrowOrdersFunds
                        memory borrowOrdersFunds = _getFundsFromInactiveBorrowOrders(
                            _ccy,
                            _user,
                            vars,
                            currentOrderBookId,
                            currentMaturity,
                            isDefaultMarket
                        );
                    // Get current funds from lending orders by lazy evaluations.
                    InactiveLendOrdersFunds
                        memory lendOrdersFunds = _getFundsFromInactiveLendOrders(
                            _ccy,
                            _user,
                            vars,
                            currentOrderBookId,
                            currentMaturity,
                            isDefaultMarket
                        );

                    // Set genesis value.
                    actualFunds.genesisValue +=
                        futureValueVaultFunds.genesisValue -
                        borrowOrdersFunds.genesisValue +
                        lendOrdersFunds.genesisValue;

                    // Set present value.
                    int256 presentValue = futureValueVaultFunds.presentValue -
                        borrowOrdersFunds.presentValue +
                        lendOrdersFunds.presentValue;
                    int256 futureValue = futureValueVaultFunds.futureValue -
                        borrowOrdersFunds.futureValue +
                        lendOrdersFunds.futureValue;

                    actualFunds.presentValue += presentValue;

                    if (isDefaultMarket) {
                        vars.presentValueOfDefaultMarket = presentValue;
                    }

                    if (presentValue > 0) {
                        actualFunds.claimableAmount += presentValue.toUint256();
                    } else if (presentValue < 0) {
                        actualFunds.debtAmount += (-presentValue).toUint256();
                    }

                    // Set future value.
                    // Note: When calculating total funds, total future value will be 0 because different maturities can not be added.
                    if (!vars.isTotal) {
                        actualFunds.futureValue += futureValue;
                    }

                    actualFunds.workingBorrowOrdersAmount += borrowOrdersFunds.workingOrdersAmount;
                    actualFunds.workingLendOrdersAmount += lendOrdersFunds.workingOrdersAmount;
                    actualFunds.borrowedAmount += borrowOrdersFunds.borrowedAmount;
                    actualFunds.lentAmount += lendOrdersFunds.lentAmount;
                }

                // Get balance fluctuation amount by auto-rolls
                if (actualFunds.genesisValue < 0) {
                    actualFunds.genesisValue += AddressResolverLib
                        .genesisValueVault()
                        .calculateBalanceFluctuationByAutoRolls(
                            _ccy,
                            actualFunds.genesisValue,
                            vars.maturities[i],
                            i == vars.maturities.length - 1 ? 0 : vars.maturities[i + 1]
                        );
                }
            }
        }

        // Add GV to PV & FV if the market is that the lending position is rolled to.
        if (vars.isDefaultMarket && actualFunds.genesisValue != 0) {
            int256 futureValue = AddressResolverLib.genesisValueVault().calculateFVFromGV(
                _ccy,
                0,
                actualFunds.genesisValue
            );

            uint256 unitPrice = _getDefaultOrderBookMarketUnitPrice(vars);
            uint256 defaultOrderBookMinDebtUnitPrice = _getDefaultOrderBookMinDebtUnitPrice(vars);

            int256 presentValue = _calculatePVFromFV(
                futureValue,
                unitPrice >= defaultOrderBookMinDebtUnitPrice
                    ? unitPrice
                    : defaultOrderBookMinDebtUnitPrice
            );

            actualFunds.presentValue += presentValue;

            // Add GV to the claimable amount or debt amount.
            // Before that, offset the present value of the default market and the genesis value in addition.
            if (presentValue > 0) {
                if (vars.presentValueOfDefaultMarket < 0) {
                    int256 offsetAmount = presentValue > -vars.presentValueOfDefaultMarket
                        ? -vars.presentValueOfDefaultMarket
                        : presentValue;
                    actualFunds.debtAmount -= (offsetAmount).toUint256();
                    presentValue -= offsetAmount;
                }

                actualFunds.claimableAmount += presentValue.toUint256();
            } else if (presentValue < 0) {
                if (vars.presentValueOfDefaultMarket > 0) {
                    int256 offsetAmount = -presentValue > vars.presentValueOfDefaultMarket
                        ? vars.presentValueOfDefaultMarket
                        : -presentValue;

                    actualFunds.claimableAmount -= (offsetAmount).toUint256();
                    presentValue += offsetAmount;
                }

                actualFunds.debtAmount += (-presentValue).toUint256();
            }

            if (!vars.isTotal) {
                actualFunds.futureValue += futureValue;
            }
        }
    }

    function getCurrentMinDebtUnitPrice(
        uint256 _maturity,
        uint256 _minDebtUnitPrice
    ) public view returns (uint256) {
        if (_minDebtUnitPrice == 0) return 0;

        return
            _maturity > block.timestamp
                ? BASE_MIN_DEBT_UNIT_PRICE -
                    ((BASE_MIN_DEBT_UNIT_PRICE - _minDebtUnitPrice) *
                        (_maturity - block.timestamp)) /
                    Constants.SECONDS_IN_YEAR
                : BASE_MIN_DEBT_UNIT_PRICE;
    }

    function calculateFunds(
        bytes32 _ccy,
        address _user,
        ILendingMarketController.AdditionalFunds memory _additionalFunds,
        uint256 _liquidationThresholdRate
    ) public view returns (ILendingMarketController.CalculatedFunds memory funds) {
        ActualFunds memory actualFunds = getActualFunds(
            _ccy,
            0,
            _user,
            Storage.slot().minDebtUnitPrices[_ccy]
        );

        funds.workingLendOrdersAmount =
            actualFunds.workingLendOrdersAmount +
            _additionalFunds.workingLendOrdersAmount;
        funds.claimableAmount = actualFunds.claimableAmount + _additionalFunds.claimableAmount;
        funds.lentAmount = actualFunds.lentAmount + _additionalFunds.lentAmount;
        funds.workingBorrowOrdersAmount =
            actualFunds.workingBorrowOrdersAmount +
            _additionalFunds.workingBorrowOrdersAmount;
        funds.debtAmount = actualFunds.debtAmount + _additionalFunds.debtAmount;
        funds.borrowedAmount = actualFunds.borrowedAmount + _additionalFunds.borrowedAmount;

        if (funds.claimableAmount > 0) {
            // If the debt and claimable amount are in the same currency, the claimable amount can be allocated
            // as collateral up to the amount that the liquidation threshold is reached.
            // For calculation purposes, the working amount for borrowing orders is treated as potential debt in addition.
            uint256 maxAllocableCollateralAmountInSameCcy = ((funds.debtAmount +
                funds.workingBorrowOrdersAmount) * _liquidationThresholdRate).div(
                    Constants.PCT_DIGIT
                );

            // If the claimable amount is over the allocable amount as collateral, the over amount is used as collateral
            // for the other currency after being multiplied by a haircut.
            if (funds.claimableAmount > maxAllocableCollateralAmountInSameCcy) {
                uint256 haircut = AddressResolverLib.currencyController().getHaircut(_ccy);
                funds.collateralAmount =
                    maxAllocableCollateralAmountInSameCcy +
                    (haircut * (funds.claimableAmount - maxAllocableCollateralAmountInSameCcy)).div(
                        Constants.PCT_DIGIT
                    );
            } else {
                funds.collateralAmount = funds.claimableAmount;
            }
        }
    }

    function calculateTotalFundsInBaseCurrency(
        address _user,
        ILendingMarketController.AdditionalFunds calldata _additionalFunds,
        uint256 _liquidationThresholdRate
    ) external view returns (ILendingMarketController.CalculatedTotalFunds memory totalFunds) {
        EnumerableSet.Bytes32Set storage currencySet = Storage.slot().usedCurrencies[_user];
        CalculatedTotalFundInBaseCurrencyVars memory vars;

        if (
            !currencySet.contains(_additionalFunds.ccy) &&
            AddressResolverLib.currencyController().currencyExists(_additionalFunds.ccy)
        ) {
            uint256 length = currencySet.length();
            vars.ccys = new bytes32[](length + 1);
            for (uint256 i; i < length; i++) {
                vars.ccys[i] = currencySet.at(i);
            }
            vars.ccys[length] = _additionalFunds.ccy;
        } else {
            vars.ccys = currencySet.values();
        }

        vars.user = _user;
        vars.additionalFunds = _additionalFunds;
        vars.liquidationThresholdRate = _liquidationThresholdRate;
        vars.isCollateral = AddressResolverLib.tokenVault().isCollateral(vars.ccys);

        // Calculate total funds from the user's order list
        for (uint256 i; i < vars.ccys.length; i++) {
            // bytes32 ccy = vars.ccys[i];
            ILendingMarketController.AdditionalFunds memory additionalFunds;

            if (vars.ccys[i] == vars.additionalFunds.ccy) {
                additionalFunds = vars.additionalFunds;
            }

            uint256[] memory amounts = new uint256[](8);

            // 0: workingLendOrdersAmount
            // 1: claimableAmount
            // 2: collateralAmount
            // 3: lentAmount
            // 4: workingBorrowOrdersAmount
            // 5: debtAmount
            // 6: borrowedAmount
            ILendingMarketController.CalculatedFunds memory funds = calculateFunds(
                vars.ccys[i],
                vars.user,
                additionalFunds,
                vars.liquidationThresholdRate
            );

            amounts[0] = funds.workingLendOrdersAmount;
            amounts[1] = funds.claimableAmount;
            amounts[2] = funds.collateralAmount;
            amounts[3] = funds.lentAmount;
            amounts[4] = funds.workingBorrowOrdersAmount;
            amounts[5] = funds.debtAmount;
            amounts[6] = funds.borrowedAmount;

            if (vars.ccys[i] == vars.additionalFunds.ccy) {
                // plusDepositAmount: borrowedAmount
                // minusDepositAmount: workingLendOrdersAmount + lentAmount
                totalFunds.plusDepositAmountInAdditionalFundsCcy += amounts[6];
                totalFunds.minusDepositAmountInAdditionalFundsCcy += amounts[0] + amounts[3];
            }

            uint256[] memory amountsInBaseCurrency = AddressResolverLib
                .currencyController()
                .convertToBaseCurrency(vars.ccys[i], amounts);

            totalFunds.claimableAmount += amountsInBaseCurrency[1];
            totalFunds.collateralAmount += amountsInBaseCurrency[2];
            totalFunds.workingBorrowOrdersAmount += amountsInBaseCurrency[4];
            totalFunds.debtAmount += amountsInBaseCurrency[5];

            // NOTE: Lent amount and working lend orders amount are excluded here as they are not used
            // for the collateral calculation.
            // Those amounts need only to check whether there is enough deposit amount in the selected currency.
            if (vars.isCollateral[i]) {
                totalFunds.workingLendOrdersAmount += amountsInBaseCurrency[0];
                totalFunds.lentAmount += amountsInBaseCurrency[3];
                totalFunds.borrowedAmount += amountsInBaseCurrency[6];
            }
        }
    }

    function getUsedMaturities(
        bytes32 _ccy,
        address _user
    ) public view returns (uint256[] memory maturities) {
        maturities = Storage.slot().usedMaturities[_ccy][_user].values();
        if (maturities.length > 0) {
            maturities = QuickSort.sort(maturities);
        }
    }

    function getPosition(
        bytes32 _ccy,
        uint256 _maturity,
        address _user
    ) public view returns (int256 presentValue, int256 futureValue) {
        FundManagementLogic.ActualFunds memory funds = getActualFunds(_ccy, _maturity, _user, 0);
        presentValue = funds.presentValue;
        futureValue = funds.futureValue;
    }

    function cleanUpAllFunds(address _user) external {
        EnumerableSet.Bytes32Set storage ccySet = Storage.slot().usedCurrencies[_user];
        uint256 length = ccySet.length();
        for (uint256 i; i < length; i++) {
            cleanUpFunds(ccySet.at(i), _user);
        }
    }

    function cleanUpFunds(
        bytes32 _ccy,
        address _user
    ) public returns (uint256 totalActiveOrderCount) {
        bool futureValueExists = false;
        uint256[] memory maturities = getUsedMaturities(_ccy, _user);
        ILendingMarket market = ILendingMarket(Storage.slot().lendingMarkets[_ccy]);

        for (uint256 i; i < maturities.length; i++) {
            uint8 orderBookId = Storage.slot().maturityOrderBookIds[_ccy][maturities[i]];
            uint256 activeMaturity = market.getMaturity(orderBookId);
            int256 currentFutureValue = convertFutureValueToGenesisValue(
                _ccy,
                orderBookId,
                activeMaturity,
                _user
            );
            (uint256 activeOrderCount, bool isCleaned) = _cleanUpOrders(
                _ccy,
                activeMaturity,
                _user
            );

            totalActiveOrderCount += activeOrderCount;

            if (isCleaned) {
                currentFutureValue = convertFutureValueToGenesisValue(
                    _ccy,
                    orderBookId,
                    activeMaturity,
                    _user
                );
            }

            if (currentFutureValue != 0) {
                futureValueExists = true;
            }

            if (currentFutureValue == 0 && activeOrderCount == 0) {
                Storage.slot().usedMaturities[_ccy][_user].remove(maturities[i]);
            }

            AddressResolverLib.genesisValueVault().cleanUpBalance(
                _ccy,
                _user,
                i == maturities.length - 1 ? 0 : maturities[i + 1]
            );
        }

        if (
            totalActiveOrderCount == 0 &&
            !futureValueExists &&
            AddressResolverLib.genesisValueVault().getBalance(_ccy, _user) == 0
        ) {
            Storage.slot().usedCurrencies[_user].remove(_ccy);
        }
    }

    function _cleanUpOrders(
        bytes32 _ccy,
        uint256 _maturity,
        address _user
    ) internal returns (uint256 activeOrderCount, bool isCleaned) {
        uint8 orderBookId = Storage.slot().maturityOrderBookIds[_ccy][_maturity];

        (
            uint256 activeLendOrderCount,
            uint256 activeBorrowOrderCount,
            uint256 removedLendOrderFutureValue,
            uint256 removedBorrowOrderFutureValue,
            uint256 removedLendOrderAmount,
            uint256 removedBorrowOrderAmount,
            uint256 userCurrentMaturity
        ) = ILendingMarket(Storage.slot().lendingMarkets[_ccy]).cleanUpOrders(orderBookId, _user);

        if (removedLendOrderAmount > removedBorrowOrderAmount) {
            AddressResolverLib.tokenVault().removeDepositAmount(
                _user,
                _ccy,
                removedLendOrderAmount - removedBorrowOrderAmount
            );
        } else if (removedLendOrderAmount < removedBorrowOrderAmount) {
            AddressResolverLib.tokenVault().addDepositAmount(
                _user,
                _ccy,
                removedBorrowOrderAmount - removedLendOrderAmount
            );
        }

        if (removedLendOrderFutureValue > 0) {
            IFutureValueVault(Storage.slot().futureValueVaults[_ccy]).increase(
                orderBookId,
                _user,
                removedLendOrderFutureValue,
                userCurrentMaturity,
                false
            );
            emit OrdersFilledInAsync(
                _user,
                _ccy,
                ProtocolTypes.Side.LEND,
                userCurrentMaturity,
                removedLendOrderAmount,
                removedLendOrderFutureValue
            );
        }

        if (removedBorrowOrderFutureValue > 0) {
            IFutureValueVault(Storage.slot().futureValueVaults[_ccy]).decrease(
                orderBookId,
                _user,
                removedBorrowOrderFutureValue,
                userCurrentMaturity,
                false
            );
            emit OrdersFilledInAsync(
                _user,
                _ccy,
                ProtocolTypes.Side.BORROW,
                userCurrentMaturity,
                removedBorrowOrderAmount,
                removedBorrowOrderFutureValue
            );
        }

        isCleaned = (removedLendOrderFutureValue + removedBorrowOrderFutureValue) > 0;
        activeOrderCount = activeLendOrderCount + activeBorrowOrderCount;
    }

    function _getFundsFromFutureValueVault(
        bytes32 _ccy,
        address _user,
        CalculateActualFundsVars memory vars,
        uint8 currentOrderBookId,
        uint256 currentMaturity,
        bool isDefaultMarket
    ) internal view returns (FutureValueVaultFunds memory funds) {
        (int256 futureValueInMaturity, uint256 fvMaturity) = vars.futureValueVault.getBalance(
            currentOrderBookId,
            _user
        );

        if (futureValueInMaturity != 0) {
            if (currentMaturity != fvMaturity) {
                if (vars.isDefaultMarket) {
                    funds.genesisValue = AddressResolverLib.genesisValueVault().calculateGVFromFV(
                        _ccy,
                        fvMaturity,
                        futureValueInMaturity
                    );
                }
            } else if (currentMaturity == fvMaturity) {
                if (vars.isTotal && !isDefaultMarket) {
                    uint256 unitPrice = _getDefaultOrderBookMarketUnitPrice(vars);

                    (funds.presentValue, funds.futureValue) = _convertFVtoOtherMaturity(
                        _ccy,
                        vars.market,
                        fvMaturity,
                        futureValueInMaturity,
                        unitPrice
                    );

                    if (funds.futureValue < 0) {
                        uint256 defaultOrderBookMinDebtUnitPrice = _getDefaultOrderBookMinDebtUnitPrice(
                                vars
                            );

                        if (unitPrice < defaultOrderBookMinDebtUnitPrice) {
                            funds.presentValue = _calculatePVFromFV(
                                funds.futureValue,
                                defaultOrderBookMinDebtUnitPrice
                            );
                        }
                    }
                } else if (vars.isTotal || !vars.isDefaultMarket || isDefaultMarket) {
                    uint256 unitPrice = vars.market.getMarketUnitPrice(vars.orderBookId);
                    funds.futureValue = futureValueInMaturity;

                    // Apply min debt unit price if the future value is negative (debt).
                    if (funds.futureValue < 0) {
                        uint256 currentMinDebtUnitPrice = getCurrentMinDebtUnitPrice(
                            currentMaturity,
                            vars.minDebtUnitPrice
                        );

                        funds.presentValue = _calculatePVFromFV(
                            futureValueInMaturity,
                            unitPrice < currentMinDebtUnitPrice
                                ? currentMinDebtUnitPrice
                                : unitPrice
                        );
                    } else {
                        funds.presentValue = _calculatePVFromFV(futureValueInMaturity, unitPrice);
                    }
                }
            }
        }
    }

    function _getFundsFromInactiveBorrowOrders(
        bytes32 _ccy,
        address _user,
        CalculateActualFundsVars memory vars,
        uint8 currentOrderBookId,
        uint256 currentMaturity,
        bool isDefaultMarket
    ) internal view returns (InactiveBorrowOrdersFunds memory funds) {
        uint256 filledFutureValue;
        uint256 orderMaturity;
        uint256 currentMinDebtUnitPrice = getCurrentMinDebtUnitPrice(
            currentMaturity,
            vars.minDebtUnitPrice
        );

        (funds.workingOrdersAmount, funds.borrowedAmount, filledFutureValue, orderMaturity) = vars
            .market
            .getTotalAmountFromBorrowOrders(currentOrderBookId, _user, currentMinDebtUnitPrice);

        if (filledFutureValue != 0) {
            if (currentMaturity != orderMaturity) {
                if (vars.isDefaultMarket) {
                    funds.genesisValue = AddressResolverLib.genesisValueVault().calculateGVFromFV(
                        _ccy,
                        orderMaturity,
                        filledFutureValue.toInt256()
                    );
                }
            } else if (currentMaturity == orderMaturity) {
                if (vars.isTotal && !isDefaultMarket) {
                    uint256 unitPrice = _getDefaultOrderBookMarketUnitPrice(vars);

                    (funds.presentValue, funds.futureValue) = _convertFVtoOtherMaturity(
                        _ccy,
                        vars.market,
                        orderMaturity,
                        filledFutureValue.toInt256(),
                        unitPrice
                    );

                    uint256 defaultOrderBookMinDebtUnitPrice = _getDefaultOrderBookMinDebtUnitPrice(
                        vars
                    );

                    if (unitPrice < defaultOrderBookMinDebtUnitPrice) {
                        funds.presentValue = _calculatePVFromFV(
                            funds.futureValue,
                            defaultOrderBookMinDebtUnitPrice
                        );
                    }
                } else if (vars.isTotal || !vars.isDefaultMarket || isDefaultMarket) {
                    uint256 unitPrice = vars.market.getMarketUnitPrice(vars.orderBookId);

                    funds.futureValue = filledFutureValue.toInt256();
                    funds.presentValue = _calculatePVFromFV(
                        funds.futureValue,
                        unitPrice < currentMinDebtUnitPrice ? currentMinDebtUnitPrice : unitPrice
                    );
                }
            }
        }
    }

    function _getFundsFromInactiveLendOrders(
        bytes32 _ccy,
        address _user,
        CalculateActualFundsVars memory vars,
        uint8 currentOrderBookId,
        uint256 currentMaturity,
        bool isDefaultMarket
    ) internal view returns (InactiveLendOrdersFunds memory funds) {
        uint256 filledFutureValue;
        uint256 orderMaturity;
        (funds.workingOrdersAmount, funds.lentAmount, filledFutureValue, orderMaturity) = vars
            .market
            .getTotalAmountFromLendOrders(currentOrderBookId, _user);

        if (filledFutureValue != 0) {
            if (currentMaturity != orderMaturity) {
                if (vars.isDefaultMarket) {
                    funds.genesisValue = AddressResolverLib.genesisValueVault().calculateGVFromFV(
                        _ccy,
                        orderMaturity,
                        filledFutureValue.toInt256()
                    );
                }
            } else if (currentMaturity == orderMaturity) {
                if (vars.isTotal && !isDefaultMarket) {
                    (funds.presentValue, funds.futureValue) = _convertFVtoOtherMaturity(
                        _ccy,
                        vars.market,
                        orderMaturity,
                        filledFutureValue.toInt256(),
                        vars.market.getMarketUnitPrice(vars.defaultOrderBookId)
                    );
                } else if (vars.isTotal || !vars.isDefaultMarket || isDefaultMarket) {
                    funds.futureValue = filledFutureValue.toInt256();
                    funds.presentValue = _calculatePVFromFV(
                        vars.market,
                        vars.orderBookId,
                        funds.futureValue
                    );
                }
            }
        }
    }

    function _convertFVtoOtherMaturity(
        bytes32 _ccy,
        ILendingMarket _market,
        uint256 _fromMaturity,
        int256 _fromFutureValue,
        uint256 _toUnitPrice
    ) internal view returns (int256 presentValue, int256 futureValue) {
        // uint256 unitPrice = _market.getMarketUnitPrice(Storage.slot().orderBookIdLists[_ccy][0]);

        if (
            AddressResolverLib.genesisValueVault().getAutoRollLog(_ccy, _fromMaturity).unitPrice ==
            0
        ) {
            presentValue = _calculatePVFromFV(
                _market,
                Storage.slot().maturityOrderBookIds[_ccy][_fromMaturity],
                _fromFutureValue
            );
            futureValue = _calculateFVFromPV(presentValue, _toUnitPrice);
        } else {
            futureValue = AddressResolverLib.genesisValueVault().calculateFVFromFV(
                _ccy,
                _fromMaturity,
                0,
                _fromFutureValue
            );
            presentValue = _calculatePVFromFV(futureValue, _toUnitPrice);
        }
    }

    function calculatePVFromFV(
        bytes32 _ccy,
        uint256 _maturity,
        int256 _futureValue
    ) public view returns (int256 presentValue) {
        presentValue = _calculatePVFromFV(
            ILendingMarket(Storage.slot().lendingMarkets[_ccy]),
            Storage.slot().maturityOrderBookIds[_ccy][_maturity],
            _futureValue
        );
    }

    function calculateFVFromPV(
        bytes32 _ccy,
        uint256 _maturity,
        int256 _presentValue
    ) public view returns (int256 futureValue) {
        uint256 unitPrice = ILendingMarket(Storage.slot().lendingMarkets[_ccy]).getMarketUnitPrice(
            Storage.slot().maturityOrderBookIds[_ccy][_maturity]
        );
        futureValue = _calculateFVFromPV(_presentValue, unitPrice);
    }

    function _convertToBaseCurrencyAtMarketTerminationPrice(
        bytes32 _ccy,
        int256 _amount
    ) internal view returns (int256) {
        uint8 decimals = AddressResolverLib.currencyController().getDecimals(_ccy);
        return
            (_amount * Storage.slot().marketTerminationPrices[_ccy]).div(
                (10 ** decimals).toInt256()
            );
    }

    function _convertFromBaseCurrencyAtMarketTerminationPrice(
        bytes32 _ccy,
        uint256 _amount
    ) internal view returns (uint256) {
        uint8 decimals = AddressResolverLib.currencyController().getDecimals(_ccy);
        return
            (_amount * 10 ** decimals).div(
                Storage.slot().marketTerminationPrices[_ccy].toUint256()
            );
    }

    function _resetFundsPerCurrency(bytes32 _ccy, address _user) internal returns (int256 amount) {
        amount = getActualFunds(_ccy, 0, _user, 0).presentValue;

        uint256[] memory maturities = Storage.slot().usedMaturities[_ccy][_user].values();
        for (uint256 j; j < maturities.length; j++) {
            IFutureValueVault(Storage.slot().futureValueVaults[_ccy]).executeForcedReset(
                Storage.slot().maturityOrderBookIds[_ccy][maturities[j]],
                _user
            );
        }

        AddressResolverLib.genesisValueVault().executeForcedReset(_ccy, _user);

        Storage.slot().usedCurrencies[_user].remove(_ccy);
    }

    function _resetFundsPerMaturity(
        bytes32 _ccy,
        uint256 _maturity,
        address _user,
        int256 _amount
    ) internal returns (int256 totalRemovedAmount) {
        int256 currentFVAmount;
        int256 currentGVAmount;

        (totalRemovedAmount, currentFVAmount) = IFutureValueVault(
            Storage.slot().futureValueVaults[_ccy]
        ).executeForcedReset(Storage.slot().maturityOrderBookIds[_ccy][_maturity], _user, _amount);

        int256 remainingAmount = _amount - totalRemovedAmount;

        bool isDefaultMarket = Storage.slot().maturityOrderBookIds[_ccy][_maturity] ==
            Storage.slot().orderBookIdLists[_ccy][0];

        if (isDefaultMarket && remainingAmount != 0) {
            int256 removedAmount;
            (removedAmount, currentGVAmount) = AddressResolverLib
                .genesisValueVault()
                .executeForcedReset(_ccy, _maturity, _user, remainingAmount);
            totalRemovedAmount += removedAmount;
        }

        if (currentFVAmount == 0 && currentGVAmount == 0) {
            Storage.slot().usedMaturities[_ccy][_user].remove(_maturity);

            if (Storage.slot().usedMaturities[_ccy][_user].length() == 0) {
                Storage.slot().usedCurrencies[_user].remove(_ccy);
            }
        }
    }

    function _getDefaultOrderBookMinDebtUnitPrice(
        CalculateActualFundsVars memory vars
    ) private view returns (uint256) {
        if (vars.defaultOrderBookMinDebtUnitPrice == 0 && vars.minDebtUnitPrice != 0) {
            vars.defaultOrderBookMinDebtUnitPrice = getCurrentMinDebtUnitPrice(
                vars.market.getMaturity(vars.defaultOrderBookId),
                vars.minDebtUnitPrice
            );
        }

        return vars.defaultOrderBookMinDebtUnitPrice;
    }

    function _getDefaultOrderBookMarketUnitPrice(
        CalculateActualFundsVars memory vars
    ) private view returns (uint256) {
        if (vars.defaultOrderBookMarketUnitPrice == 0) {
            vars.defaultOrderBookMarketUnitPrice = vars.market.getMarketUnitPrice(
                vars.defaultOrderBookId
            );
        }

        return vars.defaultOrderBookMarketUnitPrice;
    }

    function _calculatePVFromFV(
        ILendingMarket _market,
        uint8 _orderBookId,
        int256 _futureValue
    ) internal view returns (int256 presentValue) {
        uint256 unitPrice = _market.getMarketUnitPrice(_orderBookId);
        presentValue = _calculatePVFromFV(_futureValue, unitPrice);
    }

    function _calculatePVFromFV(
        int256 _futureValue,
        uint256 _unitPrice
    ) internal pure returns (int256) {
        uint256 unitPrice = _unitPrice == 0 ? Constants.PRICE_DIGIT : _unitPrice;
        // NOTE: The formula is: presentValue = futureValue * unitPrice.
        return (_futureValue * unitPrice.toInt256()).div(Constants.PRICE_DIGIT.toInt256());
    }

    function _calculatePVFromFV(
        uint256 _futureValue,
        uint256 _unitPrice
    ) internal pure returns (uint256) {
        uint256 unitPrice = _unitPrice == 0 ? Constants.PRICE_DIGIT : _unitPrice;
        // NOTE: The formula is: presentValue = futureValue * unitPrice.
        return (_futureValue * unitPrice).div(Constants.PRICE_DIGIT);
    }

    function _calculateFVFromPV(
        int256 _presentValue,
        uint256 _unitPrice
    ) internal pure returns (int256) {
        uint256 unitPrice = _unitPrice == 0 ? Constants.PRICE_DIGIT : _unitPrice;
        // NOTE: The formula is: futureValue = presentValue / unitPrice.
        return (_presentValue * Constants.PRICE_DIGIT.toInt256()).div(unitPrice.toInt256());
    }
}

contracts/protocol/libraries/logics/Lend.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

// dependencies
import {IERC20} from "../../../dependencies/openzeppelin/token/ERC20/IERC20.sol";
import {SafeCast} from "../../../dependencies/openzeppelin/utils/math/SafeCast.sol";
// interfaces
import {ILendingMarket} from "../../interfaces/ILendingMarket.sol";
import {ILendingMarketController} from "../../interfaces/ILendingMarketController.sol";
import {IFutureValueVault} from "../../interfaces/IFutureValueVault.sol";
// libraries
import {AddressResolverLib} from "../AddressResolverLib.sol";
import {BokkyPooBahsDateTimeLibrary as TimeLibrary} from "../BokkyPooBahsDateTimeLibrary.sol";
import {Constants} from "../Constants.sol";
import {FilledOrder, PartiallyFilledOrder} from "../OrderBookLib.sol";
import {RoundingUint256} from "../math/RoundingUint256.sol";
import {RoundingInt256} from "../math/RoundingInt256.sol";
// types
import {ProtocolTypes} from "../../types/ProtocolTypes.sol";
// storages
import {LendingMarketControllerStorage as Storage, ObservationPeriodLog} from "../../storages/LendingMarketControllerStorage.sol";

library LendingMarketOperationLogic {
    using SafeCast for uint256;
    using RoundingUint256 for uint256;
    using SafeCast for uint256;
    using RoundingInt256 for int256;

    uint256 public constant OBSERVATION_PERIOD = 6 hours;

    error InvalidCompoundFactor();
    error InvalidCurrency();
    error InvalidOpeningDate();
    error InvalidPreOpeningDate();
    error InvalidTimestamp();
    error LendingMarketNotInitialized();
    error NotEnoughOrderBooks();

    event LendingMarketInitialized(
        bytes32 indexed ccy,
        uint256 genesisDate,
        uint256 compoundFactor,
        uint256 orderFeeRate,
        uint256 circuitBreakerLimitRange,
        address lendingMarket,
        address futureValueVault
    );

    event MinDebtUnitPriceUpdated(bytes32 indexed ccy, uint256 minDebtUnitPrice);

    event OrderBookCreated(
        bytes32 indexed ccy,
        uint8 indexed orderBookId,
        uint256 openingDate,
        uint256 maturity
    );

    event OrderBooksRotated(bytes32 ccy, uint256 oldMaturity, uint256 newMaturity);
    event EmergencyTerminationExecuted(uint256 timestamp);

    function initializeLendingMarket(
        bytes32 _ccy,
        uint256 _genesisDate,
        uint256 _compoundFactor,
        uint256 _orderFeeRate,
        uint256 _circuitBreakerLimitRange,
        uint256 _minDebtUnitPrice
    ) external {
        if (!AddressResolverLib.currencyController().currencyExists(_ccy)) {
            revert InvalidCurrency();
        }

        if (_compoundFactor == 0) revert InvalidCompoundFactor();

        AddressResolverLib.genesisValueVault().initializeCurrencySetting(
            _ccy,
            36,
            _compoundFactor,
            calculateNextMaturity(_genesisDate, Storage.slot().marketBasePeriod)
        );

        address lendingMarket = AddressResolverLib.beaconProxyController().deployLendingMarket(
            _ccy,
            _orderFeeRate,
            _circuitBreakerLimitRange
        );
        address futureValueVault = AddressResolverLib
            .beaconProxyController()
            .deployFutureValueVault();

        Storage.slot().genesisDates[_ccy] = _genesisDate;
        Storage.slot().lendingMarkets[_ccy] = lendingMarket;
        Storage.slot().futureValueVaults[_ccy] = futureValueVault;

        updateMinDebtUnitPrice(_ccy, _minDebtUnitPrice);

        emit LendingMarketInitialized(
            _ccy,
            _genesisDate,
            _compoundFactor,
            _orderFeeRate,
            _circuitBreakerLimitRange,
            lendingMarket,
            futureValueVault
        );
    }

    function updateMinDebtUnitPrice(bytes32 _ccy, uint256 _minDebtUnitPrice) public {
        Storage.slot().minDebtUnitPrices[_ccy] = _minDebtUnitPrice;
        emit MinDebtUnitPriceUpdated(_ccy, _minDebtUnitPrice);
    }

    function createOrderBook(bytes32 _ccy, uint256 _openingDate, uint256 _preOpeningDate) external {
        if (!AddressResolverLib.genesisValueVault().isInitialized(_ccy)) {
            revert LendingMarketNotInitialized();
        }
        if (!AddressResolverLib.currencyController().currencyExists(_ccy)) {
            revert InvalidCurrency();
        }
        if (_preOpeningDate > _openingDate) revert InvalidPreOpeningDate();

        ILendingMarket market = ILendingMarket(Storage.slot().lendingMarkets[_ccy]);

        uint256[] memory maturities = market.getMaturities(Storage.slot().orderBookIdLists[_ccy]);
        uint256 newMaturity;

        if (maturities.length == 0) {
            newMaturity = AddressResolverLib.genesisValueVault().getCurrentMaturity(_ccy);
        } else {
            uint256 lastMaturity = maturities[maturities.length - 1];
            newMaturity = calculateNextMaturity(lastMaturity, Storage.slot().marketBasePeriod);
        }

        if (_openingDate >= newMaturity) revert InvalidOpeningDate();

        uint8 orderBookId = market.createOrderBook(newMaturity, _openingDate, _preOpeningDate);

        Storage.slot().orderBookIdLists[_ccy].push(orderBookId);
        Storage.slot().maturityOrderBookIds[_ccy][newMaturity] = orderBookId;

        emit OrderBookCreated(_ccy, orderBookId, _openingDate, newMaturity);
    }

    function executeItayoseCall(
        bytes32 _ccy,
        uint256 _maturity
    )
        external
        returns (
            PartiallyFilledOrder memory partiallyFilledLendingOrder,
            PartiallyFilledOrder memory partiallyFilledBorrowingOrder
        )
    {
        ILendingMarket market = ILendingMarket(Storage.slot().lendingMarkets[_ccy]);
        uint8 orderBookId = Storage.slot().maturityOrderBookIds[_ccy][_maturity];

        if (market.isItayosePeriod(orderBookId)) {
            uint256 openingUnitPrice;
            uint256 openingDate;
            uint256 totalOffsetAmount;

            (
                openingUnitPrice,
                totalOffsetAmount,
                openingDate,
                partiallyFilledLendingOrder,
                partiallyFilledBorrowingOrder
            ) = market.executeItayoseCall(orderBookId);

            if (totalOffsetAmount > 0) {
                address futureValueVault = Storage.slot().futureValueVaults[_ccy];
                IFutureValueVault(futureValueVault).setInitialTotalSupply(
                    _maturity,
                    (totalOffsetAmount * Constants.PRICE_DIGIT).div(openingUnitPrice).toInt256()
                );
            }

            // Save the openingUnitPrice as first compound factor
            // if it is a first Itayose call at the nearest market.
            if (openingUnitPrice > 0 && Storage.slot().orderBookIdLists[_ccy][0] == orderBookId) {
                // Convert the openingUnitPrice determined by Itayose to the unit price on the Genesis Date.
                uint256 convertedUnitPrice = _convertUnitPrice(
                    openingUnitPrice,
                    _maturity,
                    openingDate,
                    Storage.slot().genesisDates[_ccy]
                );

                AddressResolverLib.genesisValueVault().updateInitialCompoundFactor(
                    _ccy,
                    convertedUnitPrice
                );
            }
        }
    }

    function rotateOrderBooks(bytes32 _ccy) external returns (uint256 newMaturity) {
        if (!AddressResolverLib.currencyController().currencyExists(_ccy)) {
            revert InvalidCurrency();
        }

        uint8[] storage orderBookIds = Storage.slot().orderBookIdLists[_ccy];

        if (orderBookIds.length < 2) revert NotEnoughOrderBooks();

        ILendingMarket market = ILendingMarket(Storage.slot().lendingMarkets[_ccy]);
        uint256[] memory maturities = market.getMaturities(orderBookIds);

        uint8 maturedOrderBookId = orderBookIds[0];
        uint8 destinationOrderBookId = orderBookIds[1];
        uint256 destinationOrderBookMaturity = maturities[1];

        newMaturity = calculateNextMaturity(
            maturities[maturities.length - 1],
            Storage.slot().marketBasePeriod
        );

        // Rotate the order of the market
        for (uint256 i; i < orderBookIds.length; i++) {
            uint8 orderBookId = (orderBookIds.length - 1) == i
                ? maturedOrderBookId
                : orderBookIds[i + 1];
            orderBookIds[i] = orderBookId;
        }

        uint256 autoRollUnitPrice = _calculateAutoRollUnitPrice(_ccy, destinationOrderBookMaturity);

        market.executeAutoRoll(
            maturedOrderBookId,
            destinationOrderBookId,
            newMaturity,
            destinationOrderBookMaturity,
            autoRollUnitPrice
        );

        AddressResolverLib.genesisValueVault().executeAutoRoll(
            _ccy,
            maturities[0],
            maturities[1],
            autoRollUnitPrice,
            market.getOrderFeeRate()
        );

        uint256 maturedMaturity = maturities[0];
        Storage.slot().maturityOrderBookIds[_ccy][newMaturity] = Storage
            .slot()
            .maturityOrderBookIds[_ccy][maturedMaturity];

        emit OrderBooksRotated(_ccy, maturedMaturity, newMaturity);
    }

    function executeEmergencyTermination() external {
        Storage.slot().marketTerminationDate = block.timestamp;

        bytes32[] memory currencies = AddressResolverLib.currencyController().getCurrencies();
        bytes32[] memory collateralCurrencies = AddressResolverLib
            .tokenVault()
            .getCollateralCurrencies();

        for (uint256 i; i < currencies.length; i++) {
            bytes32 ccy = currencies[i];

            Storage.slot().marketTerminationPrices[ccy] = AddressResolverLib
                .currencyController()
                .getAggregatedLastPrice(ccy);
        }

        for (uint256 i; i < collateralCurrencies.length; i++) {
            bytes32 ccy = collateralCurrencies[i];
            address tokenAddress = AddressResolverLib.tokenVault().getTokenAddress(ccy);
            uint256 balance = IERC20(tokenAddress).balanceOf(
                address(AddressResolverLib.tokenVault())
            );

            Storage.slot().marketTerminationRatios[ccy] = AddressResolverLib
                .currencyController()
                .convertToBaseCurrency(ccy, balance);
        }

        emit EmergencyTerminationExecuted(block.timestamp);
    }

    function pauseLendingMarket(bytes32 _ccy) public {
        ILendingMarket market = ILendingMarket(Storage.slot().lendingMarkets[_ccy]);
        market.pause();
    }

    function unpauseLendingMarket(bytes32 _ccy) public {
        ILendingMarket market = ILendingMarket(Storage.slot().lendingMarkets[_ccy]);
        market.unpause();
    }

    function updateOrderLogs(
        bytes32 _ccy,
        uint256 _maturity,
        uint256 _filledUnitPrice,
        uint256 _filledAmount,
        uint256 _filledFutureValue
    ) external {
        uint8 orderBookId = Storage.slot().maturityOrderBookIds[_ccy][_maturity];

        if (Storage.slot().orderBookIdLists[_ccy][1] == orderBookId) {
            uint256 nearestMaturity = ILendingMarket(Storage.slot().lendingMarkets[_ccy])
                .getMaturity(Storage.slot().orderBookIdLists[_ccy][0]);

            if (Storage.slot().observationPeriodLogs[_ccy][_maturity].totalAmount == 0) {
                Storage.slot().estimatedAutoRollUnitPrice[_ccy][_maturity] = _convertUnitPrice(
                    _filledUnitPrice,
                    _maturity,
                    block.timestamp,
                    nearestMaturity
                );
            }

            if (
                (block.timestamp < nearestMaturity) &&
                (block.timestamp >= (nearestMaturity - OBSERVATION_PERIOD))
            ) {
                Storage.slot().observationPeriodLogs[_ccy][_maturity].totalAmount += _filledAmount;
                Storage
                .slot()
                .observationPeriodLogs[_ccy][_maturity].totalFutureValue += _filledFutureValue;
            }
        }
    }

    function calculateNextMaturity(
        uint256 _timestamp,
        uint256 _period
    ) public pure returns (uint256) {
        if (_period == 0) {
            return TimeLibrary.addDays(_timestamp, 7);
        } else {
            return _getLastFridayAfterMonths(_timestamp, _period);
        }
    }

    function _getLastFridayAfterMonths(
        uint256 _timestamp,
        uint256 _months
    ) internal pure returns (uint256 lastFridayTimestamp) {
        (uint256 year, uint256 month, ) = TimeLibrary.timestampToDate(
            TimeLibrary.addMonths(_timestamp, _months + 1)
        );
        uint256 thirdMonthEndTimestamp = TimeLibrary.timestampFromDate(year, month, 0);
        uint256 dayOfWeek = TimeLibrary.getDayOfWeek(thirdMonthEndTimestamp);
        uint256 diff = (dayOfWeek < TimeLibrary.DOW_FRI ? 7 : 0) + dayOfWeek - TimeLibrary.DOW_FRI;
        lastFridayTimestamp = TimeLibrary.subDays(thirdMonthEndTimestamp, diff);

        if (lastFridayTimestamp == 0) revert InvalidTimestamp();

        return lastFridayTimestamp;
    }

    function _calculateAutoRollUnitPrice(
        bytes32 _ccy,
        uint256 _maturity
    ) internal view returns (uint256 autoRollUnitPrice) {
        ObservationPeriodLog memory log = Storage.slot().observationPeriodLogs[_ccy][_maturity];

        if (log.totalFutureValue != 0) {
            autoRollUnitPrice = (log.totalAmount * Constants.PRICE_DIGIT).div(log.totalFutureValue);
        } else if (Storage.slot().estimatedAutoRollUnitPrice[_ccy][_maturity] != 0) {
            autoRollUnitPrice = Storage.slot().estimatedAutoRollUnitPrice[_ccy][_maturity];
        } else {
            autoRollUnitPrice = AddressResolverLib
                .genesisValueVault()
                .getLatestAutoRollLog(_ccy)
                .unitPrice;
        }
    }

    function _convertUnitPrice(
        uint256 _unitPrice,
        uint256 _maturity,
        uint256 _currentTimestamp,
        uint256 _destinationTimestamp
    ) internal pure returns (uint256) {
        // NOTE:The formula is:
        // 1) currentDuration = maturity - currentTimestamp
        // 2) destinationDuration = maturity - destinationTimestamp
        // 3) unitPrice = (currentUnitPrice * currentDuration)
        //      / ((1 - currentUnitPrice) * destinationDuration + currentUnitPrice * currentDuration)

        uint256 currentDuration = _maturity - _currentTimestamp;
        uint256 destinationDuration = _maturity - _destinationTimestamp;
        return
            (Constants.PRICE_DIGIT * _unitPrice * currentDuration) /
            (((Constants.PRICE_DIGIT - _unitPrice) * destinationDuration) +
                (_unitPrice * currentDuration));
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

// dependencies
import {EnumerableSet} from "../../../dependencies/openzeppelin/utils/structs/EnumerableSet.sol";
import {SafeCast} from "../../../dependencies/openzeppelin/utils/math/SafeCast.sol";
// interfaces
import {ILendingMarket} from "../../interfaces/ILendingMarket.sol";
import {ILendingMarketController} from "../../interfaces/ILendingMarketController.sol";
import {IFutureValueVault} from "../../interfaces/IFutureValueVault.sol";
// libraries
import {AddressResolverLib} from "../AddressResolverLib.sol";
import {Constants} from "../Constants.sol";
import {FilledOrder, PartiallyFilledOrder} from "../OrderBookLib.sol";
import {RoundingUint256} from "../math/RoundingUint256.sol";
import {LendingMarketOperationLogic} from "./LendingMarketOperationLogic.sol";
import {FundManagementLogic} from "./FundManagementLogic.sol";
// types
import {ProtocolTypes} from "../../types/ProtocolTypes.sol";
// storages
import {LendingMarketControllerStorage as Storage} from "../../storages/LendingMarketControllerStorage.sol";

library LendingMarketUserLogic {
    using EnumerableSet for EnumerableSet.Bytes32Set;
    using EnumerableSet for EnumerableSet.UintSet;
    using SafeCast for int256;
    using RoundingUint256 for uint256;

    error InvalidAmount();
    error FutureValueIsZero();
    error TooManyActiveOrders();
    error NotEnoughCollateral();

    function getOrderEstimation(
        ILendingMarketController.GetOrderEstimationParams memory input
    )
        external
        view
        returns (
            uint256 lastUnitPrice,
            uint256 filledAmount,
            uint256 filledAmountInFV,
            uint256 orderFeeInFV,
            uint256 placedAmount,
            uint256 coverage,
            bool isInsufficientDepositAmount
        )
    {
        (
            lastUnitPrice,
            filledAmount,
            filledAmountInFV,
            orderFeeInFV,
            placedAmount
        ) = _calculateFilledAmount(
            input.ccy,
            input.maturity,
            input.side,
            input.amount,
            input.unitPrice
        );

        (coverage, isInsufficientDepositAmount) = _estimateCollateralCoverage(
            input.ccy,
            input.maturity,
            input.user,
            input.side,
            input.unitPrice,
            input.additionalDepositAmount,
            input.ignoreBorrowedAmount,
            filledAmount,
            filledAmountInFV,
            orderFeeInFV,
            placedAmount
        );
    }

    function executeOrder(
        bytes32 _ccy,
        uint256 _maturity,
        address _user,
        ProtocolTypes.Side _side,
        uint256 _amount,
        uint256 _unitPrice
    ) external {
        if (_amount == 0) revert InvalidAmount();

        uint256 activeOrderCount = FundManagementLogic.cleanUpFunds(_ccy, _user);
        FundManagementLogic.registerCurrencyAndMaturity(_ccy, _maturity, _user);

        (
            FilledOrder memory filledOrder,
            PartiallyFilledOrder memory partiallyFilledOrder,
            uint256 feeInFV
        ) = ILendingMarket(Storage.slot().lendingMarkets[_ccy]).executeOrder(
                Storage.slot().maturityOrderBookIds[_ccy][_maturity],
                _side,
                _user,
                _amount,
                _unitPrice
            );

        uint256 filledAmount = filledOrder.amount;

        // The case that an order is placed in the order book
        if ((filledAmount + filledOrder.ignoredAmount) != _amount) {
            unchecked {
                activeOrderCount += 1;
            }
        }

        if (activeOrderCount > Constants.MAXIMUM_ORDER_COUNT) revert TooManyActiveOrders();

        updateFundsForTaker(
            _ccy,
            _maturity,
            _user,
            _side,
            filledAmount,
            filledOrder.futureValue,
            filledOrder.unitPrice,
            feeInFV
        );

        updateFundsForMaker(
            _ccy,
            _maturity,
            _side == ProtocolTypes.Side.LEND ? ProtocolTypes.Side.BORROW : ProtocolTypes.Side.LEND,
            partiallyFilledOrder
        );

        Storage.slot().usedCurrencies[_user].add(_ccy);

        if (!AddressResolverLib.tokenVault().isCovered(_user)) revert NotEnoughCollateral();
    }

    function executePreOrder(
        bytes32 _ccy,
        uint256 _maturity,
        address _user,
        ProtocolTypes.Side _side,
        uint256 _amount,
        uint256 _unitPrice
    ) external {
        if (_amount == 0) revert InvalidAmount();

        uint256 activeOrderCount = FundManagementLogic.cleanUpFunds(_ccy, _user);

        if (activeOrderCount + 1 > Constants.MAXIMUM_ORDER_COUNT) revert TooManyActiveOrders();

        FundManagementLogic.registerCurrencyAndMaturity(_ccy, _maturity, _user);

        ILendingMarket(Storage.slot().lendingMarkets[_ccy]).executePreOrder(
            Storage.slot().maturityOrderBookIds[_ccy][_maturity],
            _side,
            _user,
            _amount,
            _unitPrice
        );

        Storage.slot().usedCurrencies[_user].add(_ccy);

        if (!AddressResolverLib.tokenVault().isCovered(_user)) revert NotEnoughCollateral();
    }

    function unwindPosition(bytes32 _ccy, uint256 _maturity, address _user) external {
        FundManagementLogic.cleanUpFunds(_ccy, _user);

        int256 futureValue = FundManagementLogic
            .getActualFunds(_ccy, _maturity, _user, 0)
            .futureValue;

        (
            FilledOrder memory filledOrder,
            PartiallyFilledOrder memory partiallyFilledOrder,
            uint256 feeInFV,
            ProtocolTypes.Side side
        ) = _unwindPosition(_ccy, _maturity, _user, futureValue);

        updateFundsForTaker(
            _ccy,
            _maturity,
            _user,
            side,
            filledOrder.amount,
            filledOrder.futureValue,
            filledOrder.unitPrice,
            feeInFV
        );

        updateFundsForMaker(
            _ccy,
            _maturity,
            side == ProtocolTypes.Side.LEND ? ProtocolTypes.Side.BORROW : ProtocolTypes.Side.LEND,
            partiallyFilledOrder
        );

        // When the market is the nearest market and the user has only GV, a user still has future value after unwinding.
        // For that case, the `registerCurrencyAndMaturity` function needs to be called again.
        (int256 currentFutureValue, ) = IFutureValueVault(Storage.slot().futureValueVaults[_ccy])
            .getBalance(Storage.slot().maturityOrderBookIds[_ccy][_maturity], _user);

        if (currentFutureValue != 0) {
            FundManagementLogic.registerCurrencyAndMaturity(_ccy, _maturity, _user);
        }

        if (!AddressResolverLib.tokenVault().isCovered(_user)) revert NotEnoughCollateral();
    }

    function updateFundsForTaker(
        bytes32 _ccy,
        uint256 _maturity,
        address _user,
        ProtocolTypes.Side _side,
        uint256 _filledAmount,
        uint256 _filledAmountInFV,
        uint256 _filledUnitPrice,
        uint256 _feeInFV
    ) public {
        if (_filledAmountInFV != 0) {
            FundManagementLogic.updateFunds(
                _ccy,
                _maturity,
                _user,
                _side,
                _filledAmount,
                _filledAmountInFV,
                _feeInFV,
                true
            );

            LendingMarketOperationLogic.updateOrderLogs(
                _ccy,
                _maturity,
                _filledUnitPrice,
                _filledAmount,
                _filledAmountInFV
            );

            emit FundManagementLogic.OrderFilled(
                _user,
                _ccy,
                _side,
                _maturity,
                _filledAmount,
                _filledAmountInFV,
                _feeInFV
            );
        }
    }

    function updateFundsForMaker(
        bytes32 _ccy,
        uint256 _maturity,
        ProtocolTypes.Side _side,
        PartiallyFilledOrder memory partiallyFilledOrder
    ) public {
        if (partiallyFilledOrder.futureValue != 0) {
            FundManagementLogic.updateFunds(
                _ccy,
                _maturity,
                partiallyFilledOrder.maker,
                _side,
                partiallyFilledOrder.amount,
                partiallyFilledOrder.futureValue,
                0,
                false
            );

            emit FundManagementLogic.OrderPartiallyFilled(
                partiallyFilledOrder.orderId,
                partiallyFilledOrder.maker,
                _ccy,
                _side,
                _maturity,
                partiallyFilledOrder.amount,
                partiallyFilledOrder.futureValue
            );
        }
    }

    function _calculateFilledAmount(
        bytes32 _ccy,
        uint256 _maturity,
        ProtocolTypes.Side _side,
        uint256 _amount,
        uint256 _unitPrice
    )
        internal
        view
        returns (
            uint256 lastUnitPrice,
            uint256 filledAmount,
            uint256 filledAmountInFV,
            uint256 orderFeeInFV,
            uint256 placedAmount
        )
    {
        (
            lastUnitPrice,
            filledAmount,
            filledAmountInFV,
            orderFeeInFV,
            placedAmount
        ) = ILendingMarket(Storage.slot().lendingMarkets[_ccy]).calculateFilledAmount(
            Storage.slot().maturityOrderBookIds[_ccy][_maturity],
            _side,
            _amount,
            _unitPrice
        );
    }

    function _estimateCollateralCoverage(
        bytes32 _ccy,
        uint256 _maturity,
        address _user,
        ProtocolTypes.Side _side,
        uint256 _unitPrice,
        uint256 _additionalDepositAmount,
        bool _ignoreBorrowedAmount,
        uint256 _filledAmount,
        uint256 _filledAmountInFV,
        uint256 _orderFeeInFV,
        uint256 _placedAmount
    ) internal view returns (uint256 coverage, bool isInsufficientDepositAmount) {
        uint256 filledAmountWithFeeInFV = _filledAmountInFV;

        if (_side == ProtocolTypes.Side.LEND) {
            filledAmountWithFeeInFV -= _orderFeeInFV;
        } else {
            filledAmountWithFeeInFV += _orderFeeInFV;
        }

        uint256 filledAmountWithFeeInPV = _estimatePVFromFV(
            _ccy,
            _maturity,
            filledAmountWithFeeInFV,
            _unitPrice
        );

        ILendingMarketController.AdditionalFunds memory funds;
        funds.ccy = _ccy;
        // Store the _additionalDepositAmount in the borrowedAmount,
        // because the borrowedAmount is used as collateral.
        funds.borrowedAmount = _additionalDepositAmount;

        if (_placedAmount > 0) {
            if (_side == ProtocolTypes.Side.BORROW) {
                funds.workingBorrowOrdersAmount = _placedAmount;
            } else {
                funds.workingLendOrdersAmount = _placedAmount;
            }
        }

        if (filledAmountWithFeeInPV > 0) {
            if (_side == ProtocolTypes.Side.BORROW) {
                if (!_ignoreBorrowedAmount) {
                    funds.borrowedAmount += _filledAmount;
                }
                funds.debtAmount += filledAmountWithFeeInPV;
            } else {
                funds.lentAmount += _filledAmount;
                funds.claimableAmount += filledAmountWithFeeInPV;
            }
        }

        (coverage, isInsufficientDepositAmount) = AddressResolverLib.tokenVault().calculateCoverage(
            _user,
            funds
        );
    }

    function _estimatePVFromFV(
        bytes32 _ccy,
        uint256 _maturity,
        uint256 _amount,
        uint256 _unitPrice
    ) internal view returns (uint256) {
        uint256 marketUnitPrice = ILendingMarket(Storage.slot().lendingMarkets[_ccy])
            .getMarketUnitPrice(Storage.slot().maturityOrderBookIds[_ccy][_maturity]);

        if (marketUnitPrice == 0) {
            marketUnitPrice = _unitPrice;
        }

        return (_amount * marketUnitPrice).div(Constants.PRICE_DIGIT);
    }

    function _unwindPosition(
        bytes32 _ccy,
        uint256 _maturity,
        address _user,
        int256 _futureValue
    )
        internal
        returns (
            FilledOrder memory filledOrder,
            PartiallyFilledOrder memory partiallyFilledOrder,
            uint256 feeInFV,
            ProtocolTypes.Side side
        )
    {
        if (_futureValue == 0) revert FutureValueIsZero();

        if (_futureValue > 0) {
            side = ProtocolTypes.Side.BORROW;

            (filledOrder, partiallyFilledOrder, feeInFV) = ILendingMarket(
                Storage.slot().lendingMarkets[_ccy]
            ).unwindPosition(
                    Storage.slot().maturityOrderBookIds[_ccy][_maturity],
                    side,
                    _user,
                    _futureValue.toUint256()
                );
        } else if (_futureValue < 0) {
            side = ProtocolTypes.Side.LEND;

            (filledOrder, partiallyFilledOrder, feeInFV) = ILendingMarket(
                Storage.slot().lendingMarkets[_ccy]
            ).unwindPosition(
                    Storage.slot().maturityOrderBookIds[_ccy][_maturity],
                    side,
                    _user,
                    (-_futureValue).toUint256()
                );
        }
    }
}

contracts/protocol/libraries/logics/Liqu.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

// dependencies
import {SafeCast} from "../../../dependencies/openzeppelin/utils/math/SafeCast.sol";
// interfaces
import {IFutureValueVault} from "../../interfaces/IFutureValueVault.sol";
import {ILiquidationReceiver} from "../../interfaces/ILiquidationReceiver.sol";
// libraries
import {AddressResolverLib} from "../AddressResolverLib.sol";
import {FundManagementLogic} from "./FundManagementLogic.sol";
import {RoundingUint256} from "../math/RoundingUint256.sol";
// storages
import {LendingMarketControllerStorage as Storage} from "../../storages/LendingMarketControllerStorage.sol";

library LiquidationLogic {
    using RoundingUint256 for uint256;
    using SafeCast for uint256;
    using SafeCast for int256;

    error NoDebt(address user, bytes32 ccy, uint256 maturity);
    error NoLiquidationAmount(address user, bytes32 ccy);
    error InvalidLiquidation();
    error InvalidRepaymentAmount();
    error NotRepaymentPeriod();

    struct ExecuteLiquidationVars {
        uint256 liquidationAmountInCollateralCcy;
        uint256 liquidationAmountInDebtCcy;
        uint256 protocolFeeInCollateralCcy;
        uint256 liquidatorFeeInCollateralCcy;
        bool isDefaultMarket;
        uint256 receivedCollateralAmount;
    }

    event LiquidationExecuted(
        address indexed user,
        bytes32 collateralCcy,
        bytes32 indexed debtCcy,
        uint256 indexed debtMaturity,
        uint256 debtAmount
    );

    event ForcedRepaymentExecuted(
        address indexed user,
        bytes32 collateralCcy,
        bytes32 indexed debtCcy,
        uint256 indexed debtMaturity,
        uint256 debtAmount
    );

    function executeLiquidation(
        address _liquidator,
        address _user,
        bytes32 _collateralCcy,
        bytes32 _debtCcy,
        uint256 _debtMaturity
    ) external {
        ExecuteLiquidationVars memory vars;

        vars.isDefaultMarket =
            Storage.slot().maturityOrderBookIds[_debtCcy][_debtMaturity] ==
            Storage.slot().orderBookIdLists[_debtCcy][0];

        // In order to liquidate using user collateral, inactive order IDs must be cleaned
        // and converted to actual funds first.
        FundManagementLogic.cleanUpFunds(_collateralCcy, _user);
        FundManagementLogic.cleanUpFunds(_debtCcy, _user);

        uint256 debtAmount = FundManagementLogic
            .getActualFunds(_debtCcy, _debtMaturity, _user, 0)
            .debtAmount;

        if (debtAmount == 0) revert NoDebt(_user, _debtCcy, _debtMaturity);

        (
            vars.liquidationAmountInCollateralCcy,
            vars.protocolFeeInCollateralCcy,
            vars.liquidatorFeeInCollateralCcy
        ) = AddressResolverLib.tokenVault().getLiquidationAmount(
            _user,
            _collateralCcy,
            AddressResolverLib.currencyController().convert(_debtCcy, _collateralCcy, debtAmount)
        );

        if (vars.liquidationAmountInCollateralCcy == 0) {
            revert NoLiquidationAmount(_user, _collateralCcy);
        }

        vars.liquidationAmountInDebtCcy = AddressResolverLib.currencyController().convert(
            _collateralCcy,
            _debtCcy,
            vars.liquidationAmountInCollateralCcy
        );

        // Transfer collateral from users to liquidators and reserve funds.
        vars.receivedCollateralAmount =
            vars.liquidationAmountInCollateralCcy +
            vars.liquidatorFeeInCollateralCcy;

        uint256 untransferredAmount = _transferCollateral(
            _user,
            _liquidator,
            _collateralCcy,
            vars.receivedCollateralAmount
        );

        if (untransferredAmount == 0) {
            _transferCollateral(
                _user,
                address(AddressResolverLib.reserveFund()),
                _collateralCcy,
                vars.protocolFeeInCollateralCcy
            );
        } else if (untransferredAmount > 0) {
            (
                uint256 untransferredAmountInDebtCcy,
                uint256 receivedCollateralAmountInDebtCcy,
                uint256 liquidatorFeeInDebtCcy
            ) = _convertLiquidationAmounts(
                    _collateralCcy,
                    _debtCcy,
                    untransferredAmount,
                    vars.receivedCollateralAmount,
                    vars.liquidatorFeeInCollateralCcy
                );

            // Use reserve funds to cover insolvent amounts if user does not have collateral in other currencies.
            if (
                !AddressResolverLib.reserveFund().isPaused() &&
                AddressResolverLib.tokenVault().getTotalCollateralAmount(_user) == 0
            ) {
                untransferredAmountInDebtCcy = _transferPositionsPerMaturity(
                    address(AddressResolverLib.reserveFund()),
                    _liquidator,
                    _debtCcy,
                    _debtMaturity,
                    untransferredAmountInDebtCcy.toInt256(),
                    vars.isDefaultMarket
                ).toUint256();
            }

            // Adjust the liquidation amount for debt.
            vars.liquidationAmountInDebtCcy = _calculateTransferredAmount(
                receivedCollateralAmountInDebtCcy,
                untransferredAmountInDebtCcy,
                liquidatorFeeInDebtCcy
            );
        }

        if (_liquidator.code.length > 0) {
            if (
                ILiquidationReceiver(_liquidator).executeOperationForCollateral(
                    _liquidator,
                    _user,
                    _collateralCcy,
                    vars.receivedCollateralAmount
                ) == false
            ) revert ILiquidationReceiver.InvalidOperationExecution();
        }

        // Transfer the debt from users to liquidators
        if (vars.liquidationAmountInDebtCcy > 0) {
            _transferPositionsPerMaturity(
                _user,
                _liquidator,
                _debtCcy,
                _debtMaturity,
                -vars.liquidationAmountInDebtCcy.toInt256(),
                vars.isDefaultMarket
            );

            if (_liquidator.code.length > 0) {
                if (
                    ILiquidationReceiver(_liquidator).executeOperationForDebt(
                        _liquidator,
                        _user,
                        _collateralCcy,
                        vars.receivedCollateralAmount,
                        _debtCcy,
                        _debtMaturity,
                        vars.liquidationAmountInDebtCcy
                    ) == false
                ) revert ILiquidationReceiver.InvalidOperationExecution();
            }
        }

        if (!AddressResolverLib.tokenVault().isCovered(_liquidator)) revert InvalidLiquidation();

        emit LiquidationExecuted(
            _user,
            _collateralCcy,
            _debtCcy,
            _debtMaturity,
            vars.liquidationAmountInDebtCcy
        );
    }

    function executeForcedRepayment(
        address _executor,
        address _user,
        bytes32 _collateralCcy,
        bytes32 _debtCcy,
        uint256 _debtMaturity
    ) external {
        if (
            AddressResolverLib.currencyController().currencyExists(_debtCcy) ||
            block.timestamp < _debtMaturity + 1 weeks
        ) {
            revert NotRepaymentPeriod();
        }

        // In order to liquidate using user collateral, inactive order IDs must be cleaned
        // and converted to actual funds first.
        FundManagementLogic.cleanUpFunds(_collateralCcy, _user);
        FundManagementLogic.cleanUpFunds(_debtCcy, _user);

        FundManagementLogic.ActualFunds memory funds = FundManagementLogic.getActualFunds(
            _debtCcy,
            _debtMaturity,
            _user,
            0
        );

        if (funds.futureValue >= 0) revert NoDebt(_user, _debtCcy, _debtMaturity);

        uint256 liquidationAmountInDebtCcy = (-funds.futureValue).toUint256();
        uint256 liquidationAmountInCollateralCcy = AddressResolverLib.currencyController().convert(
            _debtCcy,
            _collateralCcy,
            liquidationAmountInDebtCcy
        );

        (
            uint256 protocolFeeInCollateralCcy,
            uint256 liquidatorFeeInCollateralCcy
        ) = AddressResolverLib.tokenVault().calculateLiquidationFees(
                liquidationAmountInCollateralCcy
            );

        uint256 receivedCollateralAmount = liquidationAmountInCollateralCcy +
            liquidatorFeeInCollateralCcy;

        uint256 untransferredAmount = _transferCollateral(
            _user,
            _executor,
            _collateralCcy,
            receivedCollateralAmount
        );

        if (untransferredAmount == 0) {
            _transferCollateral(
                _user,
                address(AddressResolverLib.reserveFund()),
                _collateralCcy,
                protocolFeeInCollateralCcy
            );
        } else {
            (
                uint256 untransferredAmountInDebtCcy,
                uint256 receivedCollateralAmountInDebtCcy,
                uint256 liquidatorFeeInDebtCcy
            ) = _convertLiquidationAmounts(
                    _collateralCcy,
                    _debtCcy,
                    untransferredAmount,
                    receivedCollateralAmount,
                    liquidatorFeeInCollateralCcy
                );

            // Adjust the liquidation amount for debt.
            liquidationAmountInDebtCcy = _calculateTransferredAmount(
                receivedCollateralAmountInDebtCcy,
                untransferredAmountInDebtCcy,
                liquidatorFeeInDebtCcy
            );
        }

        if (_executor.code.length > 0) {
            if (
                ILiquidationReceiver(_executor).executeOperationForCollateral(
                    _executor,
                    _user,
                    _collateralCcy,
                    receivedCollateralAmount
                ) == false
            ) revert ILiquidationReceiver.InvalidOperationExecution();

            if (
                ILiquidationReceiver(_executor).executeOperationForDebt(
                    _executor,
                    _user,
                    _collateralCcy,
                    receivedCollateralAmount,
                    _debtCcy,
                    _debtMaturity,
                    liquidationAmountInDebtCcy
                ) == false
            ) revert ILiquidationReceiver.InvalidOperationExecution();
        }

        AddressResolverLib.tokenVault().transferFrom(
            _debtCcy,
            _executor,
            _user,
            liquidationAmountInDebtCcy
        );

        uint256 repaymentAmount = FundManagementLogic.executeRepayment(
            _debtCcy,
            _debtMaturity,
            _user,
            liquidationAmountInDebtCcy
        );

        if (repaymentAmount != liquidationAmountInDebtCcy) revert InvalidRepaymentAmount();

        emit ForcedRepaymentExecuted(
            _user,
            _collateralCcy,
            _debtCcy,
            _debtMaturity,
            liquidationAmountInDebtCcy
        );
    }

    function _transferCollateral(
        address _from,
        address _to,
        bytes32 _ccy,
        uint256 _amount
    ) internal returns (uint256 untransferredAmount) {
        untransferredAmount = AddressResolverLib.tokenVault().transferFrom(
            _ccy,
            _from,
            _to,
            _amount
        );

        // If `untransferredAmount` is not 0, the user has not enough deposit in the collateral currency.
        // Therefore, the liquidators and the reserve fund obtain zero-coupon bonds instead of the user's collateral.
        if (untransferredAmount > 0) {
            untransferredAmount = _transferPositionsPerCurrency(
                _from,
                _to,
                _ccy,
                untransferredAmount.toInt256()
            ).toUint256();
        }
    }

    function _transferPositionsPerCurrency(
        address _from,
        address _to,
        bytes32 _ccy,
        int256 _amount
    ) internal returns (int256 untransferredAmount) {
        untransferredAmount = _transferGenesisValue(_from, _to, _ccy, _amount);

        uint256[] memory maturities = FundManagementLogic.getUsedMaturities(_ccy, _from);

        for (uint256 i; i < maturities.length; i++) {
            if (untransferredAmount == 0) {
                break;
            }

            untransferredAmount = _transferFutureValues(
                _from,
                _to,
                _ccy,
                maturities[i],
                untransferredAmount
            );
        }
    }

    function _transferPositionsPerMaturity(
        address _from,
        address _to,
        bytes32 _ccy,
        uint256 _maturity,
        int256 _amount,
        bool _isDefaultMarket
    ) internal returns (int256 untransferredAmount) {
        untransferredAmount = _isDefaultMarket
            ? _transferGenesisValue(_from, _to, _ccy, _amount)
            : _amount;

        untransferredAmount = _transferFutureValues(
            _from,
            _to,
            _ccy,
            _maturity,
            untransferredAmount
        );
    }

    function _transferGenesisValue(
        address _from,
        address _to,
        bytes32 _ccy,
        int256 _amount
    ) internal returns (int256 untransferredAmount) {
        untransferredAmount = _amount;
        bool isDebt = _amount < 0;

        int256 userGVAmount = AddressResolverLib.genesisValueVault().getBalance(_ccy, _from);

        if ((isDebt && userGVAmount < 0) || (!isDebt && userGVAmount > 0)) {
            uint256 currentMaturity = AddressResolverLib.genesisValueVault().getCurrentMaturity(
                _ccy
            );

            int256 gvAmount = AddressResolverLib.genesisValueVault().calculateGVFromFV(
                _ccy,
                0,
                FundManagementLogic.calculateFVFromPV(_ccy, currentMaturity, untransferredAmount)
            );

            if ((isDebt && userGVAmount > gvAmount) || (!isDebt && userGVAmount < gvAmount)) {
                gvAmount = userGVAmount;
            }

            // Due to the negative genesis value, the liquidator's genesis value is decreased.
            AddressResolverLib.genesisValueVault().transferFrom(_ccy, _from, _to, gvAmount);

            untransferredAmount -= FundManagementLogic.calculatePVFromFV(
                _ccy,
                currentMaturity,
                AddressResolverLib.genesisValueVault().calculateFVFromGV(_ccy, 0, gvAmount)
            );

            FundManagementLogic.registerCurrency(_ccy, _to);
        }
    }

    function _transferFutureValues(
        address _from,
        address _to,
        bytes32 _ccy,
        uint256 _maturity,
        int256 _amount
    ) internal returns (int256 untransferredAmount) {
        untransferredAmount = _amount;
        bool isDebt = _amount < 0;

        IFutureValueVault futureValueVault = IFutureValueVault(
            Storage.slot().futureValueVaults[_ccy]
        );
        uint8 orderBookId = Storage.slot().maturityOrderBookIds[_ccy][_maturity];

        (int256 userFVAmount, ) = futureValueVault.getBalance(orderBookId, _from);

        if ((isDebt && userFVAmount < 0) || (!isDebt && userFVAmount > 0)) {
            int256 fvAmount = FundManagementLogic.calculateFVFromPV(
                _ccy,
                _maturity,
                untransferredAmount
            );

            if ((isDebt && userFVAmount > fvAmount) || (!isDebt && userFVAmount < fvAmount)) {
                fvAmount = userFVAmount;
            }

            futureValueVault.transferFrom(orderBookId, _from, _to, fvAmount, _maturity);
            untransferredAmount -= FundManagementLogic.calculatePVFromFV(_ccy, _maturity, fvAmount);

            FundManagementLogic.registerCurrencyAndMaturity(_ccy, _maturity, _to);
        }
    }

    function _convertLiquidationAmounts(
        bytes32 _collateralCcy,
        bytes32 _debtCcy,
        uint256 _untransferredAmount,
        uint256 _receivedCollateralAmount,
        uint256 _liquidatorFeeInCollateralCcy
    )
        internal
        view
        returns (
            uint256 untransferredAmountInDebtCcy,
            uint256 receivedCollateralAmountInDebtCcy,
            uint256 liquidatorFeeInDebtCcy
        )
    {
        uint256[] memory amountsInCollateralCcy = new uint256[](3);
        amountsInCollateralCcy[0] = _untransferredAmount;
        amountsInCollateralCcy[1] = _receivedCollateralAmount;
        amountsInCollateralCcy[2] = _liquidatorFeeInCollateralCcy;

        uint256[] memory amountsInDebtCcy = AddressResolverLib.currencyController().convert(
            _collateralCcy,
            _debtCcy,
            amountsInCollateralCcy
        );

        untransferredAmountInDebtCcy = amountsInDebtCcy[0];
        receivedCollateralAmountInDebtCcy = amountsInDebtCcy[1];
        liquidatorFeeInDebtCcy = amountsInDebtCcy[2];
    }

    function _calculateTransferredAmount(
        uint256 totalAmount,
        uint256 untransferredAmount,
        uint256 feeAmount
    ) internal pure returns (uint256) {
        // NOTE: The formula is:
        // transferredTotalAmount = totalAmount - untransferredAmount;
        // untransferredFeeAmount = feeAmount * (transferredTotalAmount / totalAmount);
        uint256 transferredTotalAmount = totalAmount - untransferredAmount;
        uint256 untransferredFeeAmount = (feeAmount * transferredTotalAmount).div(totalAmount);

        return transferredTotalAmount - untransferredFeeAmount;
    }
}

contracts/protocol/libraries/logics/Orde.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import {OrderBookLib, FilledOrder, PartiallyFilledOrder} from "../OrderBookLib.sol";
import {ProtocolTypes} from "../../types/ProtocolTypes.sol";
import {LendingMarketStorage as Storage} from "../../storages/LendingMarketStorage.sol";
// libraries
import {Constants} from "../Constants.sol";
import {AddressResolverLib} from "../AddressResolverLib.sol";
import {RoundingUint256} from "../math/RoundingUint256.sol";
import {OrderReaderLogic} from "./OrderReaderLogic.sol";

library OrderActionLogic {
    using OrderBookLib for OrderBookLib.OrderBook;
    using RoundingUint256 for uint256;

    error InvalidAmount();
    error InvalidFutureValue();
    error EmptyOrderBook();
    error OppositeSideOrderExists();

    struct OrderExecutionConditions {
        bool isFilled;
        uint256 executedUnitPrice;
        bool ignoreRemainingAmount;
        bool orderExists;
    }

    struct PlacedOrder {
        uint48 orderId;
        uint256 amount;
        uint256 unitPrice;
    }

    struct ExecuteOrderVars {
        OrderExecutionConditions conditions;
        PlacedOrder placedOrder;
        bool isCircuitBreakerTriggered;
        uint256 maturity;
    }

    struct FillOrdersVars {
        uint8 orderBookId;
        uint256 remainingAmount;
        bool orderExists;
    }

    event OrderCanceled(
        uint48 orderId,
        address indexed maker,
        ProtocolTypes.Side side,
        bytes32 ccy,
        uint256 maturity,
        uint256 amount,
        uint256 unitPrice
    );

    event OrdersCleaned(
        uint48[] orderIds,
        address indexed maker,
        ProtocolTypes.Side side,
        bytes32 indexed ccy,
        uint256 maturity,
        uint256 amount,
        uint256 futureValue
    );

    event OrderExecuted(
        address indexed user,
        ProtocolTypes.Side side,
        bytes32 indexed ccy,
        uint256 indexed maturity,
        uint256 inputAmount,
        uint256 inputUnitPrice,
        uint256 filledAmount,
        uint256 filledUnitPrice,
        uint256 filledAmountInFV,
        uint256 feeInFV,
        uint48 placedOrderId,
        uint256 placedAmount,
        uint256 placedUnitPrice,
        bool isCircuitBreakerTriggered
    );

    event PreOrderExecuted(
        address indexed user,
        ProtocolTypes.Side side,
        bytes32 indexed ccy,
        uint256 indexed maturity,
        uint256 amount,
        uint256 unitPrice,
        uint48 orderId
    );

    event PositionUnwound(
        address indexed user,
        ProtocolTypes.Side side,
        bytes32 indexed ccy,
        uint256 indexed maturity,
        uint256 inputFutureValue,
        uint256 filledAmount,
        uint256 filledUnitPrice,
        uint256 filledAmountInFV,
        uint256 feeInFV,
        bool isCircuitBreakerTriggered
    );

    function cancelOrder(uint8 _orderBookId, address _user, uint48 _orderId) external {
        OrderBookLib.OrderBook storage orderBook = _getOrderBook(_orderBookId);

        (ProtocolTypes.Side side, uint256 removedAmount, uint256 unitPrice) = orderBook.removeOrder(
            _user,
            _orderId
        );

        emit OrderCanceled(
            _orderId,
            _user,
            side,
            Storage.slot().ccy,
            orderBook.maturity,
            removedAmount,
            unitPrice
        );
    }

    function cleanUpOrders(
        uint8 _orderBookId,
        address _user
    )
        external
        returns (
            uint256 activeLendOrderCount,
            uint256 activeBorrowOrderCount,
            uint256 removedLendOrderFutureValue,
            uint256 removedBorrowOrderFutureValue,
            uint256 removedLendOrderAmount,
            uint256 removedBorrowOrderAmount,
            uint256 maturity
        )
    {
        OrderBookLib.OrderBook storage orderBook = _getOrderBook(_orderBookId);
        maturity = orderBook.userCurrentMaturities[_user];

        uint48[] memory lendOrderIds;
        uint48[] memory borrowOrderIds;

        (
            lendOrderIds,
            activeLendOrderCount,
            removedLendOrderFutureValue,
            removedLendOrderAmount
        ) = _cleanLendOrders(_orderBookId, _user);

        (
            borrowOrderIds,
            activeBorrowOrderCount,
            removedBorrowOrderFutureValue,
            removedBorrowOrderAmount
        ) = _cleanBorrowOrders(_orderBookId, _user);

        if (removedLendOrderAmount > 0) {
            emit OrdersCleaned(
                lendOrderIds,
                _user,
                ProtocolTypes.Side.LEND,
                Storage.slot().ccy,
                orderBook.maturity,
                removedLendOrderAmount,
                removedLendOrderFutureValue
            );
        }

        if (removedBorrowOrderAmount > 0) {
            emit OrdersCleaned(
                borrowOrderIds,
                _user,
                ProtocolTypes.Side.BORROW,
                Storage.slot().ccy,
                orderBook.maturity,
                removedBorrowOrderAmount,
                removedBorrowOrderFutureValue
            );
        }
    }

    function executeOrder(
        uint8 _orderBookId,
        ProtocolTypes.Side _side,
        address _user,
        uint256 _amount,
        uint256 _unitPrice,
        uint256 _minimumReliableAmount
    )
        external
        returns (
            FilledOrder memory filledOrder,
            PartiallyFilledOrder memory partiallyFilledOrder,
            uint256 feeInFV
        )
    {
        if (_amount == 0) revert InvalidAmount();

        OrderBookLib.OrderBook storage orderBook = _getOrderBook(_orderBookId);
        orderBook.updateUserMaturity(_user);

        ExecuteOrderVars memory vars;
        vars.maturity = orderBook.maturity;

        (
            vars.conditions.isFilled,
            vars.conditions.executedUnitPrice,
            vars.conditions.ignoreRemainingAmount,
            vars.conditions.orderExists
        ) = orderBook.getOrderExecutionConditions(
            _side,
            _unitPrice,
            Storage.slot().circuitBreakerLimitRange,
            false
        );

        if (_unitPrice == 0 && !vars.conditions.orderExists) revert EmptyOrderBook();

        if (vars.conditions.isFilled) {
            (
                filledOrder,
                partiallyFilledOrder,
                vars.placedOrder,
                vars.isCircuitBreakerTriggered
            ) = _fillOrders(
                _orderBookId,
                _side,
                _user,
                _amount,
                vars.conditions.executedUnitPrice,
                vars.conditions.ignoreRemainingAmount
            );
            feeInFV = OrderReaderLogic.calculateOrderFeeAmount(
                vars.maturity,
                filledOrder.futureValue
            );
            orderBook.updateBlockUnitPriceHistory(
                filledOrder.amount,
                filledOrder.futureValue,
                _minimumReliableAmount
            );
        } else {
            if (!vars.conditions.ignoreRemainingAmount) {
                vars.placedOrder = PlacedOrder(
                    orderBook.placeOrder(_side, _user, _amount, vars.conditions.executedUnitPrice),
                    _amount,
                    vars.conditions.executedUnitPrice
                );
            }

            vars.isCircuitBreakerTriggered = _unitPrice == 0
                ? vars.conditions.orderExists
                : _unitPrice != vars.conditions.executedUnitPrice;
        }

        emit OrderExecuted(
            _user,
            _side,
            Storage.slot().ccy,
            vars.maturity,
            _amount,
            _unitPrice,
            filledOrder.amount,
            filledOrder.unitPrice,
            filledOrder.futureValue,
            feeInFV,
            vars.placedOrder.orderId,
            vars.placedOrder.amount,
            vars.placedOrder.unitPrice,
            vars.isCircuitBreakerTriggered
        );
    }

    function executePreOrder(
        uint8 _orderBookId,
        ProtocolTypes.Side _side,
        address _user,
        uint256 _amount,
        uint256 _unitPrice
    ) external {
        if (_amount == 0) revert InvalidAmount();

        OrderBookLib.OrderBook storage orderBook = _getOrderBook(_orderBookId);
        orderBook.updateUserMaturity(_user);

        if (
            (_side == ProtocolTypes.Side.LEND && orderBook.hasBorrowOrder(_user)) ||
            (_side == ProtocolTypes.Side.BORROW && orderBook.hasLendOrder(_user))
        ) {
            revert OppositeSideOrderExists();
        }

        uint48 orderId = orderBook.placeOrder(_side, _user, _amount, _unitPrice);
        orderBook.isPreOrder[orderId] = true;

        emit PreOrderExecuted(
            _user,
            _side,
            Storage.slot().ccy,
            orderBook.maturity,
            _amount,
            _unitPrice,
            orderId
        );
    }

    function unwindPosition(
        uint8 _orderBookId,
        ProtocolTypes.Side _side,
        address _user,
        uint256 _futureValue,
        uint256 _minimumReliableAmount
    )
        external
        returns (
            FilledOrder memory filledOrder,
            PartiallyFilledOrder memory partiallyFilledOrder,
            uint256 feeInFV
        )
    {
        if (_futureValue == 0) revert InvalidFutureValue();

        OrderExecutionConditions memory conditions;
        bool isCircuitBreakerTriggered;
        OrderBookLib.OrderBook storage orderBook = _getOrderBook(_orderBookId);
        uint256 maturity = orderBook.maturity;

        (
            conditions.isFilled,
            conditions.executedUnitPrice,
            conditions.ignoreRemainingAmount,
            conditions.orderExists
        ) = orderBook.getOrderExecutionConditions(
            _side,
            0,
            Storage.slot().circuitBreakerLimitRange,
            false
        );

        if (!conditions.orderExists) revert EmptyOrderBook();

        if (conditions.isFilled) {
            (filledOrder, partiallyFilledOrder, isCircuitBreakerTriggered) = _unwindPosition(
                _orderBookId,
                _side,
                _futureValue,
                conditions.executedUnitPrice
            );
            feeInFV = OrderReaderLogic.calculateOrderFeeAmount(maturity, filledOrder.futureValue);
            orderBook.updateBlockUnitPriceHistory(
                filledOrder.amount,
                filledOrder.futureValue,
                _minimumReliableAmount
            );
        } else {
            isCircuitBreakerTriggered = conditions.orderExists;
        }

        emit PositionUnwound(
            _user,
            _side,
            Storage.slot().ccy,
            maturity,
            _futureValue,
            filledOrder.amount,
            filledOrder.unitPrice,
            filledOrder.futureValue,
            feeInFV,
            isCircuitBreakerTriggered
        );
    }

    function _cleanLendOrders(
        uint8 _orderBookId,
        address _user
    )
        internal
        returns (
            uint48[] memory orderIds,
            uint256 activeOrderCount,
            uint256 removedFutureValue,
            uint256 removedOrderAmount
        )
    {
        OrderBookLib.OrderBook storage orderBook = _getOrderBook(_orderBookId);

        (uint48[] memory activeLendOrderIds, uint48[] memory inActiveLendOrderIds) = orderBook
            .getLendOrderIds(_user);

        orderBook.activeLendOrderIds[_user] = activeLendOrderIds;
        activeOrderCount = activeLendOrderIds.length;
        uint256 inactiveOrderCount = inActiveLendOrderIds.length;
        orderIds = new uint48[](inactiveOrderCount);

        for (uint256 i; i < inactiveOrderCount; i++) {
            (uint256 presentValue, uint256 futureValue) = OrderReaderLogic.getLendOrderAmounts(
                orderBook,
                inActiveLendOrderIds[i]
            );

            removedOrderAmount += presentValue;
            removedFutureValue += futureValue;
            orderIds[i] = inActiveLendOrderIds[i];
        }
    }

    function _cleanBorrowOrders(
        uint8 _orderBookId,
        address _user
    )
        internal
        returns (
            uint48[] memory orderIds,
            uint256 activeOrderCount,
            uint256 removedFutureValue,
            uint256 removedOrderAmount
        )
    {
        OrderBookLib.OrderBook storage orderBook = _getOrderBook(_orderBookId);

        (uint48[] memory activeBorrowOrderIds, uint48[] memory inActiveBorrowOrderIds) = orderBook
            .getBorrowOrderIds(_user);

        orderBook.activeBorrowOrderIds[_user] = activeBorrowOrderIds;
        activeOrderCount = activeBorrowOrderIds.length;
        uint256 inactiveOrderCount = inActiveBorrowOrderIds.length;
        orderIds = new uint48[](inactiveOrderCount);

        for (uint256 i; i < inactiveOrderCount; i++) {
            (uint256 presentValue, uint256 futureValue, ) = OrderReaderLogic.getBorrowOrderAmounts(
                orderBook,
                inActiveBorrowOrderIds[i]
            );

            removedOrderAmount += presentValue;
            removedFutureValue += futureValue;
            orderIds[i] = inActiveBorrowOrderIds[i];
        }
    }

    /**
     * @notice Fills orders in the order book.
     * @param _side Order position type, Borrow or Lend
     * @param _user User's address
     * @param _amount Amount of funds the maker wants to borrow/lend
     * @param _unitPrice Unit price taken
     * @param _ignoreRemainingAmount Boolean for whether to ignore the remaining amount after filling orders
     */
    function _fillOrders(
        uint8 _orderBookId,
        ProtocolTypes.Side _side,
        address _user,
        uint256 _amount,
        uint256 _unitPrice,
        bool _ignoreRemainingAmount
    )
        private
        returns (
            FilledOrder memory filledOrder,
            PartiallyFilledOrder memory partiallyFilledOrder,
            PlacedOrder memory placedOrder,
            bool isCircuitBreakerTriggered
        )
    {
        OrderBookLib.OrderBook storage orderBook = _getOrderBook(_orderBookId);
        FillOrdersVars memory vars;
        vars.orderBookId = _orderBookId;

        (filledOrder, partiallyFilledOrder, vars.remainingAmount, vars.orderExists) = orderBook
            .fillOrders(_side, _amount, 0, _unitPrice);
        filledOrder.amount = _amount - vars.remainingAmount;

        if (vars.remainingAmount > 0) {
            if (_ignoreRemainingAmount) {
                filledOrder.ignoredAmount = vars.remainingAmount;
            } else {
                // Place a new order with the remaining amount of the input amount
                placedOrder = PlacedOrder(
                    orderBook.placeOrder(_side, _user, vars.remainingAmount, _unitPrice),
                    vars.remainingAmount,
                    _unitPrice
                );
            }
        }

        isCircuitBreakerTriggered =
            vars.orderExists &&
            _ignoreRemainingAmount &&
            _amount != filledOrder.amount;
    }

    function _unwindPosition(
        uint8 _orderBookId,
        ProtocolTypes.Side _side,
        uint256 _futureValue,
        uint256 _unitPrice
    )
        private
        returns (
            FilledOrder memory filledOrder,
            PartiallyFilledOrder memory partiallyFilledOrder,
            bool isCircuitBreakerTriggered
        )
    {
        bool orderExists;
        uint256 futureValueWithFee;
        OrderBookLib.OrderBook storage orderBook = _getOrderBook(_orderBookId);
        uint256 maturity = orderBook.maturity;
        uint256 currentMaturity = maturity >= block.timestamp ? maturity - block.timestamp : 0;

        if (_side == ProtocolTypes.Side.BORROW) {
            // To unwind all positions, calculate the future value taking into account
            // the added portion of the fee.
            // NOTE: The formula is:
            // actualRate = feeRate * (currentMaturity / SECONDS_IN_YEAR)
            // amount = totalAmountInFV / (1 + actualRate)
            futureValueWithFee = (_futureValue * Constants.SECONDS_IN_YEAR * Constants.PCT_DIGIT)
                .div(
                    Constants.SECONDS_IN_YEAR *
                        Constants.PCT_DIGIT +
                        (Storage.slot().orderFeeRate * currentMaturity)
                );
        } else {
            // To unwind all positions, calculate the future value taking into account
            // the subtracted portion of the fee.
            // NOTE: The formula is:
            // actualRate = feeRate * (currentMaturity / SECONDS_IN_YEAR)
            // amount = totalAmountInFV / (1 - actualRate)
            futureValueWithFee = (_futureValue * Constants.SECONDS_IN_YEAR * Constants.PCT_DIGIT)
                .div(
                    Constants.SECONDS_IN_YEAR *
                        Constants.PCT_DIGIT -
                        (Storage.slot().orderFeeRate * currentMaturity)
                );
        }

        (filledOrder, partiallyFilledOrder, , orderExists) = orderBook.fillOrders(
            _side,
            0,
            futureValueWithFee,
            _unitPrice
        );

        isCircuitBreakerTriggered = orderExists && _futureValue != filledOrder.futureValue;
    }

    function _getOrderBook(
        uint8 _orderBookId
    ) private view returns (OrderBookLib.OrderBook storage) {
        return Storage.slot().orderBooks[_orderBookId];
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import {Constants} from "../Constants.sol";
import {OrderBookLib, FilledOrder, PartiallyFilledOrder} from "../OrderBookLib.sol";
import {ProtocolTypes} from "../../types/ProtocolTypes.sol";
import {LendingMarketStorage as Storage, ItayoseLog} from "../../storages/LendingMarketStorage.sol";
import {RoundingUint256} from "../math/RoundingUint256.sol";
import {ILendingMarket} from "../../interfaces/ILendingMarket.sol";

library OrderBookLogic {
    using OrderBookLib for OrderBookLib.OrderBook;
    using RoundingUint256 for uint256;

    error InvalidOrderFeeRate();
    error InvalidCircuitBreakerLimitRange();
    error OrderBookNotMatured();

    event OrderFeeRateUpdated(bytes32 ccy, uint256 previousRate, uint256 rate);
    event CircuitBreakerLimitRangeUpdated(bytes32 ccy, uint256 previousRate, uint256 rate);
    event OrderBookCreated(uint8 orderBookId, uint256 maturity, uint256 openingDate);

    event ItayoseExecuted(
        bytes32 ccy,
        uint256 maturity,
        uint256 openingUnitPrice,
        uint256 lastLendUnitPrice,
        uint256 lastBorrowUnitPrice,
        uint256 offsetAmount
    );

    function isReady(uint8 _orderBookId) public view returns (bool) {
        return Storage.slot().isReady[_getOrderBook(_orderBookId).maturity];
    }

    function isMatured(uint8 _orderBookId) public view returns (bool) {
        return _getOrderBook(_orderBookId).isMatured();
    }

    function isOpened(uint8 _orderBookId) public view returns (bool) {
        return
            isReady(_orderBookId) &&
            !isMatured(_orderBookId) &&
            block.timestamp >= _getOrderBook(_orderBookId).openingDate;
    }

    function isItayosePeriod(uint8 _orderBookId) public view returns (bool) {
        return
            block.timestamp >=
            (_getOrderBook(_orderBookId).openingDate - OrderBookLib.ITAYOSE_PERIOD) &&
            !isReady(_orderBookId);
    }

    function isPreOrderPeriod(uint8 _orderBookId) public view returns (bool) {
        OrderBookLib.OrderBook storage orderBook = _getOrderBook(_orderBookId);
        return
            block.timestamp >= orderBook.preOpeningDate &&
            block.timestamp < (orderBook.openingDate - OrderBookLib.ITAYOSE_PERIOD);
    }

    function getOrderBookDetail(
        uint8 _orderBookId
    )
        public
        view
        returns (bytes32 ccy, uint256 maturity, uint256 openingDate, uint256 preOpeningDate)
    {
        OrderBookLib.OrderBook storage orderBook = _getOrderBook(_orderBookId);

        ccy = Storage.slot().ccy;
        maturity = orderBook.maturity;
        openingDate = orderBook.openingDate;
        preOpeningDate = orderBook.preOpeningDate;
    }

    function getLastOrderBlockNumber(uint8 _orderBookId) external view returns (uint256) {
        return _getOrderBook(_orderBookId).lastOrderBlockNumber;
    }

    function getBlockUnitPriceHistory(uint8 _orderBookId) external view returns (uint256[] memory) {
        return _getOrderBook(_orderBookId).getBlockUnitPriceHistory(true);
    }

    function getMarketUnitPrice(uint8 _orderBookId) external view returns (uint256) {
        return _getOrderBook(_orderBookId).getMarketUnitPrice(true);
    }

    function getBlockUnitPriceAverage(
        uint8 _orderBookId,
        uint256 _count
    ) external view returns (uint256) {
        return _getOrderBook(_orderBookId).getBlockUnitPriceAverage(_count, true);
    }

    function getCircuitBreakerThresholds(
        uint8 _orderBookId
    ) external view returns (uint256 maxLendUnitPrice, uint256 minBorrowUnitPrice) {
        maxLendUnitPrice = _getOrderBook(_orderBookId).getLendCircuitBreakerThreshold(
            Storage.slot().circuitBreakerLimitRange,
            true
        );
        minBorrowUnitPrice = _getOrderBook(_orderBookId).getBorrowCircuitBreakerThreshold(
            Storage.slot().circuitBreakerLimitRange,
            true
        );
    }

    function getBestLendUnitPrice(uint8 _orderBookId) public view returns (uint256) {
        return _getOrderBook(_orderBookId).getBestLendUnitPrice();
    }

    function getBestLendUnitPrices(
        uint8[] memory _orderBookIds
    ) external view returns (uint256[] memory unitPrices) {
        unitPrices = new uint256[](_orderBookIds.length);

        for (uint256 i; i < _orderBookIds.length; i++) {
            unitPrices[i] = _getOrderBook(_orderBookIds[i]).getBestLendUnitPrice();
        }
    }

    function getBestBorrowUnitPrice(uint8 _orderBookId) public view returns (uint256) {
        return _getOrderBook(_orderBookId).getBestBorrowUnitPrice();
    }

    function getBestBorrowUnitPrices(
        uint8[] memory _orderBookIds
    ) external view returns (uint256[] memory unitPrices) {
        unitPrices = new uint256[](_orderBookIds.length);

        for (uint256 i; i < _orderBookIds.length; i++) {
            unitPrices[i] = _getOrderBook(_orderBookIds[i]).getBestBorrowUnitPrice();
        }
    }

    function getBorrowOrderBook(
        uint8 _orderBookId,
        uint256 _limit
    )
        external
        view
        returns (uint256[] memory unitPrices, uint256[] memory amounts, uint256[] memory quantities)
    {
        return _getOrderBook(_orderBookId).getBorrowOrderBook(_limit);
    }

    function getLendOrderBook(
        uint8 _orderBookId,
        uint256 _limit
    )
        external
        view
        returns (uint256[] memory unitPrices, uint256[] memory amounts, uint256[] memory quantities)
    {
        return _getOrderBook(_orderBookId).getLendOrderBook(_limit);
    }

    function getItayoseEstimation(
        uint8 _orderBookId
    )
        external
        view
        returns (
            uint256 openingUnitPrice,
            uint256 lastLendUnitPrice,
            uint256 lastBorrowUnitPrice,
            uint256 totalOffsetAmount
        )
    {
        return _getOrderBook(_orderBookId).calculateItayoseResult();
    }

    function getMaturities(
        uint8[] memory _orderBookIds
    ) public view returns (uint256[] memory maturities) {
        maturities = new uint256[](_orderBookIds.length);

        for (uint256 i; i < _orderBookIds.length; i++) {
            maturities[i] = _getOrderBook(_orderBookIds[i]).maturity;
        }
    }

    function updateOrderFeeRate(uint256 _orderFeeRate) external {
        if (_orderFeeRate >= Constants.PCT_DIGIT) revert InvalidOrderFeeRate();

        uint256 previousRate = Storage.slot().orderFeeRate;

        if (_orderFeeRate != previousRate) {
            Storage.slot().orderFeeRate = _orderFeeRate;

            emit OrderFeeRateUpdated(Storage.slot().ccy, previousRate, _orderFeeRate);
        }
    }

    function updateCircuitBreakerLimitRange(uint256 _cbLimitRange) external {
        if (_cbLimitRange >= Constants.PCT_DIGIT) revert InvalidCircuitBreakerLimitRange();

        uint256 previousRange = Storage.slot().circuitBreakerLimitRange;

        if (_cbLimitRange != previousRange) {
            Storage.slot().circuitBreakerLimitRange = _cbLimitRange;

            emit CircuitBreakerLimitRangeUpdated(Storage.slot().ccy, previousRange, _cbLimitRange);
        }
    }

    function createOrderBook(
        uint256 _maturity,
        uint256 _openingDate,
        uint256 _preOpeningDate
    ) public returns (uint8 orderBookId) {
        orderBookId = _nextOrderBookId();

        Storage.slot().isReady[_maturity] = _getOrderBook(orderBookId).initialize(
            _maturity,
            _openingDate,
            _preOpeningDate
        );

        emit OrderBookCreated(orderBookId, _maturity, _openingDate);
    }

    function executeAutoRoll(
        uint8 _maturedOrderBookId,
        uint8 _destinationOrderBookId,
        uint256 _newMaturity,
        uint256 _openingDate,
        uint256 _autoRollUnitPrice
    ) external {
        OrderBookLib.OrderBook storage maturedOrderBook = Storage.slot().orderBooks[
            _maturedOrderBookId
        ];
        if (!maturedOrderBook.isMatured()) revert OrderBookNotMatured();

        Storage.slot().isReady[_newMaturity] = maturedOrderBook.initialize(
            _newMaturity,
            _openingDate,
            _openingDate - OrderBookLib.PRE_ORDER_BASE_PERIOD
        );

        OrderBookLib.OrderBook storage destinationOrderBook = Storage.slot().orderBooks[
            _destinationOrderBookId
        ];

        // NOTE: The auto-roll destination order book has no market unit price if the order has never been filled before.
        // In this case, the market unit price is updated with the unit price of the auto-roll.
        if (destinationOrderBook.getMarketUnitPrice(false) == 0) {
            destinationOrderBook.setInitialBlockUnitPrice(_autoRollUnitPrice);
        }
    }

    function executeItayoseCall(
        uint8 _orderBookId
    )
        external
        returns (
            uint256 openingUnitPrice,
            uint256 totalOffsetAmount,
            uint256 openingDate,
            PartiallyFilledOrder memory partiallyFilledLendingOrder,
            PartiallyFilledOrder memory partiallyFilledBorrowingOrder
        )
    {
        uint256 lastLendUnitPrice;
        uint256 lastBorrowUnitPrice;
        OrderBookLib.OrderBook storage orderBook = _getOrderBook(_orderBookId);

        (openingUnitPrice, lastLendUnitPrice, lastBorrowUnitPrice, totalOffsetAmount) = orderBook
            .calculateItayoseResult();

        if (totalOffsetAmount > 0) {
            ProtocolTypes.Side[2] memory sides = [
                ProtocolTypes.Side.LEND,
                ProtocolTypes.Side.BORROW
            ];

            for (uint256 i; i < sides.length; i++) {
                ProtocolTypes.Side partiallyFilledOrderSide;
                PartiallyFilledOrder memory partiallyFilledOrder;
                FilledOrder memory filledOrder;
                (filledOrder, partiallyFilledOrder, , ) = orderBook.fillOrders(
                    sides[i],
                    totalOffsetAmount,
                    0,
                    0
                );

                if (filledOrder.futureValue > 0) {
                    orderBook.setInitialBlockUnitPrice(openingUnitPrice);
                }

                if (partiallyFilledOrder.futureValue > 0) {
                    if (sides[i] == ProtocolTypes.Side.LEND) {
                        partiallyFilledOrderSide = ProtocolTypes.Side.BORROW;
                        partiallyFilledBorrowingOrder = partiallyFilledOrder;
                    } else {
                        partiallyFilledOrderSide = ProtocolTypes.Side.LEND;
                        partiallyFilledLendingOrder = partiallyFilledOrder;
                    }
                }
            }

            emit ItayoseExecuted(
                Storage.slot().ccy,
                orderBook.maturity,
                openingUnitPrice,
                lastLendUnitPrice,
                lastBorrowUnitPrice,
                totalOffsetAmount
            );
        }

        Storage.slot().isReady[orderBook.maturity] = true;
        Storage.slot().itayoseLogs[orderBook.maturity] = ItayoseLog(
            openingUnitPrice,
            lastLendUnitPrice,
            lastBorrowUnitPrice
        );
        openingDate = orderBook.openingDate;
    }

    function _nextOrderBookId() internal returns (uint8) {
        Storage.slot().lastOrderBookId++;
        return Storage.slot().lastOrderBookId;
    }

    function _getOrderBook(
        uint8 _orderBookId
    ) private view returns (OrderBookLib.OrderBook storage) {
        return Storage.slot().orderBooks[_orderBookId];
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import {Constants} from "../Constants.sol";
import {OrderBookLib, PlacedOrder} from "../OrderBookLib.sol";
import {ProtocolTypes} from "../../types/ProtocolTypes.sol";
import {LendingMarketStorage as Storage, ItayoseLog} from "../../storages/LendingMarketStorage.sol";
import {OrderStatisticsTreeLib} from "../OrderStatisticsTreeLib.sol";
import {RoundingUint256} from "../math/RoundingUint256.sol";

library OrderReaderLogic {
    using OrderBookLib for OrderBookLib.OrderBook;
    using OrderStatisticsTreeLib for OrderStatisticsTreeLib.Tree;
    using RoundingUint256 for uint256;

    function getOrder(
        uint8 _orderBookId,
        uint48 _orderId
    )
        external
        view
        returns (
            ProtocolTypes.Side side,
            uint256 unitPrice,
            uint256 maturity,
            address maker,
            uint256 amount,
            uint256 timestamp,
            bool isPreOrder
        )
    {
        OrderBookLib.OrderBook storage orderBook = _getOrderBook(_orderBookId);
        PlacedOrder memory order = orderBook.getOrder(_orderId);

        if (order.side == ProtocolTypes.Side.LEND) {
            (maker, amount) = orderBook.lendOrders[order.maturity].getOrderById(
                order.unitPrice,
                _orderId
            );
        } else {
            (maker, amount) = orderBook.borrowOrders[order.maturity].getOrderById(
                order.unitPrice,
                _orderId
            );
        }

        if (maker != address(0)) {
            side = order.side;
            maturity = order.maturity;
            timestamp = order.timestamp;
            isPreOrder = orderBook.isPreOrder[_orderId];
            unitPrice = _getOrderUnitPrice(side, maturity, order.unitPrice, isPreOrder);
        }
    }

    function getTotalAmountFromLendOrders(
        uint8 _orderBookId,
        address _user
    )
        external
        view
        returns (
            uint256 activeAmount,
            uint256 inactiveAmount,
            uint256 inactiveFutureValue,
            uint256 maturity
        )
    {
        OrderBookLib.OrderBook storage orderBook = _getOrderBook(_orderBookId);

        (uint48[] memory activeOrderIds, uint48[] memory inActiveOrderIds) = orderBook
            .getLendOrderIds(_user);
        maturity = orderBook.userCurrentMaturities[_user];

        for (uint256 i; i < activeOrderIds.length; ) {
            PlacedOrder memory order = orderBook.getOrder(activeOrderIds[i]);
            // Sum future values in the current maturity.
            // If the market is rotated and maturity is updated, it will be 0 by treating it
            // as an order canceled in the past market.
            (, uint256 orderAmount) = orderBook.lendOrders[orderBook.maturity].getOrderById(
                order.unitPrice,
                activeOrderIds[i]
            );
            activeAmount += orderAmount;

            unchecked {
                i++;
            }
        }

        for (uint256 i; i < inActiveOrderIds.length; ) {
            // Sum future values in the maturity of orders.
            // It will be the future value when the order is created, even if the market is rotated
            // and maturity is updated.
            (uint256 presentValue, uint256 futureValue) = getLendOrderAmounts(
                orderBook,
                inActiveOrderIds[i]
            );
            inactiveAmount += presentValue;
            inactiveFutureValue += futureValue;

            unchecked {
                i++;
            }
        }
    }

    function getTotalAmountFromBorrowOrders(
        uint8 _orderBookId,
        address _user,
        uint256 _minUnitPrice
    )
        external
        view
        returns (
            uint256 activeAmount,
            uint256 inactiveAmount,
            uint256 inactiveFutureValue,
            uint256 maturity
        )
    {
        OrderBookLib.OrderBook storage orderBook = _getOrderBook(_orderBookId);

        (uint48[] memory activeOrderIds, uint48[] memory inActiveOrderIds) = orderBook
            .getBorrowOrderIds(_user);
        maturity = orderBook.userCurrentMaturities[_user];

        for (uint256 i; i < activeOrderIds.length; ) {
            // Sum future values in the current maturity.
            // If the market is rotated and maturity is updated, it will be 0 by treating it
            // as an order canceled in the past market.
            (uint256 presentValue, uint256 futureValue, uint256 unitPrice) = getBorrowOrderAmounts(
                orderBook,
                activeOrderIds[i]
            );

            // activeAmount += presentValue;
            activeAmount += unitPrice >= _minUnitPrice
                ? presentValue
                : (futureValue * _minUnitPrice).div(Constants.PRICE_DIGIT);

            unchecked {
                i++;
            }
        }

        maturity = orderBook.userCurrentMaturities[_user];

        for (uint256 i; i < inActiveOrderIds.length; ) {
            // Sum future values in the maturity of orders
            // It will be the future value when the order is created, even if the market is rotated
            // and maturity is updated.
            (uint256 presentValue, uint256 futureValue, ) = getBorrowOrderAmounts(
                orderBook,
                inActiveOrderIds[i]
            );

            inactiveAmount += presentValue;
            inactiveFutureValue += futureValue;
            // inactiveAmount += unitPrice >= _minUnitPrice
            //     ? presentValue
            //     : (futureValue * _minUnitPrice).div(Constants.PRICE_DIGIT);

            unchecked {
                i++;
            }
        }
    }

    function getLendOrderIds(
        uint8 _orderBookId,
        address _user
    ) external view returns (uint48[] memory activeOrderIds, uint48[] memory inActiveOrderIds) {
        (activeOrderIds, inActiveOrderIds) = _getOrderBook(_orderBookId).getLendOrderIds(_user);
    }

    function getBorrowOrderIds(
        uint8 _orderBookId,
        address _user
    ) external view returns (uint48[] memory activeOrderIds, uint48[] memory inActiveOrderIds) {
        (activeOrderIds, inActiveOrderIds) = _getOrderBook(_orderBookId).getBorrowOrderIds(_user);
    }

    function calculateFilledAmount(
        uint8 _orderBookId,
        ProtocolTypes.Side _side,
        uint256 _amount,
        uint256 _unitPrice
    )
        external
        view
        returns (
            uint256 lastUnitPrice,
            uint256 filledAmount,
            uint256 filledAmountInFV,
            uint256 orderFeeInFV,
            uint256 placedAmount
        )
    {
        OrderBookLib.OrderBook storage orderBook = _getOrderBook(_orderBookId);

        (bool isFilled, uint256 executedUnitPrice, bool ignoreRemainingAmount, ) = orderBook
            .getOrderExecutionConditions(
                _side,
                _unitPrice,
                Storage.slot().circuitBreakerLimitRange,
                true
            );

        if (isFilled) {
            (lastUnitPrice, filledAmount, filledAmountInFV) = orderBook.calculateFilledAmount(
                _side,
                _amount,
                executedUnitPrice
            );
            placedAmount = _amount - filledAmount;
            orderFeeInFV = calculateOrderFeeAmount(orderBook.maturity, filledAmountInFV);
        } else {
            if (!ignoreRemainingAmount) {
                placedAmount = _amount;
            }
        }
    }

    function calculateOrderFeeAmount(
        uint256 _maturity,
        uint256 _amount
    ) public view returns (uint256 orderFeeAmount) {
        if (block.timestamp >= _maturity) return 0;

        uint256 currentMaturity = _maturity - block.timestamp;

        // NOTE: The formula is:
        // actualRate = feeRate * (currentMaturity / SECONDS_IN_YEAR)
        // orderFeeAmount = amount * actualRate
        orderFeeAmount = (Storage.slot().orderFeeRate * currentMaturity * _amount).div(
            Constants.SECONDS_IN_YEAR * Constants.PCT_DIGIT
        );
    }

    function getLendOrderAmounts(
        OrderBookLib.OrderBook storage orderBook,
        uint48 _orderId
    ) public view returns (uint256 presentValue, uint256 futureValue) {
        PlacedOrder memory order = orderBook.getOrder(_orderId);
        (, uint256 orderAmount) = orderBook.lendOrders[order.maturity].getOrderById(
            order.unitPrice,
            _orderId
        );

        uint256 unitPrice = _getOrderUnitPrice(
            order.side,
            order.maturity,
            order.unitPrice,
            orderBook.isPreOrder[_orderId]
        );

        presentValue = orderAmount;
        futureValue = (orderAmount * Constants.PRICE_DIGIT).div(unitPrice);
    }

    function getBorrowOrderAmounts(
        OrderBookLib.OrderBook storage orderBook,
        uint48 _orderId
    ) public view returns (uint256 presentValue, uint256 futureValue, uint256 unitPrice) {
        PlacedOrder memory order = orderBook.getOrder(_orderId);
        (, uint256 orderAmount) = orderBook.borrowOrders[order.maturity].getOrderById(
            order.unitPrice,
            _orderId
        );
        unitPrice = _getOrderUnitPrice(
            order.side,
            order.maturity,
            order.unitPrice,
            orderBook.isPreOrder[_orderId]
        );

        presentValue = orderAmount;
        futureValue = (orderAmount * Constants.PRICE_DIGIT).div(unitPrice);
    }

    function _getOrderUnitPrice(
        ProtocolTypes.Side _side,
        uint256 _maturity,
        uint256 _unitPrice,
        bool _isPreOrder
    ) private view returns (uint256) {
        if (!_isPreOrder) return _unitPrice;
        ItayoseLog memory itayoseLog = Storage.slot().itayoseLogs[_maturity];
        if (
            itayoseLog.openingUnitPrice != 0 &&
            ((_side == ProtocolTypes.Side.BORROW && _unitPrice <= itayoseLog.lastBorrowUnitPrice) ||
                (_side == ProtocolTypes.Side.LEND && _unitPrice >= itayoseLog.lastLendUnitPrice))
        ) {
            return itayoseLog.openingUnitPrice;
        } else {
            return _unitPrice;
        }
    }

    function _getOrderBook(
        uint8 _orderBookId
    ) private view returns (OrderBookLib.OrderBook storage) {
        return Storage.slot().orderBooks[_orderBookId];
    }
}

