contracts/LendingMarket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import {Pausable} from "@openzeppelin/contracts/security/Pausable.sol";
// interfaces
import {ILendingMarket} from "./interfaces/ILendingMarket.sol";
// libraries
import {Contracts} from "./libraries/Contracts.sol";
import {OrderBookLogic} from "./libraries/OrderBookLogic.sol";
// mixins
import {MixinAddressResolver} from "./mixins/MixinAddressResolver.sol";
// types
import {ProtocolTypes} from "./types/ProtocolTypes.sol";
// utils
import {Proxyable} from "./utils/Proxyable.sol";
// storages
import {LendingMarketStorage as Storage, RemainingOrder} from "./storages/LendingMarketStorage.sol";

/**
 * @notice Implements the module that allows lending market participants to create/cancel market orders,
 * and also provides a future value calculation module.
 *
 * For updates, this contract is basically called from `LendingMarketController.sol`instead of being called \
 * directly by the user.
 *
 * @dev The market orders is stored in structured red-black trees and doubly linked lists in each node.
 */
contract LendingMarket is ILendingMarket, MixinAddressResolver, Pausable, Proxyable {
    /**
     * @notice Modifier to make a function callable only by order maker.
     * @param _orderId Market order id
     */
    modifier onlyMaker(address user, uint48 _orderId) {
        (, , , address maker, , ) = getOrder(_orderId);
        require(maker != address(0), "Order not found");
        require(user == maker, "Caller is not the maker");
        _;
    }

    /**
     * @notice Modifier to check if the market is opened.
     */
    modifier ifOpened() {
        require(isOpened(), "Market is not opened");
        _;
    }

    /**
     * @notice Modifier to check if the market is matured.
     */
    modifier ifMatured() {
        require(isMatured(), "Market is not matured");
        _;
    }

    /**
     * @notice Initializes the contract.
     * @dev Function is invoked by the proxy contract when the contract is added to the ProxyController.
     * @param _resolver The address of the Address Resolver contract
     * @param _ccy The main currency for the order book
     * @param _maturity The initial maturity of the market
     * @param _genesisDate The initial date when the first market open
     */
    function initialize(
        address _resolver,
        bytes32 _ccy,
        uint256 _maturity,
        uint256 _genesisDate
    ) public initializer onlyBeacon {
        registerAddressResolver(_resolver);

        Storage.slot().ccy = _ccy;
        Storage.slot().maturity = _maturity;
        Storage.slot().genesisDate = _genesisDate;

        buildCache();
    }

    // @inheritdoc MixinAddressResolver
    function requiredContracts() public pure override returns (bytes32[] memory contracts) {
        contracts = new bytes32[](1);
        contracts[0] = Contracts.LENDING_MARKET_CONTROLLER;
    }

    // @inheritdoc MixinAddressResolver
    function acceptedContracts() public pure override returns (bytes32[] memory contracts) {
        contracts = new bytes32[](1);
        contracts[0] = Contracts.LENDING_MARKET_CONTROLLER;
    }

    /**
     * @notice Gets the market data.
     * @return market The market data
     */
    function getMarket() external view override returns (Market memory market) {
        return
            Market({
                ccy: Storage.slot().ccy,
                maturity: Storage.slot().maturity,
                genesisDate: Storage.slot().genesisDate,
                borrowUnitPrice: getBorrowUnitPrice(),
                lendUnitPrice: getLendUnitPrice(),
                midUnitPrice: getMidUnitPrice()
            });
    }

    /**
     * @notice Gets the highest borrow price per future value.
     * @return The highest borrow price per future value
     */
    function getBorrowUnitPrice() public view override returns (uint256) {
        return OrderBookLogic.getHighestBorrowUnitPrice();
    }

    /**
     * @notice Gets the lowest lend price per future value.
     * @return The lowest lend price per future value
     */
    function getLendUnitPrice() public view override returns (uint256) {
        return OrderBookLogic.getLowestLendUnitPrice();
    }

    /**
     * @notice Gets the mid price per future value.
     * @return The mid price per future value
     */
    function getMidUnitPrice() public view override returns (uint256) {
        uint256 borrowUnitPrice = getBorrowUnitPrice();
        uint256 lendUnitPrice = getLendUnitPrice();
        return (borrowUnitPrice + lendUnitPrice) / 2;
    }

    /**
     * @notice Gets the order book of borrow.
     * @param _limit Max limit to get unit prices
     * @return unitPrices The array of borrow unit prices
     */
    function getBorrowOrderBook(uint256 _limit)
        external
        view
        override
        returns (
            uint256[] memory unitPrices,
            uint256[] memory amounts,
            uint256[] memory quantities
        )
    {
        return OrderBookLogic.getBorrowOrderBook(_limit);
    }

    /**
     * @notice Gets the order book of lend.
     * @param _limit Max limit to get unit prices
     * @return unitPrices The array of lending unit prices
     */
    function getLendOrderBook(uint256 _limit)
        external
        view
        override
        returns (
            uint256[] memory unitPrices,
            uint256[] memory amounts,
            uint256[] memory quantities
        )
    {
        return OrderBookLogic.getLendOrderBook(_limit);
    }

    /**
     * @notice Gets the current market maturity.
     * @return maturity The market maturity
     */
    function getMaturity() external view override returns (uint256 maturity) {
        return Storage.slot().maturity;
    }

    /**
     * @notice Gets the market currency.
     * @return currency The market currency
     */
    function getCurrency() external view override returns (bytes32 currency) {
        return Storage.slot().ccy;
    }

    /**
     * @notice Gets if the market is matured.
     * @return The boolean if the market is matured or not
     */
    function isMatured() public view override returns (bool) {
        return block.timestamp >= Storage.slot().maturity;
    }

    /**
     * @notice Gets if the market is opened.
     * @return The boolean if the market is opened or not
     */
    function isOpened() public view override returns (bool) {
        return !isMatured() && block.timestamp >= Storage.slot().genesisDate;
    }

    /**
     * @notice Gets the market order from the order book.
     * @param _orderId The market order id
     * @return side Order position type, Borrow or Lend
     * @return unitPrice Amount of interest unit price
     * @return maturity The maturity of the selected order
     * @return maker The order maker
     * @return amount Order amount
     * @return timestamp Timestamp when the order was created
     */
    function getOrder(uint48 _orderId)
        public
        view
        override
        returns (
            ProtocolTypes.Side side,
            uint256 unitPrice,
            uint256 maturity,
            address maker,
            uint256 amount,
            uint256 timestamp
        )
    {
        return OrderBookLogic.getOrder(_orderId);
    }

    /**
     * @notice Calculates and gets the active and inactive amounts from the user orders of lending deals.
     * @param _user User's address
     * @return activeAmount The total amount of active order on the order book
     * @return inactiveAmount The total amount of inactive orders filled on the order book
     * @return inactiveFutureValue The total future value amount of inactive orders filled on the order book
     * @return maturity The maturity of market that orders were placed.
     */
    function getTotalAmountFromLendOrders(address _user)
        external
        view
        override
        returns (
            uint256 activeAmount,
            uint256 inactiveAmount,
            uint256 inactiveFutureValue,
            uint256 maturity
        )
    {
        return OrderBookLogic.getTotalAmountFromLendOrders(_user);
    }

    /**
     * @notice Calculates and gets the active and inactive amounts from the user orders of borrowing deals.
     * @param _user User's address
     * @return activeAmount The total amount of active order on the order book
     * @return inactiveAmount The total amount of inactive orders filled on the order book
     * @return inactiveFutureValue The total future value amount of inactive orders filled on the order book
     * @return maturity The maturity of market that orders were placed.
     */
    function getTotalAmountFromBorrowOrders(address _user)
        external
        view
        override
        returns (
            uint256 activeAmount,
            uint256 inactiveAmount,
            uint256 inactiveFutureValue,
            uint256 maturity
        )
    {
        return OrderBookLogic.getTotalAmountFromBorrowOrders(_user);
    }

    /**
     * @notice Gets the order ids of active lending order on the order book
     * @param _user User's address
     */
    function getActiveLendOrderIds(address _user)
        external
        view
        override
        returns (uint48[] memory activeOrderIds)
    {
        (activeOrderIds, ) = OrderBookLogic.getActiveLendOrderIds(_user);
    }

    /**
     * @notice Gets the order ids of active borrowing order on the order book
     * @param _user User's address
     */
    function getActiveBorrowOrderIds(address _user)
        external
        view
        override
        returns (uint48[] memory activeOrderIds)
    {
        (activeOrderIds, ) = OrderBookLogic.getActiveBorrowOrderIds(_user);
    }

    /**
     * @notice Estimates the filled amount at the time of order creation on the order book
     * using the future value amount.
     * @param _side Order position type, Borrow or Lend
     * @param _futureValue Future value amount
     * @return amount The estimated amount in the present value that is filled on the order book
     */
    function estimateFilledAmount(ProtocolTypes.Side _side, uint256 _futureValue)
        external
        view
        override
        returns (uint256 amount)
    {
        return OrderBookLogic.estimateFilledAmount(_side, _futureValue);
    }

    /**
     * @notice Opens market
     * @param _maturity The new maturity
     * @return prevMaturity The previous maturity updated
     */
    function openMarket(uint256 _maturity)
        external
        override
        ifMatured
        onlyAcceptedContracts
        returns (uint256 prevMaturity)
    {
        prevMaturity = Storage.slot().maturity;
        Storage.slot().maturity = _maturity;

        emit OpenMarket(_maturity, prevMaturity);
    }

    /**
     * @notice Cancels the order.
     * @param _user User address
     * @param _orderId Market order id
     * @return side The canceled order position type
     * @return removedAmount The removed order amount from the order book by canceling
     * @return unitPrice The canceled order unit price
     */
    function cancelOrder(address _user, uint48 _orderId)
        external
        override
        onlyMaker(_user, _orderId)
        whenNotPaused
        onlyAcceptedContracts
        returns (
            ProtocolTypes.Side side,
            uint256 removedAmount,
            uint256 unitPrice
        )
    {
        (side, removedAmount, unitPrice) = OrderBookLogic.removeOrder(_user, _orderId);

        emit ILendingMarket.CancelOrder(
            _orderId,
            msg.sender,
            side,
            Storage.slot().ccy,
            Storage.slot().maturity,
            removedAmount,
            unitPrice
        );
    }

    /**
     * @notice Cleans own orders to remove order ids that are already filled on the order book.
     * @dev The order list per user is not updated in real-time when an order is filled.
     * This function removes the filled order from that order list per user to reduce gas costs
     * for calculating if the collateral is enough or not.
     *
     * @param _user User address
     * @return activeLendOrderCount The total amount of active lend order on the order book
     * @return activeBorrowOrderCount The total amount of active borrow order on the order book
     * @return removedLendOrderFutureValue The total FV amount of the removed lend order amount from the order book
     * @return removedBorrowOrderFutureValue The total FV amount of the removed borrow order amount from the order book
     * @return removedLendOrderAmount The total PV amount of the removed lend order amount from the order book
     * @return removedBorrowOrderAmount The total PV amount of the removed borrow order amount from the order book
     * @return maturity The maturity of the removed orders
     */
    function cleanOrders(address _user)
        external
        override
        returns (
            uint256 activeLendOrderCount,
            uint256 activeBorrowOrderCount,
            uint256 removedLendOrderFutureValue,
            uint256 removedBorrowOrderFutureValue,
            uint256 removedLendOrderAmount,
            uint256 removedBorrowOrderAmount,
            uint256 maturity
        )
    {
        maturity = Storage.slot().userCurrentMaturities[_user];

        uint48[] memory lendOrderIds;
        uint48[] memory borrowOrderIds;

        (
            lendOrderIds,
            activeLendOrderCount,
            removedLendOrderFutureValue,
            removedLendOrderAmount
        ) = OrderBookLogic.cleanLendOrders(_user, maturity);

        (
            borrowOrderIds,
            activeBorrowOrderCount,
            removedBorrowOrderFutureValue,
            removedBorrowOrderAmount
        ) = OrderBookLogic.cleanBorrowOrders(_user, maturity);

        if (removedLendOrderAmount > 0) {
            emit CleanOrders(
                lendOrderIds,
                _user,
                ProtocolTypes.Side.LEND,
                Storage.slot().ccy,
                Storage.slot().maturity
            );
        }

        if (removedBorrowOrderAmount > 0) {
            emit CleanOrders(
                borrowOrderIds,
                _user,
                ProtocolTypes.Side.BORROW,
                Storage.slot().ccy,
                Storage.slot().maturity
            );
        }
    }

    /**
     * @notice Creates the order. Takes the order if the order is matched,
     * and places new order if not match it.
     * @param _side Order position type, Borrow or Lend
     * @param _user User's address
     * @param _amount Amount of funds the maker wants to borrow/lend
     * @param _unitPrice Amount of unit price taker wish to borrow/lend
     * @param _ignoreRemainingAmount Boolean for whether to ignore the remaining amount after taking orders
     * @return filledFutureValue The total FV amount of the filled order amount on the order book
     * @return remainingAmount The remaining amount that is not filled in the order book
     */
    function createOrder(
        ProtocolTypes.Side _side,
        address _user,
        uint256 _amount,
        uint256 _unitPrice,
        bool _ignoreRemainingAmount
    )
        external
        override
        whenNotPaused
        onlyAcceptedContracts
        ifOpened
        returns (uint256 filledFutureValue, uint256 remainingAmount)
    {
        uint256 userMaturity = Storage.slot().userCurrentMaturities[_user];
        require(_amount > 0, "Can't place empty amount");
        require(
            userMaturity == Storage.slot().maturity ||
                (userMaturity != Storage.slot().maturity &&
                    Storage.slot().activeLendOrderIds[_user].length == 0 &&
                    Storage.slot().activeBorrowOrderIds[_user].length == 0),
            "Order found in past maturity."
        );

        if (userMaturity != Storage.slot().maturity) {
            Storage.slot().userCurrentMaturities[_user] = Storage.slot().maturity;
        }

        bool isExists = _unitPrice == 0 ||
            (
                _side == ProtocolTypes.Side.LEND
                    ? OrderBookLogic.getHighestBorrowUnitPrice() >= _unitPrice
                    : OrderBookLogic.getLowestLendUnitPrice() <= _unitPrice
            );

        if (isExists) {
            (filledFutureValue, remainingAmount) = _takeOrder(
                _side,
                _user,
                _amount,
                _unitPrice,
                _ignoreRemainingAmount
            );
        } else {
            _makeOrder(_side, _user, _amount, _unitPrice, false, 0);
        }
    }

    /**
     * @notice Pauses the lending market.
     */
    function pauseMarket() external override onlyAcceptedContracts {
        _pause();
    }

    /**
     * @notice Unpauses the lending market.
     */
    function unpauseMarket() external override onlyAcceptedContracts {
        _unpause();
    }

    /**
     * @notice Makes new market order.
     * @param _side Order position type, Borrow or Lend
     * @param _user User's address
     * @param _amount Amount of funds the maker wants to borrow/lend
     * @param _unitPrice Preferable interest unit price
     * @param _originalOrderId The original order id that filled partially
     */
    function _makeOrder(
        ProtocolTypes.Side _side,
        address _user,
        uint256 _amount,
        uint256 _unitPrice,
        bool _isInterruption,
        uint48 _originalOrderId
    ) private returns (uint48 orderId) {
        orderId = OrderBookLogic.insertOrder(_side, _user, _amount, _unitPrice, _isInterruption);

        emit ILendingMarket.MakeOrder(
            orderId,
            _originalOrderId,
            _user,
            _side,
            Storage.slot().ccy,
            Storage.slot().maturity,
            _amount,
            _unitPrice
        );
    }

    /**
     * @notice Takes the market order.
     * @param _side Order position type, Borrow or Lend
     * @param _user User's address
     * @param _amount Amount of funds the maker wants to borrow/lend
     * @param _unitPrice Amount of unit price taken
     * @param _ignoreRemainingAmount Boolean for whether to ignore the remaining amount after taking orders
     */
    function _takeOrder(
        ProtocolTypes.Side _side,
        address _user,
        uint256 _amount,
        uint256 _unitPrice,
        bool _ignoreRemainingAmount
    ) private returns (uint256 filledFutureValue, uint256 remainingAmount) {
        RemainingOrder memory remainingOrder;

        (remainingOrder, filledFutureValue, remainingAmount) = OrderBookLogic.dropOrders(
            _side,
            _amount,
            _unitPrice
        );

        emit ILendingMarket.TakeOrders(
            _user,
            _side,
            Storage.slot().ccy,
            Storage.slot().maturity,
            _amount - remainingAmount,
            _unitPrice,
            filledFutureValue
        );

        if (remainingOrder.amount > 0) {
            // Make a new order for the remaining amount of a partially filled order
            _makeOrder(
                _side == ProtocolTypes.Side.BORROW
                    ? ProtocolTypes.Side.LEND
                    : ProtocolTypes.Side.BORROW,
                remainingOrder.maker,
                remainingOrder.amount,
                remainingOrder.unitPrice,
                true,
                remainingOrder.orderId
            );
        }

        if (remainingAmount > 0 && _unitPrice != 0 && !_ignoreRemainingAmount) {
            // Make a new order for the remaining amount of input
            _makeOrder(_side, _user, remainingAmount, _unitPrice, false, 0);
        }
    }
}

contracts/libraries/OrderBookLogic.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import {ILendingMarket} from "../interfaces/ILendingMarket.sol";
import {HitchensOrderStatisticsTreeLib, RemainingOrder, OrderItem} from "../libraries/HitchensOrderStatisticsTreeLib.sol";
import {ProtocolTypes} from "../types/ProtocolTypes.sol";
import {LendingMarketStorage as Storage, MarketOrder} from "../storages/LendingMarketStorage.sol";

library OrderBookLogic {
    using HitchensOrderStatisticsTreeLib for HitchensOrderStatisticsTreeLib.Tree;

    function getHighestBorrowUnitPrice() public view returns (uint256) {
        return Storage.slot().borrowOrders[Storage.slot().maturity].last();
    }

    function getLowestLendUnitPrice() public view returns (uint256) {
        uint256 unitPrice = Storage.slot().lendOrders[Storage.slot().maturity].first();
        return unitPrice == 0 ? ProtocolTypes.PRICE_DIGIT : unitPrice;
    }

    function getBorrowOrderBook(uint256 _limit)
        public
        view
        returns (
            uint256[] memory unitPrices,
            uint256[] memory amounts,
            uint256[] memory quantities
        )
    {
        unitPrices = new uint256[](_limit);
        amounts = new uint256[](_limit);
        quantities = new uint256[](_limit);

        uint256 unitPrice = Storage.slot().borrowOrders[Storage.slot().maturity].last();
        unitPrices[0] = unitPrice;
        amounts[0] = Storage.slot().borrowOrders[Storage.slot().maturity].getNodeTotalAmount(
            unitPrice
        );
        quantities[0] = Storage.slot().borrowOrders[Storage.slot().maturity].getNodeCount(
            unitPrice
        );

        for (uint256 i = 1; i < unitPrices.length; i++) {
            if (unitPrice == 0) {
                break;
            }

            unitPrice = Storage.slot().borrowOrders[Storage.slot().maturity].prev(unitPrice);
            unitPrices[i] = unitPrice;
            amounts[i] = Storage.slot().borrowOrders[Storage.slot().maturity].getNodeTotalAmount(
                unitPrice
            );
            quantities[i] = Storage.slot().borrowOrders[Storage.slot().maturity].getNodeCount(
                unitPrice
            );
        }
    }

    function getLendOrderBook(uint256 _limit)
        public
        view
        returns (
            uint256[] memory unitPrices,
            uint256[] memory amounts,
            uint256[] memory quantities
        )
    {
        unitPrices = new uint256[](_limit);
        amounts = new uint256[](_limit);
        quantities = new uint256[](_limit);

        uint256 unitPrice = Storage.slot().lendOrders[Storage.slot().maturity].first();
        unitPrices[0] = unitPrice;
        amounts[0] = Storage.slot().lendOrders[Storage.slot().maturity].getNodeTotalAmount(
            unitPrice
        );
        quantities[0] = Storage.slot().lendOrders[Storage.slot().maturity].getNodeCount(unitPrice);

        for (uint256 i = 1; i < unitPrices.length; i++) {
            if (unitPrice == 0) {
                break;
            }

            unitPrice = Storage.slot().lendOrders[Storage.slot().maturity].next(unitPrice);
            unitPrices[i] = unitPrice;
            amounts[i] = Storage.slot().lendOrders[Storage.slot().maturity].getNodeTotalAmount(
                unitPrice
            );
            quantities[i] = Storage.slot().lendOrders[Storage.slot().maturity].getNodeCount(
                unitPrice
            );
        }
    }

    function getOrder(uint48 _orderId)
        public
        view
        returns (
            ProtocolTypes.Side side,
            uint256 unitPrice,
            uint256 maturity,
            address maker,
            uint256 amount,
            uint256 timestamp
        )
    {
        MarketOrder memory marketOrder = Storage.slot().orders[_orderId];

        OrderItem memory orderItem;
        if (marketOrder.side == ProtocolTypes.Side.LEND) {
            orderItem = Storage.slot().lendOrders[marketOrder.maturity].getOrderById(
                marketOrder.unitPrice,
                _orderId
            );
        } else {
            orderItem = Storage.slot().borrowOrders[marketOrder.maturity].getOrderById(
                marketOrder.unitPrice,
                _orderId
            );
        }

        if (orderItem.maker != address(0)) {
            return (
                marketOrder.side,
                marketOrder.unitPrice,
                marketOrder.maturity,
                orderItem.maker,
                orderItem.amount,
                orderItem.timestamp
            );
        }
    }

    function getTotalAmountFromLendOrders(address _user)
        public
        view
        returns (
            uint256 activeAmount,
            uint256 inactiveAmount,
            uint256 inactiveFutureValue,
            uint256 maturity
        )
    {
        (uint48[] memory activeOrderIds, uint48[] memory inActiveOrderIds) = getActiveLendOrderIds(
            _user
        );

        maturity = Storage.slot().userCurrentMaturities[_user];

        for (uint256 i = 0; i < activeOrderIds.length; i++) {
            MarketOrder memory marketOrder = Storage.slot().orders[activeOrderIds[i]];
            // Sum future values in the current maturity.
            // If the market is rotated and maturity is updated, it will be 0 by treating it
            // as an order canceled in the past market.
            OrderItem memory orderItem = Storage
                .slot()
                .lendOrders[Storage.slot().maturity]
                .getOrderById(marketOrder.unitPrice, activeOrderIds[i]);
            activeAmount += orderItem.amount;
        }

        for (uint256 i = 0; i < inActiveOrderIds.length; i++) {
            MarketOrder memory marketOrder = Storage.slot().orders[inActiveOrderIds[i]];
            if (maturity == 0) {
                maturity = marketOrder.maturity;
            }
            // Sum future values in the maturity of orders
            // It will be the future value when the order is created, even if the market is rotated
            // and maturity is updated.
            OrderItem memory orderItem = Storage
                .slot()
                .lendOrders[marketOrder.maturity]
                .getOrderById(marketOrder.unitPrice, inActiveOrderIds[i]);
            inactiveAmount += orderItem.amount;
            inactiveFutureValue +=
                (orderItem.amount * ProtocolTypes.PRICE_DIGIT) /
                marketOrder.unitPrice;
        }
    }

    function getTotalAmountFromBorrowOrders(address _user)
        public
        view
        returns (
            uint256 activeAmount,
            uint256 inactiveAmount,
            uint256 inactiveFutureValue,
            uint256 maturity
        )
    {
        (
            uint48[] memory activeOrderIds,
            uint48[] memory inActiveOrderIds
        ) = getActiveBorrowOrderIds(_user);

        for (uint256 i = 0; i < activeOrderIds.length; i++) {
            MarketOrder memory marketOrder = Storage.slot().orders[activeOrderIds[i]];
            // Sum future values in the current maturity.
            // If the market is rotated and maturity is updated, it will be 0 by treating it
            // as an order canceled in the past market.
            OrderItem memory orderItem = Storage
                .slot()
                .borrowOrders[Storage.slot().maturity]
                .getOrderById(marketOrder.unitPrice, activeOrderIds[i]);
            activeAmount += orderItem.amount;
        }

        maturity = Storage.slot().userCurrentMaturities[_user];

        for (uint256 i = 0; i < inActiveOrderIds.length; i++) {
            MarketOrder memory marketOrder = Storage.slot().orders[inActiveOrderIds[i]];
            // Sum future values in the maturity of orders
            // It will be the future value when the order is created, even if the market is rotated
            // and maturity is updated.
            OrderItem memory orderItem = Storage
                .slot()
                .borrowOrders[marketOrder.maturity]
                .getOrderById(marketOrder.unitPrice, inActiveOrderIds[i]);
            inactiveAmount += orderItem.amount;
            inactiveFutureValue +=
                (orderItem.amount * ProtocolTypes.PRICE_DIGIT) /
                marketOrder.unitPrice;
        }
    }

    function getActiveLendOrderIds(address _user)
        public
        view
        returns (uint48[] memory activeOrderIds, uint48[] memory inActiveOrderIds)
    {
        uint256 activeOrderCount = 0;
        uint256 inActiveOrderCount = 0;
        bool isPastMaturity = Storage.slot().userCurrentMaturities[_user] !=
            Storage.slot().maturity;

        activeOrderIds = new uint48[](
            isPastMaturity ? 0 : Storage.slot().activeLendOrderIds[_user].length
        );
        inActiveOrderIds = new uint48[](Storage.slot().activeLendOrderIds[_user].length);

        for (uint256 i = 0; i < Storage.slot().activeLendOrderIds[_user].length; i++) {
            uint48 orderId = Storage.slot().activeLendOrderIds[_user][i];
            MarketOrder memory marketOrder = Storage.slot().orders[orderId];

            if (
                !Storage
                    .slot()
                    .lendOrders[Storage.slot().userCurrentMaturities[_user]]
                    .isActiveOrderId(marketOrder.unitPrice, orderId)
            ) {
                inActiveOrderCount += 1;
                inActiveOrderIds[i - activeOrderCount] = orderId;
                if (!isPastMaturity) {
                    assembly {
                        mstore(activeOrderIds, sub(mload(activeOrderIds), 1))
                    }
                }
            } else {
                if (!isPastMaturity) {
                    activeOrderCount += 1;
                    activeOrderIds[i - inActiveOrderCount] = orderId;
                }
                assembly {
                    mstore(inActiveOrderIds, sub(mload(inActiveOrderIds), 1))
                }
            }
        }
    }

    function getActiveBorrowOrderIds(address _user)
        public
        view
        returns (uint48[] memory activeOrderIds, uint48[] memory inActiveOrderIds)
    {
        uint256 activeOrderCount = 0;
        uint256 inActiveOrderCount = 0;
        bool isPastMaturity = Storage.slot().userCurrentMaturities[_user] !=
            Storage.slot().maturity;

        activeOrderIds = new uint48[](
            isPastMaturity ? 0 : Storage.slot().activeBorrowOrderIds[_user].length
        );
        inActiveOrderIds = new uint48[](Storage.slot().activeBorrowOrderIds[_user].length);

        for (uint256 i = 0; i < Storage.slot().activeBorrowOrderIds[_user].length; i++) {
            uint48 orderId = Storage.slot().activeBorrowOrderIds[_user][i];
            MarketOrder memory marketOrder = Storage.slot().orders[orderId];

            if (
                !Storage
                    .slot()
                    .borrowOrders[Storage.slot().userCurrentMaturities[_user]]
                    .isActiveOrderId(marketOrder.unitPrice, orderId)
            ) {
                inActiveOrderCount += 1;
                inActiveOrderIds[i - activeOrderCount] = orderId;
                if (!isPastMaturity) {
                    assembly {
                        mstore(activeOrderIds, sub(mload(activeOrderIds), 1))
                    }
                }
            } else {
                activeOrderCount += 1;
                if (!isPastMaturity) {
                    activeOrderIds[i - inActiveOrderCount] = orderId;
                }
                assembly {
                    mstore(inActiveOrderIds, sub(mload(inActiveOrderIds), 1))
                }
            }
        }
    }

    function estimateFilledAmount(ProtocolTypes.Side _side, uint256 _futureValue)
        public
        view
        returns (uint256 amount)
    {
        if (_side == ProtocolTypes.Side.BORROW) {
            return
                Storage.slot().lendOrders[Storage.slot().maturity].estimateDroppedAmountFromLeft(
                    _futureValue
                );
        } else {
            return
                Storage.slot().borrowOrders[Storage.slot().maturity].estimateDroppedAmountFromRight(
                    _futureValue
                );
        }
    }

    function insertOrder(
        ProtocolTypes.Side _side,
        address _user,
        uint256 _amount,
        uint256 _unitPrice,
        bool _isInterruption
    ) public returns (uint48 orderId) {
        orderId = nextOrderId();
        Storage.slot().orders[orderId] = MarketOrder(
            _side,
            _unitPrice,
            Storage.slot().maturity,
            block.timestamp
        );

        if (_side == ProtocolTypes.Side.LEND) {
            Storage.slot().lendOrders[Storage.slot().maturity].insertOrder(
                _unitPrice,
                orderId,
                _user,
                _amount,
                _isInterruption
            );
            Storage.slot().activeLendOrderIds[_user].push(orderId);
        } else if (_side == ProtocolTypes.Side.BORROW) {
            Storage.slot().borrowOrders[Storage.slot().maturity].insertOrder(
                _unitPrice,
                orderId,
                _user,
                _amount,
                _isInterruption
            );
            Storage.slot().activeBorrowOrderIds[_user].push(orderId);
        }
    }

    function dropOrders(
        ProtocolTypes.Side _side,
        uint256 _amount,
        uint256 _unitPrice
    )
        public
        returns (
            RemainingOrder memory remainingOrder,
            uint256 filledFutureValue,
            uint256 remainingAmount
        )
    {
        if (_side == ProtocolTypes.Side.BORROW) {
            (filledFutureValue, remainingAmount, remainingOrder) = Storage
                .slot()
                .lendOrders[Storage.slot().maturity]
                .dropLeft(_amount, _unitPrice);
        } else if (_side == ProtocolTypes.Side.LEND) {
            (filledFutureValue, remainingAmount, remainingOrder) = Storage
                .slot()
                .borrowOrders[Storage.slot().maturity]
                .dropRight(_amount, _unitPrice);
        }
    }

    function cleanLendOrders(address _user, uint256 _maturity)
        public
        returns (
            uint48[] memory orderIds,
            uint256 activeOrderCount,
            uint256 removedFutureValue,
            uint256 removedOrderAmount
        )
    {
        (
            uint48[] memory activeLendOrderIds,
            uint48[] memory inActiveLendOrderIds
        ) = getActiveLendOrderIds(_user);

        Storage.slot().activeLendOrderIds[_user] = activeLendOrderIds;
        activeOrderCount = activeLendOrderIds.length;
        uint256 inactiveOrderCount = inActiveLendOrderIds.length;
        orderIds = new uint48[](inactiveOrderCount);

        for (uint256 i = 0; i < inactiveOrderCount; i++) {
            MarketOrder memory marketOrder = Storage.slot().orders[inActiveLendOrderIds[i]];
            OrderItem memory orderItem = Storage.slot().lendOrders[_maturity].getOrderById(
                marketOrder.unitPrice,
                inActiveLendOrderIds[i]
            );
            removedFutureValue += Storage.slot().lendOrders[_maturity].getFutureValue(
                marketOrder.unitPrice,
                inActiveLendOrderIds[i]
            );
            removedOrderAmount += orderItem.amount;

            orderIds[i] = orderItem.orderId;
        }
    }

    function cleanBorrowOrders(address _user, uint256 _maturity)
        public
        returns (
            uint48[] memory orderIds,
            uint256 activeOrderCount,
            uint256 removedFutureValue,
            uint256 removedOrderAmount
        )
    {
        (
            uint48[] memory activeBorrowOrderIds,
            uint48[] memory inActiveBorrowOrderIds
        ) = getActiveBorrowOrderIds(_user);

        Storage.slot().activeBorrowOrderIds[_user] = activeBorrowOrderIds;
        activeOrderCount = activeBorrowOrderIds.length;
        uint256 inactiveOrderCount = inActiveBorrowOrderIds.length;
        orderIds = new uint48[](inactiveOrderCount);

        for (uint256 i = 0; i < inactiveOrderCount; i++) {
            MarketOrder memory marketOrder = Storage.slot().orders[inActiveBorrowOrderIds[i]];
            OrderItem memory orderItem = Storage.slot().borrowOrders[_maturity].getOrderById(
                marketOrder.unitPrice,
                inActiveBorrowOrderIds[i]
            );
            removedFutureValue += Storage.slot().borrowOrders[_maturity].getFutureValue(
                marketOrder.unitPrice,
                inActiveBorrowOrderIds[i]
            );

            removedOrderAmount += orderItem.amount;

            orderIds[i] = orderItem.orderId;
        }
    }

    function removeOrder(address _user, uint48 _orderId)
        public
        returns (
            ProtocolTypes.Side,
            uint256,
            uint256
        )
    {
        MarketOrder memory marketOrder = Storage.slot().orders[_orderId];
        uint256 removedAmount;
        if (marketOrder.side == ProtocolTypes.Side.LEND) {
            removedAmount = Storage.slot().lendOrders[Storage.slot().maturity].removeOrder(
                marketOrder.unitPrice,
                _orderId
            );
            removeOrderIdFromOrders(Storage.slot().activeLendOrderIds[_user], _orderId);
        } else if (marketOrder.side == ProtocolTypes.Side.BORROW) {
            removedAmount = Storage.slot().borrowOrders[Storage.slot().maturity].removeOrder(
                marketOrder.unitPrice,
                _orderId
            );
            removeOrderIdFromOrders(Storage.slot().activeBorrowOrderIds[_user], _orderId);
        }

        return (marketOrder.side, removedAmount, marketOrder.unitPrice);
    }

    /**
     * @notice Increases and returns id of last order in order book.
     * @return The new order id
     */
    function nextOrderId() private returns (uint48) {
        Storage.slot().lastOrderId++;
        return Storage.slot().lastOrderId;
    }

    function removeOrderIdFromOrders(uint48[] storage orders, uint256 orderId) private {
        uint256 lastOrderIndex = orders.length - 1;
        for (uint256 i = 0; i <= lastOrderIndex; i++) {
            if (orders[i] == orderId) {
                if (i != lastOrderIndex) {
                    uint48 lastOrderId = orders[lastOrderIndex];
                    orders[i] = lastOrderId;
                }

                orders.pop();
                break;
            }
        }
    }
}

contracts/mocks/HitchensOrderStatisticsT.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "../libraries/HitchensOrderStatisticsTreeLib.sol";

contract HitchensOrderStatisticsTreeContract {
    using HitchensOrderStatisticsTreeLib for HitchensOrderStatisticsTreeLib.Tree;

    HitchensOrderStatisticsTreeLib.Tree tree;

    event InsertOrder(string action, uint256 amount, uint256 value, uint256 orderId);
    event RemoveOrder(string action, uint256 value, uint256 _id);

    event Drop(
        uint256 droppedAmountInFV,
        uint256 remainingOrderAmountInPV,
        uint256 remainingOrderUnitPrice
    );

    constructor() {}

    function treeRootNode() public view returns (uint256 _value) {
        _value = tree.root;
    }

    function firstValue() public view returns (uint256 _value) {
        _value = tree.first();
    }

    function lastValue() public view returns (uint256 _value) {
        _value = tree.last();
    }

    function nextValue(uint256 value) public view returns (uint256 _value) {
        _value = tree.next(value);
    }

    function prevValue(uint256 value) public view returns (uint256 _value) {
        _value = tree.prev(value);
    }

    function valueExists(uint256 value) public view returns (bool _exists) {
        _exists = tree.exists(value);
    }

    function getNode(uint256 value)
        public
        view
        returns (
            uint256 _parent,
            uint256 _left,
            uint256 _right,
            bool _red,
            uint256 _head,
            uint256 _tail,
            uint256 _orderCounter,
            uint256 _orderTotalAmount
        )
    {
        (_parent, _left, _right, _red, _head, _tail, _orderCounter, _orderTotalAmount) = tree
            .getNode(value);
    }

    function getOrderByID(uint256 value, uint48 orderOd) public view returns (OrderItem memory) {
        return tree.getOrderById(value, orderOd);
    }

    function getRootCount() public view returns (uint256 _orderCounter) {
        _orderCounter = tree.count();
    }

    function getValueCount(uint256 value) public view returns (uint256 _orderCounter) {
        _orderCounter = tree.getNodeCount(value);
    }

    function insertAmountValue(
        uint256 value,
        uint48 orderId,
        address user,
        uint256 amount
    ) public {
        emit InsertOrder("insert", amount, value, orderId);
        tree.insertOrder(value, orderId, user, amount, false);
    }

    function removeAmountValue(uint256 value, uint48 orderId) public {
        emit RemoveOrder("delete", value, orderId);
        tree.removeOrder(value, orderId);
    }

    function estimateDroppedAmountFromFirst(uint256 targetFutureValue)
        public
        view
        returns (uint256 droppedAmount)
    {
        return tree.estimateDroppedAmountFromLeft(targetFutureValue);
    }

    function estimateDroppedAmountFromLast(uint256 targetFutureValue)
        public
        view
        returns (uint256 droppedAmount)
    {
        return tree.estimateDroppedAmountFromRight(targetFutureValue);
    }

    function dropValuesFromFirst(uint256 value, uint256 limitValue) public {
        (uint256 droppedAmountInFV, , RemainingOrder memory remainingOrder) = tree.dropLeft(
            value,
            limitValue
        );
        emit Drop(droppedAmountInFV, remainingOrder.amount, remainingOrder.unitPrice);
    }

    function dropValuesFromLast(uint256 value, uint256 limitValue) public {
        (uint256 droppedAmountInFV, , RemainingOrder memory remainingOrder) = tree.dropRight(
            value,
            limitValue
        );
        emit Drop(droppedAmountInFV, remainingOrder.amount, remainingOrder.unitPrice);
    }
}

contracts/GenesisValueVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

// interfaces
import {IGenesisValueVault} from "./interfaces/IGenesisValueVault.sol";
// libraries
import {Contracts} from "./libraries/Contracts.sol";
// mixins
import {MixinAddressResolver} from "./mixins/MixinAddressResolver.sol";
// utils
import {ProtocolTypes} from "./types/ProtocolTypes.sol";
import {Proxyable} from "./utils/Proxyable.sol";
// storages
import {GenesisValueVaultStorage as Storage, MaturityUnitPrice} from "./storages/GenesisValueVaultStorage.sol";

/**
 * @notice Implements the management of the genesis value as an amount for Lending deals.
 */
contract GenesisValueVault is IGenesisValueVault, MixinAddressResolver, Proxyable {
    /**
     * @notice Initializes the contract.
     * @dev Function is invoked by the proxy contract when the contract is added to the ProxyController.
     * @param _resolver The address of the Address Resolver contract
     */
    function initialize(address _resolver) public initializer onlyProxy {
        registerAddressResolver(_resolver);
    }

    // @inheritdoc MixinAddressResolver
    function requiredContracts() public pure override returns (bytes32[] memory contracts) {
        contracts = new bytes32[](1);
        contracts[0] = Contracts.LENDING_MARKET_CONTROLLER;
    }

    // @inheritdoc MixinAddressResolver
    function acceptedContracts() public pure override returns (bytes32[] memory contracts) {
        contracts = new bytes32[](1);
        contracts[0] = Contracts.LENDING_MARKET_CONTROLLER;
    }

    function isInitialized(bytes32 _ccy) public view override returns (bool) {
        return Storage.slot().isInitialized[_ccy];
    }

    function decimals(bytes32 _ccy) public view override returns (uint8) {
        return Storage.slot().decimals[_ccy];
    }

    function getTotalLendingSupply(bytes32 _ccy) external view override returns (uint256) {
        return Storage.slot().totalLendingSupplies[_ccy];
    }

    function getTotalBorrowingSupply(bytes32 _ccy) external view override returns (uint256) {
        return Storage.slot().totalBorrowingSupplies[_ccy];
    }

    function getGenesisValue(bytes32 _ccy, address _user) public view override returns (int256) {
        return Storage.slot().balances[_ccy][_user];
    }

    function getCurrentMaturity(bytes32 _ccy) public view override returns (uint256) {
        return Storage.slot().currentMaturity[_ccy];
    }

    function getCompoundFactor(bytes32 _ccy) public view override returns (uint256) {
        return Storage.slot().compoundFactors[_ccy];
    }

    function getMaturityUnitPrice(bytes32 _ccy, uint256 _maturity)
        public
        view
        override
        returns (MaturityUnitPrice memory)
    {
        return Storage.slot().maturityUnitPrices[_ccy][_maturity];
    }

    function getGenesisValueInFutureValue(bytes32 _ccy, address _user)
        public
        view
        override
        returns (int256)
    {
        // NOTE: The formula is:
        // futureValue = genesisValue * currentCompoundFactor.
        return
            (getGenesisValue(_ccy, _user) * int256(getCompoundFactor(_ccy))) /
            int256(10**decimals(_ccy));
    }

    function calculateCurrentFVFromFVInMaturity(
        bytes32 _ccy,
        uint256 _basisMaturity,
        int256 _futureValue
    ) external view override returns (int256) {
        // NOTE: These calculation steps "FV -> GV -> FV" are needed to match the actual conversion step.
        // Otherwise, Solidity's truncation specification creates a difference in the calculated values.
        // The formula is:
        // genesisValue = featureValueInMaturity / compoundFactorInMaturity.
        // currentFeatureValue = genesisValue * currentCompoundFactor
        int256 genesisValue = calculateGVFromFV(_ccy, _basisMaturity, _futureValue);
        return calculateFVFromGV(_ccy, 0, genesisValue);
    }

    function calculateGVFromFV(
        bytes32 _ccy,
        uint256 _basisMaturity,
        int256 _futureValue
    ) public view override returns (int256) {
        uint256 compoundFactor = Storage
        .slot()
        .maturityUnitPrices[_ccy][_basisMaturity].compoundFactor;

        require(compoundFactor > 0, "Compound factor is not fixed yet");

        // NOTE: The formula is: genesisValue = featureValue / compoundFactor.
        return (_futureValue * int256(10**decimals(_ccy))) / int256(compoundFactor);
    }

    function calculateFVFromGV(
        bytes32 _ccy,
        uint256 _basisMaturity,
        int256 _genesisValue
    ) public view override returns (int256) {
        uint256 compoundFactor = _basisMaturity == 0
            ? getCompoundFactor(_ccy)
            : Storage.slot().maturityUnitPrices[_ccy][_basisMaturity].compoundFactor;

        require(compoundFactor > 0, "Compound factor is not fixed yet");

        return (_genesisValue * int256(compoundFactor)) / int256(10**decimals(_ccy));
    }

    function initialize(
        bytes32 _ccy,
        uint8 _decimals,
        uint256 _compoundFactor
    ) external override onlyAcceptedContracts {
        require(_compoundFactor != 0, "Compound factor is zero");
        require(!isInitialized(_ccy), "Already initialized currency");

        Storage.slot().isInitialized[_ccy] = true;
        Storage.slot().decimals[_ccy] = _decimals;
        Storage.slot().initialCompoundFactors[_ccy] = _compoundFactor;
        Storage.slot().compoundFactors[_ccy] = _compoundFactor;
    }

    function updateCompoundFactor(
        bytes32 _ccy,
        uint256 _maturity,
        uint256 _nextMaturity,
        uint256 _unitPrice
    ) external override onlyAcceptedContracts {
        require(_unitPrice != 0, "unitPrice is zero");
        require(
            Storage.slot().maturityUnitPrices[_ccy][_maturity].next == 0,
            "already updated maturity"
        );
        require(_nextMaturity > _maturity, "invalid maturity");
        require(
            Storage.slot().maturityUnitPrices[_ccy][_nextMaturity].compoundFactor == 0,
            "existed maturity"
        );

        if (Storage.slot().initialCompoundFactors[_ccy] == Storage.slot().compoundFactors[_ccy]) {
            Storage.slot().maturityUnitPrices[_ccy][_maturity].compoundFactor = Storage
                .slot()
                .compoundFactors[_ccy];
        } else {
            require(
                Storage.slot().maturityUnitPrices[_ccy][_maturity].compoundFactor != 0,
                "invalid compound factor"
            );
        }

        Storage.slot().maturityUnitPrices[_ccy][_maturity].next = _nextMaturity;

        // Save actual compound factor here due to calculating the genesis value from future value.
        // NOTE: The formula is: newCompoundFactor = currentCompoundFactor * (1 / unitPrice).
        Storage.slot().compoundFactors[_ccy] =
            ((Storage.slot().compoundFactors[_ccy] * ProtocolTypes.PRICE_DIGIT)) /
            _unitPrice;

        Storage.slot().currentMaturity[_ccy] = _nextMaturity;
        Storage.slot().maturityUnitPrices[_ccy][_nextMaturity] = MaturityUnitPrice({
            unitPrice: _unitPrice,
            compoundFactor: Storage.slot().compoundFactors[_ccy],
            prev: _maturity,
            next: 0
        });

        emit CompoundFactorUpdated(
            _ccy,
            Storage.slot().compoundFactors[_ccy],
            _unitPrice,
            _nextMaturity,
            _maturity
        );
    }

    function addGenesisValue(
        bytes32 _ccy,
        address _user,
        uint256 _basisMaturity,
        int256 _futureValue
    ) external override onlyAcceptedContracts returns (bool) {
        uint256 compoundFactor = Storage
        .slot()
        .maturityUnitPrices[_ccy][_basisMaturity].compoundFactor;
        int256 amount = (_futureValue * int256(10**decimals(_ccy))) / int256(compoundFactor);
        int256 balance = Storage.slot().balances[_ccy][_user];

        if (amount >= 0) {
            if (balance >= 0) {
                Storage.slot().totalLendingSupplies[_ccy] += uint256(amount);
            } else {
                int256 diff = amount + balance;
                if (diff >= 0) {
                    Storage.slot().totalLendingSupplies[_ccy] += uint256(diff);
                    Storage.slot().totalBorrowingSupplies[_ccy] -= uint256(amount - diff);
                } else {
                    Storage.slot().totalBorrowingSupplies[_ccy] -= uint256(amount);
                }
            }
        } else {
            if (balance <= 0) {
                Storage.slot().totalBorrowingSupplies[_ccy] += uint256(-amount);
            } else {
                int256 diff = amount + balance;
                if (diff <= 0) {
                    Storage.slot().totalBorrowingSupplies[_ccy] += uint256(-diff);
                    Storage.slot().totalLendingSupplies[_ccy] -= uint256(-amount + diff);
                } else {
                    Storage.slot().totalLendingSupplies[_ccy] -= uint256(-amount);
                }
            }
        }

        Storage.slot().balances[_ccy][_user] += amount;

        emit Transfer(_ccy, address(0), _user, amount);

        return true;
    }
}

