        address _resolver,
        address _oracle,
        bytes32 _jobId,
        uint256 _requestFee,
        address _link,
        bytes32 _ccy
    ) {
        registerAddressResolver(_resolver);
        setChainlinkOracle(_oracle);
        jobId = _jobId;
        ccy = _ccy;

        requestFee = _requestFee;

