pragma solidity ^0.6.12;

/**
 * @dev ProtocolTypes is a base-level contract that holds common Secured Finance protocol types
 * @author Secured Finance
 */
contract ProtocolTypes {
    // Constant values
    uint8 internal constant NUMCCY = 3;
    uint8 internal constant NUMTERM = 6;
    uint8 internal constant NUMDF = 7; // number of discount factors
    uint256 internal constant BP = 10000; // basis point

    uint256 internal constant PCT = 10000; // percentage point in basis
    uint256 internal constant PENALTYLEVEL = 1000; // 10% settlement failure penalty
    uint256 internal constant MKTMAKELEVEL = 2000; // 20% for market making

    // Lending market common types
    enum Side {
        LEND,
        BORROW
    }
    enum Ccy {
        ETH,
        FIL,
        USDC,
        BTC
    }

    // Loan common types
    enum LoanState {
        REGISTERED,
        WORKING,
        DUE,
        PAST_DUE,
        CLOSED,
        TERMINATED
    }
    enum DFTERM {
        _3m,
        _6m,
        _1y,
        _2y,
        _3y,
        _4y,
        _5y
    }

    // Collateral common types
    enum CollateralState {
        EMPTY,
        AVAILABLE,
        IN_USE,
        MARGIN_CALL,
        LIQUIDATION_IN_PROGRESS,
        LIQUIDATION
    }

    // FXMarket common types
    enum CcyPair {
        FILETH,
        FILUSDC,
        ETHUSDC,
        BTCUSDC,
        BTCETH,
        BTCFIL
    }
    enum FXSide {
        BID,
        OFFER
    }

    enum PaymentFrequency {
        ANNUAL,
        SEMI_ANNUAL,
        QUARTERLY,
        MONTHLY,
        FORWARD
    }
}
","keccak256":"0x72de205a94aefa6e5b79074f603b8056535f99918af5112343156e79b7eadd83","license":"MIT"},"contracts/interfaces/ICollateralAggregatorV2.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.6.12;
pragma experimental ABIEncoderV2;

interface ICollateralAggregator {
    event Register(address indexed addr);
    event Release(
        address indexed partyA,
        address indexed partyB,
        bytes32 ccy,
        uint256 amount0,
        uint256 amount1,
        bool isSettled
    );
    event Liquidate(
        address indexed from,
        address indexed to,
        bytes32 ccy,
        uint256 amount
    );
    event ReleaseUnsettled(address indexed party, bytes32 ccy, uint256 amount);
    event SettleCollateral(
        address indexed partyA,
        address indexed partyB,
        bytes32 ccy,
        uint256 amount0,
        uint256 amount1
    );
    event UpdatePV(
        address indexed partyA,
        address indexed partyB,
        bytes32 ccy,
        uint256 prevPV0,
        uint256 prevPV1,
        uint256 currentPV0,
        uint256 currentPV1
    );
    event UseCollateral(
        address indexed partyA,
        address indexed partyB,
        bytes32 ccy,
        uint256 amount0,
        uint256 amount1,
        bool isSettled
    );
    event UseUnsettledCollateral(
        address indexed party,
        bytes32 ccy,
        uint256 amount
    );

    function checkRegisteredUser(address addr) external view returns (bool);

    function enterVault(address _user) external;

    function enterVault(address _party0, address _party1) external;

    function exitVault(address _user) external;

    function exitVault(address _party0, address _party1) external;

    function getCcyExposures(
        address partyA,
        address partyB,
        bytes32 ccy
    )
        external
        view
        returns (
            uint256,
            uint256,
            uint256,
            uint256
        );

    function getCoverage(address _party0, address _party1)
        external
        view
        returns (uint256, uint256);

    function getExposedCurrencies(address partyA, address partyB)
        external
        view
        returns (bytes32[] memory);

    function getMaxCollateralBookWidthdraw(address _user)
        external
        view
        returns (uint256 maxWithdraw);

    function getMaxCollateralWidthdraw(address _party0, address _party1)
        external
        view
        returns (uint256, uint256);

    function getNetAndTotalPV(address _party0, address _party1)
        external
        view
        returns (
            uint256,
            uint256,
            uint256,
            uint256
        );

    function getRebalanceCollateralAmounts(address _party0, address _party1)
        external
        view
        returns (uint256, uint256);

    function getTotalUnsettledExp(address _user)
        external
        view
        returns (uint256);

    function getUnsettledCoverage(address _user)
        external
        view
        returns (uint256 coverage);

    function isCovered(
        address _party0,
        address _party1,
        bytes32 _ccy,
        uint256 _party0PV,
        uint256 _party1PV,
        bool _isSettled
    ) external view returns (bool, bool);

    function isCoveredUnsettled(
        address _user,
        bytes32 _ccy,
        uint256 _unsettledExp
    ) external view returns (bool);

    function liquidate(
        address from,
        address to,
        uint256 liquidationInETH
    ) external;

    function liquidate(
        address from,
        address to,
        bytes32 ccy,
        uint256 liquidationAmount,
        uint256 pv,
        bool isSettled
    ) external;

    function register() external;

    function register(string[] memory _addresses, uint256[] memory _chainIds)
        external;

    function releaseCollateral(
        address partyA,
        address partyB,
        bytes32 ccy,
        uint256 amount0,
        uint256 amount1,
        bool isSettled
    ) external;

    function releaseUnsettledCollateral(
        address user,
        bytes32 ccy,
        uint256 amount
    ) external;

    function settleCollateral(
        address partyA,
        address partyB,
        bytes32 ccy,
        uint256 amount0,
        uint256 amount1
    ) external;

    function updatePV(
        address party0,
        address party1,
        bytes32 ccy,
        uint256 prevPV0,
        uint256 prevPV1,
        uint256 currentPV0,
        uint256 currentPV1
    ) external;

    function useCollateral(
        address partyA,
        address partyB,
        bytes32 ccy,
        uint256 amount0,
        uint256 amount1,
        bool isSettled
    ) external;

    function useUnsettledCollateral(
        address user,
        bytes32 ccy,
        uint256 amount
    ) external;

    function getUsedVaults(address user)
        external
        view
        returns (address[] memory);

    function getUsedVaults(address party0, address party1)
        external
        view
        returns (address[] memory);
}
","keccak256":"0x1e1d54ae5ea0a4d446d99ecce509f35cd2f42826c61abfccf6df109c0911cdab","license":"MIT"},"contracts/interfaces/ILendingMarketController.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.6.12;
pragma experimental ABIEncoderV2;

struct Order {
    bytes32 ccy;
    uint256 term;
    uint8 side;
    uint256 amount;
    uint256 rate;
}

interface ILendingMarketController {
    event LendingMarketCreated(
        bytes32 ccy,
        uint256 term,
        address indexed marketAddr
    );
    event LendingMarketsPaused(bytes32 ccy);
    event LendingMarketsUnpaused(bytes32 ccy);
    event OwnerChanged(address indexed oldOwner, address indexed newOwner);

    function deployLendingMarket(bytes32 _ccy, uint256 _term)
        external
        returns (address market);

    function getBorrowRatesForCcy(bytes32 _ccy)
        external
        view
        returns (uint256[] memory rates);

    function getDiscountFactorsForCcy(bytes32 _ccy)
        external
        view
        returns (uint256[] memory, uint256[] memory);

    function getLendRatesForCcy(bytes32 _ccy)
        external
        view
        returns (uint256[] memory rates);

    function getMidRatesForCcy(bytes32 _ccy)
        external
        view
        returns (uint256[] memory rates);

    function lendingMarkets(bytes32, uint256) external view returns (address);

    function owner() external view returns (address);

    function pauseLendingMarkets(bytes32 _ccy) external returns (bool);

    function placeBulkOrders(Order[] memory orders) external returns (bool);

    function unpauseLendingMarkets(bytes32 _ccy) external returns (bool);

    function numberOfMarkets() external view returns (uint256);

    function getSupportedTerms(bytes32 _ccy)
        external
        view
        returns (uint256[] memory);
}
