pragma solidity ^0.8.9;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "../libraries/TimeSlot.sol";

library PaymentAggregatorStorage {
    using TimeSlot for TimeSlot.Slot;
    using EnumerableSet for EnumerableSet.Bytes32Set;

    bytes32 internal constant STORAGE_SLOT = keccak256("sf.storage.paymentAggregator");

    struct Storage {
        // Mapping structure for storing TimeSlots
        mapping(bytes32 => mapping(bytes32 => mapping(bytes32 => TimeSlot.Slot))) _timeSlots;
        mapping(bytes32 => mapping(bytes32 => mapping(bytes32 => EnumerableSet.Bytes32Set))) deals;
    }

    function slot() internal pure returns (Storage storage r) {
        bytes32 _slot = STORAGE_SLOT;
        /// @solidity memory-safe-assembly
        assembly {
            r.slot := _slot
        }
    }
}
","keccak256":"0x63cb9ecb4db340f32fedad7c13b15058c1065690062b8ae18389758d751fda3f","license":"MIT"},"contracts/types/ProtocolTypes.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

/**
 * @dev ProtocolTypes is a base-level contract that holds common Secured Finance protocol types
 * @author Secured Finance
 */
library ProtocolTypes {
    // Constant values
    uint256 public constant BP = 10000; // basis point
    uint256 public constant PCT = 10000; // percentage point in basis
    uint256 public constant PENALTYLEVEL = 1000; // 10% settlement failure penalty
    uint256 public constant MKTMAKELEVEL = 2000; // 20% for market making

    // Lending market common types
    enum Side {
        LEND,
        BORROW
    }
    enum Ccy {
        ETH,
        FIL,
        USDC,
        BTC
    }

    // Loan common types
    enum LoanState {
        REGISTERED,
        WORKING,
        DUE,
        PAST_DUE,
        CLOSED,
        TERMINATED
    }
    enum DFTERM {
        _3m,
        _6m,
        _1y,
        _2y,
        _3y,
        _4y,
        _5y
    }

    // Collateral common types
    enum CollateralState {
        EMPTY,
        AVAILABLE,
        IN_USE,
        MARGIN_CALL,
        LIQUIDATION_IN_PROGRESS,
        LIQUIDATION
    }

    // FXMarket common types
    enum CcyPair {
        FILETH,
        FILUSDC,
        ETHUSDC,
        BTCUSDC,
        BTCETH,
        BTCFIL
    }
    enum FXSide {
        BID,
        OFFER
    }

    enum PaymentFrequency {
        ANNUAL,
        SEMI_ANNUAL,
        QUARTERLY,
        MONTHLY,
        FORWARD
    }

    struct Currency {
        bool isSupported;
        string name;
        uint16 chainId; // chain id for address conversion
    }

    struct SettlementRequest {
        address payer;
        address receiver;
        uint16 chainId;
        uint256 timestamp;
        string txHash;
    }
}
