pragma solidity ^0.6.12;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./interfaces/ICollateralAggregator.sol";
import "./interfaces/ILoanV2.sol";
import "./interfaces/ILendingMarketController.sol";
import "./libraries/HitchensOrderStatisticsTreeLib.sol";
import "./ProtocolTypes.sol";

/**
 * @dev Lending Market contract module which allows lending market participants
 * to create/take/cancel market orders.
 *
 * It will store market orders in structured red-black tree and doubly linked list in each node.
 */
contract LendingMarket is ProtocolTypes, ReentrancyGuard, Pausable {
    using SafeMath for uint256;
    using HitchensOrderStatisticsTreeLib for HitchensOrderStatisticsTreeLib.Tree;

    // Contracts interfaces
    ICollateralAggregator collateralAggregator;
    ILoanV2 loan;

    /**
     * @dev Emitted when market order created by market maker.
     */
    event MakeOrder(
        uint256 orderId,
        address indexed maker,
        Side side,
        bytes32 ccy,
        uint256 term,
        uint256 amount,
        uint256 rate
    );

    /**
     * @dev Emitted when market order canceled by market maker.
     *
     * Requirements:
     *
     * - Market order must be active and cancelable.
     */
    event CancelOrder(
        uint256 orderId,
        address indexed maker,
        Side side,
        uint256 amount,
        uint256 rate
    );

    /**
     * @dev Emitted when market order taken by market taker.
     *
     * Requirements:
     *
     * - Market order must be active.
     */
    event TakeOrder(
        uint256 orderId,
        address indexed taker,
        Side side,
        uint256 amount,
        uint256 rate
    );

    uint256 public last_order_id;
    bytes32 public MarketCcy;
    uint256 public MarketTerm;
    address public lendingController;

    struct MarketOrder {
        Side side;
        uint256 amount;
        uint256 rate; // in basis points
        address maker;
    }

    /**
     * @dev Order Book mapping for all Market Orders.
     */
    mapping(uint256 => MarketOrder) public orders;
    HitchensOrderStatisticsTreeLib.Tree lendOrders;
    HitchensOrderStatisticsTreeLib.Tree borrowOrders;

    /**
     * @dev Constructor.
     * @param _ccy The main currency for order book lending deals
     * @param _term The main term for order book lending deals
     */
    constructor(
        bytes32 _ccy,
        uint256 _term,
        address _lendingController
    ) public {
        MarketCcy = _ccy;
        MarketTerm = _term;
        lendingController = _lendingController;
    }

    /**
     * @dev Modifier to make a function callable only by lending market controller owner.
     */
    modifier onlyLendingControllerAdmin() {
        require(
            msg.sender == ILendingMarketController(lendingController).owner() ||
                msg.sender == lendingController,
            "Incorrect access"
        );
        _;
    }

    /**
     * @dev Modifier to make a function callable only by order maker.
     * @param orderId Market order id
     */
    modifier onlyMaker(uint256 orderId) {
        require(msg.sender == getMaker(orderId), "No access to cancel order");
        _;
    }

    /**
     * @dev Triggers to make a set collateral contract address.
     * @param colAddr Collateral contract addreess
     *
     * Requirements:
     *
     * - Can be executed only by lending market controller owner.
     */
    function setCollateral(address colAddr) public onlyLendingControllerAdmin {
        collateralAggregator = ICollateralAggregator(colAddr);
    }

    /**
     * @dev Triggers to make a set loan contract address.
     * @param addr Loan smart contract addreess
     *
     * Requirements:
     *
     * - Can be executed only by lending market controller owner.
     */
    function setLoan(address addr) public onlyLendingControllerAdmin {
        loan = ILoanV2(addr);
    }

    /**
     * @dev Triggers to get order maker address.
     * @param orderId Market order id
     */
    function getMaker(uint256 orderId) public view returns (address maker) {
        return orders[orderId].maker;
    }

    /**
     * @dev Triggers to get highest borrow rate.
     */
    function getBorrowRate() public view returns (uint256 rate) {
        return borrowOrders.last();
    }

    /**
     * @dev Triggers to get highest lend rate.
     */
    function getLendRate() public view returns (uint256 rate) {
        return lendOrders.last();
    }

    /**
     * @dev Triggers to get mid rate.
     */
    function getMidRate() public view returns (uint256 rate) {
        uint256 borrowRate = getBorrowRate();
        uint256 lendRate = getLendRate();
        uint256 combinedRate = borrowRate.add(lendRate);

        return combinedRate.div(2);
    }

    /**
     * @dev Triggers to get market order information.
     * @param orderId Market order id
     */
    function getOrder(uint256 orderId)
        public
        view
        returns (MarketOrder memory)
    {
        return orders[orderId];
    }

    function getOrderFromTree(uint256 orderId)
        public
        view
        returns (
            uint256,
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        MarketOrder memory order = orders[orderId];

        if (order.side == Side.LEND) {
            return lendOrders.getOrderById(order.rate, orderId);
        } else {
            return borrowOrders.getOrderById(order.rate, orderId);
        }
    }

    /**
     * @dev Internally triggered to increase and return id of last order in order book.
     */
    function _next_id() internal returns (uint256) {
        last_order_id++;
        return last_order_id;
    }

    /**
     * @dev Triggered to cancel market order.
     * @param orderId Market order id
     *
     * Requirements:
     * - Order has to be cancelable by market maker
     */
    function cancelOrder(uint256 orderId)
        public
        onlyMaker(orderId)
        returns (bool success)
    {
        _beforeMarketOrder();

        MarketOrder memory order = orders[orderId];
        if (order.side == Side.LEND) {
            lendOrders.remove(order.amount, order.rate, orderId);
        } else if (order.side == Side.BORROW) {
            borrowOrders.remove(order.amount, order.rate, orderId);
        }
        delete orders[orderId];

        collateralAggregator.releaseUnsettledCollateral(
            order.maker,
            MarketCcy,
            order.amount.mul(MKTMAKELEVEL).div(PCT)
        );
        emit CancelOrder(
            orderId,
            order.maker,
            order.side,
            order.amount,
            order.rate
        );

        success = true;
    }

    /**
     * @dev Triggered to make new market order.
     * @param _side Borrow or Lend order position
     * @param _amount Amount of funds maker wish to borrow/lend
     * @param _rate Preferable interest rate
     */
    function makeOrder(
        Side _side,
        uint256 _amount,
        uint256 _rate
    ) internal returns (uint256 orderId) {
        MarketOrder memory order;

        require(_amount > 0, "Can't place empty amount");
        require(_rate > 0, "Can't place empty rate");
        _beforeMarketOrder();

        order.side = _side;
        order.amount = _amount;
        order.rate = _rate;
        order.maker = msg.sender;
        orderId = _next_id();

        orders[orderId] = order;
        collateralAggregator.useUnsettledCollateral(
            msg.sender,
            MarketCcy,
            _amount.mul(MKTMAKELEVEL).div(PCT)
        );
        if (order.side == Side.LEND) {
            lendOrders.insert(order.amount, order.rate, orderId);
        } else if (order.side == Side.BORROW) {
            borrowOrders.insert(order.amount, order.rate, orderId);
        }

        emit MakeOrder(
            orderId,
            order.maker,
            order.side,
            MarketCcy,
            MarketTerm,
            order.amount,
            order.rate
        );
    }

    /**
     * @dev Triggered to take market order.
     * @param orderId Market Order id in Order Book
     * @param _amount Amount of funds taker wish to borrow/lend
     *
     * Requirements:
     * - Market order has to be active
     */
    function takeOrder(
        Side side,
        uint256 orderId,
        uint256 _amount
    ) internal returns (bool) {
        MarketOrder memory order = orders[orderId];
        require(_amount <= order.amount, "Insuficient amount");
        require(order.maker != msg.sender, "Maker couldn't take its order");
        _beforeMarketOrder();

        orders[orderId].amount = order.amount.sub(_amount);
        if (order.side == Side.LEND) {
            require(
                lendOrders.fillOrder(order.rate, orderId, _amount),
                "Couldn't fill order"
            );
        } else if (order.side == Side.BORROW) {
            require(
                borrowOrders.fillOrder(order.rate, orderId, _amount),
                "Couldn't fill order"
            );
        }

        loan.register(
            order.maker,
            msg.sender,
            uint8(order.side),
            MarketCcy,
            MarketTerm,
            _amount,
            order.rate
        );

        emit TakeOrder(orderId, msg.sender, side, _amount, order.rate);

        if (order.amount == 0) {
            delete orders[orderId];
        }

        return true;
    }

    /**
     * @dev Triggered to get matching market order.
     * @param side Market order side it can be borrow or lend
     * @param amount Amount of funds taker wish to borrow/lend
     * @param rate Amount of interest rate taker wish to borrow/lend
     *
     * Returns zero if didn't find a matched order, reverts if no orders for specified interest rate
     */
    function matchOrders(
        Side side,
        uint256 amount,
        uint256 rate
    ) public view returns (uint256) {
        if (side == Side.LEND) {
            require(
                borrowOrders.exists(rate),
                "No orders exists for selected interest rate"
            );
            return borrowOrders.findOrderIdForAmount(rate, amount);
        } else {
            require(
                lendOrders.exists(rate),
                "No orders exists for selected interest rate"
            );
            return lendOrders.findOrderIdForAmount(rate, amount);
        }
    }

    /**
     * @dev Triggered to execute market order, if order matched it takes order, if not matched places new order.
     * @param side Market order side it can be borrow or lend
     * @param amount Amount of funds maker/taker wish to borrow/lend
     * @param rate Amount of interest rate maker/taker wish to borrow/lend
     *
     * Returns true after successful execution
     */
    function order(
        Side side,
        uint256 amount,
        uint256 rate
    ) public nonReentrant returns (bool) {
        uint256 orderId;

        if (side == Side.LEND) {
            orderId = borrowOrders.findOrderIdForAmount(rate, amount);
            if (orderId != 0) return takeOrder(Side.BORROW, orderId, amount);
        } else {
            orderId = lendOrders.findOrderIdForAmount(rate, amount);
            if (orderId != 0) return takeOrder(Side.LEND, orderId, amount);
        }

        makeOrder(side, amount, rate);
        return true;
    }

    /**
     * @dev Triggered to pause lending market.
     */
    function pauseMarket() public virtual onlyLendingControllerAdmin {
        _pause();
    }

    /**
     * @dev Triggered to pause lending market.
     */
    function unpauseMarket() public virtual onlyLendingControllerAdmin {
        _unpause();
    }

    /**
     * @dev Additional checks before making/taking orders.
     */
    function _beforeMarketOrder() internal view {
        require(!paused(), "Market paused");
    }
}
","keccak256":"0xbe77315defc11ed12d431feebeb75af6da8983a93d5ac19dc67fd8016c1093ca","license":"MIT"},"contracts/LendingMarketController.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.6.12;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/math/SafeMath.sol";
import "./libraries/QuickSort.sol";
import "./libraries/DiscountFactor.sol";
import "./LendingMarket.sol";
import "./interfaces/ILendingMarketController.sol";
import "./interfaces/ILendingMarket.sol";
import "./interfaces/ICurrencyController.sol";
import "./interfaces/ITermStructure.sol";

/**
 * @dev Lending Market Controller contract is managing separated lending
 * order-book markets (per term) and responsible to calculate Discount Factors per currency
 * and construct yield curve
 *
 * It will store lending market addresses by ccy and term in lendingMarkets mapping.
 */
contract LendingMarketController is ILendingMarketController {
    using SafeMath for uint256;
    using QuickSort for uint256[];

    bytes4 constant prefix = 0x21aaa47b;
    address public override owner;
    ICurrencyController public currencyController;
    ITermStructure public termStructure;
    uint256 public override numberOfMarkets = 0;

    mapping(bytes32 => mapping(uint256 => address))
        public
        override lendingMarkets;
    mapping(bytes32 => uint256[]) public supportedTerms;

    modifier onlyOwner() {
        require(msg.sender == owner, "INVALID_ACCESS");
        _;
    }

    /**
     * @dev Lending Market Controller Constructor.
     */
    constructor() public {
        owner = msg.sender;
    }

    /**
     * @dev Sets owner of the controller market.
     * @param _owner Address of new owner
     */
    function setOwner(address _owner) public onlyOwner {
        require(_owner != address(0), "new owner is the zero address");
        emit OwnerChanged(owner, _owner);
        owner = _owner;
    }

    /**
     * @dev Triggers to link with Currency Controller contract.
     * @param addr CurrencyController smart contract address
     *
     * @notice Executed only by contract owner
     */
    function setCurrencyController(address addr) public onlyOwner {
        currencyController = ICurrencyController(addr);
    }

    /**
     * @dev Triggers to link with TermStructure contract.
     * @param addr TermStructure smart contract address
     *
     * @notice Executed only by contract owner
     */
    function setTermStructure(address addr) public onlyOwner {
        termStructure = ITermStructure(addr);
    }

    // =========== YIELD CURVE FUNCTIONS ===========

    /**
     * @dev Triggers to get borrow rates for selected currency.
     * @param _ccy Currency
     */
    function getBorrowRatesForCcy(bytes32 _ccy)
        public
        view
        override
        returns (uint256[] memory)
    {
        uint256[] memory terms = supportedTerms[_ccy];
        uint256[] memory rates = new uint256[](terms.length);

        for (uint256 i = 0; i < terms.length; i++) {
            uint256 term = terms[i];
            ILendingMarket market = ILendingMarket(lendingMarkets[_ccy][term]);
            rates[i] = market.getBorrowRate();
        }

        return rates;
    }

    /**
     * @dev Triggers to get lend rates for selected currency.
     * @param _ccy Currency
     */
    function getLendRatesForCcy(bytes32 _ccy)
        public
        view
        override
        returns (uint256[] memory)
    {
        uint256[] memory terms = supportedTerms[_ccy];
        uint256[] memory rates = new uint256[](terms.length);

        for (uint256 i = 0; i < terms.length; i++) {
            uint256 term = terms[i];
            ILendingMarket market = ILendingMarket(lendingMarkets[_ccy][term]);
            rates[i] = market.getLendRate();
        }

        return rates;
    }

    /**
     * @dev Triggers to get mid rates for selected currency.
     * @param _ccy Currency
     */
    function getMidRatesForCcy(bytes32 _ccy)
        public
        view
        override
        returns (uint256[] memory)
    {
        uint256[] memory terms = supportedTerms[_ccy];
        uint256[] memory rates = new uint256[](terms.length);

        for (uint256 i = 0; i < terms.length; i++) {
            uint256 term = terms[i];
            ILendingMarket market = ILendingMarket(lendingMarkets[_ccy][term]);
            rates[i] = market.getMidRate();
        }

        return rates;
    }

    // =========== DISCOUNT FACTORS CALCULATION ===========

    function getDiscountFactorsForCcy(bytes32 _ccy)
        public
        view
        override
        returns (uint256[] memory, uint256[] memory)
    {
        uint256[] memory rates = getMidRatesForCcy(_ccy);
        return DiscountFactor.calculateDFs(rates, supportedTerms[_ccy]);
    }

    function getSupportedTerms(bytes32 _ccy)
        public
        view
        override
        returns (uint256[] memory)
    {
        return supportedTerms[_ccy];
    }

    // =========== MARKET DEPLOYMENT FUNCTIONS ===========

    /**
     * @dev Deploys new Lending Market and save address at lendingMarkets mapping.
     * @param _ccy Main currency for new lending market
     * @param _term Term for new Lending Market
     *
     * @notice Reverts on deployment market with existing currency and term
     */
    function deployLendingMarket(bytes32 _ccy, uint256 _term)
        public
        override
        onlyOwner
        returns (address market)
    {
        require(currencyController.isSupportedCcy(_ccy), "NON SUPPORTED CCY");
        require(
            termStructure.isSupportedTerm(_term, prefix, _ccy),
            "NON SUPPORTED TERM"
        );
        require(
            lendingMarkets[_ccy][_term] == address(0),
            "Couldn't rewrite existing market"
        );
        market = address(new LendingMarket(_ccy, _term, address(this)));
        lendingMarkets[_ccy][_term] = market;

        supportedTerms[_ccy].push(_term);
        supportedTerms[_ccy] = supportedTerms[_ccy].sort();

        emit LendingMarketCreated(_ccy, _term, market);
        return market;
    }

    // =========== LENDING MARKETS MANAGEMENT FUNCTIONS ===========

    /**
     * @dev Pauses previously deployed lending market by currency
     * @param _ccy Currency for pausing all lending markets
     */
    function pauseLendingMarkets(bytes32 _ccy)
        public
        override
        onlyOwner
        returns (bool)
    {
        uint256[] memory terms = supportedTerms[_ccy];

        for (uint256 i = 0; i < terms.length; i++) {
            uint256 term = terms[i];
            ILendingMarket market = ILendingMarket(lendingMarkets[_ccy][term]);
            market.pauseMarket();
        }

        emit LendingMarketsPaused(_ccy);
        return true;
    }

    /**
     * @dev Unpauses previously deployed lending market by currency
     * @param _ccy Currency for pausing all lending markets
     */
    function unpauseLendingMarkets(bytes32 _ccy)
        public
        override
        onlyOwner
        returns (bool)
    {
        uint256[] memory terms = supportedTerms[_ccy];

        for (uint256 i = 0; i < terms.length; i++) {
            uint256 term = terms[i];
            ILendingMarket market = ILendingMarket(lendingMarkets[_ccy][term]);
            market.unpauseMarket();
        }

        emit LendingMarketsUnpaused(_ccy);
        return true;
    }

    // =========== BULK TRADE FUNCTIONS ===========

    /**
     * @dev Places orders in multiple Lending Markets.
     * @param orders Lending Market orders array with ccy and terms to identify right market
     */
    function placeBulkOrders(Order[] memory orders)
        public
        override
        returns (bool)
    {
        for (uint8 i = 0; i < orders.length; i++) {
            Order memory order = orders[i];

            ILendingMarket market = ILendingMarket(
                lendingMarkets[order.ccy][order.term]
            );
            market.order(uint8(order.side), order.amount, order.rate);
        }
    }
}
","keccak256":"0x1871c2a3ed8b3386143258cc16e0823a3805d8d5cf300eb7148f36066cc71bf5","license":"MIT"},"contracts/ProtocolTypes.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.6.12;

/**
 * @dev ProtocolTypes is a base-level contract that holds common Secured Finance protocol types
 * @author Secured Finance
 */
contract ProtocolTypes {
    // Constant values
    uint8 internal constant NUMCCY = 3;
    uint8 internal constant NUMTERM = 6;
    uint8 internal constant NUMDF = 7; // number of discount factors
    uint256 internal constant BP = 10000; // basis point

    uint256 internal constant PCT = 10000; // percentage point in basis
    uint256 internal constant PENALTYLEVEL = 1000; // 10% settlement failure penalty
    uint256 internal constant MKTMAKELEVEL = 2000; // 20% for market making

    // Lending market common types
    enum Side {
        LEND,
        BORROW
    }
    enum Ccy {
        ETH,
        FIL,
        USDC,
        BTC
    }

    // Loan common types
    enum LoanState {
        REGISTERED,
        WORKING,
        DUE,
        PAST_DUE,
        CLOSED,
        TERMINATED
    }
    enum DFTERM {
        _3m,
        _6m,
        _1y,
        _2y,
        _3y,
        _4y,
        _5y
    }

    // Collateral common types
    enum CollateralState {
        EMPTY,
        AVAILABLE,
        IN_USE,
        MARGIN_CALL,
        LIQUIDATION_IN_PROGRESS,
        LIQUIDATION
    }

    // FXMarket common types
    enum CcyPair {
        FILETH,
        FILUSDC,
        ETHUSDC,
        BTCUSDC,
        BTCETH,
        BTCFIL
    }
    enum FXSide {
        BID,
        OFFER
    }

    enum PaymentFrequency {
        ANNUAL,
        SEMI_ANNUAL,
        QUARTERLY,
        MONTHLY,
        FORWARD
    }
}
