pragma solidity ^0.6.12;
pragma experimental ABIEncoderV2;

/**
 * @dev Currency Controller contract is responsible for managing supported
 * currencies in Secured Finance Protocol
 *
 * Contract links new currencies to ETH Chainlink price feeds, without existing price feed
 * contract owner is not able to add a new currency into the protocol
 *
 * Currency controller contract copies the logic of FXRatesAggregator contract, and
 * will replace that contract in connection with Collateral Aggregator
 */
interface ICurrencyController {
    event CcyAdded(
        bytes32 indexed ccy,
        string name,
        uint16 chainId,
        uint256 haircut
    );
    event CcyCollateralUpdate(bytes32 indexed ccy, bool isCollateral);
    event CcySupportUpdate(bytes32 indexed ccy, bool isSupported);
    event HaircutUpdated(bytes32 indexed ccy, uint256 haircut);
    event MinMarginUpdated(bytes32 indexed ccy, uint256 minMargin);
    event OwnerChanged(address indexed oldOwner, address indexed newOwner);
    event PriceFeedAdded(
        bytes32 ccy,
        string secondCcy,
        address indexed priceFeed
    );
    event PriceFeedRemoved(
        bytes32 ccy,
        string secondCcy,
        address indexed priceFeed
    );

    function convertBulkToETH(bytes32 _ccy, uint256[] memory _amounts)
        external
        view
        returns (uint256[] memory);

    function convertFromETH(bytes32 _ccy, uint256 _amountETH)
        external
        view
        returns (uint256);

    function convertToETH(bytes32 _ccy, uint256 _amount)
        external
        view
        returns (uint256);

    function currencies(bytes32)
        external
        view
        returns (
            bool isSupported,
            string memory name,
            uint16 chainId
        );

    function ethDecimals(bytes32) external view returns (uint8);

    function getHaircut(bytes32 _ccy) external view returns (uint256);

    function getHistoricalETHPrice(bytes32 _ccy, uint80 _roundId)
        external
        view
        returns (int256);

    function getHistoricalUSDPrice(bytes32 _ccy, uint80 _roundId)
        external
        view
        returns (int256);

    function getLastETHPrice(bytes32 _ccy) external view returns (int256);

    function getLastUSDPrice(bytes32 _ccy) external view returns (int256);

    function getMinMargin(bytes32 _ccy) external view returns (uint256);

    function haircuts(bytes32) external view returns (uint256);

    function isCollateral(bytes32) external view returns (bool);

    function isSupportedCcy(bytes32 _ccy) external view returns (bool);

    function last_ccy_index() external view returns (uint8);

    function linkPriceFeed(
        bytes32 _ccy,
        address _priceFeedAddr,
        bool _isEthPriceFeed
    ) external returns (bool);

    function minMargins(bytes32) external view returns (uint256);

    function owner() external view returns (address);

    function removePriceFeed(bytes32 _ccy, bool _isEthPriceFeed) external;

    function setOwner(address _owner) external;

    function supportCurrency(
        bytes32 _ccy,
        string memory _name,
        uint16 _chainId,
        address _ethPriceFeed,
        uint256 _haircut
    ) external returns (bool);

    function supportedCurrencies() external view returns (uint8);

    function updateCcyHaircut(bytes32 _ccy, uint256 _haircut)
        external
        returns (bool);

    function updateCollateralSupport(bytes32 _ccy, bool _isSupported)
        external
        returns (bool);

    function updateCurrencySupport(bytes32 _ccy, bool _isSupported)
        external
        returns (bool);

    function updateMinMargin(bytes32 _ccy, uint256 _minMargin)
        external
        returns (bool);

    function usdDecimals(bytes32) external view returns (uint8);
}
","keccak256":"0xfe21b4283f1cfc907ade17b20e68c80e5e4800f4ffaa7bec74ef245fe497cb3a","license":"MIT"},"contracts/interfaces/ILendingMarket.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.6.12;
pragma experimental ABIEncoderV2;

struct MarketOrder {
    uint8 side;
    uint256 amount;
    uint256 rate;
    uint256 deadline;
    address maker;
}

interface ILendingMarket {
    event CancelOrder(
        uint256 orderId,
        address indexed maker,
        uint8 side,
        uint256 amount,
        uint256 rate
    );
    event MakeOrder(
        uint256 orderId,
        address indexed maker,
        uint8 side,
        bytes32 ccy,
        uint8 term,
        uint256 amount,
        uint256 rate
    );
    event Paused(address account);
    event TakeOrder(
        uint256 orderId,
        address indexed taker,
        uint8 side,
        uint256 amount,
        uint256 rate
    );
    event Unpaused(address account);

    function MarketCcy() external view returns (bytes32);

    function MarketTerm() external view returns (uint8);

    function cancelOrder(uint256 orderId) external returns (bool success);

    function getBorrowRate() external view returns (uint256 rate);

    function getLendRate() external view returns (uint256 rate);

    function getMaker(uint256 orderId) external view returns (address maker);

    function getMidRate() external view returns (uint256 rate);

    function getOrder(uint256 orderId)
        external
        view
        returns (MarketOrder memory);

    function getOrderFromTree(uint256 orderId)
        external
        view
        returns (
            uint256,
            uint256,
            uint256,
            uint256,
            uint256
        );

    function last_order_id() external view returns (uint256);

    function lendingController() external view returns (address);

    function matchOrders(
        uint8 side,
        uint256 amount,
        uint256 rate
    ) external view returns (uint256);

    function order(
        uint8 side,
        uint256 amount,
        uint256 rate
    ) external returns (bool);

    function orders(uint256)
        external
        view
        returns (
            uint8 side,
            uint256 amount,
            uint256 rate,
            address maker
        );

    function pauseMarket() external;

    function paused() external view returns (bool);

    function setCollateral(address colAddr) external;

    function setLoan(address addr) external;

    function unpauseMarket() external;
}
