pragma solidity ^0.6.12;

import "@openzeppelin/contracts/math/SafeMath.sol";

library DiscountFactor {
    using SafeMath for uint256;

    uint256 internal constant BP = 10000; // basis point
    uint256 internal constant NON_ANNUAL_TERMS = 3;

    function determineDF(
        uint256 rate,
        uint256 term,
        uint256[] memory cache,
        uint256 dfSum,
        uint256 index
    ) internal pure returns (uint256 df) {
        if (term < 365) {
            df = BP.mul(BP).div((BP.add(rate.mul(term).div(360))));
        } else if (term == 365) {
            df = BP.mul(BP).div((BP.add(rate)));
            dfSum = dfSum.add(df);
        } else {
            uint256 rateSum = (rate.mul(dfSum)).div(BP);
            if (rateSum > BP) {
                df = 0;
            } else {
                df = BP.mul(BP.sub(rate.mul(dfSum).div(BP))).div(BP.add(rate));
            }
            dfSum = dfSum.add(df);
        }

        cache[index] = df;

        return dfSum;
    }

    function calculateDFs(uint256[] memory rates, uint256[] memory terms)
        public
        pure
        returns (uint256[] memory, uint256[] memory)
    {
        require(rates.length == terms.length, "INVALID_PARAMS");

        (
            uint256[] memory bootstrapedRates,
            uint256[] memory bootstrapedTerms
        ) = bootstrapTerms(rates, terms);

        uint256 len = bootstrapedTerms.length;
        uint256[] memory dfs = new uint256[](len);
        uint256 dfSum;

        for (uint256 i = 0; i < len; i++) {
            dfSum = determineDF(
                bootstrapedRates[i],
                bootstrapedTerms[i],
                dfs,
                dfSum,
                i
            );
        }

        return (dfs, bootstrapedTerms);
    }

    function maxDFs(uint256 maxTerm) internal pure returns (uint256) {
        return maxTerm.div(365).add(NON_ANNUAL_TERMS);
    }

    struct TermBootstrapingLocalVars {
        uint256 extendedTerms;
        uint256 delta;
        uint256 numItems;
        uint256 lastKnownRate;
        uint256 nextKnownRate;
        uint256 nextKnownTerm;
        bool upwards;
        uint256 deltaRate;
        uint256 step;
    }

    function bootstrapTerms(uint256[] memory rates, uint256[] memory terms)
        public
        pure
        returns (uint256[] memory, uint256[] memory)
    {
        uint256 len = maxDFs(terms[terms.length - 1]);

        uint256[] memory filledRates = new uint256[](len);
        uint256[] memory filledTerms = new uint256[](len);
        TermBootstrapingLocalVars memory vars;

        for (uint256 i = 0; i < terms.length.sub(1); i++) {
            if (terms[i] < 365) {
                filledRates[i] = rates[i];
                filledTerms[i] = terms[i];
                continue;
            }
            vars.delta = terms[i + 1].sub(terms[i]);

            if (vars.delta <= 365) {
                filledRates[i] = rates[i];
                filledTerms[i] = terms[i];
                continue;
            }

            vars.numItems = vars.delta.div(365);
            vars.lastKnownRate = rates[i];

            if (vars.extendedTerms == 0) {
                filledRates[i] = vars.lastKnownRate;
                filledTerms[i] = terms[i];
            }
            vars.nextKnownRate = rates[i + 1];
            vars.nextKnownTerm = terms[i + 1];
            vars.upwards = vars.nextKnownRate > vars.lastKnownRate
                ? true
                : false;
            vars.deltaRate = vars.upwards
                ? vars.nextKnownRate.sub(vars.lastKnownRate)
                : vars.lastKnownRate.sub(vars.nextKnownRate);
            vars.step = vars.deltaRate.div(vars.numItems);

            for (uint256 j = 1; j < vars.numItems; j++) {
                vars.extendedTerms = vars.extendedTerms.add(1);

                uint256 newIndex = i.add(vars.extendedTerms);
                uint256 missedRate = vars.upwards
                    ? filledRates[newIndex.sub(1)].add(vars.step)
                    : filledRates[newIndex.sub(1)].sub(vars.step);
                uint256 missedTerm = terms[i].add(uint256(365).mul(j));

                filledRates[newIndex] = missedRate;
                filledTerms[newIndex] = missedTerm;

                if (j == vars.numItems.sub(1)) {
                    uint256 shifterIndex = newIndex.add(1);

                    filledRates[shifterIndex] = vars.nextKnownRate;
                    filledTerms[shifterIndex] = vars.nextKnownTerm;
                }
            }
        }

        return (filledRates, filledTerms);
    }

    struct DFInterpolationLocalVars {
        uint256 timeDelta;
        uint256 termSeconds;
        uint256 prevTermSeconds;
        uint256 left;
        uint256 right;
        uint256 total;
    }

    /**
     * @dev Triggers to adjust discount factors by interpolating to current loan maturity
     * @param discountFactors Discount factors array
     * @param terms Array of terms
     * @param date Date to calculate discount factors for
     *
     */
    function interpolateDF(
        uint256[] memory discountFactors,
        uint256[] memory terms,
        uint256 date
    ) public view returns (uint256) {
        DFInterpolationLocalVars memory vars;
        vars.timeDelta = date.sub(block.timestamp);

        if (vars.timeDelta <= terms[0].mul(86400)) {
            vars.termSeconds = terms[0].mul(86400);
            vars.left = vars.termSeconds.sub(vars.timeDelta);

            return
                (BP.mul(vars.left).add(discountFactors[0].mul(vars.timeDelta)))
                    .div(vars.termSeconds);
        } else {
            for (uint256 i = 1; i < terms.length; i++) {
                vars.termSeconds = terms[i].mul(86400);
                vars.prevTermSeconds = terms[i - 1].mul(86400);

                if (
                    vars.prevTermSeconds < vars.timeDelta &&
                    vars.timeDelta <= vars.termSeconds
                ) {
                    vars.left = vars.timeDelta.sub(vars.prevTermSeconds);

                    if (vars.left == 0) {
                        return discountFactors[i]; // gas savings only
                    }

                    vars.right = vars.termSeconds.sub(vars.timeDelta);
                    if (vars.right == 0) {
                        return discountFactors[i];
                    }

                    vars.total = vars.termSeconds.sub(vars.prevTermSeconds);

                    return (
                        (discountFactors[i - 1].mul(vars.right))
                            .add((discountFactors[i].mul(vars.left)))
                            .div(vars.total)
                    );
                }
            }
        }
    }
}
","keccak256":"0x6767e46906ab5eeca1cedda8afbcc4a01658c8e08220bdacd8756fc126b650b9","license":"MIT"},"contracts/libraries/HitchensOrderStatisticsTreeLib.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.6.12;

import "@openzeppelin/contracts/math/SafeMath.sol";

library HitchensOrderStatisticsTreeLib {
    using SafeMath for uint256;
    uint256 private constant EMPTY = 0;

    struct Node {
        uint256 parent;
        uint256 left;
        uint256 right;
        bool red;
        uint256 head;
        uint256 tail;
        uint256 orderCounter;
        mapping(uint256 => OrderItem) orders;
    }

    struct OrderItem {
        uint256 orderId;
        uint256 next;
        uint256 prev;
        uint256 timestamp;
        uint256 amount;
    }

    struct Tree {
        uint256 root;
        mapping(uint256 => Node) nodes;
    }

    function first(Tree storage self) internal view returns (uint256 _value) {
        _value = self.root;
        if (_value == EMPTY) return 0;
        while (self.nodes[_value].left != EMPTY) {
            _value = self.nodes[_value].left;
        }
    }

    function last(Tree storage self) internal view returns (uint256 _value) {
        _value = self.root;
        if (_value == EMPTY) return 0;
        while (self.nodes[_value].right != EMPTY) {
            _value = self.nodes[_value].right;
        }
    }

    function next(Tree storage self, uint256 value)
        internal
        view
        returns (uint256 _cursor)
    {
        require(
            value != EMPTY,
            "OrderStatisticsTree(401) - Starting value cannot be zero"
        );
        if (self.nodes[value].right != EMPTY) {
            _cursor = treeMinimum(self, self.nodes[value].right);
        } else {
            _cursor = self.nodes[value].parent;
            while (_cursor != EMPTY && value == self.nodes[_cursor].right) {
                value = _cursor;
                _cursor = self.nodes[_cursor].parent;
            }
        }
    }

    function prev(Tree storage self, uint256 value)
        internal
        view
        returns (uint256 _cursor)
    {
        require(
            value != EMPTY,
            "OrderStatisticsTree(402) - Starting value cannot be zero"
        );
        if (self.nodes[value].left != EMPTY) {
            _cursor = treeMaximum(self, self.nodes[value].left);
        } else {
            _cursor = self.nodes[value].parent;
            while (_cursor != EMPTY && value == self.nodes[_cursor].left) {
                value = _cursor;
                _cursor = self.nodes[_cursor].parent;
            }
        }
    }

    function exists(Tree storage self, uint256 value)
        internal
        view
        returns (bool _exists)
    {
        if (value == EMPTY) return false;
        if (value == self.root) return true;
        if (self.nodes[value].parent != EMPTY) return true;
        return false;
    }

    function amountExistsInNode(
        Tree storage self,
        uint256 amount,
        uint256 value
    ) internal view returns (bool) {
        if (!exists(self, value)) return false;
        return isAmountExistsInList(self, value, amount);
    }

    function orderExistsInNode(
        Tree storage self,
        uint256 amount,
        uint256 value,
        uint256 orderId
    ) internal view returns (bool) {
        if (!exists(self, value)) return false;
        return isOrderIdExists(self, value, amount, orderId);
    }

    function getNode(Tree storage self, uint256 value)
        internal
        view
        returns (
            uint256,
            uint256,
            uint256,
            bool,
            uint256,
            uint256,
            uint256
        )
    {
        require(
            exists(self, value),
            "OrderStatisticsTree(403) - Value does not exist."
        );
        Node storage gn = self.nodes[value];
        return (
            gn.parent,
            gn.left,
            gn.right,
            gn.red,
            gn.head,
            gn.tail,
            gn.orderCounter
        );
    }

    function getNodeCount(Tree storage self, uint256 value)
        internal
        view
        returns (uint256)
    {
        Node storage gn = self.nodes[value];
        return gn.orderCounter;
    }

    function count(Tree storage self) internal view returns (uint256 _count) {
        return getNodeCount(self, self.root);
    }

    function insert(
        Tree storage self,
        uint256 amount,
        uint256 value,
        uint256 orderId
    ) internal {
        require(
            value != EMPTY,
            "OrderStatisticsTree(405) - Value to insert cannot be zero"
        );
        uint256 cursor;
        uint256 probe = self.root;
        while (probe != EMPTY) {
            cursor = probe;
            if (value < probe) {
                probe = self.nodes[probe].left;
            } else if (value > probe) {
                probe = self.nodes[probe].right;
            } else if (value == probe) {
                insertOrder(self, probe, amount, orderId);
                return;
            }
        }
        Node storage nValue = self.nodes[value];
        nValue.parent = cursor;
        nValue.left = EMPTY;
        nValue.right = EMPTY;
        nValue.red = true;
        insertOrder(self, value, amount, orderId);
        if (cursor == EMPTY) {
            self.root = value;
        } else if (value < cursor) {
            self.nodes[cursor].left = value;
        } else {
            self.nodes[cursor].right = value;
        }
        insertFixup(self, value);
    }

    function remove(
        Tree storage self,
        uint256 amount,
        uint256 value,
        uint256 orderId
    ) internal {
        require(
            value != EMPTY,
            "OrderStatisticsTree(407) - Value to delete cannot be zero"
        );
        require(
            orderExistsInNode(self, amount, value, orderId),
            "OrderStatisticsTree(408) - Value to delete does not exist."
        );
        Node storage nValue = self.nodes[value];
        removeOrder(self, value, orderId);
        uint256 probe;
        uint256 cursor;
        if (nValue.orderCounter == 0) {
            if (
                self.nodes[value].left == EMPTY ||
                self.nodes[value].right == EMPTY
            ) {
                cursor = value;
            } else {
                cursor = self.nodes[value].right;
                while (self.nodes[cursor].left != EMPTY) {
                    cursor = self.nodes[cursor].left;
                }
            }
            if (self.nodes[cursor].left != EMPTY) {
                probe = self.nodes[cursor].left;
            } else {
                probe = self.nodes[cursor].right;
            }
            uint256 cursorParent = self.nodes[cursor].parent;
            self.nodes[probe].parent = cursorParent;
            if (cursorParent != EMPTY) {
                if (cursor == self.nodes[cursorParent].left) {
                    self.nodes[cursorParent].left = probe;
                } else {
                    self.nodes[cursorParent].right = probe;
                }
            } else {
                self.root = probe;
            }
            bool doFixup = !self.nodes[cursor].red;
            if (cursor != value) {
                replaceParent(self, cursor, value);
                self.nodes[cursor].left = self.nodes[value].left;
                self.nodes[self.nodes[cursor].left].parent = cursor;
                self.nodes[cursor].right = self.nodes[value].right;
                self.nodes[self.nodes[cursor].right].parent = cursor;
                self.nodes[cursor].red = self.nodes[value].red;
                (cursor, value) = (value, cursor);
            }
            if (doFixup) {
                removeFixup(self, probe);
            }
            delete self.nodes[cursor];
        }
    }

    function treeMinimum(Tree storage self, uint256 value)
        private
        view
        returns (uint256)
    {
        while (self.nodes[value].left != EMPTY) {
            value = self.nodes[value].left;
        }
        return value;
    }

    function treeMaximum(Tree storage self, uint256 value)
        private
        view
        returns (uint256)
    {
        while (self.nodes[value].right != EMPTY) {
            value = self.nodes[value].right;
        }
        return value;
    }

    function rotateLeft(Tree storage self, uint256 value) private {
        uint256 cursor = self.nodes[value].right;
        uint256 parent = self.nodes[value].parent;
        uint256 cursorLeft = self.nodes[cursor].left;
        self.nodes[value].right = cursorLeft;
        if (cursorLeft != EMPTY) {
            self.nodes[cursorLeft].parent = value;
        }
        self.nodes[cursor].parent = parent;
        if (parent == EMPTY) {
            self.root = cursor;
        } else if (value == self.nodes[parent].left) {
            self.nodes[parent].left = cursor;
        } else {
            self.nodes[parent].right = cursor;
        }
        self.nodes[cursor].left = value;
        self.nodes[value].parent = cursor;
    }

    function rotateRight(Tree storage self, uint256 value) private {
        uint256 cursor = self.nodes[value].left;
        uint256 parent = self.nodes[value].parent;
        uint256 cursorRight = self.nodes[cursor].right;
        self.nodes[value].left = cursorRight;
        if (cursorRight != EMPTY) {
            self.nodes[cursorRight].parent = value;
        }
        self.nodes[cursor].parent = parent;
        if (parent == EMPTY) {
            self.root = cursor;
        } else if (value == self.nodes[parent].right) {
            self.nodes[parent].right = cursor;
        } else {
            self.nodes[parent].left = cursor;
        }
        self.nodes[cursor].right = value;
        self.nodes[value].parent = cursor;
    }

    function insertFixup(Tree storage self, uint256 value) private {
        uint256 cursor;
        while (value != self.root && self.nodes[self.nodes[value].parent].red) {
            uint256 valueParent = self.nodes[value].parent;
            if (
                valueParent == self.nodes[self.nodes[valueParent].parent].left
            ) {
                cursor = self.nodes[self.nodes[valueParent].parent].right;
                if (self.nodes[cursor].red) {
                    self.nodes[valueParent].red = false;
                    self.nodes[cursor].red = false;
                    self.nodes[self.nodes[valueParent].parent].red = true;
                    value = self.nodes[valueParent].parent;
                } else {
                    if (value == self.nodes[valueParent].right) {
                        value = valueParent;
                        rotateLeft(self, value);
                    }
                    valueParent = self.nodes[value].parent;
                    self.nodes[valueParent].red = false;
                    self.nodes[self.nodes[valueParent].parent].red = true;
                    rotateRight(self, self.nodes[valueParent].parent);
                }
            } else {
                cursor = self.nodes[self.nodes[valueParent].parent].left;
                if (self.nodes[cursor].red) {
                    self.nodes[valueParent].red = false;
                    self.nodes[cursor].red = false;
                    self.nodes[self.nodes[valueParent].parent].red = true;
                    value = self.nodes[valueParent].parent;
                } else {
                    if (value == self.nodes[valueParent].left) {
                        value = valueParent;
                        rotateRight(self, value);
                    }
                    valueParent = self.nodes[value].parent;
                    self.nodes[valueParent].red = false;
                    self.nodes[self.nodes[valueParent].parent].red = true;
                    rotateLeft(self, self.nodes[valueParent].parent);
                }
            }
        }
        self.nodes[self.root].red = false;
    }

    function replaceParent(
        Tree storage self,
        uint256 a,
        uint256 b
    ) private {
        uint256 bParent = self.nodes[b].parent;
        self.nodes[a].parent = bParent;
        if (bParent == EMPTY) {
            self.root = a;
        } else {
            if (b == self.nodes[bParent].left) {
                self.nodes[bParent].left = a;
            } else {
                self.nodes[bParent].right = a;
            }
        }
    }

    function removeFixup(Tree storage self, uint256 value) private {
        uint256 cursor;
        while (value != self.root && !self.nodes[value].red) {
            uint256 valueParent = self.nodes[value].parent;
            if (value == self.nodes[valueParent].left) {
                cursor = self.nodes[valueParent].right;
                if (self.nodes[cursor].red) {
                    self.nodes[cursor].red = false;
                    self.nodes[valueParent].red = true;
                    rotateLeft(self, valueParent);
                    cursor = self.nodes[valueParent].right;
                }
                if (
                    !self.nodes[self.nodes[cursor].left].red &&
                    !self.nodes[self.nodes[cursor].right].red
                ) {
                    self.nodes[cursor].red = true;
                    value = valueParent;
                } else {
                    if (!self.nodes[self.nodes[cursor].right].red) {
                        self.nodes[self.nodes[cursor].left].red = false;
                        self.nodes[cursor].red = true;
                        rotateRight(self, cursor);
                        cursor = self.nodes[valueParent].right;
                    }
                    self.nodes[cursor].red = self.nodes[valueParent].red;
                    self.nodes[valueParent].red = false;
                    self.nodes[self.nodes[cursor].right].red = false;
                    rotateLeft(self, valueParent);
                    value = self.root;
                }
            } else {
                cursor = self.nodes[valueParent].left;
                if (self.nodes[cursor].red) {
                    self.nodes[cursor].red = false;
                    self.nodes[valueParent].red = true;
                    rotateRight(self, valueParent);
                    cursor = self.nodes[valueParent].left;
                }
                if (
                    !self.nodes[self.nodes[cursor].right].red &&
                    !self.nodes[self.nodes[cursor].left].red
                ) {
                    self.nodes[cursor].red = true;
                    value = valueParent;
                } else {
                    if (!self.nodes[self.nodes[cursor].left].red) {
                        self.nodes[self.nodes[cursor].right].red = false;
                        self.nodes[cursor].red = true;
                        rotateLeft(self, cursor);
                        cursor = self.nodes[valueParent].left;
                    }
                    self.nodes[cursor].red = self.nodes[valueParent].red;
                    self.nodes[valueParent].red = false;
                    self.nodes[self.nodes[cursor].left].red = false;
                    rotateRight(self, valueParent);
                    value = self.root;
                }
            }
        }
        self.nodes[value].red = false;
    }

    // Double linked list functions
    /**
     * @dev Retrieves the Object denoted by `_id`.
     */
    function getOrderById(
        Tree storage self,
        uint256 value,
        uint256 orderId
    )
        internal
        view
        returns (
            uint256,
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        require(
            exists(self, value),
            "OrderStatisticsTree(403) - Value does not exist."
        );
        Node storage gn = self.nodes[value];

        OrderItem memory order = gn.orders[orderId];
        return (
            order.orderId,
            order.next,
            order.prev,
            order.timestamp,
            order.amount
        );
    }

    /**
     * @dev Return boolean if value, amount and orderId exist in doubly linked list
     */
    function isOrderIdExists(
        Tree storage self,
        uint256 value,
        uint256 amount,
        uint256 orderId
    ) internal view returns (bool) {
        Node storage gn = self.nodes[value];

        OrderItem memory order = gn.orders[orderId];
        if (order.amount != amount) {
            return false;
        }

        return true;
    }

    /**
     * @dev Return boolean if value and amount exist in doubly linked list.
     */
    function isAmountExistsInList(
        Tree storage self,
        uint256 value,
        uint256 amount
    ) internal view returns (bool) {
        Node storage gn = self.nodes[value];

        OrderItem memory order = gn.orders[gn.head];
        while (order.next != 0 && order.amount < amount) {
            order = gn.orders[order.next];
        }
        if (order.amount == 0) {
            return false;
        }
        return true;
    }

    /**
     * @dev Return the id of the first OrderItem matching `_amount` in the amount field.
     */
    function findOrderIdForAmount(
        Tree storage self,
        uint256 value,
        uint256 amount
    ) internal view returns (uint256) {
        Node storage gn = self.nodes[value];

        OrderItem memory order = gn.orders[gn.head];
        while (order.orderId != gn.tail && order.amount < amount) {
            order = gn.orders[order.next];
        }
        if (order.amount >= amount) {
            return order.orderId;
        } else {
            return 0;
        }
    }

    /**
     * @dev Reduces order amount once market order taken.
     */
    function fillOrder(
        Tree storage self,
        uint256 value,
        uint256 orderId,
        uint256 _amount
    ) internal returns (bool) {
        Node storage gn = self.nodes[value];

        OrderItem memory order = gn.orders[orderId];
        uint256 newAmount = order.amount.sub(_amount);
        gn.orders[orderId].amount = newAmount;

        if (gn.orders[orderId].amount == 0) {
            remove(self, newAmount, value, orderId);
        } else {
            if (gn.orders[gn.head].amount < newAmount) {
                OrderItem memory rootOrder = gn.orders[gn.head];
                while (
                    rootOrder.orderId != gn.tail && rootOrder.amount < newAmount
                ) {
                    rootOrder = gn.orders[rootOrder.next];
                }
                if (order.amount > _amount) {
                    OrderItem memory prevOrder = gn.orders[rootOrder.prev];
                    _link(self, value, order.orderId, rootOrder.orderId);
                    _link(self, value, prevOrder.orderId, order.orderId);
                } else {
                    OrderItem memory nextOrder = gn.orders[rootOrder.next];
                    _link(self, value, order.orderId, nextOrder.orderId);
                    _link(self, value, rootOrder.orderId, order.orderId);
                }
            } else {
                _link(self, value, order.orderId, gn.head);
                _setHead(self, value, order.orderId);
                if (gn.tail == 0) _setTail(self, value, order.orderId);
            }
        }

        return true;
    }

    /**
     * @dev Up size order by market maker.
     */
    function upSizeOrder(
        Tree storage self,
        uint256 value,
        uint256 orderId,
        uint256 _amount
    ) internal returns (bool) {
        require(_amount > 0, "Couldn't up size order with 0");
        Node storage gn = self.nodes[value];

        OrderItem memory order = gn.orders[orderId];
        uint256 newAmount = order.amount.add(_amount);
        gn.orders[orderId].amount = newAmount;

        if (gn.orders[gn.head].amount < newAmount) {
            OrderItem memory rootOrder = gn.orders[gn.head];
            while (
                rootOrder.orderId != gn.tail && rootOrder.amount < newAmount
            ) {
                rootOrder = gn.orders[rootOrder.next];
            }
            if (order.amount > _amount) {
                OrderItem memory prevOrder = gn.orders[rootOrder.prev];
                _link(self, value, order.orderId, rootOrder.orderId);
                _link(self, value, prevOrder.orderId, order.orderId);
            } else {
                OrderItem memory nextOrder = gn.orders[rootOrder.next];
                _link(self, value, order.orderId, nextOrder.orderId);
                _link(self, value, rootOrder.orderId, order.orderId);
            }
        } else {
            _link(self, value, order.orderId, gn.head);
            _setHead(self, value, order.orderId);
            if (gn.tail == 0) _setTail(self, value, order.orderId);
        }

        return true;
    }

    /**
     * @dev Insert a new OrderItem as the new Head with `_amount` in the amount field, and orderId.
     */
    function addHead(
        Tree storage self,
        uint256 value,
        uint256 _amount,
        uint256 _orderId
    ) internal {
        Node storage gn = self.nodes[value];
        uint256 orderId = _createOrder(self, value, _amount, _orderId);
        _link(self, value, orderId, gn.head);
        _setHead(self, value, orderId);
        if (gn.tail == 0) _setTail(self, value, orderId);
    }

    /**
     * @dev Insert a new OrderItem as the new Tail with `_amount` in the amount field, and orderId.
     */
    function addTail(
        Tree storage self,
        uint256 value,
        uint256 _amount,
        uint256 _orderId
    ) internal {
        Node storage gn = self.nodes[value];

        if (gn.head == 0) {
            addHead(self, value, _amount, _orderId);
        } else {
            uint256 orderId = _createOrder(self, value, _amount, _orderId);
            _link(self, value, gn.tail, orderId);
            _setTail(self, value, orderId);
        }
    }

    /**
     * @dev Remove the OrderItem denoted by `_id` from the List.
     */
    function removeOrder(
        Tree storage self,
        uint256 value,
        uint256 orderId
    ) internal {
        require(
            exists(self, value),
            "OrderStatisticsTree(403) - Value does not exist."
        );
        Node storage gn = self.nodes[value];

        OrderItem memory order = gn.orders[orderId];
        if (gn.head == orderId && gn.tail == orderId) {
            _setHead(self, value, 0);
            _setTail(self, value, 0);
        } else if (gn.head == orderId) {
            _setHead(self, value, order.next);
            gn.orders[order.next].prev = 0;
        } else if (gn.tail == orderId) {
            _setTail(self, value, order.prev);
            gn.orders[order.prev].next = 0;
        } else {
            _link(self, value, order.prev, order.next);
        }
        delete gn.orders[order.orderId];
        gn.orderCounter -= 1;
    }

    /**
     * @dev Insert a new OrderItem after the last OrderItem with the same `_amount`.
     */
    function insertOrder(
        Tree storage self,
        uint256 value,
        uint256 _amount,
        uint256 _orderId
    ) internal {
        require(_amount > 0, "Insuficient amount");

        Node storage gn = self.nodes[value];
        if (gn.head == 0) {
            addHead(self, value, _amount, _orderId);
        } else {
            if (gn.orders[gn.head].amount < _amount) {
                OrderItem memory order = gn.orders[gn.head];
                while (order.next != 0 && order.amount <= _amount) {
                    order = gn.orders[order.next];
                }
                if (order.amount > _amount) {
                    insertOrderBefore(
                        self,
                        value,
                        order.orderId,
                        _amount,
                        _orderId
                    );
                } else {
                    insertOrderAfter(
                        self,
                        value,
                        order.orderId,
                        _amount,
                        _orderId
                    );
                }
            } else {
                addHead(self, value, _amount, _orderId);
            }
        }
    }

    /**
     * @dev Insert a new OrderImer after the Order denoted by `_id` with `_amount` and `_orderId` in the amount field.
     */
    function insertOrderAfter(
        Tree storage self,
        uint256 value,
        uint256 _prevId,
        uint256 _amount,
        uint256 _orderId
    ) internal {
        require(_amount > 0, "Insuficient amount");

        Node storage gn = self.nodes[value];

        if (_prevId == gn.tail) {
            addTail(self, value, _amount, _orderId);
        } else {
            OrderItem memory prevOrder = gn.orders[_prevId];
            OrderItem memory nextOrder = gn.orders[prevOrder.next];
            uint256 newOrderId = _createOrder(self, value, _amount, _orderId);
            _link(self, value, newOrderId, nextOrder.orderId);
            _link(self, value, prevOrder.orderId, newOrderId);
        }
    }

    /**
     * @dev Insert a new Object before the Object denoted by `_id` with `_amount` and `_orderId` in the data field.
     */
    function insertOrderBefore(
        Tree storage self,
        uint256 value,
        uint256 _nextId,
        uint256 _amount,
        uint256 _orderId
    ) internal {
        Node storage gn = self.nodes[value];

        if (_nextId == gn.head) {
            addHead(self, value, _amount, _orderId);
        } else {
            insertOrderAfter(
                self,
                value,
                gn.orders[_nextId].prev,
                _amount,
                _orderId
            );
        }
    }

    /**
     * @dev Internal function to update the Head pointer.
     */
    function _setHead(
        Tree storage self,
        uint256 value,
        uint256 orderId
    ) internal {
        Node storage gn = self.nodes[value];

        gn.head = orderId;
    }

    /**
     * @dev Internal function to update the Tail pointer.
     */
    function _setTail(
        Tree storage self,
        uint256 value,
        uint256 orderId
    ) internal {
        Node storage gn = self.nodes[value];

        gn.tail = orderId;
    }

    /**
     * @dev Internal function to create an unlinked Order.
     */
    function _createOrder(
        Tree storage self,
        uint256 value,
        uint256 amount,
        uint256 orderId
    ) internal returns (uint256) {
        Node storage gn = self.nodes[value];
        uint256 newId;
        if (gn.orderCounter == 0) {
            newId = 1;
        }
        gn.orderCounter += 1;
        OrderItem memory order = OrderItem(
            orderId,
            0,
            0,
            block.timestamp,
            amount
        );
        gn.orders[order.orderId] = order;
        return order.orderId;
    }

    /**
     * @dev Internal function to link an Object to another.
     */
    function _link(
        Tree storage self,
        uint256 value,
        uint256 _prevId,
        uint256 _nextId
    ) internal {
        Node storage gn = self.nodes[value];

        gn.orders[_prevId].next = _nextId;
        gn.orders[_nextId].prev = _prevId;
    }
}
","keccak256":"0xc55252beddee94272d469c12e35cb339c2f7a7b2e391532989a9e26a39ae4358","license":"MIT"},"contracts/libraries/QuickSort.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.6.12;

library QuickSort {
    function sort(uint256[] memory data)
        public
        pure
        returns (uint256[] memory)
    {
        quickSort(data, int256(0), int256(data.length - 1));
        return data;
    }

    function quickSort(
        uint256[] memory arr,
        int256 left,
        int256 right
    ) internal pure {
        int256 i = left;
        int256 j = right;
        if (i == j) return;
        uint256 pivot = arr[uint256(left + (right - left) / 2)];
        while (i <= j) {
            while (arr[uint256(i)] < pivot) i++;
            while (pivot < arr[uint256(j)]) j--;
            if (i <= j) {
                (arr[uint256(i)], arr[uint256(j)]) = (
                    arr[uint256(j)],
                    arr[uint256(i)]
                );
                i++;
                j--;
            }
        }
        if (left < j) quickSort(arr, left, j);
        if (i < right) quickSort(arr, i, right);
    }
}
