pragma solidity ^0.8.9;

import {Currency} from "../storages/CurrencyControllerStorage.sol";

/**
 * @dev Currency Controller contract is responsible for managing supported
 * currencies in Secured Finance Protocol
 *
 * Contract links new currencies to ETH Chainlink price feeds, without existing price feed
 * contract owner is not able to add a new currency into the protocol
 */
interface ICurrencyController {
    event CcyAdded(bytes32 indexed ccy, string name, uint256 haircut);
    event CcySupportUpdate(bytes32 indexed ccy, bool isSupported);
    event HaircutUpdated(bytes32 indexed ccy, uint256 haircut);
    event PriceFeedAdded(bytes32 ccy, string secondCcy, address indexed priceFeed);
    event PriceFeedRemoved(bytes32 ccy, string secondCcy, address indexed priceFeed);

    function convertFromETH(bytes32 _ccy, uint256 _amountETH) external view returns (uint256);

    function convertToETH(bytes32 _ccy, uint256 _amount) external view returns (uint256);

    function convertToETH(bytes32 _ccy, int256 _amount) external view returns (int256);

    function getCurrencies(bytes32) external view returns (Currency memory);

    function getEthDecimals(bytes32) external view returns (uint8);

    function getUsdDecimals(bytes32) external view returns (uint8);

    function getHaircut(bytes32 _ccy) external view returns (uint256);

    function getHistoricalETHPrice(bytes32 _ccy, uint80 _roundId) external view returns (int256);

    function getHistoricalUSDPrice(bytes32 _ccy, uint80 _roundId) external view returns (int256);

    function getLastETHPrice(bytes32 _ccy) external view returns (int256);

    function getLastUSDPrice(bytes32 _ccy) external view returns (int256);

    function isSupportedCcy(bytes32 _ccy) external view returns (bool);

    function linkPriceFeed(
        bytes32 _ccy,
        address _priceFeedAddr,
        bool _isEthPriceFeed
    ) external returns (bool);

    function removePriceFeed(bytes32 _ccy, bool _isEthPriceFeed) external;

    function supportCurrency(
        bytes32 _ccy,
        string memory _name,
        address _ethPriceFeed,
        uint256 _haircut
    ) external;

    function updateCcyHaircut(bytes32 _ccy, uint256 _haircut) external;

    function updateCurrencySupport(bytes32 _ccy, bool _isSupported) external;
}
","keccak256":"0xdb2d6560f24ffc5cc53e0e7da9e8c5cf0f4c92385d549e60189ea01116b29ea5","license":"MIT"},"contracts/interfaces/ILendingMarketController.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "../types/ProtocolTypes.sol";

struct Order {
    bytes32 ccy;
    uint256 term;
    ProtocolTypes.Side side;
    uint256 amount;
    uint256 rate;
}

interface ILendingMarketController {
    event LendingMarketCreated(
        bytes32 ccy,
        address indexed marketAddr,
        uint256 index,
        uint256 maturity
    );
    event LendingMarketsRotated(bytes32 ccy, uint256 oldMaturity, uint256 newMaturity);
    event OrderFilled(
        address indexed taker,
        bytes32 indexed ccy,
        uint48[] orderIds,
        address[] makers,
        uint256[] amounts,
        ProtocolTypes.Side side,
        uint256 indexed maturity,
        uint256 rate
    );
    event OrderCanceled(
        uint48 orderId,
        address indexed maker,
        bytes32 indexed ccy,
        ProtocolTypes.Side side,
        uint256 maturity,
        uint256 amount,
        uint256 rate
    );

    function getBasisDate(bytes32 _ccy) external view returns (uint256);

    function getLendingMarkets(bytes32 _ccy) external view returns (address[] memory);

    function getLendingMarket(bytes32 _ccy, uint256 _maturity) external view returns (address);

    function getBorrowRates(bytes32 _ccy) external view returns (uint256[] memory rates);

    function getLendRates(bytes32 _ccy) external view returns (uint256[] memory rates);

    function getBorrowOrderBook(
        bytes32 _ccy,
        uint256 _maturity,
        uint256 _limit
    )
        external
        view
        returns (
            uint256[] memory rates,
            uint256[] memory amounts,
            uint256[] memory quantities
        );

    function getLendOrderBook(
        bytes32 _ccy,
        uint256 _maturity,
        uint256 _limit
    )
        external
        view
        returns (
            uint256[] memory rates,
            uint256[] memory amounts,
            uint256[] memory quantities
        );

    function getMidRates(bytes32 _ccy) external view returns (uint256[] memory rates);

    function getMaturities(bytes32 _ccy) external view returns (uint256[] memory);

    function getTotalPresentValue(bytes32 ccy, address account) external view returns (int256);

    function getTotalPresentValueInETH(address account)
        external
        view
        returns (int256 totalPresentValue);

    function isInitializedLendingMarket(bytes32 _ccy) external view returns (bool);

    function initializeLendingMarket(
        bytes32 _ccy,
        uint256 _basisDate,
        uint256 _compoundFactor
    ) external;

    function createLendingMarket(bytes32 _ccy) external returns (address market);

    function createOrder(
        bytes32 _ccy,
