    function removeEscrowedAmount(
        address payer,
        address receiver,
        bytes32 ccy,
        uint256 amount
    ) external;

    function removeEscrowedAmounts(
        address[] calldata users,
        address receiver,
        bytes32 ccy,
        uint256[] calldata amounts
    ) external;
}
