pragma solidity 0.8.19;

library Constants {
    /// @dev Used for price digits in the basis (10000 -> 1)
    uint256 internal constant PRICE_DIGIT = 10000;

    /// @dev Used for percentage digits in the basis (10000 -> 100%)
    uint256 internal constant PCT_DIGIT = 10000;

    /// @dev Used for seconds in year (60 * 60 * 24 * 365)
    uint256 internal constant SECONDS_IN_YEAR = 31536000;

    /// @dev Used for maximum order count per currency
    uint256 internal constant MAXIMUM_ORDER_COUNT = 20;
}
","keccak256":"0x21443c5b15f48894fc2ab2f1ea266a01d59e7808f50c569bf5556f6bb298f5d6","license":"BUSL-1.1"},"contracts/protocol/libraries/math/RoundingInt256.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.19;

library RoundingInt256 {
    function div(int256 a, int256 b) internal pure returns (int256) {
        require(b != 0);

        int256 diff = (a * 10) / b - (a / b) * 10;
        if (diff >= 5) {
            return (a / b) + 1;
        } else if (diff <= -5) {
            return (a / b) - 1;
        } else {
            return a / b;
        }
    }
}
