pragma solidity ^0.8.9;

interface IBeaconProxyController {
    event BeaconProxyCreated(
        bytes32 indexed id,
        address indexed proxyAddress,
        address indexed implementationAddress
    );

    event BeaconProxyUpdated(
        bytes32 indexed id,
        address indexed proxyAddress,
        address indexed newImplementationAddress,
        address oldImplementationAddress
    );

    function getBeaconProxyAddress(bytes32 beaconName) external view returns (address);

    function setFutureValueVaultImpl(address newImpl) external;

    function setLendingMarketImpl(address newImpl) external;

    function deployFutureValueVault() external returns (address futureValueVault);

    function deployLendingMarket(
        bytes32 ccy,
        uint256 maturity,
        uint256 openingDate
    ) external returns (address market);
}
","keccak256":"0xcedc431629fa9537e673fc37ceae4cc645cf1f4ff9071b05396b9e3944ce6dd9","license":"MIT"},"contracts/interfaces/ICurrencyController.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import {Currency} from "../storages/CurrencyControllerStorage.sol";

/**
 * @dev Currency Controller contract is responsible for managing supported
 * currencies in Secured Finance Protocol
 *
 * Contract links new currencies to ETH Chainlink price feeds, without existing price feed
 * contract owner is not able to add a new currency into the protocol
 */
interface ICurrencyController {
    event CurrencyAdded(bytes32 indexed ccy, uint256 haircut);
    event CurrencyRemoved(bytes32 indexed ccy);

    event HaircutUpdated(bytes32 indexed ccy, uint256 haircut);

    event PriceFeedAdded(bytes32 ccy, string secondCcy, address indexed priceFeed);
    event PriceFeedRemoved(bytes32 ccy, string secondCcy, address indexed priceFeed);

    function convertFromETH(bytes32 _ccy, uint256 _amountETH)
        external
        view
        returns (uint256 amount);

    function convertToETH(bytes32 _ccy, uint256 _amount) external view returns (uint256 amount);

    function convertToETH(bytes32 _ccy, int256 _amount) external view returns (int256 amount);

    function convertToETH(bytes32 _ccy, uint256[] memory _amounts)
        external
        view
        returns (uint256[] memory amounts);

    function getEthDecimals(bytes32) external view returns (uint8);

    function getUsdDecimals(bytes32) external view returns (uint8);

    function getCurrencies() external view returns (bytes32[] memory);

    function getHaircut(bytes32 _ccy) external view returns (uint256);

    function getHistoricalETHPrice(bytes32 _ccy, uint80 _roundId) external view returns (int256);

    function getHistoricalUSDPrice(bytes32 _ccy, uint80 _roundId) external view returns (int256);

    function getLastETHPrice(bytes32 _ccy) external view returns (int256);

    function getLastUSDPrice(bytes32 _ccy) external view returns (int256);

    function currencyExists(bytes32 _ccy) external view returns (bool);

    function linkPriceFeed(
        bytes32 _ccy,
        address _priceFeedAddr,
        bool _isEthPriceFeed
    ) external returns (bool);

    function removePriceFeed(bytes32 _ccy, bool _isEthPriceFeed) external;

    function addCurrency(
        bytes32 _ccy,
        address _ethPriceFeed,
        uint256 _haircut
    ) external;

    function updateHaircut(bytes32 _ccy, uint256 _haircut) external;

    function removeCurrency(bytes32 _ccy) external;
}
","keccak256":"0x9f2a9f700546079ae054a7904b66ff83a1fa9a5643a0cb7ae1766a0dc265ec50","license":"MIT"},"contracts/interfaces/IFutureValueVault.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

interface IFutureValueVault {
    event Transfer(address indexed from, address indexed to, int256 value);

    function getTotalSupply(uint256 maturity) external view returns (uint256);

    function getFutureValue(address user)
        external
        view
        returns (int256 futureValue, uint256 maturity);

    function hasFutureValueInPastMaturity(address user, uint256 maturity)
        external
        view
        returns (bool);

    function addLendFutureValue(
        address user,
        uint256 amount,
        uint256 maturity,
        bool isTaker
    ) external returns (bool);

    function addBorrowFutureValue(
        address user,
        uint256 amount,
        uint256 maturity,
        bool isTaker
    ) external returns (bool);

    function offsetFutureValue(
        address lender,
        address borrower,
        uint256 maximumFVAmount
    ) external returns (uint256 offsetAmount);

    function removeFutureValue(address user, uint256 activeMaturity)
        external
        returns (
            int256 removedAmount,
            int256 currentAmount,
            uint256 maturity,
            bool removeFutureValue
        );
}
","keccak256":"0x026b0cc678d22e00305b931c4cea0c1d63d162d4b8163fb6158b50f7961bece7","license":"MIT"},"contracts/interfaces/IGenesisValueVault.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import {AutoRollLog} from "../storages/GenesisValueVaultStorage.sol";

interface IGenesisValueVault {
    event Transfer(bytes32 indexed ccy, address indexed from, address indexed to, int256 value);
    event AutoRollExecuted(
        bytes32 indexed ccy,
        uint256 lendingCompoundFactor,
        uint256 borrowingCompoundFactor,
        uint256 unitPrice,
        uint256 currentMaturity,
        uint256 previousMaturity
    );

    function isInitialized(bytes32 ccy) external view returns (bool);

    function decimals(bytes32 ccy) external view returns (uint8);

    function getTotalLendingSupply(bytes32 ccy) external view returns (uint256);

    function getTotalBorrowingSupply(bytes32 ccy) external view returns (uint256);

    function getGenesisValue(bytes32 ccy, address user) external view returns (int256);

    function getMaturityGenesisValue(bytes32 _ccy, uint256 _maturity)
        external
        view
        returns (int256);

    function getCurrentMaturity(bytes32 ccy) external view returns (uint256);

    function getLendingCompoundFactor(bytes32 ccy) external view returns (uint256);

    function getBorrowingCompoundFactor(bytes32 ccy) external view returns (uint256);

    function getAutoRollLog(bytes32 ccy, uint256 maturity)
        external
        view
        returns (AutoRollLog memory);

    function getGenesisValueInFutureValue(bytes32 ccy, address user) external view returns (int256);

    function calculateFVFromFV(
        bytes32 _ccy,
        uint256 _basisMaturity,
        uint256 _destinationMaturity,
        int256 _futureValue
    ) external view returns (int256);

    function calculateGVFromFV(
        bytes32 ccy,
        uint256 basisMaturity,
        int256 futureValue
    ) external view returns (int256);

    function calculateFVFromGV(
        bytes32 ccy,
        uint256 basisMaturity,
        int256 genesisValue
    ) external view returns (int256);

    function getBalanceFluctuationByAutoRolls(
        bytes32 ccy,
        address user,
        uint256 maturity
    ) external view returns (int256 fluctuation);

    function calculateBalanceFluctuationByAutoRolls(
        bytes32 ccy,
        int256 balance,
        uint256 fromMaturity,
        uint256 toMaturity
    ) external view returns (int256 fluctuation);

    function initializeCurrencySetting(
        bytes32 ccy,
        uint8 decimals,
        uint256 compoundFactor,
        uint256 maturity
    ) external;

    function executeAutoRoll(
        bytes32 ccy,
        uint256 maturity,
        uint256 nextMaturity,
        uint256 unitPrice,
        uint256 feeRate,
        uint256 totalFVAmount
    ) external;

    function updateGenesisValueWithFutureValue(
        bytes32 ccy,
        address user,
        uint256 basisMaturity,
        int256 fvAmount
    ) external;

    function updateGenesisValueWithResidualAmount(
        bytes32 ccy,
        address user,
        uint256 basisMaturity
    ) external;

    function offsetGenesisValue(
        bytes32 ccy,
        uint256 maturity,
        address lender,
        address borrower,
        int256 maximumGVAmount
    ) external returns (int256 offsetAmount);

    function cleanUpGenesisValue(
        bytes32 ccy,
        address user,
        uint256 maturity
    ) external;
}
","keccak256":"0x6e68921ed561969e72b49bc2d3c86b624406575eff8dd756f6672ecc2440f1e0","license":"MIT"},"contracts/interfaces/ILendingMarket.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "../types/ProtocolTypes.sol";
import {MarketOrder} from "../storages/LendingMarketStorage.sol";

interface ILendingMarket {
    event OrderCanceled(
        uint48 orderId,
        address indexed maker,
        ProtocolTypes.Side side,
        bytes32 ccy,
        uint256 maturity,
        uint256 amount,
        uint256 unitPrice
    );
    event OrderMade(
        uint48 orderId,
        uint48 originalOrderId,
        address indexed maker,
        ProtocolTypes.Side side,
        bytes32 ccy,
        uint256 maturity,
        uint256 amount,
        uint256 unitPrice
    );
    event OrdersTaken(
        address indexed taker,
        ProtocolTypes.Side side,
        bytes32 ccy,
        uint256 maturity,
        uint256 filledAmount,
        uint256 unitPrice,
        uint256 filledFutureValue
    );

    event OrdersCleaned(
        uint48[] orderIds,
        address indexed maker,
        ProtocolTypes.Side side,
        bytes32 indexed ccy,
        uint256 maturity
    );

    event MarketOpened(uint256 maturity, uint256 prevMaturity);

    event ItayoseExecuted(bytes32 ccy, uint256 maturity, uint256 openingPrice);

    struct Market {
        bytes32 ccy;
        uint256 maturity;
        uint256 openingDate;
        uint256 borrowUnitPrice;
        uint256 lendUnitPrice;
        uint256 midUnitPrice;
    }

    function getMarket() external view returns (Market memory);

    function getBorrowUnitPrice() external view returns (uint256 unitPrice);

    function getLendUnitPrice() external view returns (uint256 unitPrice);

    function getMidUnitPrice() external view returns (uint256 unitPrice);

    function getBorrowOrderBook(uint256 limit)
        external
        view
        returns (
            uint256[] memory unitPrices,
            uint256[] memory amounts,
            uint256[] memory quantities
        );

    function getLendOrderBook(uint256 limit)
        external
        view
        returns (
            uint256[] memory unitPrices,
            uint256[] memory amounts,
            uint256[] memory quantities
        );

    function getMaturity() external view returns (uint256);

    function getCurrency() external view returns (bytes32);

    function getOpeningDate() external view returns (uint256);

    function isItayosePeriod() external view returns (bool);

    function getOpeningUnitPrice() external view returns (uint256);

    function isMatured() external view returns (bool);

    function isOpened() external view returns (bool);

    function isPreOrderPeriod() external returns (bool);

    function getOrder(uint48 _orderId)
        external
        view
        returns (
            ProtocolTypes.Side,
            uint256 unitPrice,
            uint256 maturity,
            address maker,
            uint256 amount,
            uint256 timestamp
        );

    function getTotalAmountFromLendOrders(address _user)
        external
        view
        returns (
            uint256 activeAmount,
            uint256 inactiveAmount,
            uint256 inactiveFutureValue,
            uint256 maturity
        );

    function getTotalAmountFromBorrowOrders(address _user)
        external
        view
        returns (
            uint256 activeAmount,
            uint256 inactiveAmount,
            uint256 inactiveFutureValue,
            uint256 maturity
        );

    function getActiveLendOrderIds(address _user)
        external
        view
        returns (uint48[] memory activeOrderIds);

    function getActiveBorrowOrderIds(address _user)
        external
        view
        returns (uint48[] memory activeOrderIds);

    function estimateFilledAmount(ProtocolTypes.Side _side, uint256 _futureValue)
        external
        view
        returns (uint256 amount);

    function openMarket(uint256 maturity, uint256 openingDate) external returns (uint256);

    function cancelOrder(address user, uint48 orderId)
        external
        returns (
            ProtocolTypes.Side,
            uint256,
            uint256
        );

    function createPreOrder(
        ProtocolTypes.Side side,
        address user,
        uint256 amount,
        uint256 unitPrice
    ) external;

    function unwindOrder(
        ProtocolTypes.Side _side,
        address _user,
        uint256 _futureValue
    ) external returns (uint256 filledAmount, uint256 filledFutureValue);

    function executeItayoseCall() external;

    function cleanOrders(address _user)
        external
        returns (
            uint256 activeLendOrderCount,
            uint256 activeBorrowOrderCount,
            uint256 removedLendOrderFutureValue,
            uint256 removedBorrowOrderFutureValue,
            uint256 removedLendOrderAmount,
            uint256 removedBorrowOrderAmount,
            uint256 maturity
        );

    function createOrder(
        ProtocolTypes.Side side,
        address account,
        uint256 amount,
        uint256 unitPrice,
        bool ignoreRemainingAmount
    ) external returns (uint256 executedRate, uint256 remainingAmount);

    function pauseMarket() external;

    function unpauseMarket() external;
}
