library OwnableStorage {
    bytes32 internal constant STORAGE_SLOT = keccak256("sf.storage.ownable");

    struct Storage {
        address owner;
    }

    function slot() internal pure returns (Storage storage r) {
        bytes32 _slot = STORAGE_SLOT;
        /// @solidity memory-safe-assembly
        assembly {
            r.slot := _slot
        }
    }
}
