contracts/protocol/libraries/QuickSort.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

library QuickSort {
    function sort(uint256[] memory data) public pure returns (uint256[] memory) {
        quickSort(data, int256(0), int256(data.length - 1));
        return data;
    }

    function quickSort(
        uint256[] memory arr,
        int256 left,
        int256 right
    ) internal pure {
        int256 i = left;
        int256 j = right;
        if (i == j) return;
        uint256 pivot = arr[uint256(left + (right - left) / 2)];
        while (i <= j) {
            while (arr[uint256(i)] < pivot) i++;
            while (pivot < arr[uint256(j)]) j--;
            if (i <= j) {
                (arr[uint256(i)], arr[uint256(j)]) = (arr[uint256(j)], arr[uint256(i)]);
                i++;
                j--;
            }
        }
        if (left < j) quickSort(arr, left, j);
        if (i < right) quickSort(arr, i, right);
    }
}

contracts/protocol/libraries/math/Roundi.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

library RoundingInt256 {
    function div(int256 a, int256 b) internal pure returns (int256) {
        require(b != 0);

        int256 diff = (a * 10) / b - (a / b) * 10;
        if (diff >= 5) {
            return (a / b) + 1;
        } else if (diff <= -5) {
            return (a / b) - 1;
        } else {
            return a / b;
        }
    }
}

