// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

library ReserveFundStorage {
    bytes32 internal constant STORAGE_SLOT = keccak256("sf.storage.reserveFund");

    struct Storage {
        bool paused;
    }

    function slot() internal pure returns (Storage storage r) {
        bytes32 _slot = STORAGE_SLOT;
        /// @solidity memory-safe-assembly
        assembly {
            r.slot := _slot
        }
    }
}

contracts/protocol/LendingMarket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

// interfaces
import {ILendingMarket} from "./interfaces/ILendingMarket.sol";
// libraries
import {Contracts} from "./libraries/Contracts.sol";
import {OrderBookLogic} from "./libraries/logics/OrderBookLogic.sol";
import {RoundingUint256} from "./libraries/math/RoundingUint256.sol";
// mixins
import {MixinAddressResolver} from "./mixins/MixinAddressResolver.sol";
// types
import {ProtocolTypes} from "./types/ProtocolTypes.sol";
// utils
import {Pausable} from "./utils/Pausable.sol";
import {Proxyable} from "./utils/Proxyable.sol";
// storages
import {LendingMarketStorage as Storage, ItayoseLog} from "./storages/LendingMarketStorage.sol";

/**
 * @notice Implements the module that allows lending market participants to create/cancel market orders,
 * and also provides a future value calculation module.
 *
 * For updates, this contract is basically called from `LendingMarketController.sol`instead of being called \
 * directly by the user.
 *
 * @dev The market orders is stored in structured red-black trees and doubly linked lists in each node.
 */
contract LendingMarket is ILendingMarket, MixinAddressResolver, Pausable, Proxyable {
    using RoundingUint256 for uint256;

    uint256 private constant PRE_ORDER_PERIOD = 7 days;
    uint256 private constant ITAYOSE_PERIOD = 1 hours;

    /**
     * @notice Modifier to make a function callable only by order maker.
     * @param _orderId Market order id
     */
    modifier onlyMaker(address user, uint48 _orderId) {
        (, , , address maker, , , ) = getOrder(_orderId);
        require(maker != address(0), "Order not found");
        require(user == maker, "Caller is not the maker");
        _;
    }

    /**
     * @notice Modifier to check if the market is opened.
     */
    modifier ifOpened() {
        require(isOpened(), "Market is not opened");
        _;
    }

    /**
     * @notice Modifier to check if the market is matured.
     */
    modifier ifMatured() {
        require(isMatured(), "Market is not matured");
        _;
    }

    modifier ifItayosePeriod() {
        require(isItayosePeriod(), "Not in the Itayose period");
        _;
    }

    modifier ifPreOrderPeriod() {
        require(isPreOrderPeriod(), "Not in the pre-order period");
        _;
    }

    /**
     * @notice Initializes the contract.
     * @dev Function is invoked by the proxy contract when the contract is added to the ProxyController.
     * @param _resolver The address of the Address Resolver contract
     * @param _ccy The main currency for the order book
     * @param _maturity The initial maturity of the market
     * @param _openingDate The timestamp when the market opens
     */
    function initialize(
        address _resolver,
        bytes32 _ccy,
        uint256 _maturity,
        uint256 _openingDate
    ) public initializer onlyBeacon {
        registerAddressResolver(_resolver);

        Storage.slot().ccy = _ccy;
        Storage.slot().maturity = _maturity;
        Storage.slot().openingDate = _openingDate;

        if (block.timestamp >= (_openingDate - ITAYOSE_PERIOD)) {
            Storage.slot().isReady[Storage.slot().maturity] = true;
        }

        buildCache();
    }

    // @inheritdoc MixinAddressResolver
    function requiredContracts() public pure override returns (bytes32[] memory contracts) {
        contracts = new bytes32[](1);
        contracts[0] = Contracts.LENDING_MARKET_CONTROLLER;
    }

    // @inheritdoc MixinAddressResolver
    function acceptedContracts() public pure override returns (bytes32[] memory contracts) {
        contracts = new bytes32[](1);
        contracts[0] = Contracts.LENDING_MARKET_CONTROLLER;
    }

    /**
     * @notice Gets the market data.
     * @return market The market data
     */
    function getMarket() external view override returns (Market memory market) {
        return
            Market({
                ccy: Storage.slot().ccy,
                maturity: Storage.slot().maturity,
                openingDate: Storage.slot().openingDate,
                borrowUnitPrice: OrderBookLogic.getLowestBorrowingUnitPrice(),
                lendUnitPrice: OrderBookLogic.getHighestLendingUnitPrice(),
                midUnitPrice: getMidUnitPrice(),
                openingUnitPrice: Storage.slot().openingUnitPrices[Storage.slot().maturity],
                isReady: isReady()
            });
    }

    /**
     * @notice Gets the highest borrow price per future value.
     * @return The highest borrow price per future value
     */
    function getBorrowUnitPrice() external view override returns (uint256) {
        return OrderBookLogic.getLowestBorrowingUnitPrice();
    }

    /**
     * @notice Gets the lowest lend price per future value.
     * @return The lowest lend price per future value
     */
    function getLendUnitPrice() external view override returns (uint256) {
        return OrderBookLogic.getHighestLendingUnitPrice();
    }

    /**
     * @notice Gets the mid price per future value.
     * @return The mid price per future value
     */
    function getMidUnitPrice() public view override returns (uint256) {
        uint256 borrowUnitPrice = OrderBookLogic.getLowestBorrowingUnitPrice();
        uint256 lendUnitPrice = OrderBookLogic.getHighestLendingUnitPrice();
        return (borrowUnitPrice + lendUnitPrice).div(2);
    }

    /**
     * @notice Gets the order book of borrow.
     * @param _limit Max limit to get unit prices
     * @return unitPrices The array of borrow unit prices
     */
    function getBorrowOrderBook(uint256 _limit)
        external
        view
        override
        returns (
            uint256[] memory unitPrices,
            uint256[] memory amounts,
            uint256[] memory quantities
        )
    {
        return OrderBookLogic.getBorrowOrderBook(_limit);
    }

    /**
     * @notice Gets the order book of lend.
     * @param _limit Max limit to get unit prices
     * @return unitPrices The array of lending unit prices
     */
    function getLendOrderBook(uint256 _limit)
        external
        view
        override
        returns (
            uint256[] memory unitPrices,
            uint256[] memory amounts,
            uint256[] memory quantities
        )
    {
        return OrderBookLogic.getLendOrderBook(_limit);
    }

    /**
     * @notice Gets the current market maturity.
     * @return maturity The market maturity
     */
    function getMaturity() external view override returns (uint256 maturity) {
        return Storage.slot().maturity;
    }

    /**
     * @notice Gets the market currency.
     * @return currency The market currency
     */
    function getCurrency() external view override returns (bytes32 currency) {
        return Storage.slot().ccy;
    }

    /**
     * @notice Gets the market opening date.
     * @return openingDate The market opening date
     */
    function getOpeningDate() external view override returns (uint256 openingDate) {
        return Storage.slot().openingDate;
    }

    /**
     * @notice Gets the market opening unit price.
     * @return openingUnitPrices The market opening unit price
     */
    function getOpeningUnitPrice() external view override returns (uint256 openingUnitPrices) {
        return Storage.slot().openingUnitPrices[Storage.slot().maturity];
    }

    /**
     * @notice Gets if the market is ready.
     * @return The boolean if the market is ready or not
     */
    function isReady() public view override returns (bool) {
        return Storage.slot().isReady[Storage.slot().maturity];
    }

    /**
     * @notice Gets if the market is matured.
     * @return The boolean if the market is matured or not
     */
    function isMatured() public view override returns (bool) {
        return block.timestamp >= Storage.slot().maturity;
    }

    /**
     * @notice Gets if the market is opened.
     * @return The boolean if the market is opened or not
     */
    function isOpened() public view override returns (bool) {
        return
            Storage.slot().isReady[Storage.slot().maturity] &&
            !isMatured() &&
            block.timestamp >= Storage.slot().openingDate;
    }

    /**
     * @notice Gets if the market is under the Itayose period.
     * @return The boolean if the market is under the Itayose period.
     */
    function isItayosePeriod() public view returns (bool) {
        return
            block.timestamp >= (Storage.slot().openingDate - ITAYOSE_PERIOD) &&
            !Storage.slot().isReady[Storage.slot().maturity];
    }

    /**
     * @notice Gets if the market is under the pre-order period.
     * @return The boolean if the market is under the pre-order period.
     */
    function isPreOrderPeriod() public view override returns (bool) {
        return
            block.timestamp >= (Storage.slot().openingDate - PRE_ORDER_PERIOD) &&
            block.timestamp < (Storage.slot().openingDate - ITAYOSE_PERIOD);
    }

    /**
     * @notice Gets the market order from the order book.
     * @param _orderId The market order id
     * @return side Order position type, Borrow or Lend
     * @return unitPrice Amount of interest unit price
     * @return maturity The maturity of the selected order
     * @return maker The order maker
     * @return amount Order amount
     * @return timestamp Timestamp when the order was created
     * @return isPreOrder The boolean if the order is a pre-order.
     */
    function getOrder(uint48 _orderId)
        public
        view
        override
        returns (
            ProtocolTypes.Side side,
            uint256 unitPrice,
            uint256 maturity,
            address maker,
            uint256 amount,
            uint256 timestamp,
            bool isPreOrder
        )
    {
        return OrderBookLogic.getOrder(_orderId);
    }

    /**
     * @notice Calculates and gets the active and inactive amounts from the user orders of lending deals.
     * @param _user User's address
     * @return activeAmount The total amount of active order on the order book
     * @return inactiveAmount The total amount of inactive orders filled on the order book
     * @return inactiveFutureValue The total future value amount of inactive orders filled on the order book
     * @return maturity The maturity of market that orders were placed.
     */
    function getTotalAmountFromLendOrders(address _user)
        external
        view
        override
        returns (
            uint256 activeAmount,
            uint256 inactiveAmount,
            uint256 inactiveFutureValue,
            uint256 maturity
        )
    {
        return OrderBookLogic.getTotalAmountFromLendOrders(_user);
    }

    /**
     * @notice Calculates and gets the active and inactive amounts from the user orders of borrowing deals.
     * @param _user User's address
     * @return activeAmount The total amount of active order on the order book
     * @return inactiveAmount The total amount of inactive orders filled on the order book
     * @return inactiveFutureValue The total future value amount of inactive orders filled on the order book
     * @return maturity The maturity of market that orders were placed.
     */
    function getTotalAmountFromBorrowOrders(address _user)
        external
        view
        override
        returns (
            uint256 activeAmount,
            uint256 inactiveAmount,
            uint256 inactiveFutureValue,
            uint256 maturity
        )
    {
        return OrderBookLogic.getTotalAmountFromBorrowOrders(_user);
    }

    /**
     * @notice Gets active and inactive order IDs in the lending order book
     * @param _user User's address
     */
    function getLendOrderIds(address _user)
        external
        view
        override
        returns (uint48[] memory activeOrderIds, uint48[] memory inActiveOrderIds)
    {
        (activeOrderIds, inActiveOrderIds) = OrderBookLogic.getLendOrderIds(_user);
    }

    /**
     * @notice Gets active and inactive order IDs in the borrowing order book
     * @param _user User's address
     */
    function getBorrowOrderIds(address _user)
        external
        view
        override
        returns (uint48[] memory activeOrderIds, uint48[] memory inActiveOrderIds)
    {
        (activeOrderIds, inActiveOrderIds) = OrderBookLogic.getBorrowOrderIds(_user);
    }

    /**
     * @notice Estimates the filled amount at the time of order creation on the order book
     * using the future value amount.
     * @param _side Order position type, Borrow or Lend
     * @param _futureValue Future value amount
     * @return amount The estimated amount in the present value that is filled on the order book
     */
    function estimateFilledAmount(ProtocolTypes.Side _side, uint256 _futureValue)
        external
        view
        override
        returns (uint256 amount)
    {
        return OrderBookLogic.estimateFilledAmount(_side, _futureValue);
    }

    /**
     * @notice Opens market
     * @param _maturity The new maturity
     * @param _openingDate The timestamp when the market opens
     * @return prevMaturity The previous maturity updated
     */
    function openMarket(uint256 _maturity, uint256 _openingDate)
        external
        override
        ifMatured
        onlyAcceptedContracts
        returns (uint256 prevMaturity)
    {
        prevMaturity = Storage.slot().maturity;
        Storage.slot().maturity = _maturity;
        Storage.slot().openingDate = _openingDate;

        emit MarketOpened(_maturity, prevMaturity);
    }

    /**
     * @notice Cancels the order.
     * @param _user User address
     * @param _orderId Market order id
     */
    function cancelOrder(address _user, uint48 _orderId)
        external
        override
        onlyMaker(_user, _orderId)
        whenNotPaused
        onlyAcceptedContracts
    {
        (ProtocolTypes.Side side, uint256 removedAmount, uint256 unitPrice) = OrderBookLogic
            .removeOrder(_user, _orderId);

        emit OrderCanceled(
            _orderId,
            _user,
            side,
            Storage.slot().ccy,
            Storage.slot().maturity,
            removedAmount,
            unitPrice
        );
    }

    /**
     * @notice Cleans up own orders to remove order ids that are already filled on the order book.
     * @dev The order list per user is not updated in real-time when an order is filled.
     * This function removes the filled order from that order list per user to reduce gas costs
     * for lazy evaluation if the collateral is enough or not.
     *
     * @param _user User address
     * @return activeLendOrderCount The total amount of active lend order on the order book
     * @return activeBorrowOrderCount The total amount of active borrow order on the order book
     * @return removedLendOrderFutureValue The total FV amount of the removed lend order amount from the order book
     * @return removedBorrowOrderFutureValue The total FV amount of the removed borrow order amount from the order book
     * @return removedLendOrderAmount The total PV amount of the removed lend order amount from the order book
     * @return removedBorrowOrderAmount The total PV amount of the removed borrow order amount from the order book
     * @return maturity The maturity of the removed orders
     */
    function cleanUpOrders(address _user)
        external
        override
        returns (
            uint256 activeLendOrderCount,
            uint256 activeBorrowOrderCount,
            uint256 removedLendOrderFutureValue,
            uint256 removedBorrowOrderFutureValue,
            uint256 removedLendOrderAmount,
            uint256 removedBorrowOrderAmount,
            uint256 maturity
        )
    {
        maturity = Storage.slot().userCurrentMaturities[_user];

        uint48[] memory lendOrderIds;
        uint48[] memory borrowOrderIds;

        (
            lendOrderIds,
            activeLendOrderCount,
            removedLendOrderFutureValue,
            removedLendOrderAmount
        ) = OrderBookLogic.cleanLendOrders(_user);

        (
            borrowOrderIds,
            activeBorrowOrderCount,
            removedBorrowOrderFutureValue,
            removedBorrowOrderAmount
        ) = OrderBookLogic.cleanBorrowOrders(_user);

        if (removedLendOrderAmount > 0) {
            emit OrdersCleaned(
                lendOrderIds,
                _user,
                ProtocolTypes.Side.LEND,
                Storage.slot().ccy,
                Storage.slot().maturity
            );
        }

        if (removedBorrowOrderAmount > 0) {
            emit OrdersCleaned(
                borrowOrderIds,
                _user,
                ProtocolTypes.Side.BORROW,
                Storage.slot().ccy,
                Storage.slot().maturity
            );
        }
    }

    /**
     * @notice Creates the order. Takes the order if the order is matched,
     * and places new order if not match it.
     * @param _side Order position type, Borrow or Lend
     * @param _user User's address
     * @param _amount Amount of funds the maker wants to borrow/lend
     * @param _unitPrice Amount of unit price taker wish to borrow/lend
     * @param _circuitBreakerLimitRange Rate limit range for the circuit breaker
     * @return filledOrder User's Filled order of the user
     * @return partiallyFilledOrder Partially filled order on the order book
     */
    function createOrder(
        ProtocolTypes.Side _side,
        address _user,
        uint256 _amount,
        uint256 _unitPrice,
        uint256 _circuitBreakerLimitRange
    )
        external
        override
        whenNotPaused
        onlyAcceptedContracts
        ifOpened
        returns (FilledOrder memory filledOrder, PartiallyFilledOrder memory partiallyFilledOrder)
    {
        require(_amount > 0, "Amount is zero");
        _updateUserMaturity(_user);

        (bool isFilled, uint256 executedUnitPrice, bool ignoreRemainingAmount) = OrderBookLogic
            .checkCircuitBreakerThreshold(_side, _unitPrice, _circuitBreakerLimitRange);

        if (isFilled) {
            (filledOrder, partiallyFilledOrder) = _takeOrder(
                _side,
                _user,
                _amount,
                executedUnitPrice,
                ignoreRemainingAmount
            );
        } else if (!ignoreRemainingAmount) {
            _makeOrder(_side, _user, _amount, executedUnitPrice, false);
        } else {
            emit OrderBlockedByCircuitBreaker(
                _user,
                Storage.slot().ccy,
                _side,
                Storage.slot().maturity,
                executedUnitPrice
            );
        }
    }

    /**
     * @notice Creates a pre-order. A pre-order will only be accepted from 168 hours (7 days) to 1 hour
     * before the market opens (Pre-order period). At the end of this period, Itayose will be executed.
     *
     * @param _side Order position type, Borrow or Lend
     * @param _amount Amount of funds the maker wants to borrow/lend
     * @param _unitPrice Amount of unit price taker wish to borrow/lend
     */
    function createPreOrder(
        ProtocolTypes.Side _side,
        address _user,
        uint256 _amount,
        uint256 _unitPrice
    ) external override whenNotPaused onlyAcceptedContracts ifPreOrderPeriod {
        require(_amount > 0, "Amount is zero");

        _updateUserMaturity(_user);

        if (
            (_side == ProtocolTypes.Side.LEND && OrderBookLogic.hasBorrowOrder(_user)) ||
            (_side == ProtocolTypes.Side.BORROW && OrderBookLogic.hasLendOrder(_user))
        ) {
            revert("Opposite side order exists");
        }

        _makeOrder(_side, _user, _amount, _unitPrice, true);
    }

    /**
     * @notice Unwinds lending or borrowing positions by a specified future value amount.
     * @param _side Order position type, Borrow or Lend
     * @param _user User's address
     * @param _futureValue Amount of future value unwound
     * @param _circuitBreakerLimitRange Rate limit range for the circuit breaker
     * @return filledOrder User's Filled order of the user
     * @return partiallyFilledOrder Partially filled order
     */
    function unwind(
        ProtocolTypes.Side _side,
        address _user,
        uint256 _futureValue,
        uint256 _circuitBreakerLimitRange
    )
        external
        override
        whenNotPaused
        onlyAcceptedContracts
        ifOpened
        returns (FilledOrder memory filledOrder, PartiallyFilledOrder memory partiallyFilledOrder)
    {
        require(_futureValue > 0, "Can't place empty future value amount");
        return _unwind(_side, _user, _futureValue, _circuitBreakerLimitRange);
    }

    /**
     * @notice Executes Itayose to aggregate pre-orders and determine the opening unit price.
     * After this action, the market opens.
     * @dev If the opening date had already passed when this contract was created, this Itayose need not be executed.
     * @return openingUnitPrice The opening price when Itayose is executed
     * @return totalOffsetAmount The total filled amount when Itayose is executed
     * @return openingDate The timestamp when the market opens
     * @return partiallyFilledLendingOrder Partially filled lending order on the order book
     * @return partiallyFilledBorrowingOrder Partially filled borrowing order on the order book
     */
    function executeItayoseCall()
        external
        override
        whenNotPaused
        onlyAcceptedContracts
        ifItayosePeriod
        returns (
            uint256 openingUnitPrice,
            uint256 totalOffsetAmount,
            uint256 openingDate,
            PartiallyFilledOrder memory partiallyFilledLendingOrder,
            PartiallyFilledOrder memory partiallyFilledBorrowingOrder
        )
    {
        uint256 lastLendUnitPrice;
        uint256 lastBorrowUnitPrice;

        (
            openingUnitPrice,
            lastLendUnitPrice,
            lastBorrowUnitPrice,
            totalOffsetAmount
        ) = OrderBookLogic.getOpeningUnitPrice();

        if (totalOffsetAmount > 0) {
            ProtocolTypes.Side[2] memory sides = [
                ProtocolTypes.Side.LEND,
                ProtocolTypes.Side.BORROW
            ];

            for (uint256 i; i < sides.length; i++) {
                ProtocolTypes.Side partiallyFilledOrderSide;
                (
                    ,
                    ,
                    ,
                    uint48 partiallyFilledOrderId,
                    address partiallyFilledMaker,
                    uint256 partiallyFilledAmount,
                    uint256 partiallyFilledFutureValue,

                ) = OrderBookLogic.dropOrders(sides[i], totalOffsetAmount, 0, 0);

                if (partiallyFilledFutureValue > 0) {
                    if (sides[i] == ProtocolTypes.Side.LEND) {
                        partiallyFilledOrderSide = ProtocolTypes.Side.BORROW;
                        partiallyFilledBorrowingOrder.maker = partiallyFilledMaker;
                        partiallyFilledBorrowingOrder.amount = partiallyFilledAmount;
                        partiallyFilledBorrowingOrder.futureValue = partiallyFilledFutureValue;
                    } else {
                        partiallyFilledOrderSide = ProtocolTypes.Side.LEND;
                        partiallyFilledLendingOrder.maker = partiallyFilledMaker;
                        partiallyFilledLendingOrder.amount = partiallyFilledAmount;
                        partiallyFilledLendingOrder.futureValue = partiallyFilledFutureValue;
                    }

                    emit OrderPartiallyTaken(
                        partiallyFilledOrderId,
                        partiallyFilledMaker,
                        partiallyFilledOrderSide,
                        Storage.slot().ccy,
                        Storage.slot().maturity,
                        partiallyFilledAmount,
                        partiallyFilledFutureValue
                    );
                }
            }

            emit ItayoseExecuted(
                Storage.slot().ccy,
                Storage.slot().maturity,
                openingUnitPrice,
                lastLendUnitPrice,
                lastBorrowUnitPrice,
                totalOffsetAmount
            );
        }

        Storage.slot().isReady[Storage.slot().maturity] = true;
        Storage.slot().openingUnitPrices[Storage.slot().maturity] = openingUnitPrice;
        Storage.slot().itayoseLogs[Storage.slot().maturity] = ItayoseLog(
            lastLendUnitPrice,
            lastBorrowUnitPrice
        );
        openingDate = Storage.slot().openingDate;
    }

    /**
     * @notice Pauses the lending market.
     */
    function pauseMarket() external override onlyAcceptedContracts {
        _pause();
    }

    /**
     * @notice Unpauses the lending market.
     */
    function unpauseMarket() external override onlyAcceptedContracts {
        _unpause();
    }

    function _updateUserMaturity(address _user) private {
        uint256 userMaturity = Storage.slot().userCurrentMaturities[_user];
        require(
            userMaturity == Storage.slot().maturity ||
                (userMaturity != Storage.slot().maturity &&
                    Storage.slot().activeLendOrderIds[_user].length == 0 &&
                    Storage.slot().activeBorrowOrderIds[_user].length == 0),
            "Order found in past maturity"
        );

        if (userMaturity != Storage.slot().maturity) {
            Storage.slot().userCurrentMaturities[_user] = Storage.slot().maturity;
        }
    }

    /**
     * @notice Makes new market order.
     * @param _side Order position type, Borrow or Lend
     * @param _user User's address
     * @param _amount Amount of funds the maker wants to borrow/lend
     * @param _unitPrice Preferable interest unit price
     */
    function _makeOrder(
        ProtocolTypes.Side _side,
        address _user,
        uint256 _amount,
        uint256 _unitPrice,
        bool _isPreOrder
    ) private returns (uint48 orderId) {
        orderId = OrderBookLogic.insertOrder(_side, _user, _amount, _unitPrice);
        if (_isPreOrder) {
            Storage.slot().isPreOrder[orderId] = true;
        }

        emit OrderMade(
            orderId,
            _user,
            _side,
            Storage.slot().ccy,
            Storage.slot().maturity,
            _amount,
            _unitPrice,
            _isPreOrder
        );
    }

    /**
     * @notice Takes the market order.
     * @param _side Order position type, Borrow or Lend
     * @param _user User's address
     * @param _amount Amount of funds the maker wants to borrow/lend
     * @param _unitPrice Amount of unit price taken
     * @param _ignoreRemainingAmount Boolean for whether to ignore the remaining amount after taking orders
     */
    function _takeOrder(
        ProtocolTypes.Side _side,
        address _user,
        uint256 _amount,
        uint256 _unitPrice,
        bool _ignoreRemainingAmount
    )
        private
        returns (FilledOrder memory filledOrder, PartiallyFilledOrder memory partiallyFilledOrder)
    {
        uint48 partiallyFilledOrderId;
        uint256 remainingAmount;

        (
            filledOrder.unitPrice,
            ,
            filledOrder.futureValue,
            partiallyFilledOrderId,
            partiallyFilledOrder.maker,
            partiallyFilledOrder.amount,
            partiallyFilledOrder.futureValue,
            remainingAmount
        ) = OrderBookLogic.dropOrders(_side, _amount, 0, _unitPrice);

        filledOrder.amount = _amount - remainingAmount;

        emit OrdersTaken(
            _user,
            _side,
            Storage.slot().ccy,
            Storage.slot().maturity,
            filledOrder.amount,
            filledOrder.unitPrice,
            filledOrder.futureValue
        );

        if (partiallyFilledOrder.futureValue > 0) {
            emit OrderPartiallyTaken(
                partiallyFilledOrderId,
                partiallyFilledOrder.maker,
                _side == ProtocolTypes.Side.LEND
                    ? ProtocolTypes.Side.BORROW
                    : ProtocolTypes.Side.LEND,
                Storage.slot().ccy,
                Storage.slot().maturity,
                partiallyFilledOrder.amount,
                partiallyFilledOrder.futureValue
            );
        }

        if (remainingAmount > 0) {
            if (_ignoreRemainingAmount) {
                filledOrder.ignoredAmount = remainingAmount;
            } else {
                // Make a new order for the remaining amount of input
                _makeOrder(_side, _user, remainingAmount, _unitPrice, false);
            }
        }
    }

    function _unwind(
        ProtocolTypes.Side _side,
        address _user,
        uint256 _futureValue,
        uint256 _circuitBreakerLimitRange
    )
        private
        returns (FilledOrder memory filledOrder, PartiallyFilledOrder memory partiallyFilledOrder)
    {
        (bool isFilled, uint256 executedUnitPrice, ) = OrderBookLogic.checkCircuitBreakerThreshold(
            _side,
            0,
            _circuitBreakerLimitRange
        );

        if (isFilled) {
            uint48 partiallyFilledOrderId;

            (
                filledOrder.unitPrice,
                filledOrder.amount,
                filledOrder.futureValue,
                partiallyFilledOrderId,
                partiallyFilledOrder.maker,
                partiallyFilledOrder.amount,
                partiallyFilledOrder.futureValue,

            ) = OrderBookLogic.dropOrders(_side, 0, _futureValue, executedUnitPrice);

            emit OrdersTaken(
                _user,
                _side,
                Storage.slot().ccy,
                Storage.slot().maturity,
                filledOrder.amount,
                filledOrder.unitPrice,
                filledOrder.futureValue
            );

            if (partiallyFilledOrder.futureValue > 0) {
                emit OrderPartiallyTaken(
                    partiallyFilledOrderId,
                    partiallyFilledOrder.maker,
                    _side == ProtocolTypes.Side.LEND
                        ? ProtocolTypes.Side.BORROW
                        : ProtocolTypes.Side.LEND,
                    Storage.slot().ccy,
                    Storage.slot().maturity,
                    partiallyFilledOrder.amount,
                    partiallyFilledOrder.futureValue
                );
            }
        } else {
            emit OrderBlockedByCircuitBreaker(
                _user,
                Storage.slot().ccy,
                _side,
                Storage.slot().maturity,
                executedUnitPrice
            );
        }
    }
}

contracts/protocol/libraries/logics/Orde.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import {OrderStatisticsTreeLib, PartiallyFilledOrder, OrderItem} from "../OrderStatisticsTreeLib.sol";
import {Constants} from "../Constants.sol";
import {RoundingUint256} from "../math/RoundingUint256.sol";
import {ILendingMarket} from "../../interfaces/ILendingMarket.sol";
import {ProtocolTypes} from "../../types/ProtocolTypes.sol";
import {LendingMarketStorage as Storage, MarketOrder} from "../../storages/LendingMarketStorage.sol";

library OrderBookLogic {
    using OrderStatisticsTreeLib for OrderStatisticsTreeLib.Tree;
    using RoundingUint256 for uint256;

    function getHighestLendingUnitPrice() public view returns (uint256) {
        return Storage.slot().lendOrders[Storage.slot().maturity].last();
    }

    function getLowestBorrowingUnitPrice() public view returns (uint256) {
        uint256 unitPrice = Storage.slot().borrowOrders[Storage.slot().maturity].first();
        return unitPrice == 0 ? Constants.PRICE_DIGIT : unitPrice;
    }

    function hasBorrowOrder(address _user) external view returns (bool) {
        return Storage.slot().activeBorrowOrderIds[_user].length != 0;
    }

    function hasLendOrder(address _user) external view returns (bool) {
        return Storage.slot().activeLendOrderIds[_user].length != 0;
    }

    function getLendOrderBook(uint256 _limit)
        external
        view
        returns (
            uint256[] memory unitPrices,
            uint256[] memory amounts,
            uint256[] memory quantities
        )
    {
        unitPrices = new uint256[](_limit);
        amounts = new uint256[](_limit);
        quantities = new uint256[](_limit);

        uint256 unitPrice = Storage.slot().lendOrders[Storage.slot().maturity].last();
        unitPrices[0] = unitPrice;
        amounts[0] = Storage.slot().lendOrders[Storage.slot().maturity].getNodeTotalAmount(
            unitPrice
        );
        quantities[0] = Storage.slot().lendOrders[Storage.slot().maturity].getNodeCount(unitPrice);

        for (uint256 i = 1; i < unitPrices.length; i++) {
            if (unitPrice == 0) {
                break;
            }

            unitPrice = Storage.slot().lendOrders[Storage.slot().maturity].prev(unitPrice);
            unitPrices[i] = unitPrice;
            amounts[i] = Storage.slot().lendOrders[Storage.slot().maturity].getNodeTotalAmount(
                unitPrice
            );
            quantities[i] = Storage.slot().lendOrders[Storage.slot().maturity].getNodeCount(
                unitPrice
            );
        }
    }

    function getBorrowOrderBook(uint256 _limit)
        external
        view
        returns (
            uint256[] memory unitPrices,
            uint256[] memory amounts,
            uint256[] memory quantities
        )
    {
        unitPrices = new uint256[](_limit);
        amounts = new uint256[](_limit);
        quantities = new uint256[](_limit);

        uint256 unitPrice = Storage.slot().borrowOrders[Storage.slot().maturity].first();
        unitPrices[0] = unitPrice;
        amounts[0] = Storage.slot().borrowOrders[Storage.slot().maturity].getNodeTotalAmount(
            unitPrice
        );
        quantities[0] = Storage.slot().borrowOrders[Storage.slot().maturity].getNodeCount(
            unitPrice
        );

        for (uint256 i = 1; i < unitPrices.length; i++) {
            if (unitPrice == 0) {
                break;
            }

            unitPrice = Storage.slot().borrowOrders[Storage.slot().maturity].next(unitPrice);
            unitPrices[i] = unitPrice;
            amounts[i] = Storage.slot().borrowOrders[Storage.slot().maturity].getNodeTotalAmount(
                unitPrice
            );
            quantities[i] = Storage.slot().borrowOrders[Storage.slot().maturity].getNodeCount(
                unitPrice
            );
        }
    }

    function getOrder(uint48 _orderId)
        external
        view
        returns (
            ProtocolTypes.Side side,
            uint256 unitPrice,
            uint256 maturity,
            address maker,
            uint256 amount,
            uint256 timestamp,
            bool isPreOrder
        )
    {
        MarketOrder memory marketOrder = Storage.slot().orders[_orderId];

        OrderItem memory orderItem;
        if (marketOrder.side == ProtocolTypes.Side.LEND) {
            orderItem = Storage.slot().lendOrders[marketOrder.maturity].getOrderById(
                marketOrder.unitPrice,
                _orderId
            );
        } else {
            orderItem = Storage.slot().borrowOrders[marketOrder.maturity].getOrderById(
                marketOrder.unitPrice,
                _orderId
            );
        }

        if (orderItem.maker != address(0)) {
            side = marketOrder.side;
            unitPrice = marketOrder.unitPrice;
            maturity = marketOrder.maturity;
            maker = orderItem.maker;
            amount = orderItem.amount;
            timestamp = orderItem.timestamp;
            isPreOrder = Storage.slot().isPreOrder[_orderId];
        }
    }

    function getTotalAmountFromLendOrders(address _user)
        external
        view
        returns (
            uint256 activeAmount,
            uint256 inactiveAmount,
            uint256 inactiveFutureValue,
            uint256 maturity
        )
    {
        (uint48[] memory activeOrderIds, uint48[] memory inActiveOrderIds) = getLendOrderIds(_user);
        maturity = Storage.slot().userCurrentMaturities[_user];

        for (uint256 i = 0; i < activeOrderIds.length; i++) {
            MarketOrder memory marketOrder = Storage.slot().orders[activeOrderIds[i]];
            // Sum future values in the current maturity.
            // If the market is rotated and maturity is updated, it will be 0 by treating it
            // as an order canceled in the past market.
            OrderItem memory orderItem = Storage
                .slot()
                .lendOrders[Storage.slot().maturity]
                .getOrderById(marketOrder.unitPrice, activeOrderIds[i]);
            activeAmount += orderItem.amount;
        }

        for (uint256 i = 0; i < inActiveOrderIds.length; i++) {
            // Sum future values in the maturity of orders.
            // It will be the future value when the order is created, even if the market is rotated
            // and maturity is updated.
            (uint256 presentValue, uint256 futureValue) = _getLendOrderAmounts(inActiveOrderIds[i]);
            inactiveAmount += presentValue;
            inactiveFutureValue += futureValue;
        }
    }

    function getTotalAmountFromBorrowOrders(address _user)
        external
        view
        returns (
            uint256 activeAmount,
            uint256 inactiveAmount,
            uint256 inactiveFutureValue,
            uint256 maturity
        )
    {
        (uint48[] memory activeOrderIds, uint48[] memory inActiveOrderIds) = getBorrowOrderIds(
            _user
        );
        maturity = Storage.slot().userCurrentMaturities[_user];

        for (uint256 i = 0; i < activeOrderIds.length; i++) {
            MarketOrder memory marketOrder = Storage.slot().orders[activeOrderIds[i]];
            // Sum future values in the current maturity.
            // If the market is rotated and maturity is updated, it will be 0 by treating it
            // as an order canceled in the past market.
            OrderItem memory orderItem = Storage
                .slot()
                .borrowOrders[Storage.slot().maturity]
                .getOrderById(marketOrder.unitPrice, activeOrderIds[i]);
            activeAmount += orderItem.amount;
        }

        maturity = Storage.slot().userCurrentMaturities[_user];

        for (uint256 i = 0; i < inActiveOrderIds.length; i++) {
            // Sum future values in the maturity of orders
            // It will be the future value when the order is created, even if the market is rotated
            // and maturity is updated.
            (uint256 presentValue, uint256 futureValue) = _getBorrowOrderAmounts(
                inActiveOrderIds[i]
            );
            inactiveAmount += presentValue;
            inactiveFutureValue += futureValue;
        }
    }

    function getLendOrderIds(address _user)
        public
        view
        returns (uint48[] memory activeOrderIds, uint48[] memory inActiveOrderIds)
    {
        uint256 activeOrderCount = 0;
        uint256 inActiveOrderCount = 0;
        bool isPastMaturity = Storage.slot().userCurrentMaturities[_user] !=
            Storage.slot().maturity;

        activeOrderIds = new uint48[](
            isPastMaturity ? 0 : Storage.slot().activeLendOrderIds[_user].length
        );
        inActiveOrderIds = new uint48[](Storage.slot().activeLendOrderIds[_user].length);

        for (uint256 i = 0; i < Storage.slot().activeLendOrderIds[_user].length; i++) {
            uint48 orderId = Storage.slot().activeLendOrderIds[_user][i];
            MarketOrder memory marketOrder = Storage.slot().orders[orderId];

            if (
                !Storage
                    .slot()
                    .lendOrders[Storage.slot().userCurrentMaturities[_user]]
                    .isActiveOrderId(marketOrder.unitPrice, orderId)
            ) {
                inActiveOrderCount += 1;
                inActiveOrderIds[i - activeOrderCount] = orderId;
                if (!isPastMaturity) {
                    assembly {
                        mstore(activeOrderIds, sub(mload(activeOrderIds), 1))
                    }
                }
            } else {
                if (!isPastMaturity) {
                    activeOrderCount += 1;
                    activeOrderIds[i - inActiveOrderCount] = orderId;
                }
                assembly {
                    mstore(inActiveOrderIds, sub(mload(inActiveOrderIds), 1))
                }
            }
        }
    }

    function getBorrowOrderIds(address _user)
        public
        view
        returns (uint48[] memory activeOrderIds, uint48[] memory inActiveOrderIds)
    {
        uint256 activeOrderCount = 0;
        uint256 inActiveOrderCount = 0;
        bool isPastMaturity = Storage.slot().userCurrentMaturities[_user] !=
            Storage.slot().maturity;

        activeOrderIds = new uint48[](
            isPastMaturity ? 0 : Storage.slot().activeBorrowOrderIds[_user].length
        );
        inActiveOrderIds = new uint48[](Storage.slot().activeBorrowOrderIds[_user].length);

        for (uint256 i = 0; i < Storage.slot().activeBorrowOrderIds[_user].length; i++) {
            uint48 orderId = Storage.slot().activeBorrowOrderIds[_user][i];
            MarketOrder memory marketOrder = Storage.slot().orders[orderId];

            if (
                !Storage
                    .slot()
                    .borrowOrders[Storage.slot().userCurrentMaturities[_user]]
                    .isActiveOrderId(marketOrder.unitPrice, orderId)
            ) {
                inActiveOrderCount += 1;
                inActiveOrderIds[i - activeOrderCount] = orderId;
                if (!isPastMaturity) {
                    assembly {
                        mstore(activeOrderIds, sub(mload(activeOrderIds), 1))
                    }
                }
            } else {
                activeOrderCount += 1;
                if (!isPastMaturity) {
                    activeOrderIds[i - inActiveOrderCount] = orderId;
                }
                assembly {
                    mstore(inActiveOrderIds, sub(mload(inActiveOrderIds), 1))
                }
            }
        }
    }

    function estimateFilledAmount(ProtocolTypes.Side _side, uint256 _futureValue)
        external
        view
        returns (uint256 amount)
    {
        if (_side == ProtocolTypes.Side.BORROW) {
            return
                Storage.slot().lendOrders[Storage.slot().maturity].estimateDroppedAmountFromRight(
                    _futureValue
                );
        } else {
            return
                Storage.slot().borrowOrders[Storage.slot().maturity].estimateDroppedAmountFromLeft(
                    _futureValue
                );
        }
    }

    function insertOrder(
        ProtocolTypes.Side _side,
        address _user,
        uint256 _amount,
        uint256 _unitPrice
    ) external returns (uint48 orderId) {
        orderId = _nextOrderId();
        Storage.slot().orders[orderId] = MarketOrder(
            _side,
            _unitPrice,
            Storage.slot().maturity,
            block.timestamp
        );

        if (_side == ProtocolTypes.Side.LEND) {
            Storage.slot().lendOrders[Storage.slot().maturity].insertOrder(
                _unitPrice,
                orderId,
                _user,
                _amount
            );
            Storage.slot().activeLendOrderIds[_user].push(orderId);
        } else if (_side == ProtocolTypes.Side.BORROW) {
            Storage.slot().borrowOrders[Storage.slot().maturity].insertOrder(
                _unitPrice,
                orderId,
                _user,
                _amount
            );
            Storage.slot().activeBorrowOrderIds[_user].push(orderId);
        }
    }

    function dropOrders(
        ProtocolTypes.Side _side,
        uint256 _amount,
        uint256 _futureValue,
        uint256 _unitPrice
    )
        external
        returns (
            uint256 filledUnitPrice,
            uint256 filledAmount,
            uint256 filledFutureValue,
            uint48 partiallyFilledOrderId,
            address partiallyFilledMaker,
            uint256 partiallyFilledAmount,
            uint256 partiallyFilledFutureValue,
            uint256 remainingAmount
        )
    {
        PartiallyFilledOrder memory partiallyFilledOrder;

        if (_side == ProtocolTypes.Side.BORROW) {
            (
                filledUnitPrice,
                filledAmount,
                filledFutureValue,
                remainingAmount,
                partiallyFilledOrder
            ) = Storage.slot().lendOrders[Storage.slot().maturity].dropRight(
                _amount,
                _unitPrice,
                _futureValue
            );
        } else if (_side == ProtocolTypes.Side.LEND) {
            (
                filledUnitPrice,
                filledAmount,
                filledFutureValue,
                remainingAmount,
                partiallyFilledOrder
            ) = Storage.slot().borrowOrders[Storage.slot().maturity].dropLeft(
                _amount,
                _unitPrice,
                _futureValue
            );
        }

        partiallyFilledOrderId = partiallyFilledOrder.orderId;
        partiallyFilledMaker = partiallyFilledOrder.maker;
        partiallyFilledAmount = partiallyFilledOrder.amount;
        partiallyFilledFutureValue = partiallyFilledOrder.futureValue;
    }

    function cleanLendOrders(address _user)
        external
        returns (
            uint48[] memory orderIds,
            uint256 activeOrderCount,
            uint256 removedFutureValue,
            uint256 removedOrderAmount
        )
    {
        (
            uint48[] memory activeLendOrderIds,
            uint48[] memory inActiveLendOrderIds
        ) = getLendOrderIds(_user);

        Storage.slot().activeLendOrderIds[_user] = activeLendOrderIds;
        activeOrderCount = activeLendOrderIds.length;
        uint256 inactiveOrderCount = inActiveLendOrderIds.length;
        orderIds = new uint48[](inactiveOrderCount);

        for (uint256 i = 0; i < inactiveOrderCount; i++) {
            (uint256 presentValue, uint256 futureValue) = _getLendOrderAmounts(
                inActiveLendOrderIds[i]
            );

            removedOrderAmount += presentValue;
            removedFutureValue += futureValue;
            orderIds[i] = inActiveLendOrderIds[i];
        }
    }

    function cleanBorrowOrders(address _user)
        external
        returns (
            uint48[] memory orderIds,
            uint256 activeOrderCount,
            uint256 removedFutureValue,
            uint256 removedOrderAmount
        )
    {
        (
            uint48[] memory activeBorrowOrderIds,
            uint48[] memory inActiveBorrowOrderIds
        ) = getBorrowOrderIds(_user);

        Storage.slot().activeBorrowOrderIds[_user] = activeBorrowOrderIds;
        activeOrderCount = activeBorrowOrderIds.length;
        uint256 inactiveOrderCount = inActiveBorrowOrderIds.length;
        orderIds = new uint48[](inactiveOrderCount);

        for (uint256 i = 0; i < inactiveOrderCount; i++) {
            (uint256 presentValue, uint256 futureValue) = _getBorrowOrderAmounts(
                inActiveBorrowOrderIds[i]
            );

            removedOrderAmount += presentValue;
            removedFutureValue += futureValue;
            orderIds[i] = inActiveBorrowOrderIds[i];
        }
    }

    function removeOrder(address _user, uint48 _orderId)
        external
        returns (
            ProtocolTypes.Side,
            uint256,
            uint256
        )
    {
        MarketOrder memory marketOrder = Storage.slot().orders[_orderId];
        uint256 removedAmount;
        if (marketOrder.side == ProtocolTypes.Side.LEND) {
            removedAmount = Storage.slot().lendOrders[Storage.slot().maturity].removeOrder(
                marketOrder.unitPrice,
                _orderId
            );
            _removeOrderIdFromOrders(Storage.slot().activeLendOrderIds[_user], _orderId);
        } else if (marketOrder.side == ProtocolTypes.Side.BORROW) {
            removedAmount = Storage.slot().borrowOrders[Storage.slot().maturity].removeOrder(
                marketOrder.unitPrice,
                _orderId
            );
            _removeOrderIdFromOrders(Storage.slot().activeBorrowOrderIds[_user], _orderId);
        }

        return (marketOrder.side, removedAmount, marketOrder.unitPrice);
    }

    function getOpeningUnitPrice()
        external
        view
        returns (
            uint256 openingUnitPrice,
            uint256 lastLendUnitPrice,
            uint256 lastBorrowUnitPrice,
            uint256 totalOffsetAmount
        )
    {
        uint256 lendUnitPrice = getHighestLendingUnitPrice();
        uint256 borrowUnitPrice = getLowestBorrowingUnitPrice();
        uint256 lendAmount = Storage.slot().lendOrders[Storage.slot().maturity].getNodeTotalAmount(
            lendUnitPrice
        );
        uint256 borrowAmount = Storage
            .slot()
            .borrowOrders[Storage.slot().maturity]
            .getNodeTotalAmount(borrowUnitPrice);

        OrderStatisticsTreeLib.Tree storage borrowOrders = Storage.slot().borrowOrders[
            Storage.slot().maturity
        ];
        OrderStatisticsTreeLib.Tree storage lendOrders = Storage.slot().lendOrders[
            Storage.slot().maturity
        ];

        // return mid price when no lending and borrowing orders overwrap
        if (borrowUnitPrice > lendUnitPrice) {
            openingUnitPrice = (lendUnitPrice + borrowUnitPrice).div(2);
            return (openingUnitPrice, 0, 0, 0);
        }

        while (borrowUnitPrice <= lendUnitPrice && borrowUnitPrice > 0 && lendUnitPrice > 0) {
            lastLendUnitPrice = lendUnitPrice;
            lastBorrowUnitPrice = borrowUnitPrice;

            if (lendAmount > borrowAmount) {
                openingUnitPrice = lendUnitPrice;
                totalOffsetAmount += borrowAmount;
                lendAmount -= borrowAmount;
                borrowUnitPrice = borrowOrders.next(borrowUnitPrice);
                borrowAmount = borrowOrders.getNodeTotalAmount(borrowUnitPrice);
            } else if (lendAmount < borrowAmount) {
                openingUnitPrice = borrowUnitPrice;
                totalOffsetAmount += lendAmount;
                borrowAmount -= lendAmount;
                lendUnitPrice = lendOrders.prev(lendUnitPrice);
                lendAmount = lendOrders.getNodeTotalAmount(lendUnitPrice);
            } else {
                openingUnitPrice = (lendUnitPrice + borrowUnitPrice).div(2);
                totalOffsetAmount += lendAmount;
                lendUnitPrice = lendOrders.prev(lendUnitPrice);
                borrowUnitPrice = borrowOrders.next(borrowUnitPrice);
                lendAmount = lendOrders.getNodeTotalAmount(lendUnitPrice);
                borrowAmount = borrowOrders.getNodeTotalAmount(borrowUnitPrice);
            }
        }
    }

    function checkCircuitBreakerThreshold(
        ProtocolTypes.Side _side,
        uint256 _unitPrice,
        uint256 _circuitBreakerLimitRange
    )
        external
        returns (
            bool isFilled,
            uint256 executedUnitPrice,
            bool ignoreRemainingAmount
        )
    {
        require(_circuitBreakerLimitRange < Constants.PCT_DIGIT, "CB limit can not be so high");
        uint256 cbThresholdUnitPrice = Storage.slot().circuitBreakerThresholdUnitPrices[
            block.number
        ][_side];
        bool isLend = _side == ProtocolTypes.Side.LEND;
        bool orderExists;
        uint256 bestUnitPrice;

        if (isLend) {
            bestUnitPrice = Storage.slot().borrowOrders[Storage.slot().maturity].first();
            orderExists = bestUnitPrice != 0;

            if (orderExists && cbThresholdUnitPrice == 0) {
                cbThresholdUnitPrice = _getLendCircuitBreakerThreshold(
                    _circuitBreakerLimitRange,
                    bestUnitPrice
                );
                Storage.slot().circuitBreakerThresholdUnitPrices[block.number][
                        _side
                    ] = cbThresholdUnitPrice;
            }
        } else {
            bestUnitPrice = Storage.slot().lendOrders[Storage.slot().maturity].last();
            orderExists = bestUnitPrice != 0;

            if (orderExists && cbThresholdUnitPrice == 0) {
                cbThresholdUnitPrice = _getBorrowCircuitBreakerThreshold(
                    _circuitBreakerLimitRange,
                    bestUnitPrice
                );

                Storage.slot().circuitBreakerThresholdUnitPrices[block.number][
                        _side
                    ] = cbThresholdUnitPrice;
            }
        }

        if (_unitPrice == 0 && !orderExists) revert("Order not found");

        if (
            _unitPrice == 0 ||
            (orderExists &&
                ((isLend && _unitPrice > cbThresholdUnitPrice) ||
                    (!isLend && _unitPrice < cbThresholdUnitPrice)))
        ) {
            executedUnitPrice = cbThresholdUnitPrice;
            ignoreRemainingAmount = true;
        } else {
            executedUnitPrice = _unitPrice;
            ignoreRemainingAmount = false;
        }

        isFilled = isLend
            ? (bestUnitPrice == 0 ? Constants.PRICE_DIGIT : bestUnitPrice) <= executedUnitPrice
            : bestUnitPrice >= executedUnitPrice;
    }

    function _getBorrowCircuitBreakerThreshold(
        uint256 _circuitBreakerLimitRange,
        uint256 _unitPrice
    ) internal pure returns (uint256 cbThresholdUnitPrice) {
        // NOTE: Formula of circuit breaker threshold for borrow orders:
        // cbThreshold = 100 / (1 + (100 / price - 1) * (1 + range))
        uint256 numerator = _unitPrice * Constants.PRICE_DIGIT * Constants.PCT_DIGIT;
        uint256 denominator = _unitPrice *
            Constants.PCT_DIGIT +
            (Constants.PRICE_DIGIT - _unitPrice) *
            (Constants.PCT_DIGIT + _circuitBreakerLimitRange);
        cbThresholdUnitPrice = numerator.div(denominator);

        if (_unitPrice > cbThresholdUnitPrice + Constants.MAXIMUM_CIRCUIT_BREAKER_THRESHOLD) {
            cbThresholdUnitPrice = _unitPrice - Constants.MAXIMUM_CIRCUIT_BREAKER_THRESHOLD;
        } else if (
            _unitPrice < cbThresholdUnitPrice + Constants.MINIMUM_CIRCUIT_BREAKER_THRESHOLD
        ) {
            cbThresholdUnitPrice = _unitPrice > Constants.MINIMUM_CIRCUIT_BREAKER_THRESHOLD
                ? _unitPrice - Constants.MINIMUM_CIRCUIT_BREAKER_THRESHOLD
                : 1;
        }
    }

    function _getLendCircuitBreakerThreshold(uint256 _circuitBreakerLimitRange, uint256 _unitPrice)
        internal
        pure
        returns (uint256 cbThresholdUnitPrice)
    {
        // NOTE: Formula of circuit breaker threshold for lend orders:
        // cbThreshold = 100 / (1 + (100 / price - 1) * (1 - range))
        uint256 num = _unitPrice * Constants.PRICE_DIGIT * Constants.PCT_DIGIT;
        uint256 den = _unitPrice *
            Constants.PCT_DIGIT +
            (Constants.PRICE_DIGIT - _unitPrice) *
            (Constants.PCT_DIGIT - _circuitBreakerLimitRange);
        cbThresholdUnitPrice = num.div(den);

        if (cbThresholdUnitPrice > _unitPrice + Constants.MAXIMUM_CIRCUIT_BREAKER_THRESHOLD) {
            cbThresholdUnitPrice = _unitPrice + Constants.MAXIMUM_CIRCUIT_BREAKER_THRESHOLD;
        } else if (
            cbThresholdUnitPrice < _unitPrice + Constants.MINIMUM_CIRCUIT_BREAKER_THRESHOLD
        ) {
            cbThresholdUnitPrice = _unitPrice + Constants.MINIMUM_CIRCUIT_BREAKER_THRESHOLD <=
                Constants.PRICE_DIGIT
                ? _unitPrice + Constants.MINIMUM_CIRCUIT_BREAKER_THRESHOLD
                : Constants.PRICE_DIGIT;
        }
    }

    /**
     * @notice Increases and returns id of last order in order book.
     * @return The new order id
     */
    function _nextOrderId() internal returns (uint48) {
        Storage.slot().lastOrderId++;
        return Storage.slot().lastOrderId;
    }

    function _removeOrderIdFromOrders(uint48[] storage orders, uint256 orderId) internal {
        uint256 lastOrderIndex = orders.length - 1;
        for (uint256 i = 0; i <= lastOrderIndex; i++) {
            if (orders[i] == orderId) {
                if (i != lastOrderIndex) {
                    uint48 lastOrderId = orders[lastOrderIndex];
                    orders[i] = lastOrderId;
                }

                orders.pop();
                break;
            }
        }
    }

    function _getLendOrderAmounts(uint48 _orderId)
        internal
        view
        returns (uint256 presentValue, uint256 futureValue)
    {
        MarketOrder memory marketOrder = Storage.slot().orders[_orderId];
        OrderItem memory orderItem = Storage.slot().lendOrders[marketOrder.maturity].getOrderById(
            marketOrder.unitPrice,
            _orderId
        );
        uint256 unitPrice = marketOrder.unitPrice;

        if (Storage.slot().isPreOrder[_orderId]) {
            uint256 openingUnitPrice = Storage.slot().openingUnitPrices[marketOrder.maturity];
            uint256 lastLendUnitPrice = Storage
                .slot()
                .itayoseLogs[marketOrder.maturity]
                .lastLendUnitPrice;
            unitPrice = lastLendUnitPrice <= unitPrice ? openingUnitPrice : unitPrice;
        }

        presentValue = orderItem.amount;
        futureValue = (orderItem.amount * Constants.PRICE_DIGIT).div(unitPrice);
    }

    function _getBorrowOrderAmounts(uint48 _orderId)
        internal
        view
        returns (uint256 presentValue, uint256 futureValue)
    {
        MarketOrder memory marketOrder = Storage.slot().orders[_orderId];
        OrderItem memory orderItem = Storage.slot().borrowOrders[marketOrder.maturity].getOrderById(
            marketOrder.unitPrice,
            _orderId
        );
        uint256 unitPrice = marketOrder.unitPrice;

        if (Storage.slot().isPreOrder[_orderId]) {
            uint256 openingUnitPrice = Storage.slot().openingUnitPrices[marketOrder.maturity];
            uint256 lastBorrowUnitPrice = Storage
                .slot()
                .itayoseLogs[marketOrder.maturity]
                .lastBorrowUnitPrice;
            unitPrice = lastBorrowUnitPrice >= unitPrice ? openingUnitPrice : unitPrice;
        }

        presentValue = orderItem.amount;
        futureValue = (orderItem.amount * Constants.PRICE_DIGIT).div(unitPrice);
    }
}

contracts/mocks/LendingMarketCaller.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import {IBeaconProxyController} from "../protocol/interfaces/IBeaconProxyController.sol";
import {ILendingMarket} from "../protocol/interfaces/ILendingMarket.sol";
import {ProtocolTypes} from "../protocol/types/ProtocolTypes.sol";

contract LendingMarketCaller {
    IBeaconProxyController public beaconProxyController;
    address[] public lendingMarkets;

    constructor(address _beaconProxyController) {
        beaconProxyController = IBeaconProxyController(_beaconProxyController);
    }

    function getLendingMarkets() external view returns (address[] memory) {
        return lendingMarkets;
    }

    function deployLendingMarket(
        bytes32 _ccy,
        uint256 _maturity,
        uint256 _openingDate
    ) external {
        address lendingMarket = beaconProxyController.deployLendingMarket(
            _ccy,
            _maturity,
            _openingDate
        );
        lendingMarkets.push(lendingMarket);
    }

    function openMarket(
        uint256 _maturity,
        uint256 _openingDate,
        uint256 _index
    ) external {
        ILendingMarket(lendingMarkets[_index]).openMarket(_maturity, _openingDate);
    }

    function createOrder(
        ProtocolTypes.Side _side,
        uint256 _amount,
        uint256 _unitPrice,
        uint256 _circuitBreakerLimitRange,
        uint256 _index
    ) external {
        ILendingMarket(lendingMarkets[_index]).createOrder(
            _side,
            msg.sender,
            _amount,
            _unitPrice,
            _circuitBreakerLimitRange
        );
    }

    function createPreOrder(
        ProtocolTypes.Side _side,
        uint256 _amount,
        uint256 _unitPrice,
        uint256 _index
    ) external {
        ILendingMarket(lendingMarkets[_index]).createPreOrder(
            _side,
            msg.sender,
            _amount,
            _unitPrice
        );
    }

    function unwind(
        ProtocolTypes.Side _side,
        uint256 _futureValue,
        uint256 _circuitBreakerLimitRange,
        uint256 _index
    ) external {
        ILendingMarket(lendingMarkets[_index]).unwind(
            _side,
            msg.sender,
            _futureValue,
            _circuitBreakerLimitRange
        );
    }

    function executeItayoseCall(uint256 _index)
        external
        returns (
            uint256 openingUnitPrice,
            uint256 totalOffsetAmount,
            uint256 openingDate,
            ILendingMarket.PartiallyFilledOrder memory lendingOrder,
            ILendingMarket.PartiallyFilledOrder memory borrowingOrder
        )
    {
        return ILendingMarket(lendingMarkets[_index]).executeItayoseCall();
    }
}

contracts/protocol/BeaconProxyController.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import {UpgradeableBeacon} from "./utils/UpgradeableBeacon.sol";
// interfaces
import {IBeaconProxyController} from "./interfaces/IBeaconProxyController.sol";
import {IProxyController} from "./interfaces/IProxyController.sol";
// libraries
import {AddressResolverLib} from "./libraries/AddressResolverLib.sol";
import {BeaconContracts, Contracts} from "./libraries/Contracts.sol";
// mixins
import {MixinAddressResolver} from "./mixins/MixinAddressResolver.sol";
// utils
import {Ownable} from "./utils/Ownable.sol";
import {Proxyable} from "./utils/Proxyable.sol";
import {UpgradeabilityBeaconProxy} from "./utils/UpgradeabilityBeaconProxy.sol";
// storages
import {BeaconProxyControllerStorage as Storage} from "./storages/BeaconProxyControllerStorage.sol";

/**
 * @notice Implements the management of beacon proxy contracts.
 *
 * All beacon proxy contracts are deployed from this contract.
 * This contract is also used to update the beacon proxy implementation.
 */
contract BeaconProxyController is IBeaconProxyController, MixinAddressResolver, Ownable, Proxyable {
    /**
     * @notice Modifier to make a function callable only by LendingMarketController contract.
     */
    modifier onlyLendingMarketController() {
        require(
            getAddress(Contracts.LENDING_MARKET_CONTROLLER) == msg.sender,
            "Caller is not the LendingMarketController"
        );
        _;
    }

    /**
     * @notice Initializes the contract.
     * @dev Function is invoked by the proxy contract when the contract is added to the ProxyController.
     * @param _owner The address of the contract owner
     * @param _resolver The address of the Address Resolver contract
     */
    function initialize(address _owner, address _resolver) public initializer onlyProxy {
        _transferOwnership(_owner);
        registerAddressResolver(_resolver);
    }

    // @inheritdoc MixinAddressResolver
    function requiredContracts() public pure override returns (bytes32[] memory contracts) {
        contracts = new bytes32[](1);
        contracts[0] = Contracts.LENDING_MARKET_CONTROLLER;
    }

    /**
     * @notice Gets the beacon proxy address to the selected name.
     * @param beaconName The cache name of the beacon proxy
     * @return beaconProxyAddress The beacon proxy address
     */
    function getBeaconProxyAddress(bytes32 beaconName)
        external
        view
        override
        returns (address beaconProxyAddress)
    {
        beaconProxyAddress = Storage.slot().registeredBeaconProxies[beaconName];
        UpgradeabilityBeaconProxy beaconProxy = UpgradeabilityBeaconProxy(
            payable(beaconProxyAddress)
        );

        require(beaconProxy.implementation() != address(0), "Beacon proxy address not found");
    }

    /**
     * @notice Sets the implementation contract of FutureValueVault
     * @param newImpl The address of implementation contract
     */
    function setFutureValueVaultImpl(address newImpl) external override onlyOwner {
        _updateBeaconImpl(BeaconContracts.FUTURE_VALUE_VAULT, newImpl);
    }

    /**
     * @notice Sets the implementation contract of LendingMarket
     * @param newImpl The address of implementation contract
     */
    function setLendingMarketImpl(address newImpl) external override onlyOwner {
        _updateBeaconImpl(BeaconContracts.LENDING_MARKET, newImpl);
    }

    /**
     * @notice Deploys new FutureValueVault
     * @notice Reverts on deployment market with existing currency and term
     */
    function deployFutureValueVault()
        external
        override
        onlyLendingMarketController
        returns (address futureValue)
    {
        bytes memory data = abi.encodeWithSignature("initialize(address)", address(resolver()));
        futureValue = _createProxy(BeaconContracts.FUTURE_VALUE_VAULT, data);
    }

    /**
     * @notice Deploys new LendingMarket
     * @param _ccy Main currency for new lending market
     * @param _maturity The initial maturity of the market
     * @param _openingDate The timestamp when the market opens
     * @return market The proxy contract address of created lending market
     */
    function deployLendingMarket(
        bytes32 _ccy,
        uint256 _maturity,
        uint256 _openingDate
    ) external override onlyLendingMarketController returns (address market) {
        bytes memory data = abi.encodeWithSignature(
            "initialize(address,bytes32,uint256,uint256)",
            address(resolver()),
            _ccy,
            _maturity,
            _openingDate
        );
        market = _createProxy(BeaconContracts.LENDING_MARKET, data);
    }

    function _createProxy(bytes32 beaconName, bytes memory data) internal returns (address) {
        address beaconProxyAddress = Storage.slot().registeredBeaconProxies[beaconName];
        require(beaconProxyAddress != address(0), "Beacon proxy is empty");

        return address(new UpgradeabilityBeaconProxy(beaconProxyAddress, data));
    }

    function _updateBeaconImpl(bytes32 name, address newAddress)
        internal
        returns (address beaconProxyAddress)
    {
        beaconProxyAddress = Storage.slot().registeredBeaconProxies[name];
        UpgradeableBeacon beacon;

        if (beaconProxyAddress == address(0)) {
            beacon = new UpgradeableBeacon(newAddress);

            Storage.slot().registeredBeaconProxies[name] = beaconProxyAddress = address(beacon);

            emit BeaconProxyCreated(name, beaconProxyAddress, newAddress);
        } else {
            beacon = UpgradeableBeacon(beaconProxyAddress);
            address oldAddress = beacon.implementation();
            beacon.upgradeTo(newAddress);
            emit BeaconProxyUpdated(name, beaconProxyAddress, newAddress, oldAddress);
        }
    }
}

contracts/protocol/utils/UpgradeableBeac.content:
// SPDX-License-Identifier: MIT
// Based on OpenZeppelin Contracts v4.4.1 (proxy/beacon/UpgradeableBeacon.sol)

pragma solidity ^0.8.0;

import "./Ownable.sol";
import "../../dependencies/openzeppelin/contracts/proxy/beacon/IBeacon.sol";
import "../../dependencies/openzeppelin/contracts/utils/Address.sol";

/**
 * @dev This contract is used in conjunction with one or more instances of {BeaconProxy} to determine their
 * implementation contract, which is where they will delegate all function calls.
 *
 * An owner is able to change the implementation the beacon points to, thus upgrading the proxies that use this beacon.
 */
contract UpgradeableBeacon is IBeacon, Ownable {
    address private _implementation;

    /**
     * @dev Emitted when the implementation returned by the beacon is changed.
     */
    event Upgraded(address indexed implementation);

    /**
     * @dev Sets the address of the initial implementation, and the deployer account as the owner who can upgrade the
     * beacon.
     */
    constructor(address implementation_) {
        _transferOwnership(msg.sender);
        _setImplementation(implementation_);
    }

    /**
     * @dev Returns the current implementation address.
     */
    function implementation() public view virtual override returns (address) {
        return _implementation;
    }

    /**
