contracts/protocol/ProxyController.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import {Ownable} from "./utils/Ownable.sol";
import {IAddressResolver} from "./interfaces/IAddressResolver.sol";
import {IProxyController} from "./interfaces/IProxyController.sol";
import {Contracts} from "./libraries/Contracts.sol";
import {UpgradeabilityProxy} from "./utils/UpgradeabilityProxy.sol";

/**
 * @notice Implements the management of proxy contracts.
 *
 * All proxy contracts are deployed from this contract.
 * This contract is also used to update the proxy implementation.
 */

contract ProxyController is IProxyController, Ownable {
    IAddressResolver private resolver;
    bytes32 private constant ADDRESS_RESOLVER = "AddressResolver";

    /**
     * @notice Contract constructor function.
     * @param _resolver The address of the Address Resolver contract
     *
     * @dev Set a proxy contract address of AddressResolver if it already exists.
     * If not, set zero address here and call `setAddressResolverImpl` using the implementation
     * address of AddressResolver to create a proxy contract.
     */
    constructor(address _resolver) {
        _transferOwnership(msg.sender);
        if (_resolver != address(0)) {
            UpgradeabilityProxy proxy = UpgradeabilityProxy(payable(_resolver));
            require(proxy.implementation() != address(0), "Proxy address not found");
            resolver = IAddressResolver(_resolver);
        }
    }

    /**
     * @notice Gets the proxy address of AddressResolver
     * @return The contract address of AddressResolver
     */
    function getAddressResolverAddress() public view returns (address) {
        return (address(resolver));
    }

    /**
     * @notice Gets the proxy address fro selected name
     * @param name The cache name of the contract
     * @return proxyAddress The proxy address for selected name
     */
    function getAddress(bytes32 name) public view returns (address proxyAddress) {
        proxyAddress = resolver.getAddress(name, "Address not found");
        UpgradeabilityProxy proxy = UpgradeabilityProxy(payable(proxyAddress));

        require(proxy.implementation() != address(0), "Proxy address not found");
    }

    /**
     * @notice Sets the implementation contract of AddressResolver
     * @param newImpl The address of implementation contract
     */
    function setAddressResolverImpl(address newImpl) external onlyOwner {
        bytes memory data = abi.encodeWithSignature("initialize(address)", msg.sender);
        address proxyAddress = _updateImpl(ADDRESS_RESOLVER, newImpl, data);
        resolver = IAddressResolver(proxyAddress);
    }

    /**
     * @notice Sets the implementation contract of CurrencyController
     * @param newImpl The address of implementation contract
     */
    function setBeaconProxyControllerImpl(address newImpl) external onlyOwner {
        bytes memory data = abi.encodeWithSignature(
            "initialize(address,address)",
            msg.sender,
            resolver
        );
        _updateImpl(Contracts.BEACON_PROXY_CONTROLLER, newImpl, data);
    }

    /**
     * @notice  Sets the implementation contract of TokenVault
     * @param newImpl The address of implementation contract
     * @param liquidationThresholdRate  The rate used as the auto liquidation threshold
     * @param liquidationProtocolFeeRate The liquidation fee rate received by protocol
     * @param liquidatorFeeRate The liquidation fee rate received by liquidators
     * @param WETH9 The address of WETH
     */
    function setTokenVaultImpl(
        address newImpl,
        uint256 liquidationThresholdRate,
        uint256 liquidationProtocolFeeRate,
        uint256 liquidatorFeeRate,
        address WETH9
    ) external onlyOwner {
        bytes memory data = abi.encodeWithSignature(
            "initialize(address,address,uint256,uint256,uint256,address)",
            msg.sender,
            resolver,
            liquidationThresholdRate,
            liquidationProtocolFeeRate,
            liquidatorFeeRate,
            WETH9
        );
        _updateImpl(Contracts.TOKEN_VAULT, newImpl, data);
    }

    /**
     * @notice Sets the implementation contract of CurrencyController
     * @param newImpl The address of implementation contract
     * @param baseCcy The base currency name in bytes32
     */
    function setCurrencyControllerImpl(address newImpl, bytes32 baseCcy) external onlyOwner {
        bytes memory data = abi.encodeWithSignature(
            "initialize(address,bytes32)",
            msg.sender,
            baseCcy
        );
        _updateImpl(Contracts.CURRENCY_CONTROLLER, newImpl, data);
    }

    /**
     * @notice Sets the implementation contract of GenesisValueVault
     * @param newImpl The address of implementation contract
     */
    function setGenesisValueVaultImpl(address newImpl) external onlyOwner {
        bytes memory data = abi.encodeWithSignature("initialize(address)", resolver);
        _updateImpl(Contracts.GENESIS_VALUE_VAULT, newImpl, data);
    }

    /**
     * @notice Sets the implementation contract of LendingMarketController
     * @param newImpl The address of implementation contract
     * @param observationPeriod The observation period to calculate the volume-weighted average price of transactions
     */
    function setLendingMarketControllerImpl(
        address newImpl,
        uint256 marketBasePeriod,
        uint256 observationPeriod
    ) external onlyOwner {
        bytes memory data = abi.encodeWithSignature(
            "initialize(address,address,uint256,uint256)",
            msg.sender,
            resolver,
            marketBasePeriod,
            observationPeriod
        );
        _updateImpl(Contracts.LENDING_MARKET_CONTROLLER, newImpl, data);
    }

    /**
     * @notice Sets the implementation contract of ReserveFund
     * @param newImpl The address of implementation contract
     * @param WETH9 The address of WETH
     */
    function setReserveFundImpl(address newImpl, address WETH9) external onlyOwner {
        bytes memory data = abi.encodeWithSignature(
            "initialize(address,address,address)",
            msg.sender,
            resolver,
            WETH9
        );
        _updateImpl(Contracts.RESERVE_FUND, newImpl, data);
    }

    /**
     * @notice Updates admin addresses of proxy contract
     * @param newAdmin The address of new admin
     * @param destinations The destination contract addresses
     */
    function changeProxyAdmins(address newAdmin, address[] calldata destinations)
        external
        onlyOwner
    {
        for (uint256 i = 0; i < destinations.length; i++) {
            UpgradeabilityProxy proxy = UpgradeabilityProxy(payable(destinations[i]));
            proxy.changeAdmin(newAdmin);
        }
    }

    /**
     * @notice Updates the implementation contract of specified contract
     * The first time the contract address is set, `UpgradeabilityProxy` is created.
     * From the second time, the contract address set in the created `UpgradeabilityProxy`
     * will be updated.
     *
     * @param name The cache name of the contract
     * @param newAddress The address of implementation contract
     * @param data the data in a delegate call to a specified function
     */
    function _updateImpl(
        bytes32 name,
        address newAddress,
        bytes memory data
    ) internal returns (address proxyAddress) {
        proxyAddress = _getAddress(name);
        UpgradeabilityProxy proxy;

        if (proxyAddress == address(0)) {
            proxy = new UpgradeabilityProxy(payable(newAddress), data);
            proxyAddress = address(proxy);

            emit ProxyCreated(name, proxyAddress, newAddress);
        } else {
            proxy = UpgradeabilityProxy(payable(proxyAddress));
            address oldAddress = proxy.implementation();
            proxy.upgradeTo(newAddress);
            emit ProxyUpdated(name, proxyAddress, newAddress, oldAddress);
        }
    }

    function _getAddress(bytes32 name) internal view returns (address) {
        if (name == ADDRESS_RESOLVER) {
            return address(resolver);
        } else {
            return resolver.getAddress(name);
        }
    }
}

contracts/protocol/FutureValueVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import {SafeCast} from "../dependencies/openzeppelin/contracts/utils/math/SafeCast.sol";
import {FutureValueVaultStorage as Storage} from "./storages/FutureValueVaultStorage.sol";
// interfaces
import {IFutureValueVault} from "./interfaces/IFutureValueVault.sol";
// libraries
import {Contracts} from "./libraries/Contracts.sol";
// mixins
import {MixinAddressResolver} from "./mixins/MixinAddressResolver.sol";
// types
import {ProtocolTypes} from "./types/ProtocolTypes.sol";
// utils
import {Proxyable} from "./utils/Proxyable.sol";

/**
 * @notice Implements the management of the future value as an amount for Lending deals in each currency.
 */
contract FutureValueVault is IFutureValueVault, MixinAddressResolver, Proxyable {
    using SafeCast for uint256;
    using SafeCast for int256;

    /**
     * @notice Initializes the contract.
     * @dev Function is invoked by the proxy contract when the contract is added to the ProxyController.
     * @param _resolver The address of the Address Resolver contract
     */
    function initialize(address _resolver) external initializer onlyBeacon {
        registerAddressResolver(_resolver);
        buildCache();
    }

    // @inheritdoc MixinAddressResolver
    function requiredContracts() public pure override returns (bytes32[] memory contracts) {
        contracts = new bytes32[](1);
        contracts[0] = Contracts.LENDING_MARKET_CONTROLLER;
    }

    // @inheritdoc MixinAddressResolver
    function acceptedContracts() public pure override returns (bytes32[] memory contracts) {
        contracts = new bytes32[](1);
        contracts[0] = Contracts.LENDING_MARKET_CONTROLLER;
    }

    /**
     * @notice Gets the total supply.
     * @param _maturity The maturity of the market
     */
    function getTotalSupply(uint256 _maturity) external view override returns (uint256) {
        return Storage.slot().totalSupply[_maturity];
    }

    /**
     * @notice Gets the future value of the account.
     * @param _user User's address
     * @return futureValue The future value
     * @return maturity The maturity of the market that the future value was added
     */
    function getFutureValue(address _user)
        public
        view
        override
        returns (int256 futureValue, uint256 maturity)
    {
        return (Storage.slot().balances[_user], Storage.slot().futureValueMaturities[_user]);
    }

    /**
     * @notice Gets if the account has the future value amount in the selected maturity.
     * @param _user User's address
     * @param _maturity The maturity of the market
     * @return The boolean if the lending market is initialized or not
     */
    function hasFutureValueInPastMaturity(address _user, uint256 _maturity)
        public
        view
        override
        returns (bool)
    {
        if (Storage.slot().futureValueMaturities[_user] == _maturity) {
            return false;
        } else {
            return Storage.slot().balances[_user] != 0;
        }
    }

    /**
     * @notice Adds the future value amount for lending deals.
     * @dev Since the total supply can be determined by totaling only the amounts on one side of the order
     * when the order is fulfilled, the total supply is incremented only when the executor of the original order
     * is the taker.
     * @param _user User's address
     * @param _amount The amount to add
     * @param _maturity The maturity of the market
     * @param _isTaker The boolean if the original order is created by a taker
     */
    function addLendFutureValue(
        address _user,
        uint256 _amount,
        uint256 _maturity,
        bool _isTaker
    ) public override onlyAcceptedContracts {
        require(_user != address(0), "Add to the zero address of lender");
        require(
            !hasFutureValueInPastMaturity(_user, _maturity),
            "Lender has the future value in past maturity"
        );

        int256 previousBalance = Storage.slot().balances[_user];
        Storage.slot().futureValueMaturities[_user] = _maturity;
        Storage.slot().balances[_user] += _amount.toInt256();
        emit Transfer(address(0), _user, _amount.toInt256());

        int256 currentBalance = Storage.slot().balances[_user];
        _updateTotalSupply(_maturity, previousBalance, currentBalance, _isTaker);
    }

    /**
     * @notice Adds the future value amount for borrowing deals.
     * @dev Since the total supply can be determined by totaling only the amounts on one side of the order
     * when the order is fulfilled, the total supply is incremented only when the executor of the original order
     * is the taker.
     * @param _user User's address
     * @param _amount The amount to add
     * @param _maturity The maturity of the market
     * @param _isTaker The boolean if the original order is created by a taker
     */
    function addBorrowFutureValue(
        address _user,
        uint256 _amount,
        uint256 _maturity,
        bool _isTaker
    ) public override onlyAcceptedContracts {
        require(_user != address(0), "Add to the zero address of borrower");
        require(
            !hasFutureValueInPastMaturity(_user, _maturity),
            "Borrower has the future value in past maturity"
        );

        int256 previousBalance = Storage.slot().balances[_user];
        Storage.slot().futureValueMaturities[_user] = _maturity;
        Storage.slot().balances[_user] -= _amount.toInt256();
        emit Transfer(address(0), _user, -(_amount.toInt256()));

        int256 currentBalance = Storage.slot().balances[_user];
        _updateTotalSupply(_maturity, previousBalance, currentBalance, _isTaker);
    }

    /**
     * @notice Transfers the future value from sender to receiver.
     * @param _sender Sender's address
     * @param _receiver Receiver's address
     * @param _amount Amount of funds to sent
     * @param _maturity The maturity of the market
     */
    function transferFrom(
        address _sender,
        address _receiver,
        int256 _amount,
        uint256 _maturity
    ) external override onlyAcceptedContracts {
        require(
            !hasFutureValueInPastMaturity(_sender, _maturity),
            "Sender has the future value in past maturity"
        );
        require(
            !hasFutureValueInPastMaturity(_receiver, _maturity),
            "Receiver has the future value in past maturity"
        );

        Storage.slot().futureValueMaturities[_receiver] = _maturity;
        Storage.slot().balances[_sender] -= _amount;
        Storage.slot().balances[_receiver] += _amount;

        emit Transfer(_sender, _receiver, _amount);
    }

    /**
     * @notice Removes all future values if there is an amount in the past maturity.
     * @param _user User's address
     * @return removedAmount Removed future value amount
     * @return currentAmount Current future value amount after update
     * @return maturity Maturity of future value
     * @return isAllRemoved The boolean if the all future value amount in the selected maturity is removed
     */
    function removeFutureValue(address _user, uint256 _activeMaturity)
        external
        override
        onlyAcceptedContracts
        returns (
            int256 removedAmount,
            int256 currentAmount,
            uint256 maturity,
            bool isAllRemoved
        )
    {
        currentAmount = Storage.slot().balances[_user];

        if (Storage.slot().futureValueMaturities[_user] != _activeMaturity && currentAmount != 0) {
            removedAmount = currentAmount;
            maturity = Storage.slot().futureValueMaturities[_user];

            isAllRemoved = false;
            if (removedAmount >= 0) {
                Storage.slot().removedLendingSupply[maturity] += removedAmount.toUint256();
            } else {
                Storage.slot().removedBorrowingSupply[maturity] += (-removedAmount).toUint256();
            }

            Storage.slot().balances[_user] = 0;
            currentAmount = 0;

            emit Transfer(_user, address(0), removedAmount);
        }

        isAllRemoved =
            Storage.slot().removedLendingSupply[maturity] == Storage.slot().totalSupply[maturity] &&
            Storage.slot().removedBorrowingSupply[maturity] == Storage.slot().totalSupply[maturity];
    }

    /**
     * @notice Adds initial total supply at market opening
     * @param _maturity The maturity of the market
     * @param _amount The amount to add
     */
    function addInitialTotalSupply(uint256 _maturity, int256 _amount)
        external
        override
        onlyAcceptedContracts
    {
        require(Storage.slot().totalSupply[_maturity] == 0, "Initial total supply is not 0");
        _updateTotalSupply(_maturity, 0, _amount, true);
    }

    /**
     * @notice Resets the future value of the user
     * @param _user User's address
     */
    function resetFutureValue(address _user) external override onlyAcceptedContracts {
        int256 removedAmount = Storage.slot().balances[_user];
        if (removedAmount != 0) {
            Storage.slot().balances[_user] = 0;

            emit Transfer(_user, address(0), removedAmount);
        }
    }

    function _updateTotalSupply(
        uint256 _maturity,
        int256 _previous,
        int256 _current,
        bool _isTaker
    ) private {
        uint256 absPrevious = _previous >= 0 ? _previous.toUint256() : (-_previous).toUint256();
        uint256 absCurrent = _current >= 0 ? _current.toUint256() : (-_current).toUint256();

        // Since total supply can be calculated only by taker amount, total supply will not be increased by maker amount.
        // However, if a maker has an offset volume when cleaning up its own orders, the total supply must be reduced.
        if (absPrevious > absCurrent) {
            Storage.slot().totalSupply[_maturity] -= absPrevious - absCurrent;
        } else if (_isTaker) {
            Storage.slot().totalSupply[_maturity] += absCurrent - absPrevious;
        }
    }
}

