// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import {ISwapRouter} from "../dependencies/uniswap/contracts/ISwapRouter.sol";
// libraries
import {Contracts} from "../protocol/libraries/Contracts.sol";
import {ERC20Handler} from "../protocol/libraries/ERC20Handler.sol";
// mixins
import {MixinAddressResolver} from "../protocol/mixins/MixinAddressResolver.sol";

contract MockUniswapRouter is MixinAddressResolver {
    mapping(address => bytes32) private currencies;

    constructor(address _resolver, address _WETH9) {
        registerAddressResolver(_resolver);
        ERC20Handler.initialize(_WETH9);

        buildCache();
    }

    receive() external payable {}

    function requiredContracts() public pure override returns (bytes32[] memory contracts) {
        contracts = new bytes32[](1);
        contracts[0] = Contracts.CURRENCY_CONTROLLER;
    }

    function setToken(bytes32 ccy, address token) external {
        currencies[token] = ccy;
    }

    function exactOutputSingle(ISwapRouter.ExactOutputSingleParams calldata params)
        external
        returns (uint256 amountIn)
    {
        amountIn = currencyController().convert(
            currencies[params.tokenOut],
            currencies[params.tokenIn],
            params.amountOut
        );

        require(amountIn <= params.amountInMaximum, "Too much requested");

        ERC20Handler.safeTransferFrom(params.tokenIn, msg.sender, address(this), amountIn);
        ERC20Handler.safeTransfer(params.tokenOut, msg.sender, params.amountOut);
    }

    function exactInputSingle(ISwapRouter.ExactInputSingleParams calldata params)
        external
        payable
        returns (uint256 amountOut)
    {
        amountOut = currencyController().convert(
            currencies[params.tokenIn],
            currencies[params.tokenOut],
            params.amountIn
        );

        require(amountOut >= params.amountOutMinimum, "Too little received");

        if (ERC20Handler.baseCurrency() != params.tokenIn) {
            ERC20Handler.safeTransferFrom(
                params.tokenIn,
                msg.sender,
                address(this),
                params.amountIn
            );
        }

        if (ERC20Handler.baseCurrency() != params.tokenOut) {
            ERC20Handler.safeTransfer(params.tokenOut, msg.sender, amountOut);
        } else {
            ERC20Handler.safeTransferETH(msg.sender, amountOut);
        }
    }
}

contracts/mocks/MockUniswapQuoter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

// libraries
import {Contracts} from "../protocol/libraries/Contracts.sol";
import {ERC20Handler} from "../protocol/libraries/ERC20Handler.sol";
// mixins
import {MixinAddressResolver} from "../protocol/mixins/MixinAddressResolver.sol";

contract MockUniswapQuoter is MixinAddressResolver {
    mapping(address => bytes32) private currencies;

    constructor(address _resolver, address _WETH9) {
        registerAddressResolver(_resolver);
        ERC20Handler.initialize(_WETH9);

        buildCache();
    }

    function requiredContracts() public pure override returns (bytes32[] memory contracts) {
        contracts = new bytes32[](1);
        contracts[0] = Contracts.CURRENCY_CONTROLLER;
    }

    function setToken(bytes32 ccy, address token) external {
        currencies[token] = ccy;
    }

    function quoteExactInputSingle(
        address tokenIn,
        address tokenOut,
        uint24 fee,
        uint256 amountIn,
        uint160 sqrtPriceLimitX96
    ) external view returns (uint256 amountOut) {
        uint256 amountInBaseCurrency = currencyController().convertToBaseCurrency(
            currencies[tokenIn],
            amountIn
        );

        // NOTE: This fee and sqrtPriceLimitX96 are not intended to be used like this,
        // but are used here to adjust the amountOut value for testing purposes.
        amountOut =
            currencyController().convertFromBaseCurrency(
                currencies[tokenOut],
                amountInBaseCurrency
            ) -
            fee +
            sqrtPriceLimitX96;
    }
}

contracts/mocks/tokens/MockWETH9.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "../../protocol/interfaces/IWETH9.sol";

contract MockWETH9 is IWETH9 {
    string public name = "Wrapped Ether";
    string public symbol = "WETH";
    uint8 public decimals = 18;

    mapping(address => uint256) public override balanceOf;
    mapping(address => mapping(address => uint256)) public override allowance;

    receive() external payable {
        deposit();
    }

    function deposit() public payable override {
        balanceOf[msg.sender] += msg.value;
        emit Deposit(msg.sender, msg.value);
    }

    function withdraw(uint256 wad) public override {
        require(balanceOf[msg.sender] >= wad);
        balanceOf[msg.sender] -= wad;
        payable(msg.sender).transfer(wad);
        emit Withdrawal(msg.sender, wad);
    }

    function totalSupply() public view override returns (uint256) {
        return address(this).balance;
    }

    function approve(address guy, uint256 wad) public override returns (bool) {
        allowance[msg.sender][guy] = wad;
        emit Approval(msg.sender, guy, wad);
        return true;
    }

    function transfer(address dst, uint256 wad) public override returns (bool) {
        return transferFrom(msg.sender, dst, wad);
    }

    function transferFrom(
        address src,
        address dst,
        uint256 wad
    ) public override returns (bool) {
        require(balanceOf[src] >= wad);

        if (src != msg.sender && allowance[src][msg.sender] != type(uint256).max - 1) {
            require(allowance[src][msg.sender] >= wad);
            allowance[src][msg.sender] -= wad;
        }

        balanceOf[src] -= wad;
        balanceOf[dst] += wad;

        emit Transfer(src, dst, wad);

        return true;
    }
}

